// Mock data for now
export interface Notification {
    id: number;
    title: string;
    description: string; // mapped from message
    created_at: string; // mapped from timestamp
    is_read: boolean; // mapped from read
    type: 'info' | 'success' | 'warning' | 'error';
}

export const notificationService = {
    getNotifications: async (skip: number = 0, limit: number = 5): Promise<Notification[]> => {
        return [
            { id: 1, title: "New Task Assigned", description: "You have been assigned to 'Update Homepage'", created_at: new Date().toISOString(), is_read: false, type: 'info' },
            { id: 2, title: "System Update", description: "Maintenance scheduled for tonight", created_at: new Date(Date.now() - 86400000).toISOString(), is_read: true, type: 'warning' }
        ];
    },

    markAsRead: async (id: number) => {
        console.log(`Notification ${id} marked as read`);
        return true;
    }
};
