import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar as CalendarIcon,
  MessageSquare,
  CheckCircle,
  Clock,
  ArrowRight,
  Activity,
  Zap,
  Star,
  Shield,
  Layers,
  ChevronRight,
  Plus,
  ArrowUpRight,
  ClipboardList,
  Video,
  Users,
  Sparkles
} from "lucide-react";
import { motion } from "framer-motion";
import { taskService, Task } from "../../services/taskService";
import { meetingService } from "../../services/meetingService";
import { messageService, Chat } from "../../services/messageService";
import { userService } from "../../services/userService";
import { authService } from "../../services/authService";


interface DashboardEvent {
  id: string;
  title: string;
  time: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  timestamp: Date;
}

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [stats, setStats] = useState({
    tasksPending: 0,
    meetingsToday: 0,
    unreadMessages: 0,
    teamMembers: 0,
    tasksCompleted: 0
  });
  const [todaysEvents, setTodaysEvents] = useState<DashboardEvent[]>([]);
  const [recentTasks, setRecentTasks] = useState<Task[]>([]);

  const fetchData = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const [user, tasks, meetings, chats, users] = await Promise.all([
        authService.getCurrentUser(),
        taskService.getTasks(),
        meetingService.getAllMeetings(),
        messageService.getChats(),
        userService.getAllUsers()
      ]);

      setCurrentUser(user);

      const pendingTasks = tasks.filter(t => t.status !== "Completed");
      const completedTasks = tasks.filter(t => t.status === "Completed");
      const unreadCount = chats.reduce((acc: number, curr: Chat) => acc + (curr.unread_count || 0), 0);
      const todayStr = new Date().toLocaleDateString("en-CA");
      const todayMeetings = meetings.filter(m => new Date(m.start_time).toLocaleDateString("en-CA") === todayStr);

      setStats({
        tasksPending: pendingTasks.length,
        meetingsToday: todayMeetings.length,
        unreadMessages: unreadCount,
        teamMembers: users.length,
        tasksCompleted: completedTasks.length
      });

      const taskEvents: DashboardEvent[] = tasks
        .filter(t => t.due_date && new Date(t.due_date).toLocaleDateString("en-CA") === todayStr && t.status !== "Completed")
        .map(t => ({
          id: `task-${t.id}`,
          title: t.title,
          time: "Due Today",
          type: "task",
          status: t.status,
          details: t.priority,
          timestamp: new Date(t.created_at || new Date())
        }));

      const meetingEvents: DashboardEvent[] = todayMeetings.map(m => {
        const startDate = new Date(m.start_time);
        return {
          id: `meeting-${m.id}`,
          title: m.title,
          time: startDate.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: true }),
          type: "meeting",
          details: m.location || "Virtual HQ",
          timestamp: startDate
        };
      });

      const allEvents = [...taskEvents, ...meetingEvents].sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());
      setTodaysEvents(allEvents);
      setRecentTasks(tasks.slice(0, 5));

    } catch (error) {
      console.error("Failed to load dashboard data", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    const pollInterval = setInterval(() => {
      fetchData(false);
    }, 30000);

    return () => {
      clearInterval(pollInterval);
    };
  }, []);

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "Good morning";
    if (hour < 18) return "Good afternoon";
    return "Good evening";
  };

  if (loading) {
    return (
      <div className="min-h-[80vh] flex flex-col items-center justify-center">
        <div className="relative">
          <div className="w-16 h-16 border-4 border-indigo-100 border-t-indigo-600 rounded-full animate-spin" />
          <Sparkles className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 text-indigo-600 w-6 h-6 animate-pulse" />
        </div>
        <p className="mt-8 text-sm font-bold text-slate-400 uppercase tracking-widest animate-pulse">Synchronizing workspace</p>
      </div>
    );
  }

  return (
    <div className="space-y-10 animate-fade-in pb-12">
      {/* Welcome Hero */}
      {/* Welcome Hero - Minimalist White */}
      {/* Welcome Hero - Corporate Blue */}
      <div className="bg-blue-600 rounded-2xl p-10 lg:p-14 text-white shadow-lg relative overflow-hidden">
        {/* Subtle geometric pattern overlay */}
        <div className="absolute top-0 right-0 w-96 h-96 bg-white/5 rounded-full blur-3xl -translate-y-1/2 translate-x-1/2" />
        <div className="absolute bottom-0 left-0 w-64 h-64 bg-blue-500/30 rounded-full blur-2xl translate-y-1/2 -translate-x-1/2" />

        <div className="relative z-10 max-w-2xl">
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.2 }}
          >
            <span className="inline-flex items-center gap-2 px-3 py-1 bg-blue-700 rounded-md text-[11px] font-bold uppercase tracking-wider mb-6 text-blue-100 border border-blue-500/50">
              <Sparkles size={14} className="text-blue-200" />
              Overview
            </span>
            <h1 className="text-4xl lg:text-5xl font-bold tracking-tight mb-4">
              Welcome back, {currentUser?.name?.split(' ')[0] || 'Member'}
            </h1>
            <p className="text-lg text-blue-100 font-medium leading-relaxed mb-10 max-w-lg">
              You have <span className="text-white font-bold">{stats.tasksCompleted} tasks</span> completed this week. Check your schedule for upcoming meetings.
            </p>

            <div className="flex flex-wrap gap-4">
              <button
                onClick={() => navigate('/tasks/new')}
                className="px-6 py-3 bg-white text-blue-700 rounded-lg font-bold text-sm hover:bg-blue-50 transition-colors shadow-sm flex items-center gap-2"
              >
                <Plus size={18} strokeWidth={2.5} />
                New Task
              </button>
              <button
                onClick={() => navigate('/meetings/schedule')}
                className="px-6 py-3 bg-blue-700 text-white border border-blue-500 rounded-lg font-bold text-sm hover:bg-blue-800 transition-colors flex items-center gap-2"
              >
                <Video size={18} />
                Schedule Meeting
              </button>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Quick Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          label="Pending Tasks"
          value={stats.tasksPending}
          icon={ClipboardList}
          color="bg-blue-50 text-blue-600"
          onClick={() => navigate('/tasks')}
        />
        <StatCard
          label="Meetings Today"
          value={stats.meetingsToday}
          icon={Video}
          color="bg-blue-50 text-blue-600"
          onClick={() => navigate('/meetings')}
        />
        <StatCard
          label="Unread Messages"
          value={stats.unreadMessages}
          icon={MessageSquare}
          color="bg-blue-50 text-blue-600"
          onClick={() => navigate('/messages')}
        />
        <StatCard
          label="Team Power"
          value={stats.teamMembers}
          symbol="+"
          icon={Users}
          color="bg-blue-50 text-blue-600"
          onClick={() => navigate('/team')}
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">
        {/* Schedule Timeline */}
        <div className="lg:col-span-2 space-y-8">
          <div className="flex items-center justify-between px-4">
            <div>
              <h3 className="text-2xl font-black text-slate-900 tracking-tight">Today's Pulse</h3>
              <p className="text-sm text-slate-400 font-bold uppercase tracking-widest mt-1">Live Feed</p>
            </div>
            <button
              onClick={() => navigate('/calendar')}
              className="group flex items-center gap-2 text-xs font-bold text-indigo-600 hover:text-indigo-700 transition-colors"
            >
              System Calendar
              <ArrowRight size={14} className="group-hover:translate-x-1 transition-transform" />
            </button>
          </div>

          <div className="glass-card rounded-[40px] overflow-hidden border border-slate-100 shadow-sm">
            {todaysEvents.length > 0 ? (
              <div className="divide-y divide-slate-100/50">
                {todaysEvents.map((event, idx) => (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: idx * 0.1 }}
                    key={idx}
                    className="p-8 flex items-center gap-8 hover:bg-slate-50/80 transition-all group cursor-pointer"
                    onClick={() => navigate(event.type === 'meeting' ? '/meetings' : '/tasks')}
                  >
                    <div className="w-20 text-center shrink-0">
                      <p className="text-sm font-black text-slate-900 leading-none">{event.time.split(' ')[0]}</p>
                      <p className="text-[10px] font-bold text-slate-400 uppercase tracking-tighter mt-1">{event.time.split(' ')[1] || 'Today'}</p>
                    </div>

                    <div className={`w-14 h-14 rounded-[20px] flex items-center justify-center shrink-0 ${event.type === 'meeting' ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-100' : 'bg-slate-900 text-white shadow-lg shadow-slate-200'}`}>
                      {event.type === 'meeting' ? <Video size={20} /> : <ClipboardList size={20} />}
                    </div>

                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-1">
                        <span className={`px-2 py-0.5 rounded-full text-[9px] font-black uppercase tracking-wider ${event.type === 'meeting' ? 'bg-indigo-50 text-indigo-600' : 'bg-slate-100 text-slate-600'}`}>
                          {event.type}
                        </span>
                        {event.details && (
                          <span className="text-[10px] font-bold text-slate-400">â€¢ {event.details}</span>
                        )}
                      </div>
                      <h4 className="text-lg font-bold text-slate-900 leading-tight group-hover:text-indigo-600 transition-colors">{event.title}</h4>
                    </div>

                    <div className="w-10 h-10 rounded-full border border-slate-100 flex items-center justify-center text-slate-300 group-hover:bg-indigo-600 group-hover:text-white group-hover:border-indigo-600 transition-all duration-300">
                      <ChevronRight size={18} />
                    </div>
                  </motion.div>
                ))}
              </div>
            ) : (
              <div className="p-24 text-center">
                <div className="w-20 h-20 bg-slate-50 border border-slate-100 rounded-[32px] flex items-center justify-center mx-auto mb-6 shadow-inner">
                  <CalendarIcon className="text-slate-200" size={32} />
                </div>
                <h4 className="text-xl font-black text-slate-900">Zero Disruptions</h4>
                <p className="text-sm text-slate-400 font-bold mt-2 uppercase tracking-widest">Focus mode activated</p>
              </div>
            )}
          </div>
        </div>

        {/* Action Center */}
        <div className="space-y-8">
          <div className="px-4">
            <h3 className="text-2xl font-black text-slate-900 tracking-tight">Focus Stack</h3>
            <p className="text-sm text-slate-400 font-bold uppercase tracking-widest mt-1">Priority Flow</p>
          </div>

          <div className="space-y-6">
            <div className="glass-card rounded-[40px] border border-slate-100 shadow-sm overflow-hidden p-3">
              {recentTasks.length > 0 ? (
                <div className="space-y-2">
                  {recentTasks.slice(0, 4).map((task, idx) => (
                    <motion.div
                      initial={{ opacity: 0, scale: 0.95 }}
                      animate={{ opacity: 1, scale: 1 }}
                      transition={{ delay: idx * 0.05 }}
                      key={task.id}
                      className="p-5 flex items-center justify-between group cursor-pointer hover:bg-slate-50 rounded-[28px] transition-all"
                      onClick={() => navigate('/tasks')}
                    >
                      <div className="flex items-center gap-5">
                        <div className={`w-3 h-3 rounded-full ${task.priority === 'High' ? 'bg-rose-500 animate-pulse' : task.priority === 'Medium' ? 'bg-amber-500' : 'bg-emerald-500'}`} />
                        <div>
                          <p className={`text-sm font-bold ${task.status === 'Completed' ? 'text-slate-300 line-through' : 'text-slate-900 underline-offset-4 decoration-2 group-hover:underline'}`}>
                            {task.title}
                          </p>
                          <p className="text-[10px] font-black text-slate-400 uppercase tracking-[0.15em] mt-1.5 flex items-center gap-2">
                            {task.priority} Priority
                            <span className="w-1 h-1 rounded-full bg-slate-300" />
                            {task.status}
                          </p>
                        </div>
                      </div>
                      <div className="translate-x-4 opacity-0 group-hover:translate-x-0 group-hover:opacity-100 transition-all">
                        <ArrowUpRight size={18} className="text-indigo-600" />
                      </div>
                    </motion.div>
                  ))}
                  <button onClick={() => navigate('/tasks')} className="w-full py-4 text-[10px] font-black uppercase tracking-[0.3em] text-slate-400 hover:text-indigo-600 transition-colors border-t border-slate-50 mt-2">
                    Access Complete Stack
                  </button>
                </div>
              ) : (
                <div className="py-16 text-center">
                  <p className="text-sm font-black text-slate-300 uppercase tracking-widest">Stack Empty</p>
                </div>
              )}
            </div>

            {/* Pro Evolution Card */}
            <div className="group relative bg-slate-900 rounded-[40px] p-10 text-white overflow-hidden shadow-2xl shadow-indigo-900/10">
              <div className="absolute top-0 right-0 w-64 h-64 bg-indigo-600/20 rounded-full blur-[80px] group-hover:bg-indigo-600/30 transition-colors" />
              <div className="absolute -bottom-20 -left-20 w-80 h-80 bg-violet-600/10 rounded-full blur-[100px]" />

              <div className="relative z-10 flex flex-col h-full">
                <div className="w-14 h-14 bg-white/10 backdrop-blur-md rounded-[20px] flex items-center justify-center mb-10 border border-white/20 group-hover:scale-110 transition-transform duration-500">
                  <Sparkles size={24} className="text-indigo-400" />
                </div>
                <h4 className="text-2xl font-black mb-3">Collabryta <br /><span className="text-indigo-400">Platinum</span></h4>
                <p className="text-sm text-slate-400 font-bold leading-relaxed mb-10">
                  Scale your operations with advanced neural networking and 2.5TB cloud sync.
                </p>
                <button className="flex items-center justify-between w-full p-2 pl-6 bg-white text-slate-900 rounded-[24px] font-black text-xs hover:bg-indigo-50 transition-all group-hover:shadow-xl group-hover:shadow-white/10">
                  ACTIVATE PLAN
                  <div className="w-10 h-10 rounded-full bg-slate-900 flex items-center justify-center text-white">
                    <ArrowRight size={18} />
                  </div>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Redesigned Premium Stat Card
const StatCard = ({ label, value, icon: Icon, gradient, symbol = "", onClick }: any) => (
  <button
    onClick={onClick}
    className="group relative h-44 glass-card rounded-[40px] p-8 transition-all duration-500 hover:-translate-y-2 hover:shadow-2xl overflow-hidden"
  >
    <div className={`absolute top-0 right-0 w-32 h-32 bg-gradient-to-br ${gradient} opacity-[0.03] group-hover:opacity-[0.08] transition-opacity blur-2xl -mr-10 -mt-10`} />

    <div className="flex flex-col justify-between h-full relative z-10">
      <div className="flex items-center justify-between">
        <div className={`p-4 rounded-2xl bg-gradient-to-br ${gradient} text-white shadow-xl transform transition-transform duration-500 group-hover:rotate-6`}>
          <Icon size={20} strokeWidth={2.5} />
        </div>
        <div className="opacity-0 group-hover:opacity-100 transition-opacity">
          <ArrowUpRight size={20} className="text-slate-300" />
        </div>
      </div>
      <div>
        <div className="flex items-baseline gap-1">
          <h3 className="text-4xl font-black text-slate-900 tracking-tight">{value}</h3>
          {symbol && <span className="text-xl font-black text-slate-400">{symbol}</span>}
        </div>
        <p className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mt-2">{label}</p>
      </div>
    </div>
  </button>
);

export default DashboardPage;
