import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Save,
    Loader2,
    Calendar,
    User,
    AlignLeft,
    Type,
    AlertCircle,
    Activity,
    Shield,
    Zap,
    Sparkles,
    CheckCircle2,
    Target,
    ArrowUpRight,
    Plus,
    Clock,
    UserPlus,
    Flag
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";
import { motion, AnimatePresence } from "framer-motion";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);
    const today = new Date().toISOString().split('T')[0];

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);
    const [error, setError] = useState<string | null>(null);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal",
        assigned_to: "",
        due_date: today
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : taskData.status,
                        priority: taskData.priority || "Normal",
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : today
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, today]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError(null);
        if (formData.due_date < today) {
            setError("Due date cannot be in the past.");
            return;
        }

        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: (formData.status === "Upcoming" ? "todo" : formData.status === "In Progress" ? "in_progress" : "done") as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (err: any) {
            console.error(err);
            setError(err.response?.data?.detail?.[0]?.msg || err.response?.data?.detail || "Something went wrong. Please try again.");
            setSubmitting(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen flex flex-col items-center justify-center bg-[#fcfdfe]">
                <div className="w-12 h-12 border-4 border-slate-100 border-t-indigo-600 rounded-full animate-spin mb-6" />
                <p className="text-sm font-semibold text-slate-400">Loading objective data...</p>
            </div>
        );
    }

    return (
        <div className="min-h-screen text-slate-900 pb-20 animate-fade-in relative">
            {/* Background Decor */}
            {/* Background Decor */}
            <div className="fixed top-0 left-0 w-full h-[300px] bg-gradient-to-b from-indigo-50/50 to-transparent pointer-events-none" />

            <div className="max-w-6xl mx-auto relative z-10">

                {/* Header Navigation */}
                <div className="flex items-center justify-between mb-12">
                    <button
                        onClick={() => navigate(-1)}
                        className="flex items-center gap-3 text-slate-500 hover:text-indigo-600 transition-all font-bold text-sm group"
                    >
                        <div className="w-10 h-10 rounded-xl bg-white border border-slate-200 flex items-center justify-center shadow-sm group-hover:bg-indigo-50 group-hover:border-indigo-100 transition-all hover:scale-110">
                            <ChevronLeft size={18} className="group-hover:-translate-x-0.5 transition-transform" />
                        </div>
                        Back to Inventory
                    </button>

                    <div className="flex items-center gap-3 px-4 py-2 bg-indigo-50 text-indigo-600 rounded-xl font-bold text-[11px] uppercase tracking-widest border border-indigo-100 shadow-sm">
                        <Target size={14} />
                        Objective Protocol
                    </div>
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-12 gap-12 items-start">
                    {/* Left: Branding & Info */}
                    <div className="lg:col-span-4 space-y-8">
                        <div>
                            <h1 className="text-5xl font-black tracking-tight text-slate-900 mb-4 leading-tight">
                                {isEditing ? 'Modify Objective' : 'Create Objective'}
                            </h1>
                            <p className="text-sm font-medium text-slate-500 leading-relaxed">
                                {isEditing
                                    ? 'Recalibrate the parameters and requirements for this operational unit.'
                                    : 'Define a new mission segment and integrate it into the collective squad stream.'}
                            </p>
                        </div>

                        <div className="glass-card rounded-[32px] p-8 space-y-8">
                            <div className="flex items-start gap-4">
                                <div className="w-12 h-12 rounded-2xl bg-amber-50 text-amber-600 flex items-center justify-center shrink-0 border border-amber-100/50 shadow-sm">
                                    <Zap size={20} />
                                </div>
                                <div>
                                    <h4 className="text-sm font-bold text-slate-900 mb-1">Instant Sync</h4>
                                    <p className="text-xs font-semibold text-slate-400 leading-relaxed">Assigned members will receive alerts immediately.</p>
                                </div>
                            </div>
                            <div className="flex items-start gap-4">
                                <div className="w-12 h-12 rounded-2xl bg-emerald-50 text-emerald-600 flex items-center justify-center shrink-0 border border-emerald-100/50 shadow-sm">
                                    <CheckCircle2 size={20} />
                                </div>
                                <div>
                                    <h4 className="text-sm font-black text-slate-900 mb-1">Live Tracking</h4>
                                    <p className="text-xs font-semibold text-slate-400 leading-relaxed">Monitor progress across the project timeline.</p>
                                </div>
                            </div>
                        </div>

                        <div className="bg-slate-900 rounded-[32px] p-8 text-white relative overflow-hidden shadow-xl shadow-slate-200 group hover:scale-[1.02] transition-transform duration-500">
                            <div className="absolute top-0 right-0 p-6 opacity-10 group-hover:opacity-20 transition-opacity">
                                <Sparkles size={80} />
                            </div>
                            <h4 className="text-md font-bold mb-3 flex items-center gap-2"><Flag size={16} className="text-indigo-400" /> Efficiency Tip</h4>
                            <p className="text-xs font-medium text-slate-300 leading-relaxed">
                                Detailed descriptions help the team understand the scope and deliver better results.
                            </p>
                        </div>
                    </div>

                    {/* Right: Form */}
                    <div className="lg:col-span-8">
                        <AnimatePresence>
                            {error && (
                                <motion.div
                                    initial={{ opacity: 0, y: -10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    exit={{ opacity: 0, y: -10 }}
                                    className="mb-8 p-6 bg-rose-50 border border-rose-100 rounded-[24px] text-rose-600 flex items-center gap-4 shadow-sm"
                                >
                                    <AlertCircle size={20} />
                                    <p className="text-sm font-bold">{error}</p>
                                </motion.div>
                            )}
                        </AnimatePresence>

                        <div className="bg-white rounded-[40px] border border-slate-200 p-8 md:p-12 shadow-xl shadow-slate-200/50 relative overflow-hidden">
                            {/* Subtle background pattern */}
                            <div className="absolute top-0 right-0 w-64 h-64 bg-gradient-to-br from-indigo-50/50 to-transparent rounded-bl-[100px] pointer-events-none" />

                            <form onSubmit={handleSubmit} className="space-y-8 relative z-10">
                                <div className="space-y-3">
                                    <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Objective Title</label>
                                    <div className="relative group">
                                        <input
                                            type="text"
                                            value={formData.title}
                                            onChange={e => setFormData({ ...formData, title: e.target.value })}
                                            placeholder="e.g. Design System Implementation"
                                            required
                                            className="input-premium text-lg pr-12"
                                        />
                                        <Type size={20} className="absolute right-5 top-1/2 -translate-y-1/2 text-slate-300 group-hover:text-indigo-400 transition-colors" />
                                    </div>
                                </div>

                                <div className="space-y-3">
                                    <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Description Briefing</label>
                                    <div className="relative group">
                                        <textarea
                                            value={formData.description}
                                            onChange={e => setFormData({ ...formData, description: e.target.value })}
                                            placeholder="Outline the mission requirements and expected outcomes..."
                                            rows={5}
                                            className="w-full px-6 py-5 bg-slate-50/50 border border-slate-200 rounded-[28px] focus:border-indigo-500 focus:ring-4 focus:ring-indigo-500/10 outline-none text-sm font-medium text-slate-900 transition-all shadow-inner resize-none leading-relaxed hover:bg-white focus:bg-white"
                                        />
                                        <AlignLeft size={20} className="absolute right-5 top-6 text-slate-300 group-hover:text-indigo-400 transition-colors" />
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                                    <div className="space-y-3">
                                        <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Assign Personnel</label>
                                        <div className="relative group">
                                            <UserPlus className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-400 group-hover:text-indigo-500 transition-colors" size={18} />
                                            <select
                                                value={formData.assigned_to}
                                                onChange={e => setFormData({ ...formData, assigned_to: e.target.value })}
                                                className="w-full pl-14 pr-10 py-4 bg-slate-50/50 border border-slate-200 rounded-2xl focus:border-indigo-500 focus:ring-4 focus:ring-indigo-500/10 outline-none text-sm font-bold text-slate-700 transition-all shadow-sm appearance-none cursor-pointer hover:bg-white hover:border-indigo-200"
                                            >
                                                <option value="" className="text-slate-300">Select Member</option>
                                                {users.map(u => (
                                                    <option key={u.id} value={u.id}>{u.name || u.email}</option>
                                                ))}
                                            </select>
                                            <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400 group-hover:text-indigo-500 transition-colors">
                                                <ChevronLeft size={16} className="-rotate-90" />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="space-y-3">
                                        <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Due Date</label>
                                        <div className="relative group">
                                            <Calendar className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-400 group-hover:text-indigo-500 transition-colors" size={18} />
                                            <input
                                                type="date"
                                                min={today}
                                                value={formData.due_date}
                                                onChange={e => setFormData({ ...formData, due_date: e.target.value })}
                                                className="input-premium pl-14"
                                                required
                                            />
                                        </div>
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                                    <div className="space-y-4">
                                        <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Phase Status</label>
                                        <div className="flex flex-wrap p-2 bg-slate-50 border border-slate-200 rounded-2xl gap-1">
                                            {["Upcoming", "In Progress", "Completed"].map(s => (
                                                <button
                                                    key={s}
                                                    type="button"
                                                    onClick={() => setFormData({ ...formData, status: s })}
                                                    className={`flex-1 px-3 py-2.5 text-[10px] font-black uppercase tracking-wider rounded-xl transition-all ${formData.status === s
                                                        ? 'bg-white text-indigo-600 shadow-md ring-1 ring-black/5'
                                                        : 'text-slate-400 hover:text-slate-600 hover:bg-white/50'
                                                        }`}
                                                >
                                                    {s}
                                                </button>
                                            ))}
                                        </div>
                                    </div>

                                    <div className="space-y-4">
                                        <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Priority Level</label>
                                        <div className="flex flex-wrap p-2 bg-slate-50 border border-slate-200 rounded-2xl gap-1">
                                            {["Low", "Normal", "High"].map(p => (
                                                <button
                                                    key={p}
                                                    type="button"
                                                    onClick={() => setFormData({ ...formData, priority: p })}
                                                    className={`flex-1 px-3 py-2.5 text-[10px] font-black uppercase tracking-wider rounded-xl transition-all ${formData.priority === p
                                                        ? p === 'High' ? 'bg-rose-500 text-white shadow-md shadow-rose-200' : 'bg-white text-indigo-600 shadow-md ring-1 ring-black/5'
                                                        : 'text-slate-400 hover:text-slate-600 hover:bg-white/50'
                                                        }`}
                                                >
                                                    {p}
                                                </button>
                                            ))}
                                        </div>
                                    </div>
                                </div>

                                <div className="pt-10 flex flex-col sm:flex-row items-center justify-end gap-4 border-t border-slate-100 mt-6">
                                    <button
                                        type="button"
                                        onClick={() => navigate(-1)}
                                        className="w-full sm:w-auto px-8 py-4 text-xs font-black text-slate-400 hover:text-slate-900 uppercase tracking-wider hover:bg-slate-50 rounded-2xl transition-all"
                                    >
                                        Discard Changes
                                    </button>
                                    <button
                                        type="submit"
                                        disabled={submitting}
                                        className="w-full sm:w-auto btn-premium shadow-xl shadow-indigo-200 hover:shadow-indigo-300 min-w-[220px]"
                                    >
                                        {submitting ? (
                                            <Loader2 size={20} className="animate-spin" />
                                        ) : (
                                            <>
                                                {isEditing ? 'Update Objective' : 'Create Objective'}
                                                <ArrowUpRight size={20} strokeWidth={2.5} />
                                            </>
                                        )}
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddTaskPage;
