import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Save,
    Loader2,
    Calendar,
    User,
    AlignLeft,
    Type,
    AlertCircle,
    Activity,
    Shield,
    Zap,
    Sparkles,
    CheckCircle2,
    Target,
    ArrowUpRight
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";
import { motion, AnimatePresence } from "framer-motion";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);
    const today = new Date().toISOString().split('T')[0];

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);
    const [error, setError] = useState<string | null>(null);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal",
        assigned_to: "",
        due_date: today
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : taskData.status,
                        priority: taskData.priority || "Normal",
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : today
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, today]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError(null);
        if (formData.due_date < today) {
            setError("TEMPORAL BREACH: DUE DATE CANNOT BE IN THE PAST.");
            return;
        }

        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: (formData.status === "Upcoming" ? "todo" : formData.status === "In Progress" ? "in_progress" : "done") as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (err: any) {
            console.error(err);
            setError(err.response?.data?.detail?.[0]?.msg || err.response?.data?.detail || "SEQUENCE FAILURE. RETRY PROTOCOL.");
            setSubmitting(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen flex flex-col items-center justify-center bg-white">
                <div className="w-10 h-10 border border-zinc-200 border-t-black rounded-full animate-spin mb-4" />
                <p className="text-[10px] font-black tracking-[0.2em] text-zinc-400 uppercase">ACCESSING OBJECTIVE PARAMETERS</p>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-white text-black p-10 lg:p-16 animate-fade-in font-sans relative">
            <div className="max-w-[1000px] mx-auto">

                {/* Header Navigation */}
                <div className="flex items-center justify-between mb-20 border-b border-zinc-100 pb-10">
                    <button
                        onClick={() => navigate(-1)}
                        className="flex items-center gap-4 text-zinc-300 hover:text-black transition-all group"
                    >
                        <ChevronLeft size={18} className="group-hover:-translate-x-1 transition-transform" />
                        <span className="text-[10px] font-black uppercase tracking-[0.2em]">RETURN TO INVENTORY</span>
                    </button>

                    <div className="flex items-center gap-4 text-zinc-400">
                        <Target size={14} className="text-zinc-200" />
                        <span className="text-[10px] font-black uppercase tracking-[0.3em]">OPERATIONAL PROTOCOL</span>
                    </div>
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-12 gap-16 items-start">
                    {/* Left: Branding & Tips */}
                    <div className="lg:col-span-4 space-y-12">
                        <div>
                            <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">MISSION / DEPLOYMENT</p>
                            <h1 className="text-5xl font-bold tracking-tighter leading-none uppercase mb-6">
                                {isEditing ? 'MODIFY' : 'CREATE'}<br />OBJECTIVE
                            </h1>
                            <p className="text-[11px] font-medium text-zinc-500 uppercase tracking-tight leading-relaxed">
                                {isEditing ? 'RECALIBRATE THE PARAMETERS OF THIS OPERATIONAL UNIT.' : 'DEFINE A NEW MISSION SEGMENT AND INTEGRATE IT INTO THE SQUAD STREAM.'}
                            </p>
                        </div>

                        <div className="space-y-px bg-zinc-100 border border-zinc-100">
                            <div className="flex items-start gap-6 p-8 bg-white">
                                <Zap size={18} className="text-black shrink-0 mt-1" />
                                <div>
                                    <p className="text-[10px] font-black text-black uppercase tracking-widest mb-1.5">INSTANT SYNC</p>
                                    <p className="text-[10px] font-bold text-zinc-400 uppercase tracking-tight leading-snug">ASSIGNEES WILL RECEIVE IMMEDIATE ALERTS.</p>
                                </div>
                            </div>
                            <div className="flex items-start gap-6 p-8 bg-white">
                                <CheckCircle2 size={18} className="text-black shrink-0 mt-1" />
                                <div>
                                    <p className="text-[10px] font-black text-black uppercase tracking-widest mb-1.5">STATUS TRACKING</p>
                                    <p className="text-[10px] font-bold text-zinc-400 uppercase tracking-tight leading-snug">MONITOR PROGRESS ACROSS THE TIMELINE.</p>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Right: Form */}
                    <div className="lg:col-span-8">
                        <AnimatePresence>
                            {error && (
                                <motion.div
                                    initial={{ opacity: 0, y: -10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    exit={{ opacity: 0, y: -10 }}
                                    className="mb-10 p-6 bg-black text-white flex items-center gap-4"
                                >
                                    <AlertCircle size={18} className="text-zinc-500" />
                                    <p className="text-[10px] font-black uppercase tracking-widest">{error}</p>
                                </motion.div>
                            )}
                        </AnimatePresence>

                        <div className="bg-white border border-zinc-100 p-12 lg:p-16 shadow-2xl relative">
                            <div className="absolute -top-px -left-px w-10 h-10 border-t border-l border-black" />
                            <div className="absolute -bottom-px -right-px w-10 h-10 border-b border-r border-black" />

                            <form onSubmit={handleSubmit} className="space-y-12">
                                <div className="space-y-4">
                                    <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">OBJECTIVE IDENTIFIER</label>
                                    <div className="relative border-b border-zinc-200 focus-within:border-black transition-all">
                                        <input
                                            type="text"
                                            value={formData.title}
                                            onChange={e => setFormData({ ...formData, title: e.target.value })}
                                            placeholder="DEPLOYMENT IDENTIFIER..."
                                            required
                                            className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none placeholder:text-zinc-100 uppercase"
                                        />
                                        <Type size={16} className="absolute right-0 top-1/2 -translate-y-1/2 text-zinc-100" />
                                    </div>
                                </div>

                                <div className="space-y-4">
                                    <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">TACTICAL BRIEFING</label>
                                    <div className="relative border-b border-zinc-200 focus-within:border-black transition-all">
                                        <textarea
                                            value={formData.description}
                                            onChange={e => setFormData({ ...formData, description: e.target.value })}
                                            placeholder="OUTLINE THE MISSION REQUIREMENTS..."
                                            rows={4}
                                            className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none placeholder:text-zinc-100 uppercase resize-none leading-relaxed"
                                        />
                                        <AlignLeft size={16} className="absolute right-0 top-5 text-zinc-100" />
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                                    <div className="space-y-4">
                                        <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">ASSIGN PERSONNEL</label>
                                        <div className="relative border-b border-zinc-200 focus-within:border-black transition-all">
                                            <select
                                                value={formData.assigned_to}
                                                onChange={e => setFormData({ ...formData, assigned_to: e.target.value })}
                                                className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none appearance-none cursor-pointer uppercase"
                                            >
                                                <option value="" className="text-zinc-300">SELECT MEMBER</option>
                                                {users.map(u => (
                                                    <option key={u.id} value={u.id} className="text-black bg-white">{u.name?.toUpperCase() || u.email.toUpperCase()}</option>
                                                ))}
                                            </select>
                                            <User size={16} className="absolute right-0 top-1/2 -translate-y-1/2 text-zinc-100 pointer-events-none" />
                                        </div>
                                    </div>

                                    <div className="space-y-4">
                                        <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">TEMPORAL DEADLINE</label>
                                        <div className="relative border-b border-zinc-200 focus-within:border-black transition-all">
                                            <input
                                                type="date"
                                                min={today}
                                                value={formData.due_date}
                                                onChange={e => setFormData({ ...formData, due_date: e.target.value })}
                                                className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none uppercase"
                                            />
                                            <Calendar size={16} className="absolute right-0 top-1/2 -translate-y-1/2 text-zinc-100 pointer-events-none" />
                                        </div>
                                    </div>
                                </div>

                                <div className="space-y-6">
                                    <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">PHASE STATUS</label>
                                    <div className="flex flex-wrap gap-px bg-zinc-100 border border-zinc-100">
                                        {["Upcoming", "In Progress", "Completed"].map(s => (
                                            <button
                                                key={s}
                                                type="button"
                                                onClick={() => setFormData({ ...formData, status: s })}
                                                className={`flex-1 px-8 py-4 text-[10px] font-black uppercase tracking-widest transition-all ${formData.status === s
                                                    ? 'bg-black text-white'
                                                    : 'bg-white text-zinc-300 hover:text-black hover:bg-zinc-50'
                                                    }`}
                                            >
                                                {s}
                                            </button>
                                        ))}
                                    </div>
                                </div>

                                <div className="pt-10 flex items-center justify-end gap-10 border-t border-zinc-50">
                                    <button
                                        type="button"
                                        onClick={() => navigate(-1)}
                                        className="text-[10px] font-black text-zinc-300 hover:text-black transition-colors uppercase tracking-[0.3em]"
                                    >
                                        ABORT
                                    </button>
                                    <button
                                        type="submit"
                                        disabled={submitting}
                                        className="flex items-center gap-6 px-12 py-5 bg-black text-white text-[11px] font-black tracking-[0.4em] uppercase hover:bg-zinc-800 transition-all active:scale-95 disabled:opacity-20 flex items-center gap-2"
                                    >
                                        {submitting ? <Loader2 size={18} className="animate-spin" /> : (
                                            <>
                                                {isEditing ? 'UPDATE OBJECTIVE' : 'COMMIT OBJECTIVE'}
                                                <ArrowUpRight size={18} />
                                            </>
                                        )}
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddTaskPage;
