import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  FileText,
  FileSpreadsheet,
  Image as ImageIcon,
  File,
  Video,
  Upload,
  Search,
  MoreHorizontal,
  Download,
  Trash2,
  Clock,
  ChevronRight,
  HardDrive,
  Zap,
  Layers,
  Activity,
  Shield,
  Star,
  Plus,
  Loader2,
  ArrowUpRight
} from "lucide-react";
import { fileService, FileData } from "../../services/fileService";

import { motion, AnimatePresence } from "framer-motion";

const FilesPage: React.FC = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [recentFiles, setRecentFiles] = useState<FileData[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);

  const fetchFiles = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const files = await fileService.getAllFiles();
      setRecentFiles(files);
    } catch (error) {
      console.error("Failed to load files", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  const getFileCounts = (files: FileData[]) => {
    const counts = { word: 0, pdf: 0, spread: 0, image: 0, other: 0 };
    files.forEach(f => {
      const ext = f.filename.split('.').pop()?.toLowerCase() || '';
      if (['doc', 'docx'].includes(ext)) counts.word++;
      else if (['pdf'].includes(ext)) counts.pdf++;
      else if (['xls', 'xlsx', 'csv'].includes(ext)) counts.spread++;
      else if (['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext)) counts.image++;
      else counts.other++;
    });
    return counts;
  };

  const counts = getFileCounts(recentFiles);

  const fileCategories = [
    { id: "word", name: "DOCUMENTS", count: counts.word, icon: <FileText size={20} /> },
    { id: "pdf", name: "PDF ARCHIVE", count: counts.pdf, icon: <FileText size={20} /> },
    { id: "spread", name: "DATASHEETS", count: counts.spread, icon: <FileSpreadsheet size={20} /> },
    { id: "image", name: "VISUAL ASSETS", count: counts.image, icon: <ImageIcon size={20} /> },
    { id: "other", name: "UNCATEGORIZED", count: counts.other, icon: <File size={20} /> },
  ];

  useEffect(() => {
    fetchFiles();
    const pollInterval = setInterval(() => {
      fetchFiles(false);
    }, 60000); // Poll every 60 seconds for files

    return () => {
      clearInterval(pollInterval);
    };
  }, []);

  const formatSize = (bytes: number) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const mapFileType = (ftype: string | undefined, filename: string) => {
    const ext = filename.split('.').pop()?.toLowerCase() || '';
    if (['doc', 'docx'].includes(ext)) return <FileText size={24} className="text-black" />;
    if (['pdf'].includes(ext)) return <FileText size={24} className="text-black" />;
    if (['xls', 'xlsx', 'csv'].includes(ext)) return <FileSpreadsheet size={24} className="text-black" />;
    if (['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext)) return <ImageIcon size={24} className="text-black" />;
    if (['mp4', 'mov', 'avi'].includes(ext)) return <Video size={24} className="text-black" />;
    return <File size={24} className="text-zinc-400" />;
  };

  const filteredFiles = recentFiles.filter(file => {
    const matchesSearch = file.filename.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (file.title && file.title.toLowerCase().includes(searchQuery.toLowerCase()));

    if (!selectedCategory) return matchesSearch;

    const ext = file.filename.split('.').pop()?.toLowerCase() || '';
    let isCategoryMatch = false;

    switch (selectedCategory) {
      case 'word': isCategoryMatch = ['doc', 'docx'].includes(ext); break;
      case 'pdf': isCategoryMatch = ['pdf'].includes(ext); break;
      case 'spread': isCategoryMatch = ['xls', 'xlsx', 'csv'].includes(ext); break;
      case 'image': isCategoryMatch = ['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext); break;
      case 'other':
        isCategoryMatch = !['doc', 'docx', 'pdf', 'xls', 'xlsx', 'csv', 'jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext);
        break;
      default: isCategoryMatch = true;
    }

    return matchesSearch && isCategoryMatch;
  });

  return (
    <div className="min-h-screen bg-white text-black p-10 lg:p-16 animate-fade-in font-sans relative">
      <div className="max-w-[1200px] mx-auto">

        {/* Header Area */}
        <div className="flex flex-col md:flex-row md:items-end justify-between gap-10 mb-20 border-b border-zinc-100 pb-12">
          <div>
            <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">RESOURCES / REPOSITORY</p>
            <h1 className="text-5xl font-bold tracking-tighter leading-none uppercase">FILES</h1>
          </div>

          <div className="flex items-center gap-6">
            <div className="relative border-b border-zinc-200 focus-within:border-black transition-all">
              <Search className="absolute left-0 top-1/2 -translate-y-1/2 text-zinc-300" size={14} />
              <input
                className="w-64 bg-transparent border-none py-3 pl-8 pr-4 text-[11px] font-bold tracking-widest text-black focus:ring-0 outline-none transition-all placeholder:text-zinc-200 uppercase"
                placeholder="SEARCH ARCHIVES..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
            <button
              onClick={() => navigate("/files/upload")}
              className="flex items-center gap-4 px-10 py-4 bg-black text-white hover:bg-zinc-800 transition-all active:scale-95"
            >
              <span className="text-[11px] font-black tracking-widest uppercase">UPLOAD ASSET</span>
              <Plus size={16} />
            </button>
          </div>
        </div>

        {/* Categories Grid */}
        <div className="grid grid-cols-2 md:grid-cols-5 gap-px bg-zinc-100 border border-zinc-100 mb-20">
          {fileCategories.map((cat) => (
            <div
              key={cat.id}
              onClick={() => setSelectedCategory(selectedCategory === cat.id ? null : cat.id)}
              className={`p-10 transition-all cursor-pointer relative group ${selectedCategory === cat.id
                ? 'bg-black text-white'
                : 'bg-white hover:bg-zinc-50'
                }`}
            >
              <div className={`mb-8 ${selectedCategory === cat.id ? 'text-zinc-600' : 'text-zinc-100'}`}>
                {cat.icon}
              </div>
              <h3 className="text-[11px] font-black tracking-widest uppercase mb-1">{cat.name}</h3>
              <p className={`text-[9px] font-bold uppercase tracking-[0.2em] ${selectedCategory === cat.id ? 'text-zinc-500' : 'text-zinc-300'}`}>
                {cat.count} UNITS
              </p>
            </div>
          ))}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-16">

          {/* File List */}
          <div className="lg:col-span-2 space-y-12">
            <div className="flex items-end justify-between border-b border-zinc-100 pb-6">
              <div>
                <h3 className="text-2xl font-bold tracking-tighter uppercase leading-none">RECENT ACCESS</h3>
                <p className="text-[10px] font-bold tracking-widest text-zinc-400 uppercase mt-2">Asset Retrieval Flow</p>
              </div>
            </div>

            {loading ? (
              <div className="py-40 text-center">
                <Loader2 className="w-8 h-8 text-black animate-spin mx-auto mb-4" />
                <p className="text-[10px] font-black tracking-widest text-zinc-400 uppercase">ACCESSING DATA CORE</p>
              </div>
            ) : filteredFiles.length === 0 ? (
              <div className="py-40 text-center bg-zinc-50/50 border border-zinc-100">
                <p className="text-[11px] font-black text-zinc-200 tracking-[0.4em] uppercase">NO ASSETS DETECTED IN SELECTED PARAMETERS</p>
              </div>
            ) : (
              <div className="space-y-px bg-zinc-100 border border-zinc-100">
                {filteredFiles.map((file) => (
                  <div key={file.id} className="p-10 lg:p-12 bg-white flex items-center justify-between group hover:bg-zinc-50 transition-all cursor-pointer">
                    <div className="flex items-center gap-10">
                      <div className="w-16 h-16 bg-zinc-100 flex items-center justify-center transition-all group-hover:bg-black group-hover:text-white">
                        {mapFileType(file.file_type, file.filename)}
                      </div>
                      <div className="min-w-0">
                        <h4 className="text-xl font-bold text-black tracking-tighter uppercase mb-2 group-hover:underline underline-offset-4 decoration-1">{file.filename}</h4>
                        <div className="flex items-center gap-6 text-[10px] font-bold text-zinc-400 uppercase tracking-widest">
                          <span className="flex items-center gap-2"><HardDrive size={10} /> {formatSize(file.file_size_bytes)}</span>
                          <span className="flex items-center gap-2"><Clock size={10} /> {new Date(file.uploaded_at).toLocaleDateString()}</span>
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center gap-8 opacity-0 group-hover:opacity-100 transition-all">
                      <button className="p-3 text-zinc-300 hover:text-black transition-all"><Download size={18} /></button>
                      <button className="p-3 text-zinc-300 hover:text-black transition-all"><Trash2 size={18} /></button>
                      <button className="p-3 text-zinc-300 hover:text-black transition-all"><MoreHorizontal size={18} /></button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Side Module */}
          <div className="space-y-20">
            <div className="bg-black text-white p-12 relative overflow-hidden">
              <div className="relative z-10">
                <div className="flex items-center gap-4 mb-10">
                  <HardDrive size={18} className="text-zinc-600" />
                  <p className="text-[10px] font-black uppercase tracking-[0.3em] text-zinc-500">UTILIZATION RATIO</p>
                </div>

                <div className="space-y-10 mb-12">
                  <div>
                    <div className="flex justify-between text-[10px] font-black mb-4 uppercase tracking-widest">
                      <span className="text-zinc-500">CLOUD MATRIX</span>
                      <span className="text-white">4.2 GB / 10 GB</span>
                    </div>
                    <div className="h-1 w-full bg-zinc-900 overflow-hidden">
                      <div className="h-full w-[42%] bg-white" />
                    </div>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-px bg-zinc-800 border border-zinc-800">
                  <div className="p-8 bg-black">
                    <p className="text-4xl font-bold tracking-tighter">{recentFiles.length}</p>
                    <p className="text-[9px] font-black text-zinc-600 uppercase tracking-widest mt-2">RECORDS</p>
                  </div>
                  <div className="p-8 bg-black">
                    <p className="text-4xl font-bold tracking-tighter">128</p>
                    <p className="text-[9px] font-black text-zinc-600 uppercase tracking-widest mt-2">SHARED</p>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-black p-12 text-white relative group overflow-hidden">
              <Star size={24} className="text-zinc-700 mb-8" />
              <p className="text-[9px] font-black uppercase tracking-[0.4em] text-zinc-500 mb-4">SYSTEM EXPANSION</p>
              <h4 className="text-2xl font-bold tracking-tighter uppercase mb-4">UNLIMITED ARCHIVE</h4>
              <p className="text-sm font-medium tracking-tight leading-relaxed uppercase mb-10">
                NEARING CAPACITY. INITIALIZE INFRASTRUCTURE SCALE-UP TO BYPASS LIMITATIONS.
              </p>
              <button className="w-full py-5 bg-white text-black text-[10px] font-black tracking-widest uppercase hover:bg-zinc-100 transition-all flex items-center justify-center gap-4">
                SCALE CORE
                <ArrowUpRight size={16} />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FilesPage;
