import React, { useState, DragEvent, ChangeEvent } from "react";
import { Upload, FileText, ArrowLeft } from "lucide-react";
import { useNavigate } from "react-router-dom";

import { fileService } from "../../services/fileService";

const UploadFilePage: React.FC = () => {
  const navigate = useNavigate();
  const [dragActive, setDragActive] = useState(false);
  const [fileName, setFileName] = useState<string | null>(null);
  const [file, setFile] = useState<File | null>(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [uploading, setUploading] = useState(false);

  const handleDrag = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFile(e.dataTransfer.files[0]);
      setFileName(e.dataTransfer.files[0].name);
    }
  };

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
      setFileName(e.target.files[0].name);
    }
  };

  const handleSubmit = async () => {
    if (!file || !title) {
      alert("Please select a file and enter a title before uploading.");
      return;
    }

    setUploading(true);
    try {
      await fileService.uploadFile(file, title, description);
      alert(`✅ File "${title}" uploaded successfully!`);
      setTimeout(() => navigate("/files"), 500);
    } catch (error) {
      console.error("Upload failed", error);
      alert("Failed to upload file. Please try again.");
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="max-w-3xl mx-auto space-y-6 animate-in fade-in duration-500">

      {/* Header */}
      <div>
        <button
          onClick={() => navigate("/files")}
          className="flex items-center gap-2 text-sm text-slate-500 hover:text-blue-600 transition-colors mb-4"
        >
          <ArrowLeft size={16} /> Back to Files
        </button>
        <h1 className="text-2xl font-bold text-slate-900">Upload File</h1>
        <p className="text-slate-500 mt-1">Add new documents, images, or assets to your workspace.</p>
      </div>

      {/* Main Card */}
      <div className="bg-white rounded-xl border border-slate-200 shadow-sm p-8">

        {/* Drag & Drop Zone */}
        <div
          className={`border-2 border-dashed rounded-xl p-12 text-center transition-all ${dragActive ? "border-blue-500 bg-blue-50" : "border-slate-300 bg-slate-50 hover:border-slate-400"
            }`}
          onDragEnter={handleDrag}
          onDragOver={handleDrag}
          onDragLeave={handleDrag}
          onDrop={handleDrop}
        >
          <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center text-blue-600 shadow-sm mx-auto mb-4">
            <Upload size={24} />
          </div>
          <h3 className="text-lg font-semibold text-slate-800 mb-1">
            {fileName ? "File Selected" : "Click to upload or drag and drop"}
          </h3>
          <p className="text-sm text-slate-500 mb-4">
            {fileName ? <span className="text-blue-600 font-medium">{fileName}</span> : "SVG, PNG, JPG or PDF (max. 10MB)"}
          </p>

          {!fileName && (
            <>
              <label
                htmlFor="fileUpload"
                className="inline-flex items-center justify-center px-4 py-2 bg-white border border-slate-300 rounded-lg text-sm font-medium text-slate-700 hover:bg-slate-50 cursor-pointer shadow-sm transition-colors"
              >
                Browse files
              </label>
              <input
                id="fileUpload"
                type="file"
                className="hidden"
                onChange={handleFileChange}
              />
            </>
          )}
        </div>

        {/* Inputs */}
        <div className="mt-8 space-y-4">
          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-1">
              File Title <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              placeholder="e.g. Project Requirements v2"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="w-full px-4 py-2.5 bg-white border border-slate-300 rounded-lg text-slate-800 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
            />
          </div>

          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-1">
              Description <span className="text-slate-400 font-normal">(Optional)</span>
            </label>
            <textarea
              placeholder="Add details about this file..."
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows={3}
              className="w-full px-4 py-2.5 bg-white border border-slate-300 rounded-lg text-slate-800 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all resize-none"
            />
          </div>
        </div>

        {/* Actions */}
        <div className="mt-8 flex justify-end gap-3">
          <button
            onClick={() => navigate("/files")}
            className="px-4 py-2 text-slate-600 hover:bg-slate-50 rounded-lg font-medium transition-colors"
          >
            Cancel
          </button>
          <button
            onClick={handleSubmit}
            disabled={!fileName || !title}
            className="px-6 py-2 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all shadow-sm flex items-center gap-2"
          >
            <FileText size={18} />
            Upload File
          </button>
        </div>

      </div>
    </div>
  );
};

export default UploadFilePage;
