import React, { useState, useEffect } from "react";
import {
  Plus,
  Search,
  Loader2,
  Trash2,
  Edit3,
  Calendar,
  User as UserIcon,
  Filter,
  CheckCircle2,
  Clock,
  AlertCircle
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("All");

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const handleStatusUpdate = async (id: number, newStatus: string) => {
    try {
      await taskService.updateTask(id, { status: newStatus });
      setTasks(prev => prev.map(t => t.id === id ? { ...t, status: newStatus } : t));
    } catch (error) {
      console.error("Failed to update status");
    }
  };

  const handleDelete = async (id: number) => {
    if (!window.confirm("Are you sure you want to delete this task?")) return;
    try {
      await taskService.deleteTask(id);
      setTasks(prev => prev.filter(t => t.id !== id));
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (task.description && task.description.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesStatus = statusFilter === "All" || normalizeStatus(task.status) === statusFilter;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="min-h-screen bg-slate-50 p-4 md:p-8 font-sans">
      <div className="max-w-6xl mx-auto space-y-6">

        {/* Header Area */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
          <div>
            <h1 className="text-2xl font-bold text-slate-900">Tasks</h1>
            <p className="text-slate-500 text-sm">Manage and track your team's project progress.</p>
          </div>
          <button
            onClick={() => navigate("/tasks/new")}
            className="flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2.5 rounded-lg font-semibold transition shadow-sm active:scale-95"
          >
            <Plus size={18} />
            Add Task
          </button>
        </div>

        {/* Filters & Search Bar */}
        <div className="bg-white p-4 rounded-xl border border-slate-200 shadow-sm flex flex-col md:flex-row items-center gap-4">
          <div className="relative flex-1 w-full">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
            <input
              type="text"
              placeholder="Search tasks..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-2 bg-slate-50 border border-slate-200 rounded-lg text-slate-900 focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 outline-none transition-all"
            />
          </div>
          <div className="flex items-center gap-2 w-full md:w-auto">
            <Filter size={16} className="text-slate-400 hidden sm:block" />
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="flex-1 md:flex-none bg-slate-50 border border-slate-200 rounded-lg px-3 py-2 text-sm font-medium text-slate-700 outline-none focus:border-indigo-500 cursor-pointer"
            >
              <option value="All">All Statuses</option>
              <option value="Upcoming">Upcoming</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
          </div>
        </div>

        {/* Tasks List */}
        <div className="space-y-3">
          {loading ? (
            <div className="py-20 flex flex-col items-center justify-center gap-3">
              <Loader2 className="animate-spin text-indigo-600" size={32} />
              <p className="text-slate-500 font-medium">Loading tasks...</p>
            </div>
          ) : (
            <>
              {filteredTasks.length === 0 ? (
                <div className="py-20 bg-white rounded-xl border border-slate-200 border-dashed text-center">
                  <div className="w-12 h-12 bg-slate-50 rounded-full flex items-center justify-center mx-auto mb-3 text-slate-300">
                    <AlertCircle size={24} />
                  </div>
                  <h3 className="text-slate-900 font-semibold">No tasks found</h3>
                  <p className="text-slate-500 text-sm mt-1">Try adjusting your filters or create a new task.</p>
                </div>
              ) : (
                filteredTasks.map(task => (
                  <div
                    key={task.id}
                    className="bg-white p-4 rounded-xl border border-slate-200 shadow-sm hover:border-indigo-200 transition-all flex flex-col md:flex-row items-center gap-4 group"
                  >
                    {/* Status Indicator Pill */}
                    <div className="w-full md:w-32 flex-shrink-0">
                      <StatusPill status={normalizeStatus(task.status)} />
                    </div>

                    {/* Main Content */}
                    <div className="flex-1 min-w-0 w-full">
                      <h3 className="text-slate-900 font-bold truncate">{task.title}</h3>
                      <p className="text-slate-500 text-sm truncate">{task.description || "No description provided."}</p>
                    </div>

                    {/* Logistics Info (Assignee & Date) */}
                    <div className="flex items-center gap-6 w-full md:w-auto px-2">
                      <div className="flex items-center gap-2 text-slate-600">
                        <div className="w-7 h-7 rounded-full bg-indigo-50 flex items-center justify-center text-[10px] font-bold text-indigo-600 border border-indigo-100 uppercase">
                          {task.assigned_to_user ? task.assigned_to_user.name.charAt(0) : <UserIcon size={12} />}
                        </div>
                        <span className="text-xs font-medium hidden lg:inline">{task.assigned_to_user?.name || "Unassigned"}</span>
                      </div>
                      <div className="flex items-center gap-2 text-slate-500">
                        <Calendar size={14} />
                        <span className="text-xs font-medium">{task.due_date ? new Date(task.due_date).toLocaleDateString() : "No date"}</span>
                      </div>
                    </div>

                    {/* Actions */}
                    <div className="flex items-center gap-2 w-full md:w-auto justify-end border-t md:border-t-0 pt-3 md:pt-0 border-slate-100">
                      <button
                        onClick={() => navigate(`/tasks/edit/${task.id}`)}
                        className="p-2 text-slate-400 hover:text-indigo-600 hover:bg-indigo-50 rounded-lg transition"
                        title="Edit Task"
                      >
                        <Edit3 size={18} />
                      </button>
                      <button
                        onClick={() => handleDelete(task.id)}
                        className="p-2 text-slate-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition"
                        title="Delete Task"
                      >
                        <Trash2 size={18} />
                      </button>
                    </div>
                  </div>
                ))
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

/* ðŸ§© Component: StatusPill */
const StatusPill = ({ status }: { status: string }) => {
  const styles: any = {
    "Upcoming": "bg-blue-50 text-blue-700 border-blue-200",
    "In Progress": "bg-orange-50 text-orange-700 border-orange-200",
    "Completed": "bg-green-50 text-green-700 border-green-200",
  };
  return (
    <span className={`px-2.5 py-1 rounded-full text-[11px] font-bold uppercase tracking-wider border ${styles[status] || styles["Upcoming"]}`}>
      {status}
    </span>
  );
};

export default TasksPage;
