import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar,
  Clock,
  Plus,
  Trash2,
  ExternalLink,
  Users,
  Video,
  History,
  AlertCircle,
  MapPin,
  ChevronRight,
  ArrowRight,
  Layers,
  Zap,
  Shield,
  Star,
  Activity
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { meetingService, Meeting } from "../../services/meetingService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";

const MeetingsPage: React.FC = () => {
  const navigate = useNavigate();
  const [meetings, setMeetings] = useState<Meeting[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  const fetchMeetings = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await meetingService.getAllMeetings();
      setMeetings(data);
    } catch (error) {
      console.error("Failed to fetch meetings", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    fetchMeetings();

    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (message.type === 'meeting') {
        fetchMeetings(false);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const handleDelete = async (id: number) => {
    if (window.confirm("Terminate this session record?")) {
      try {
        await meetingService.deleteMeeting(id);
        setMeetings(prev => prev.filter(m => m.id !== id));
      } catch (error) {
        console.error("Failed to delete meeting", error);
      }
    }
  };

  const getStatus = (start: string, end: string) => {
    const now = currentTime;
    const startTime = new Date(start);
    const endTime = new Date(end);
    if (now >= startTime && now <= endTime) return 'present';
    if (now > endTime) return 'past';
    return 'upcoming';
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    }).toUpperCase();
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString([], { weekday: 'long', month: 'long', day: 'numeric' });
  };

  const isToday = (dateString: string) => {
    const d = new Date(dateString);
    const today = new Date();
    return d.getFullYear() === today.getFullYear() &&
      d.getMonth() === today.getMonth() &&
      d.getDate() === today.getDate();
  };

  const presentMeetings = meetings.filter(m => getStatus(m.start_time, m.end_time) === 'present');

  const upcomingMeetings = meetings.filter(m => {
    return getStatus(m.start_time, m.end_time) === 'upcoming' && isToday(m.start_time);
  }).sort((a, b) => new Date(a.start_time).getTime() - new Date(b.start_time).getTime());

  const pastMeetings = meetings.filter(m => {
    if (getStatus(m.start_time, m.end_time) !== 'past') return false;
    const endTime = new Date(m.end_time);
    const fortyEightHoursAgo = new Date(currentTime.getTime() - (48 * 60 * 60 * 1000));
    return endTime > fortyEightHoursAgo;
  }).sort((a, b) => new Date(b.start_time).getTime() - new Date(a.start_time).getTime());

  const currentOrNext = presentMeetings.length > 0 ? presentMeetings[0] : (upcomingMeetings.length > 0 ? upcomingMeetings[0] : null);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: { staggerChildren: 0.1 }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  const TimelineNode = ({ meeting, type, idx }: { meeting: Meeting, type: 'upcoming' | 'past', idx: number }) => (
    <motion.div
      variants={itemVariants}
      className="relative pl-16 pb-16 last:pb-0"
    >
      {/* Connection Line */}
      <div className="absolute left-[15px] top-8 bottom-0 w-[2px] bg-slate-100 last:hidden" />

      {/* Node Dot with Pulsing Effect for upcoming */}
      <div className={`absolute left-0 top-1 w-8 h-8 rounded-2xl border-4 border-white shadow-xl z-20 flex items-center justify-center transition-all ${type === 'upcoming' ? 'bg-indigo-600' : 'bg-slate-300'
        }`}>
        {type === 'upcoming' && <div className="absolute inset-0 bg-indigo-500 rounded-2xl animate-ping opacity-20" />}
        <Clock size={12} className="text-white relative z-10" />
      </div>

      <div className={`group bg-white/80 backdrop-blur-md p-8 rounded-[40px] border-2 transition-all duration-500 hover:shadow-2xl hover:shadow-indigo-500/10 cursor-pointer ${type === 'upcoming' ? 'border-slate-50 hover:border-indigo-100' : 'border-transparent opacity-60 hover:opacity-100 grayscale hover:grayscale-0'
        }`}>
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-8">
          <div className="space-y-4">
            <div className="flex items-center gap-4">
              <span className={`px-4 py-1.5 rounded-xl text-[10px] font-black uppercase tracking-[0.2em] border shadow-sm ${type === 'upcoming' ? 'bg-indigo-50 text-indigo-700 border-indigo-100' : 'bg-slate-50 text-slate-500 border-slate-100'
                }`}>
                {formatTime(meeting.start_time)}
              </span>
              <h3 className="text-2xl font-[1000] text-slate-900 group-hover:text-indigo-600 transition-colors tracking-tight leading-none">{meeting.title}</h3>
            </div>

            <div className="flex flex-wrap items-center gap-8 text-sm text-slate-500 font-bold">
              <span className="flex items-center gap-2.5 px-4 py-2 bg-slate-50 rounded-2xl border border-slate-100/50"><Users size={16} className="text-indigo-500" /> {meeting.participants?.length || 0} Operatives</span>
              {meeting.location && <span className="flex items-center gap-2.5 px-4 py-2 bg-rose-50 rounded-2xl border border-rose-100/50 text-rose-600"><MapPin size={16} /> {meeting.location}</span>}
              <span className="flex items-center gap-2.5 px-4 py-2 bg-indigo-50/50 rounded-2xl border border-indigo-100/50 text-indigo-600 uppercase tracking-widest text-[10px]"><Clock size={16} /> 60m Cluster</span>
            </div>
          </div>

          <div className="flex items-center gap-4">
            {meeting.meeting_link && (
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => window.open(meeting.meeting_link, '_blank')}
                className="flex items-center gap-3 px-8 py-4 bg-slate-900 text-white rounded-[24px] text-xs font-black uppercase tracking-widest group/btn transition-all shadow-xl shadow-slate-900/10"
              >
                Establish Link <ExternalLink size={16} className="group-hover/btn:translate-x-1 group-hover/btn:-translate-y-1 transition-transform" />
              </motion.button>
            )}
            <button
              onClick={() => handleDelete(meeting.id)}
              className="p-4 text-slate-200 hover:text-rose-500 hover:bg-rose-50 rounded-[20px] transition-all"
            >
              <Trash2 size={20} />
            </button>
          </div>
        </div>
      </div>
    </motion.div>
  );

  return (
    <div className="min-h-screen bg-[#f8fafc] text-slate-900 overflow-hidden font-sans">
      {/* Dynamic Background Elements */}
      <div className="fixed top-0 right-0 w-[600px] h-[600px] bg-indigo-50/50 rounded-full blur-[120px] -mr-64 -mt-64 animate-pulse" />
      <div className="fixed bottom-0 left-0 w-[500px] h-[500px] bg-sky-50/50 rounded-full blur-[100px] -ml-48 -mb-48" />

      <div className="max-w-[1400px] mx-auto px-8 py-16 lg:px-12 relative z-10">

        {/* Elite Header */}
        <div className="flex flex-col lg:flex-row lg:items-end justify-between gap-12 mb-20">
          <div className="space-y-6">
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              className="inline-flex items-center gap-2 px-4 py-2 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-[0.3em] border border-indigo-100"
            >
              <Activity size={14} className="fill-indigo-600" /> Temporal Matrix
            </motion.div>
            <h1 className="text-7xl font-[1000] tracking-tight leading-[0.95] text-slate-900">
              Nexus <span className="gradient-text">Synchronization.</span>
            </h1>
            <p className="text-slate-500 font-medium text-xl max-w-xl leading-relaxed">
              Orchestrate mission-critical sessions and align your squad with high-velocity data exchanges.
            </p>
          </div>

          <div className="flex items-center gap-6">
            <button
              onClick={() => navigate("/calendar")}
              className="h-16 px-10 rounded-[28px] bg-white border-2 border-slate-100 font-black uppercase tracking-widest text-[10px] text-slate-500 hover:border-indigo-600 hover:text-indigo-600 hover:shadow-xl transition-all shadow-[0_4px_20px_-10px_rgba(0,0,0,0.05)]"
            >
              Grid Analysis
            </button>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => navigate("/meetings/schedule")}
              className="h-16 px-12 bg-slate-900 text-white rounded-[28px] font-black uppercase tracking-widest text-[10px] shadow-2xl shadow-slate-200 hover:bg-indigo-600 transition-all flex items-center gap-4"
            >
              <Plus size={20} className="stroke-[3]" /> Initialize Session
            </motion.button>
          </div>
        </div>

        {loading ? (
          <div className="py-40 flex flex-col items-center justify-center gap-8">
            <div className="w-20 h-20 bg-indigo-600 rounded-[30px] flex items-center justify-center animate-bounce shadow-2xl shadow-indigo-100">
              <Zap size={32} className="text-white fill-white" />
            </div>
            <p className="text-[10px] font-black uppercase tracking-[0.5em] text-slate-400">Synchronizing temporal data</p>
          </div>
        ) : (
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate="visible"
            className="grid grid-cols-1 lg:grid-cols-12 gap-16"
          >

            {/* Left Column: Live & Timeline */}
            <div className="lg:col-span-7 space-y-16">
              {/* Hero: Live Protocol */}
              {currentOrNext && (
                <motion.section
                  variants={itemVariants}
                  className="relative overflow-hidden group rounded-[48px] shadow-2xl shadow-indigo-500/10"
                >
                  <div className="absolute inset-0 bg-gradient-to-br from-indigo-600 via-indigo-700 to-violet-800" />
                  <div className="absolute top-0 right-0 w-80 h-80 bg-white/5 rounded-full blur-3xl -mr-40 -mt-40 transition-transform group-hover:scale-125" />
                  <div className="absolute bottom-0 left-0 w-60 h-60 bg-sky-400/10 rounded-full blur-2xl -ml-20 -mb-20" />

                  <div className="relative p-12 md:p-16 text-white flex flex-col md:flex-row justify-between gap-12">
                    <div className="space-y-8 flex-1">
                      <div className="flex items-center gap-4">
                        <div className="px-5 py-2 bg-white/10 backdrop-blur-xl border border-white/20 rounded-2xl text-[10px] font-black uppercase tracking-[0.3em]">
                          {presentMeetings.length > 0 ? 'Protocol Active' : 'Sequential Queue'}
                        </div>
                        <span className="text-white/60 text-sm font-black tracking-widest uppercase">{formatTime(currentOrNext.start_time)}</span>
                      </div>
                      <h2 className="text-5xl font-[1000] leading-none tracking-tight max-w-sm">{currentOrNext.title}</h2>
                      <div className="flex flex-wrap items-center gap-8">
                        <div className="flex items-center gap-3 px-6 py-3 bg-white/5 backdrop-blur-md rounded-3xl border border-white/10 shadow-xl">
                          <Users size={18} className="text-sky-400" /> <span className="text-xs font-black uppercase tracking-widest">{currentOrNext.participants?.length || 0} Authenticated</span>
                        </div>
                        {currentOrNext.meeting_link && (
                          <button
                            onClick={() => window.open(currentOrNext.meeting_link, '_blank')}
                            className="flex items-center gap-3 text-xs font-black uppercase tracking-[0.2em] border-b-2 border-white/20 hover:border-white transition-all pb-1"
                          >
                            Establish Link <ArrowRight size={18} />
                          </button>
                        )}
                      </div>
                    </div>

                    <div className="flex flex-col items-center justify-center space-y-6">
                      <div className="relative">
                        <div className="absolute inset-0 bg-white/20 rounded-[40px] blur-2xl animate-pulse" />
                        <div className="w-28 h-28 rounded-[40px] bg-white text-indigo-600 flex items-center justify-center shadow-2xl relative z-10 transition-transform hover:rotate-6">
                          <Video size={40} className="stroke-[2.5]" />
                        </div>
                      </div>
                      <button
                        onClick={() => window.open(currentOrNext.meeting_link, '_blank')}
                        className="w-full px-8 py-5 bg-white text-slate-900 rounded-3xl font-black uppercase tracking-widest text-[10px] shadow-2xl hover:bg-slate-50 transition-all active:scale-95"
                      >
                        Join Protocol
                      </button>
                    </div>
                  </div>
                </motion.section>
              )}

              {/* Today's Sequence */}
              <section className="space-y-12">
                <div className="flex items-center justify-between border-b border-slate-100 pb-8">
                  <h2 className="text-xs font-black uppercase tracking-[0.4em] text-slate-400">Temporal Sequence</h2>
                  <div className="flex items-center gap-3">
                    <span className="w-3 h-3 bg-indigo-600 rounded-full animate-ping" />
                    <span className="text-[10px] font-black text-indigo-600 uppercase tracking-widest">{upcomingMeetings.length} Operations Pending</span>
                  </div>
                </div>

                <div className="space-y-0">
                  {upcomingMeetings.length > 0 ? (
                    <div>
                      {upcomingMeetings.map((m, idx) => (
                        <TimelineNode key={m.id} meeting={m} type="upcoming" idx={idx} />
                      ))}
                    </div>
                  ) : (
                    <motion.div
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      className="p-24 bg-white/50 backdrop-blur-sm border-2 border-dashed border-slate-100 rounded-[56px] text-center group"
                    >
                      <div className="w-24 h-24 bg-slate-50 rounded-[40px] flex items-center justify-center mx-auto mb-8 group-hover:scale-110 transition-transform">
                        <Layers size={40} className="text-slate-200" />
                      </div>
                      <h3 className="text-3xl font-[1000] text-slate-900 tracking-tight">Sequence Termination</h3>
                      <p className="text-slate-400 font-bold uppercase tracking-widest text-[10px] mt-2">All daily synchronization protocols completed.</p>
                    </motion.div>
                  )}
                </div>
              </section>
            </div>

            {/* Right Column: Analytics & Archive */}
            <aside className="lg:col-span-5 space-y-16">
              <div className="sticky top-12 space-y-12">
                {/* Visual Analytics Deck */}
                <div className="bg-slate-900 rounded-[56px] p-12 text-white shadow-2xl space-y-12 relative overflow-hidden group">
                  <div className="absolute top-0 right-0 p-12 opacity-5 scale-150 rotate-12 transition-transform group-hover:rotate-0">
                    <Shield size={200} />
                  </div>

                  <div className="space-y-4">
                    <div className="flex items-center gap-3">
                      <Star size={16} className="text-amber-400 fill-amber-400" />
                      <h3 className="text-[10px] font-black uppercase tracking-[0.4em] text-white/40">Squad Velocity</h3>
                    </div>
                    <p className="text-4xl font-[1000] leading-tight">Optimization <span className="text-indigo-400 underline decoration-indigo-600/30 underline-offset-8">Achieved</span></p>
                  </div>

                  <div className="grid grid-cols-2 gap-6">
                    <div className="p-8 bg-white/5 rounded-[32px] border border-white/5 hover:bg-white/10 transition-colors group/card">
                      <p className="text-[10px] font-black text-white/30 mb-3 uppercase tracking-widest group-hover/card:text-indigo-400 transition-colors">Operations</p>
                      <p className="text-4xl font-[1000] tracking-tighter">{meetings.length}</p>
                    </div>
                    <div className="p-8 bg-white/5 rounded-[32px] border border-white/5 hover:bg-white/10 transition-colors group/card">
                      <p className="text-[10px] font-black text-white/30 mb-3 uppercase tracking-widest group-hover/card:text-indigo-400 transition-colors">Participants</p>
                      <p className="text-4xl font-[1000] tracking-tighter">842</p>
                    </div>
                  </div>

                  <div className="space-y-6 pt-6">
                    <div className="flex justify-between items-center">
                      <span className="text-[10px] font-black uppercase tracking-widest text-white/40">Matrix Load Factor</span>
                      <span className="text-xs font-black text-indigo-400 uppercase tracking-widest">Nominal</span>
                    </div>
                    <div className="w-full h-3 bg-white/5 rounded-full overflow-hidden p-1 border border-white/10">
                      <div className="w-[72%] h-full bg-gradient-to-r from-indigo-500 via-violet-500 to-sky-400 rounded-full" />
                    </div>
                  </div>
                </div>

                {/* Historical Log */}
                <div className="space-y-10">
                  <h2 className="text-xs font-black uppercase tracking-[0.4em] text-slate-400 flex items-center gap-6">
                    <span className="w-12 h-[2px] bg-slate-100"></span>
                    Nexus Archive
                  </h2>
                  <div className="space-y-4">
                    {pastMeetings.slice(0, 5).map(m => (
                      <motion.div
                        key={m.id}
                        whileHover={{ x: 8 }}
                        className="p-6 bg-white border border-slate-50 rounded-[32px] flex items-center justify-between group cursor-pointer hover:border-indigo-100 hover:shadow-xl transition-all"
                      >
                        <div className="flex items-center gap-5">
                          <div className="w-14 h-14 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:bg-slate-900 group-hover:text-white transition-all shadow-sm">
                            <History size={20} />
                          </div>
                          <div>
                            <h4 className="text-base font-black text-slate-800 truncate max-w-[180px] tracking-tight">{m.title}</h4>
                            <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-1">
                              {formatDate(m.start_time).split(',')[0]}  Executed
                            </p>
                          </div>
                        </div>
                        <button
                          onClick={() => handleDelete(m.id)}
                          className="p-3 text-slate-200 hover:text-rose-500 hover:bg-rose-50 rounded-xl transition-all"
                        >
                          <Trash2 size={18} />
                        </button>
                      </motion.div>
                    ))}
                  </div>
                </div>
              </div>
            </aside>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default MeetingsPage;
