import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { authService } from "../../services/authService";
import { Mail, Phone, MapPin, Briefcase, Camera, Save, Loader2, Lock, Star, Shield, Activity, Zap, ArrowRight, UserCheck } from "lucide-react";

const ProfilePage: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [profile, setProfile] = useState<any>({
    name: "",
    email: "",
    role: "Operative",
    job_title: "",
    avatar: "https://i.pravatar.cc/150?u=a042581f4e29026704d",
    phone: "",
    address: "",
    bio: "",
    two_factor_enabled: false
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userData = await authService.getCurrentUser();
        setProfile({
          ...userData,
          avatar: userData.avatar || "https://i.pravatar.cc/150?u=a042581f4e29026704d"
        });
      } catch (error) {
        console.error("Failed to load profile", error);
      } finally {
        setLoading(false);
      }
    };
    fetchUser();
  }, []);

  const handleSave = async () => {
    try {
      setSaving(true);
      const { id, email, hashed_password, last_seen, status, ...updateData } = profile;
      await authService.updateProfile(updateData);
      setIsEditing(false);
    } catch (error) {
      console.error("Failed to update profile", error);
    } finally {
      setSaving(false);
    }
  };

  const fileInputRef = React.useRef<HTMLInputElement>(null);

  const handleImageClick = () => {
    if (isEditing) {
      fileInputRef.current?.click();
    }
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfile((prev: any) => ({ ...prev, avatar: reader.result as string }));
      };
      reader.readAsDataURL(file);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-[#f8fafc]">
        <div className="w-16 h-16 bg-indigo-600 rounded-3xl flex items-center justify-center animate-bounce shadow-2xl">
          <Activity size={32} className="text-white" />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#f8fafc] text-slate-900 p-8 lg:p-12 overflow-hidden relative font-sans">
      {/* Background Orbs */}
      <div className="fixed top-0 right-0 w-[800px] h-[800px] bg-indigo-50/50 rounded-full blur-[150px] -mr-64 -mt-64 animate-pulse" />
      <div className="fixed bottom-0 left-0 w-[600px] h-[600px] bg-sky-50/50 rounded-full blur-[120px] -ml-48 -mb-48" />

      <div className="max-w-7xl mx-auto relative z-10">

        {/* Elite Header */}
        <div className="flex flex-col lg:flex-row lg:items-end justify-between gap-12 mb-20">
          <div className="space-y-6">
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              className="inline-flex items-center gap-2 px-4 py-2 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-[0.3em] border border-indigo-100 shadow-sm"
            >
              <UserCheck size={14} className="fill-indigo-600" /> Identity Core
            </motion.div>
            <h1 className="text-7xl font-[1000] tracking-tight leading-[0.95] text-slate-900">
              Personal <span className="gradient-text">Protocol.</span>
            </h1>
            <p className="text-slate-500 font-medium text-xl max-w-xl leading-relaxed">
              Management interface for your high-performance identity within the team continuum.
            </p>
          </div>

          <div className="flex items-center gap-6">
            {isEditing && (
              <button
                onClick={() => setIsEditing(false)}
                className="h-16 px-8 rounded-[28px] font-black uppercase tracking-widest text-[10px] text-slate-400 hover:text-slate-900 transition-all"
              >
                Cancel Protocol
              </button>
            )}
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => isEditing ? handleSave() : setIsEditing(true)}
              disabled={saving}
              className={`h-16 px-12 rounded-[28px] font-black uppercase tracking-widest text-[10px] shadow-2xl transition-all flex items-center gap-4 ${isEditing ? 'bg-indigo-600 text-white shadow-indigo-200' : 'bg-slate-900 text-white shadow-slate-200 hover:bg-indigo-600'
                }`}
            >
              {saving ? <Loader2 size={20} className="animate-spin" /> : isEditing ? <Save size={20} /> : <Zap size={20} />}
              {saving ? "Synchronizing..." : isEditing ? "Commit Changes" : "Modify Identity"}
            </motion.button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-12 gap-16 items-start">

          {/* Left Column: Profile Avatar Card */}
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="lg:col-span-4"
          >
            <div className="bg-white rounded-[56px] border-2 border-slate-50 p-12 shadow-2xl shadow-indigo-500/5 flex flex-col items-center text-center relative overflow-hidden group">
              <div className="absolute top-0 right-0 p-12 opacity-5 scale-150 rotate-12 transition-transform group-hover:rotate-0">
                <Star size={200} />
              </div>

              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                accept="image/*"
                className="hidden"
              />
              <div
                className={`relative mb-10 ${isEditing ? 'cursor-pointer' : ''}`}
                onClick={handleImageClick}
              >
                <div className="w-48 h-48 rounded-[64px] overflow-hidden border-8 border-white shadow-2xl transition-transform group-hover:rotate-3">
                  <img
                    src={profile.avatar}
                    alt={profile.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                {isEditing && (
                  <div className="absolute inset-0 flex items-center justify-center bg-black/40 rounded-[64px] opacity-0 hover:opacity-100 transition-opacity">
                    <Camera className="text-white w-10 h-10" />
                  </div>
                )}
                <div className="absolute -bottom-4 -right-4 w-14 h-14 bg-indigo-600 text-white rounded-[24px] flex items-center justify-center shadow-xl border-4 border-white">
                  <Shield size={24} className="fill-white" />
                </div>
              </div>

              <h2 className="text-4xl font-[1000] text-slate-900 tracking-tight leading-none uppercase">{profile.name}</h2>
              <p className="text-indigo-600 font-black text-[10px] uppercase tracking-[0.4em] mt-4">{profile.job_title || profile.role}</p>

              <div className="w-full mt-12 space-y-4">
                <div className="flex items-center gap-5 p-6 bg-slate-50/50 rounded-[32px] border-2 border-slate-50 group-hover:border-indigo-100 transition-colors">
                  <div className="w-12 h-12 bg-white rounded-2xl flex items-center justify-center shadow-sm text-indigo-600">
                    <Mail size={20} />
                  </div>
                  <div className="text-left">
                    <p className="text-[8px] font-black text-slate-400 uppercase tracking-widest">Digital Coordinate</p>
                    <p className="text-sm font-black text-slate-700 truncate max-w-[180px]">{profile.email}</p>
                  </div>
                </div>
                <div className="flex items-center gap-5 p-6 bg-slate-50/50 rounded-[32px] border-2 border-slate-50 group-hover:border-indigo-100 transition-colors">
                  <div className="w-12 h-12 bg-white rounded-2xl flex items-center justify-center shadow-sm text-indigo-600">
                    <Briefcase size={20} />
                  </div>
                  <div className="text-left">
                    <p className="text-[8px] font-black text-slate-400 uppercase tracking-widest">Matrix Status</p>
                    <p className="text-sm font-black text-slate-700">{profile.role}</p>
                  </div>
                </div>
              </div>
            </div>
          </motion.div>

          {/* Right Column: Form Modules */}
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            className="lg:col-span-8 space-y-12"
          >
            {/* Module 1: Core Logistics */}
            <div className="bg-white rounded-[56px] border-2 border-slate-50 shadow-2xl shadow-indigo-500/5 overflow-hidden">
              <div className="px-12 py-8 bg-slate-50/50 border-b border-slate-50 flex items-center gap-6">
                <div className="p-4 bg-white rounded-2xl shadow-sm text-indigo-600">
                  <Activity size={24} />
                </div>
                <div>
                  <h3 className="text-xl font-[1000] text-slate-900 tracking-tight leading-none uppercase">Core Logistics</h3>
                  <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-2">Manage your tactical data fields</p>
                </div>
              </div>

              <div className="p-12 grid grid-cols-1 md:grid-cols-2 gap-8">
                <div className="space-y-3">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Authorized Name</label>
                  <input
                    type="text"
                    disabled={!isEditing}
                    value={profile.name}
                    onChange={(e) => setProfile({ ...profile, name: e.target.value })}
                    className="w-full px-8 py-5 bg-white border-2 border-slate-50 rounded-[24px] focus:border-indigo-600/20 focus:outline-none text-slate-900 font-bold text-sm shadow-sm transition-all disabled:bg-slate-50/50 disabled:text-slate-400"
                  />
                </div>

                <div className="space-y-3">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Deployment Title</label>
                  <input
                    type="text"
                    disabled={!isEditing}
                    placeholder="e.g. Lead Strategist"
                    value={profile.job_title || ''}
                    onChange={(e) => setProfile({ ...profile, job_title: e.target.value })}
                    className="w-full px-8 py-5 bg-white border-2 border-slate-50 rounded-[24px] focus:border-indigo-600/20 focus:outline-none text-slate-900 font-bold text-sm shadow-sm transition-all disabled:bg-slate-50/50 disabled:text-slate-400"
                  />
                </div>

                <div className="space-y-3">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Email (Read-Only)</label>
                  <div className="relative">
                    <input
                      type="email"
                      disabled={true}
                      value={profile.email}
                      className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[24px] text-slate-300 font-bold text-sm cursor-not-allowed"
                    />
                    <Lock size={16} className="absolute right-8 top-1/2 -translate-y-1/2 text-slate-200" />
                  </div>
                </div>

                <div className="space-y-3">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Comms Link (Phone)</label>
                  <div className="relative">
                    <Phone className="absolute left-8 top-1/2 -translate-y-1/2 text-slate-300" size={16} />
                    <input
                      type="text"
                      disabled={!isEditing}
                      placeholder="+1 (555) 000-0000"
                      value={profile.phone || ''}
                      onChange={(e) => setProfile({ ...profile, phone: e.target.value })}
                      className="w-full pl-16 pr-8 py-5 bg-white border-2 border-slate-50 rounded-[24px] focus:border-indigo-600/20 focus:outline-none text-slate-900 font-bold text-sm shadow-sm transition-all disabled:bg-slate-50/50 disabled:text-slate-400"
                    />
                  </div>
                </div>

                <div className="md:col-span-2 space-y-3">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Neural Bio (Mission Brief)</label>
                  <textarea
                    rows={4}
                    disabled={!isEditing}
                    value={profile.bio || ''}
                    onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                    placeholder="Brief the squad on your expertise..."
                    className="w-full px-8 py-5 bg-white border-2 border-slate-50 rounded-[32px] focus:border-indigo-600/20 focus:outline-none text-slate-900 font-bold text-sm shadow-sm transition-all disabled:bg-slate-50/50 disabled:text-slate-400 resize-none"
                  />
                </div>
              </div>
            </div>

            {/* Module 2: Security Deck Upgrade */}
            <div className="bg-slate-900 rounded-[56px] p-12 text-white shadow-2xl relative overflow-hidden group">
              <div className="absolute top-0 right-0 p-12 opacity-10 group-hover:scale-110 transition-transform">
                <Shield size={150} />
              </div>
              <div className="relative z-10 flex flex-col md:flex-row items-center justify-between gap-12">
                <div className="space-y-6">
                  <div className="flex items-center gap-4">
                    <div className="w-16 h-16 bg-white/10 rounded-2xl flex items-center justify-center text-indigo-400">
                      <Lock size={32} />
                    </div>
                    <h4 className="text-3xl font-[1000] tracking-tight leading-none uppercase">Identity Shield</h4>
                  </div>
                  <p className="text-white/50 text-base font-bold max-w-lg leading-relaxed">
                    Upgrade your matrix identity with two-factor operational authentication.
                    Protect your squad's data with enterprise-grade encryption protocols.
                  </p>
                </div>
                <button className="h-16 px-10 bg-white text-slate-900 rounded-[24px] font-[1000] uppercase tracking-widest text-[10px] shadow-2xl hover:bg-emerald-500 hover:text-white transition-all flex items-center gap-4">
                  Protocol Settings <ArrowRight size={18} />
                </button>
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
