  Globe, BriefCase, Loader2, Video, ArrowUpRight, Plus,
  Info, ChevronRight
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { meetingService } from "../../services/meetingService";

const ScheduleMeetingPage: React.FC = () => {
  const navigate = useNavigate();
  const [meeting, setMeeting] = useState({
    title: "",
    date: "",
    time: "",
    end_time: "",
    notes: "",
    meeting_link: "",
    is_recurring_mon_fri: false
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!meeting.title.trim() || !meeting.date || !meeting.time || !meeting.end_time) {
      return;
    }

    setLoading(true);
    try {
      const createMeetingData = (dateStr: string) => ({
        title: meeting.title,
        description: meeting.notes,
        start_time: new Date(`${dateStr}T${meeting.time}:00`).toISOString(),
        end_time: new Date(`${dateStr}T${meeting.end_time}:00`).toISOString(),
        meeting_link: meeting.meeting_link,
        location: "Virtual Meeting"
      });

      if (meeting.is_recurring_mon_fri) {
        const baseDate = new Date(meeting.date);
        const datesToCreate: string[] = [];
        const day = baseDate.getDay();
        const diff = baseDate.getDate() - day + (day === 0 ? -6 : 1);
        const monday = new Date(baseDate.setDate(diff));

        for (let i = 0; i < 5; i++) {
          const d = new Date(monday);
          d.setDate(monday.getDate() + i);
          datesToCreate.push(d.toISOString().split('T')[0]);
        }
        await Promise.all(datesToCreate.map(d => meetingService.createMeeting(createMeetingData(d))));
      } else {
        await meetingService.createMeeting(createMeetingData(meeting.date));
      }
      navigate("/meetings");
    } catch (error) {
      console.error("Failed to schedule meeting", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#fcfdfe] text-slate-900 px-8 py-10 lg:px-12 animate-fade-in relative">
      <div className="max-w-6xl mx-auto">

        {/* Header Navigation */}
        <div className="flex items-center justify-between mb-12">
          <button
            onClick={() => navigate("/meetings")}
            className="flex items-center gap-3 text-slate-400 hover:text-indigo-600 transition-all font-bold text-sm group"
          >
            <div className="w-10 h-10 rounded-xl bg-white border border-slate-100 flex items-center justify-center shadow-sm group-hover:bg-indigo-50 group-hover:border-indigo-100 transition-all">
              <ArrowLeft size={18} className="group-hover:-translate-x-1 transition-transform" />
            </div>
            Back to Meetings
          </button>

          <div className="flex items-center gap-3 px-4 py-2 bg-indigo-50 text-indigo-600 rounded-xl font-bold text-[11px] uppercase tracking-widest border border-indigo-100">
            <Video size={14} />
            Meeting Protocol
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-12 gap-12 items-start">
          {/* Left: Info */}
          <div className="lg:col-span-4 space-y-8">
            <div>
              <h1 className="text-4xl font-bold tracking-tight text-slate-900 mb-4 leading-tight">Schedule a Meeting</h1>
              <p className="text-sm font-medium text-slate-500 leading-relaxed">
                Connect with your team by setting up a new session. Your team will receive notifications once the meeting is scheduled.
              </p>
            </div>

            <div className="bg-white rounded-[32px] border border-slate-100 p-8 shadow-sm space-y-8">
              <div className="flex items-start gap-4">
                <div className="w-12 h-12 rounded-2xl bg-emerald-50 text-emerald-600 flex items-center justify-center shrink-0">
                  <Shield size={20} />
                </div>
                <div>
                  <h4 className="text-sm font-bold text-slate-900 mb-1">Secure Channel</h4>
                  <p className="text-xs font-semibold text-slate-400">All meetings are encrypted and identity-verified.</p>
                </div>
              </div>
              <div className="flex items-start gap-4">
                <div className="w-12 h-12 rounded-2xl bg-indigo-50 text-indigo-600 flex items-center justify-center shrink-0">
                  <Zap size={20} />
                </div>
                <div>
                  <h4 className="text-sm font-bold text-slate-900 mb-1">Instant Sync</h4>
                  <p className="text-xs font-semibold text-slate-400">Calendar invitations are automatically sent.</p>
                </div>
              </div>
            </div>

            <div className="bg-slate-900 rounded-[32px] p-8 text-white relative overflow-hidden shadow-xl shadow-slate-200">
              <div className="absolute top-0 right-0 p-6 opacity-10">
                <Sparkles size={80} />
              </div>
              <h4 className="text-md font-bold mb-3">Scheduling Tip</h4>
              <p className="text-xs font-medium text-slate-400 leading-relaxed">
                Choose a title that clearly defines the purpose of your session for better attendance rates.
              </p>
            </div>
          </div>

          {/* Right: Form */}
          <div className="lg:col-span-8">
            <div className="bg-white rounded-[40px] border border-slate-100 p-8 md:p-12 shadow-sm relative">
              <form onSubmit={handleSubmit} className="space-y-8">
                <div className="space-y-2">
                  <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Meeting Title</label>
                  <input
                    type="text"
                    value={meeting.title}
                    onChange={(e) => setMeeting({ ...meeting, title: e.target.value })}
                    placeholder="e.g. Weekly Strategy Sync"
                    className="input-field"
                    required
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                  <div className="space-y-2">
                    <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Date</label>
                    <div className="relative">
                      <Calendar className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                      <input
                        type="date"
                        value={meeting.date}
                        onChange={(e) => setMeeting({ ...meeting, date: e.target.value })}
                        className="input-field pl-12"
                        required
                      />
                    </div>
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Start</label>
                      <div className="relative">
                        <Clock className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={16} />
                        <input
                          type="time"
                          value={meeting.time}
                          onChange={(e) => setMeeting({ ...meeting, time: e.target.value })}
                          className="input-field pl-12"
                          required
                        />
                      </div>
                    </div>
                    <div className="space-y-2">
                      <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">End</label>
                      <div className="relative">
                        <Clock className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={16} />
                        <input
                          type="time"
                          value={meeting.end_time}
                          onChange={(e) => setMeeting({ ...meeting, end_time: e.target.value })}
                          className="input-field pl-12"
                          required
                        />
                      </div>
                    </div>
                  </div>
                </div>

                <div
                  className={`p-6 rounded-[28px] border transition-all cursor-pointer flex items-center justify-between gap-6 ${meeting.is_recurring_mon_fri
                      ? 'bg-indigo-600 border-indigo-600 text-white shadow-lg shadow-indigo-100'
                      : 'bg-slate-50 border-slate-100 hover:border-indigo-100'
                    }`}
                  onClick={() => setMeeting({ ...meeting, is_recurring_mon_fri: !meeting.is_recurring_mon_fri })}
                >
                  <div className="flex items-center gap-5">
                    <div className={`w-12 h-12 rounded-2xl flex items-center justify-center transition-all ${meeting.is_recurring_mon_fri ? 'bg-white/20 text-white' : 'bg-white text-slate-400 border border-slate-100 shadow-sm'
                      }`}>
                      <Activity size={20} />
                    </div>
                    <div>
                      <p className={`text-sm font-bold mb-0.5 ${meeting.is_recurring_mon_fri ? 'text-white' : 'text-slate-900'}`}>Recurring Loop</p>
                      <p className={`text-[10px] font-bold uppercase tracking-wider ${meeting.is_recurring_mon_fri ? 'text-indigo-100' : 'text-slate-400'}`}>Schedule for Mon - Fri</p>
                    </div>
                  </div>
                  <div className={`w-12 h-6 relative rounded-full transition-all ${meeting.is_recurring_mon_fri ? 'bg-white/40' : 'bg-slate-200'}`}>
                    <div className={`absolute top-1 w-4 h-4 bg-white rounded-full shadow-sm transition-all ${meeting.is_recurring_mon_fri ? 'left-7' : 'left-1'}`} />
                  </div>
                </div>

                <div className="space-y-2">
                  <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Meeting Link</label>
                  <div className="relative">
                    <LinkIcon className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                    <input
                      type="url"
                      value={meeting.meeting_link}
                      onChange={(e) => setMeeting({ ...meeting, meeting_link: e.target.value })}
                      placeholder="https://vc.example.com/stream/..."
                      className="input-field pl-12"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Meeting Notes</label>
                  <textarea
                    value={meeting.notes}
                    onChange={(e) => setMeeting({ ...meeting, notes: e.target.value })}
                    rows={4}
                    placeholder="Briefly outline the goals for this session..."
                    className="w-full px-6 py-4 bg-white border border-slate-100 rounded-[28px] focus:border-indigo-500 focus:ring-4 focus:ring-indigo-500/5 outline-none text-sm font-medium text-slate-900 transition-all shadow-sm resize-none leading-relaxed"
                  />
                </div>

                <div className="pt-8 flex flex-col sm:flex-row items-center justify-end gap-4 border-t border-slate-50">
                  <button
                    type="button"
                    onClick={() => navigate("/meetings")}
                    className="w-full sm:w-auto px-10 py-4 text-xs font-bold text-slate-400 hover:text-slate-900 transition-colors"
                  >
                    Discard Changes
                  </button>
                  <button
                    type="submit"
                    disabled={loading}
                    className="w-full sm:w-auto btn-primary shadow-lg shadow-indigo-100 min-w-[200px]"
                  >
                    {loading ? (
                      <Loader2 size={18} className="animate-spin" />
                    ) : (
                      <>
                        Schedule Meeting
                        <ArrowUpRight size={18} />
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ScheduleMeetingPage;
