import React, { useState, useEffect, useRef, useCallback } from "react";
import {
    Search, Users, Paperclip, Smile, Send,
    Check, ArrowLeft, X, Sparkles, Zap, Shield, Plus
} from "lucide-react";
import { messageService, Chat, Message } from "../../services/messageService";
import { userService, User } from "../../services/userService";
import { authService } from "../../services/authService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";
import { motion, AnimatePresence } from "framer-motion";

const CreateGroupModal = ({ isOpen, onClose, users, onCreate }: any) => {
    const [groupName, setGroupName] = useState("");
    const [selectedIds, setSelectedIds] = useState<number[]>([]);

    const toggleUser = (id: number) => {
        setSelectedIds(prev => prev.includes(id) ? prev.filter(x => x !== id) : [...prev, id]);
    };

    const handleSubmit = () => {
        if (!groupName || selectedIds.length === 0) return;
        onCreate(groupName, selectedIds);
        onClose();
        setGroupName("");
        setSelectedIds([]);
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <div className="fixed inset-0 z-[100] flex items-center justify-center p-4">
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="absolute inset-0 bg-slate-900/40 backdrop-blur-sm"
                        onClick={onClose}
                    />
                    <motion.div
                        initial={{ scale: 0.95, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        exit={{ scale: 0.95, opacity: 0 }}
                        className="bg-white rounded-3xl w-full max-w-md p-8 shadow-2xl relative z-10"
                    >
                        <div className="flex justify-between items-center mb-6">
                            <h3 className="text-xl font-bold text-slate-900">New Group Chat</h3>
                            <button onClick={onClose} className="p-2 text-slate-400 hover:text-slate-600 rounded-lg">
                                <X size={20} />
                            </button>
                        </div>

                        <div className="space-y-6">
                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Group Name</label>
                                <input
                                    className="w-full px-4 py-3 bg-slate-50 border border-slate-100 rounded-xl focus:bg-white focus:border-indigo-500 transition-all font-medium text-slate-900 outline-none"
                                    value={groupName}
                                    onChange={e => setGroupName(e.target.value)}
                                    placeholder="e.g. Project Apollo"
                                />
                            </div>

                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Add Members</label>
                                <div className="max-h-60 overflow-y-auto space-y-1 pr-2 custom-scrollbar">
                                    {users.map((u: User) => (
                                        <button
                                            key={u.id}
                                            onClick={() => toggleUser(u.id)}
                                            className={`w-full flex items-center gap-4 p-3 rounded-xl transition-all ${selectedIds.includes(u.id) ? 'bg-indigo-50 text-indigo-700' : 'hover:bg-slate-50 text-slate-600'
                                                }`}
                                        >
                                            <div className={`w-9 h-9 rounded-lg flex items-center justify-center text-[11px] font-bold ${selectedIds.includes(u.id) ? 'bg-indigo-600 text-white' : 'bg-slate-200 text-slate-500'
                                                }`}>
                                                {u.name.substring(0, 2).toUpperCase()}
                                            </div>
                                            <span className="text-sm font-semibold">{u.name}</span>
                                            {selectedIds.includes(u.id) && <Check size={16} className="ml-auto" />}
                                        </button>
                                    ))}
                                </div>
                            </div>

                            <div className="flex gap-3 pt-4">
                                <button onClick={onClose} className="flex-1 py-3 text-slate-500 font-bold text-sm">Cancel</button>
                                <button
                                    onClick={handleSubmit}
                                    disabled={!groupName || selectedIds.length === 0}
                                    className="flex-1 py-3 bg-indigo-600 text-white rounded-xl font-bold text-sm disabled:opacity-50 hover:bg-indigo-700 transition-all"
                                >
                                    Create Group
                                </button>
                            </div>
                        </div>
                    </motion.div>
                </div>
            )}
        </AnimatePresence>
    );
};

const MessagesPage: React.FC = () => {
    const [chats, setChats] = useState<Chat[]>([]);
    const [messages, setMessages] = useState<Message[]>([]);
    const [currentUser, setCurrentUser] = useState<any>(null);
    const [activeChatId, setActiveChatId] = useState<number | null>(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [allUsers, setAllUsers] = useState<User[]>([]);
    const [inputText, setInputText] = useState("");
    const [isMobileChatOpen, setIsMobileChatOpen] = useState(false);
    const [isGroupModalOpen, setIsGroupModalOpen] = useState(false);

    const messagesContainerRef = useRef<HTMLDivElement>(null);

    const loadChats = useCallback(async () => {
        try {
            const data = await messageService.getChats();
            setChats(data);
        } catch (e) {
            console.error("Failed to load chats:", e);
        }
    }, []);

    const loadUserAndData = useCallback(async () => {
        try {
            const user = await authService.getCurrentUser();
            setCurrentUser(user);
            await loadChats();
            const users = await userService.getAllUsers();
            setAllUsers(users.filter(u => u.id !== user.id));
        } catch (e) {
            console.error("Failed to load user data:", e);
        }
    }, [loadChats]);

    const loadMessages = useCallback(async (chatId: number) => {
        try {
            const msgs = await messageService.getMessages(chatId);
            setMessages(msgs);
            await messageService.markAsRead(chatId);
            loadChats();
        } catch (e) {
            console.error("Failed to load messages:", e);
        }
    }, [loadChats]);

    const scrollToBottom = useCallback((smooth = true) => {
        if (messagesContainerRef.current) {
            const { scrollHeight, clientHeight } = messagesContainerRef.current;
            messagesContainerRef.current.scrollTo({
                top: scrollHeight - clientHeight,
                behavior: smooth ? 'smooth' : 'auto'
            });
        }
    }, []);

    useEffect(() => {
        loadUserAndData();
    }, [loadUserAndData]);

    useEffect(() => {
        const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
            if (message.type === 'message') {
                const msg = message.data;
                if (activeChatId && msg.chat_id === activeChatId) {
                    setMessages(prev => {
                        if (prev.some(m => m.id === msg.id)) return prev;
                        return [...prev, msg];
                    });
                    messageService.markAsRead(activeChatId);
                }
                loadChats();
            }
        });
        return () => {
            unsubscribe();
        };
    }, [activeChatId, loadChats]);

    useEffect(() => {
        if (activeChatId) {
            loadMessages(activeChatId);
            setTimeout(() => scrollToBottom(false), 100);
        }
    }, [activeChatId, loadMessages, scrollToBottom]);

    useEffect(() => {
        scrollToBottom();
    }, [messages, scrollToBottom]);

    const handleSendMessage = async () => {
        if (!inputText.trim() || !activeChatId) return;
        try {
            await messageService.sendMessage(activeChatId, inputText);
            setInputText("");
            loadMessages(activeChatId);
            loadChats();
            setTimeout(() => scrollToBottom(), 100);
        } catch (e) {
            console.error("Failed to send message:", e);
        }
    };

    const handleCreateP2PChat = async (userId: number) => {
        try {
            const chat = await messageService.createChat([userId], undefined, false);
            setChats(prev => prev.find(c => c.id === chat.id) ? prev : [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
            setSearchQuery("");
        } catch (e) {
            console.error("Failed to create P2P chat:", e);
        }
    };

    const handleCreateGroup = async (name: string, userIds: number[]) => {
        try {
            const chat = await messageService.createChat(userIds, name, true);
            setChats(prev => [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
        } catch (e) {
            console.error("Failed to create group chat:", e);
        }
    };

    const filteredChats = chats.filter(c => c.name?.toLowerCase().includes(searchQuery.toLowerCase()));
    const suggestedUsers = searchQuery ? allUsers.filter(u => u.name.toLowerCase().includes(searchQuery.toLowerCase())) : [];
    const activeChat = chats.find(c => c.id === activeChatId);

    return (
        <div className="flex h-screen bg-[#fcfdfe] overflow-hidden font-sans">
            <CreateGroupModal
                isOpen={isGroupModalOpen}
                onClose={() => setIsGroupModalOpen(false)}
                users={allUsers}
                onCreate={handleCreateGroup}
            />

            {/* Sidebar */}
            <div className={`w-full md:w-[350px] lg:w-[400px] bg-white border-r border-slate-100 flex flex-col p-6 space-y-6 ${isMobileChatOpen ? 'hidden md:flex' : 'flex'}`}>
                <div className="flex justify-between items-center">
                    <h1 className="text-2xl font-bold text-slate-900 tracking-tight">Messages</h1>
                    <button
                        onClick={() => setIsGroupModalOpen(true)}
                        className="p-2 border border-slate-100 bg-slate-50 text-indigo-600 rounded-xl hover:bg-indigo-50 transition-colors"
                        title="New Group"
                    >
                        <Plus size={20} />
                    </button>
                </div>

                <div className="relative">
                    <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={16} />
                    <input
                        className="w-full bg-slate-50 border-none rounded-xl py-3 pl-11 pr-4 text-sm font-medium text-slate-900 focus:bg-slate-100 focus:ring-0 outline-none transition-all placeholder:text-slate-400"
                        placeholder="Search conversations..."
                        value={searchQuery}
                        onChange={e => setSearchQuery(e.target.value)}
                    />
                </div>

                <div className="flex-1 overflow-y-auto space-y-1 -mx-2 px-2 custom-scrollbar">
                    {filteredChats.map(chat => (
                        <div
                            key={chat.id}
                            onClick={() => { setActiveChatId(chat.id); setIsMobileChatOpen(true); }}
                            className={`p-4 rounded-2xl flex items-center gap-4 cursor-pointer transition-all duration-200 ${activeChatId === chat.id ? 'bg-indigo-50' : 'hover:bg-slate-50'
                                }`}
                        >
                            <div className={`w-12 h-12 rounded-xl flex items-center justify-center text-white font-bold text-sm shrink-0 ${activeChatId === chat.id ? 'bg-indigo-600' : 'bg-slate-800'
                                }`}>
                                {chat.is_group ? <Users size={20} /> : (chat.name?.substring(0, 1).toUpperCase())}
                            </div>

                            <div className="flex-1 min-w-0">
                                <div className="flex justify-between items-center mb-0.5">
                                    <h3 className={`text-sm font-bold truncate ${activeChatId === chat.id ? 'text-indigo-900' : 'text-slate-900'}`}>{chat.name}</h3>
                                    <span className="text-[10px] font-bold text-slate-400 uppercase">
                                        {chat.last_message_time ? new Date(chat.last_message_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : ''}
                                    </span>
                                </div>
                                <div className="flex justify-between items-center">
                                    <p className="text-xs font-medium text-slate-500 truncate">{chat.last_message || "No messages yet"}</p>
                                    {chat.unread_count && chat.unread_count > 0 && (
                                        <div className="w-5 h-5 bg-indigo-600 text-white rounded-full flex items-center justify-center text-[10px] font-bold">
                                            {chat.unread_count}
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>
                    ))}

                    {suggestedUsers.length > 0 && (
                        <div className="pt-4 space-y-2">
                            <h4 className="px-4 text-[10px] font-bold text-slate-400 uppercase tracking-widest">Suggested</h4>
                            {suggestedUsers.map(user => (
                                <div
                                    key={user.id}
                                    onClick={() => handleCreateP2PChat(user.id)}
                                    className="p-3 hover:bg-slate-50 rounded-xl flex items-center gap-3 cursor-pointer group"
                                >
                                    <div className="w-10 h-10 rounded-lg bg-slate-100 flex items-center justify-center text-slate-500 font-bold text-xs uppercase group-hover:bg-indigo-100 group-hover:text-indigo-600 transition-colors">
                                        {user.name.substring(0, 2)}
                                    </div>
                                    <span className="text-sm font-semibold text-slate-700">{user.name}</span>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Chat Area */}
            {activeChat ? (
                <div className={`flex-1 flex flex-col bg-white ${isMobileChatOpen ? 'flex' : 'hidden md:flex'}`}>
                    {/* Chat Header */}
                    <div className="h-20 bg-white border-b border-slate-100 flex items-center justify-between px-8 shrink-0">
                        <div className="flex items-center gap-4">
                            <button onClick={() => setIsMobileChatOpen(false)} className="md:hidden p-2 text-slate-400 hover:text-slate-600">
                                <ArrowLeft size={20} />
                            </button>
                            <div className="relative">
                                <div className={`w-11 h-11 rounded-lg flex items-center justify-center text-white font-bold shrink-0 ${activeChat.is_group ? 'bg-indigo-600' : 'bg-slate-800'}`}>
                                    {activeChat.is_group ? <Users size={20} /> : activeChat.name?.substring(0, 1).toUpperCase()}
                                </div>
                                <div className="absolute -bottom-0.5 -right-0.5 w-3 h-3 bg-emerald-500 rounded-full border-2 border-white" />
                            </div>
                            <div>
                                <h2 className="text-base font-bold text-slate-900 leading-none mb-1">{activeChat.name}</h2>
                                <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">Active session</p>
                            </div>
                        </div>
                        <div className="flex items-center gap-1">
                            <button className="p-2.5 text-slate-400 hover:text-indigo-600 hover:bg-slate-50 rounded-lg transition-colors"><Shield size={18} /></button>
                        </div>
                    </div>

                    {/* Chat Messages */}
                    <div className="flex-1 relative flex flex-col overflow-hidden">
                        <div ref={messagesContainerRef} className="flex-1 overflow-y-auto p-8 space-y-6 custom-scrollbar">
                            <AnimatePresence mode="popLayout">
                                {messages.map((msg, idx) => {
                                    const isMe = msg.sender_id === currentUser?.id;
                                    const showSender = !isMe && activeChat.is_group && (idx === 0 || messages[idx - 1].sender_id !== msg.sender_id);

                                    return (
                                        <motion.div
                                            initial={{ opacity: 0, y: 5 }}
                                            animate={{ opacity: 1, y: 0 }}
                                            key={msg.id}
                                            className={`flex w-full ${isMe ? 'justify-end' : 'justify-start'}`}
                                        >
                                            <div className={`flex flex-col max-w-[70%] ${isMe ? 'items-end' : 'items-start'}`}>
                                                {showSender && <span className="text-[10px] font-bold text-slate-400 uppercase mb-1.5 ml-1">{msg.sender?.name}</span>}
                                                <div className={`px-5 py-3 rounded-2xl text-sm font-medium ${isMe
                                                    ? 'bg-indigo-600 text-white rounded-br-sm'
                                                    : 'bg-slate-100 text-slate-800 rounded-bl-sm'
                                                    }`}>
                                                    <p className="leading-relaxed">{msg.content}</p>
                                                </div>
                                                <div className="flex items-center gap-1.5 mt-1.5 px-1">
                                                    <span className="text-[10px] font-bold text-slate-300 uppercase">
                                                        {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                                    </span>
                                                    {isMe && <Check size={10} className="text-slate-300" />}
                                                </div>
                                            </div>
                                        </motion.div>
                                    );
                                })}
                            </AnimatePresence>
                        </div>
                    </div>

                    {/* Chat Input */}
                    <div className="p-6 bg-white border-t border-slate-50">
                        <div className="flex items-center gap-4 max-w-5xl mx-auto">
                            <button className="p-3 text-slate-400 hover:text-indigo-600 hover:bg-slate-50 rounded-xl transition-all"><Paperclip size={20} /></button>
                            <div className="flex-1 relative">
                                <input
                                    className="w-full px-5 py-3.5 bg-slate-50 border-none rounded-2xl text-sm font-medium text-slate-900 focus:bg-white focus:ring-1 focus:ring-slate-100 outline-none transition-all placeholder:text-slate-300"
                                    placeholder="Type a message..."
                                    value={inputText}
                                    onChange={e => setInputText(e.target.value)}
                                    onKeyDown={e => e.key === 'Enter' && handleSendMessage()}
                                />
                            </div>
                            <button
                                onClick={handleSendMessage}
                                disabled={!inputText.trim()}
                                className="p-3.5 bg-indigo-600 text-white rounded-2xl hover:bg-indigo-700 transition-all disabled:opacity-30 shadow-lg shadow-indigo-100"
                            >
                                <Send size={20} />
                            </button>
                        </div>
                    </div>
                </div>
            ) : (
                <div className="hidden md:flex flex-1 flex-col items-center justify-center bg-slate-50/50">
                    <div className="w-16 h-16 bg-white rounded-2xl flex items-center justify-center mb-6 shadow-sm border border-slate-100">
                        <Users size={28} className="text-slate-200" />
                    </div>
                    <h3 className="text-lg font-bold text-slate-900 mb-1">Select a conversation</h3>
                    <p className="text-sm font-medium text-slate-500">Pick an operative or squad to start chatting.</p>
                </div>
            )}
        </div>
    );
};

export default MessagesPage;
