import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Calendar,
    User,
    Save,
    Clock,
    CheckCircle2,
    Loader2
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";
import { motion } from "framer-motion";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal",
        assigned_to: "",
        due_date: new Date().toISOString().split('T')[0]
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : taskData.status,
                        priority: taskData.priority,
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : new Date().toISOString().split('T')[0]
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, navigate]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: formData.status as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (error) {
            console.error(error);
            setSubmitting(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-slate-50 flex items-center justify-center">
                <Loader2 className="w-8 h-8 text-indigo-600 animate-spin" />
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-[#F8FAFC] flex justify-center py-10 px-4">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.4 }}
                className="w-full max-w-2xl bg-white rounded-3xl shadow-xl shadow-slate-200/50 overflow-hidden flex flex-col min-h-[600px]"
            >
                {/* üü† Header Toolbar */}
                <div className="flex items-center justify-between px-6 py-4 border-b border-slate-100 bg-white/50 backdrop-blur-sm sticky top-0 z-10">
                    <button
                        onClick={() => navigate(-1)}
                        className="p-2 rounded-full hover:bg-slate-50 text-slate-400 hover:text-slate-700 transition"
                    >
                        <ChevronLeft size={22} />
                    </button>

                    <span className="text-sm font-semibold text-slate-400 tracking-wide uppercase">
                        {isEditing ? "Editing Task" : "New Task"}
                    </span>

                    <div className="w-10"></div> {/* Spacer */}
                </div>

                <form onSubmit={handleSubmit} className="flex-1 flex flex-col">
                    <div className="p-8 space-y-6 flex-1">

                        {/* Status Indicator (Purely Visual) */}
                        <div className="flex items-center gap-2 mb-2">
                            <div className="bg-indigo-50 text-indigo-600 px-3 py-1 rounded-full text-xs font-bold flex items-center gap-1.5">
                                <div className="w-1.5 h-1.5 rounded-full bg-indigo-500 animate-pulse"></div>
                                {formData.status}
                            </div>
                            <div className="text-slate-300">/</div>
                            <span className="text-slate-400 text-xs font-medium">Priority: {formData.priority}</span>
                        </div>

                        {/* üìù Title Input - Clean & Bold */}
                        <input
                            type="text"
                            value={formData.title}
                            onChange={e => setFormData({ ...formData, title: e.target.value })}
                            placeholder="Untitled Task"
                            required
                            className="w-full text-4xl font-bold text-slate-800 placeholder:text-slate-200 outline-none bg-transparent leading-tight"
                            autoFocus
                        />

                        {/* üß© Meta Tools Row */}
                        <div className="flex flex-wrap gap-4 pt-2">
                            {/* Assignee Pill */}
                            <div className="group relative flex items-center gap-2 bg-slate-50 hover:bg-white border border-transparent hover:border-slate-200 hover:shadow-sm rounded-full px-1 pl-1 pr-4 py-1 transition-all cursor-pointer">
                                <div className="w-8 h-8 rounded-full bg-indigo-100 text-indigo-600 flex items-center justify-center">
                                    <User size={16} />
                                </div>
                                <select
                                    value={formData.assigned_to}
                                    onChange={e => setFormData({ ...formData, assigned_to: e.target.value })}
                                    className="appearance-none bg-transparent outline-none text-sm font-medium text-slate-600 cursor-pointer min-w-[100px]"
                                >
                                    <option value="">Assign Person</option>
                                    {users.map(u => (
                                        <option key={u.id} value={u.id}>{u.name}</option>
                                    ))}
                                </select>
                            </div>

                            {/* Due Date Pill */}
                            <div className="group relative flex items-center gap-2 bg-slate-50 hover:bg-white border border-transparent hover:border-slate-200 hover:shadow-sm rounded-full px-1 pl-1 pr-4 py-1 transition-all cursor-pointer">
                                <div className="w-8 h-8 rounded-full bg-orange-100 text-orange-600 flex items-center justify-center">
                                    <Calendar size={16} />
                                </div>
                                <input
                                    type="date"
                                    value={formData.due_date}
                                    onChange={e => setFormData({ ...formData, due_date: e.target.value })}
                                    className="bg-transparent outline-none text-sm font-medium text-slate-600 cursor-pointer"
                                />
                            </div>
                        </div>

                        {/* üìÑ Description Area */}
                        <div className="pt-6 border-t border-slate-50 mt-6 h-full min-h-[200px]">
                            <textarea
                                value={formData.description}
                                onChange={e => setFormData({ ...formData, description: e.target.value })}
                                placeholder="Type '/' for commands or start writing task details..."
                                className="w-full h-full min-h-[300px] bg-transparent outline-none text-lg text-slate-600 leading-8 resize-none placeholder:text-slate-300 font-serif"
                                style={{ fontFamily: '"Georgia", serif' }} // Just for a 'doc' feel
                            />
                        </div>
                    </div>

                    {/* ü¶∂ Footer Actions */}
                    <div className="px-8 py-5 bg-slate-50 border-t border-slate-100 flex items-center justify-between">
                        <div className="flex items-center gap-2 text-xs text-slate-400">
                            <Clock size={12} />
                            <span> Auto-saves disabled</span>
                        </div>
                        <div className="flex items-center gap-4">
                            <button
                                type="button"
                                onClick={() => navigate(-1)}
                                className="text-sm font-semibold text-slate-500 hover:text-slate-800 transition"
                            >
                                Cancel
                            </button>
                            <button
                                type="submit"
                                disabled={submitting}
                                className="flex items-center gap-2 bg-slate-900 text-white px-6 py-2.5 rounded-xl text-sm font-bold shadow-lg shadow-slate-200 hover:bg-slate-800 hover:-translate-y-0.5 transition-all active:scale-95 disabled:opacity-70 disabled:cursor-not-allowed"
                            >
                                {submitting ? <Loader2 size={16} className="animate-spin" /> : <Save size={16} />}
                                {isEditing ? "Save Changes" : "Create Task"}
                            </button>
                        </div>
                    </div>
                </form>
            </motion.div>
        </div>
    );
};

export default AddTaskPage;
