import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    Users, Mail, Shield, MoreVertical, RefreshCw, Activity, Zap,
    Star, ShieldCheck, Plus, Search, Loader2, ArrowUpRight,
    MessageSquare, ExternalLink, User as UserIcon
} from "lucide-react";
import { userService, User } from "../../services/userService";

const TeamPage: React.FC = () => {
    const [members, setMembers] = useState<User[]>([]);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState("");

    const fetchTeam = async () => {
        try {
            setLoading(true);
            const data = await userService.getAllUsers();
            setMembers(data);
        } catch (error) {
            console.error("Failed to fetch team", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchTeam();
        const interval = setInterval(fetchTeam, 60000);
        return () => clearInterval(interval);
    }, []);

    const onlineCount = members.filter(m => m.status === 'Online').length;
    const adminCount = members.filter(m => m.role === 'Admin').length;

    const getInitials = (name: string) => {
        return name?.split(" ").map(n => n[0]).join("").toUpperCase().substring(0, 2) || "U";
    };

    const filteredMembers = members.filter(m =>
        m.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        m.email?.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="min-h-screen bg-[#fcfdfe] text-slate-900 px-8 py-10 lg:px-12 animate-fade-in relative">
            <div className="max-w-7xl mx-auto">

                {/* Header */}
                <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
                    <div>
                        <h1 className="text-4xl font-extrabold tracking-tight text-slate-900">Team</h1>
                        <p className="text-slate-500 font-medium mt-1">Manage team members and their roles in your workspace.</p>
                    </div>

                    <div className="flex items-center gap-4">
                        <div className="relative group hidden sm:block">
                            <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-blue-600 transition-colors" size={18} />
                            <input
                                className="w-64 pl-12 pr-4 py-2.5 bg-white border border-slate-200 rounded-xl focus:ring-4 focus:ring-blue-500/10 focus:border-blue-500 focus:outline-none transition-all font-bold text-sm text-slate-900 placeholder:text-slate-400 shadow-sm"
                                placeholder="Search members..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                        </div>
                        <button
                            onClick={fetchTeam}
                            disabled={loading}
                            className="p-3 bg-white border border-slate-200 rounded-xl text-slate-400 hover:text-blue-600 hover:border-blue-200 transition-all shadow-sm group"
                        >
                            <RefreshCw size={20} className={loading ? "animate-spin" : "group-hover:rotate-180 transition-transform duration-500"} />
                        </button>
                        <button
                            className="px-6 py-2.5 bg-blue-600 text-white rounded-xl font-bold shadow-sm hover:bg-blue-700 transition-all flex items-center gap-2"
                        >
                            <Plus size={18} strokeWidth={2.5} />
                            Invite Member
                        </button>
                    </div>
                </div>

                {/* Stats Grid */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
                    <StatCard
                        label="Total Members"
                        value={members.length}
                        icon={Users}
                        color="blue"
                    />
                    <StatCard
                        label="Active Now"
                        value={onlineCount}
                        icon={Activity}
                        color="emerald"
                    />
                    <StatCard
                        label="Administrators"
                        value={adminCount}
                        icon={ShieldCheck}
                        color="blue"
                    />
                </div>

                {/* Team Directory Header */}
                <div className="flex items-center justify-between mb-8 px-2">
                    <div>
                        <h3 className="text-xl font-bold text-slate-900">Team Directory</h3>
                        <p className="text-sm text-slate-500 font-medium">Detailed view of all workspace personnel</p>
                    </div>
                </div>

                {loading && members.length === 0 ? (
                    <div className="py-24 text-center bg-white rounded-3xl border border-slate-200 shadow-sm">
                        <div className="w-12 h-12 border-4 border-slate-50 border-t-blue-600 rounded-full animate-spin mx-auto mb-4" />
                        <p className="text-sm font-bold text-slate-400 uppercase tracking-widest">Loading team directory...</p>
                    </div>
                ) : (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                        {filteredMembers.map((member) => (
                            <motion.div
                                layout
                                key={member.id}
                                className="bg-white p-6 rounded-2xl border border-slate-200 shadow-sm hover:shadow-lg hover:shadow-blue-900/5 hover:border-blue-200 group transition-all relative"
                            >
                                <button className="absolute top-6 right-6 p-2 text-slate-300 hover:text-slate-900 hover:bg-slate-50 rounded-lg transition-all opacity-0 group-hover:opacity-100">
                                    <MoreVertical size={18} />
                                </button>

                                <div className="flex items-center gap-5 mb-6">
                                    <div className="relative shrink-0">
                                        <div className="w-16 h-16 rounded-2xl overflow-hidden bg-slate-50 border border-slate-100 transition-all flex items-center justify-center">
                                            {member.avatar ? (
                                                <img src={member.avatar} alt={member.name} className="w-full h-full object-cover" />
                                            ) : (
                                                <span className="text-xl font-black text-slate-300">{getInitials(member.name)}</span>
                                            )}
                                        </div>
                                        <div className={`absolute -bottom-1 -right-1 w-4 h-4 rounded-full border-2 border-white shadow-sm transition-colors ${member.status === 'Online' ? 'bg-emerald-500' : 'bg-slate-300'}`} />
                                    </div>
                                    <div className="min-w-0">
                                        <h3 className="text-lg font-bold text-slate-900 truncate mb-0.5">{member.name || 'Operative'}</h3>
                                        <p className="text-xs font-medium text-slate-400 truncate">{member.email}</p>
                                    </div>
                                </div>

                                <div className="flex items-center gap-3 mb-6">
                                    <span className={`px-3 py-1 text-[10px] font-bold rounded-lg uppercase tracking-wider border ${member.role === 'Admin'
                                        ? 'bg-blue-50 border-blue-100 text-blue-700'
                                        : 'bg-slate-50 border-slate-100 text-slate-600'
                                        }`}>
                                        {member.role || 'Member'}
                                    </span>
                                    <span className={`px-3 py-1 text-[10px] font-bold rounded-lg uppercase tracking-wider border ${member.status === 'Online'
                                        ? 'bg-emerald-50 border-emerald-100 text-emerald-600'
                                        : 'bg-slate-50 border-slate-100 text-slate-400'
                                        }`}>
                                        {member.status === 'Online' ? 'Active' : 'Offline'}
                                    </span>
                                </div>

                                <div className="pt-5 border-t border-slate-50 flex items-center justify-between">
                                    <button className="text-xs font-bold text-slate-400 hover:text-blue-600 transition-colors flex items-center gap-1.5 group/btn">
                                        View Profile
                                        <ArrowUpRight size={14} className="group-hover/btn:translate-x-0.5 group-hover/btn:-translate-y-0.5 transition-transform" />
                                    </button>
                                    <div className="flex items-center gap-2">
                                        <button className="p-2 bg-slate-50 text-slate-400 hover:bg-blue-50 hover:text-blue-600 rounded-lg transition-all">
                                            <MessageSquare size={16} />
                                        </button>
                                        <button className="p-2 bg-slate-50 text-slate-400 hover:bg-blue-50 hover:text-blue-600 rounded-lg transition-all">
                                            <Mail size={16} />
                                        </button>
                                    </div>
                                </div>
                            </motion.div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

const StatCard = ({ label, value, icon: Icon, color }: any) => {
    const isBlue = color === 'blue' || color === 'indigo';
    return (
        <div className="bg-white p-6 rounded-2xl border border-slate-200 shadow-sm relative overflow-hidden group hover:border-blue-200 hover:shadow-md transition-all">
            <div className={`absolute -right-4 -bottom-4 opacity-[0.03] group-hover:opacity-[0.08] transition-opacity ${isBlue ? 'text-blue-900' : 'text-emerald-900'} group-hover:scale-110 transition-transform duration-700`}>
                <Icon size={100} />
            </div>
            <div className="relative z-10 flex items-center gap-5">
                <div className={`w-14 h-14 rounded-xl flex items-center justify-center transition-transform group-hover:scale-110 ${isBlue ? 'bg-blue-50 text-blue-600' : 'bg-emerald-50 text-emerald-600'}`}>
                    <Icon size={24} strokeWidth={2.5} />
                </div>
                <div>
                    <h3 className="text-3xl font-bold text-slate-900 leading-none tracking-tight">{value}</h3>
                    <p className="text-xs font-bold text-slate-400 uppercase tracking-widest mt-1.5">{label}</p>
                </div>
            </div>
        </div>
    );
};

export default TeamPage;
