import React, { useState, useEffect } from "react";
import {
  Plus,
  Search,
  Loader2,
  Trash2,
  Edit3,
  Calendar,
  User as UserIcon,
  Filter,
  AlertCircle,
  X,
  Clock,
  CheckCircle2,
  Zap,
  ArrowRight,
  Shield,
  Layers,
  ChevronRight
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("All");
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  useEffect(() => {
    fetchTasks();
    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (message.type === 'task') {
        fetchTasks(false);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const fetchTasks = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in_progress" || s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getTimeStatus = (dueDate: string | null | undefined, status: string) => {
    const normStatus = normalizeStatus(status);
    if (normStatus === "Completed") return { label: "Completed", color: "text-emerald-600 bg-emerald-50 border-emerald-100" };
    if (!dueDate) return { label: "Planned", color: "text-slate-400 bg-slate-50 border-slate-100" };

    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const due = new Date(dueDate);
    due.setHours(0, 0, 0, 0);

    if (due < today) return { label: "Overdue", color: "text-rose-600 bg-rose-50 border-rose-100" };
    if (due.getTime() === today.getTime()) return { label: "Due Today", color: "text-amber-600 bg-amber-50 border-amber-100" };

    return { label: "Normal", color: "text-indigo-600 bg-indigo-50 border-indigo-100" };
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Are you sure you want to delete this task?")) return;
    try {
      await taskService.deleteTask(id);
      setTasks(prev => prev.filter(t => t.id !== id));
      if (selectedTask?.id === id) setSelectedTask(null);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (task.description && task.description.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesStatus = statusFilter === "All" || normalizeStatus(task.status) === statusFilter;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="min-h-screen bg-[#fcfdfe] relative font-sans overflow-hidden">

      {/* ðŸ“‹ Main Content Area */}
      <div className={`p-6 lg:p-10 max-w-[1200px] mx-auto space-y-10 transition-all duration-500 ${selectedTask ? 'lg:mr-[400px]' : ''}`}>

        {/* Header Section */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">
          <div>
            <h1 className="text-3xl font-bold tracking-tight text-slate-900 mb-1">Tasks</h1>
            <p className="text-slate-500 font-medium">Manage and monitor your team's tactical objectives.</p>
          </div>
          <button
            onClick={() => navigate("/tasks/add")}
            className="flex items-center justify-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-3 rounded-xl font-semibold transition-all shadow-lg shadow-indigo-100 active:scale-95"
          >
            <Plus size={18} />
            <span>Create Task</span>
          </button>
        </div>

        {/* Toolbar */}
        <div className="bg-white p-2 rounded-2xl border border-slate-100 shadow-sm flex flex-col md:flex-row items-center gap-2">
          <div className="relative flex-1 w-full">
            <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
            <input
              type="text"
              placeholder="Search tasks..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-11 pr-4 py-3 bg-transparent border-none focus:ring-0 text-sm font-medium text-slate-900 placeholder:text-slate-400"
            />
          </div>
          <div className="flex items-center gap-2 w-full md:w-auto p-1 bg-slate-50 rounded-xl">
            {["All", "Upcoming", "In Progress", "Completed"].map((status) => (
              <button
                key={status}
                onClick={() => setStatusFilter(status)}
                className={`px-4 py-2 rounded-lg text-xs font-bold transition-all ${statusFilter === status
                    ? "bg-white text-indigo-700 shadow-sm ring-1 ring-slate-200"
                    : "text-slate-500 hover:text-slate-700"
                  }`}
              >
                {status}
              </button>
            ))}
          </div>
        </div>

        {/* Task List Rendering */}
        <div className="space-y-4">
          {loading ? (
            <div className="py-20 text-center">
              <Loader2 className="w-10 h-10 text-indigo-600 animate-spin mx-auto mb-4" />
              <p className="text-sm font-medium text-slate-400">Loading tasks...</p>
            </div>
          ) : filteredTasks.length === 0 ? (
            <div className="py-20 text-center bg-white rounded-3xl border border-slate-100 shadow-sm">
              <div className="w-16 h-16 bg-slate-50 rounded-full flex items-center justify-center mx-auto mb-4">
                <Layers size={24} className="text-slate-300" />
              </div>
              <h3 className="font-bold text-slate-900">No tasks found</h3>
              <p className="text-sm text-slate-400 mt-1">Try adjusting your search or filters.</p>
            </div>
          ) : (
            <AnimatePresence mode="popLayout">
              {filteredTasks.map((task, idx) => {
                const timeStatus = getTimeStatus(task.due_date, task.status);
                const isSelected = selectedTask?.id === task.id;
                return (
                  <motion.div
                    key={task.id}
                    layout
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: idx * 0.02 }}
                    onClick={() => setSelectedTask(task)}
                    className={`group relative p-6 rounded-2xl border transition-all duration-200 cursor-pointer ${isSelected ? 'bg-indigo-50/30 border-indigo-200 shadow-md' : 'bg-white border-slate-100 hover:border-indigo-100 hover:shadow-sm'
                      }`}
                  >
                    <div className="flex flex-col md:flex-row md:items-center gap-6">
                      <div className={`w-fit px-3 py-1.5 rounded-lg text-[11px] font-bold uppercase tracking-wider border ${timeStatus.color}`}>
                        {timeStatus.label}
                      </div>

                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-3 mb-1">
                          <h4 className="text-base font-bold text-slate-900 group-hover:text-indigo-600 transition-colors truncate">
                            {task.title}
                          </h4>
                          {task.priority === 'High' && (
                            <span className="px-1.5 py-0.5 bg-rose-50 text-rose-600 text-[10px] font-bold rounded">HIGH</span>
                          )}
                        </div>
                        <p className="text-sm text-slate-500 line-clamp-1">{task.description || "No description provided."}</p>
                      </div>

                      <div className="flex items-center gap-6 shrink-0 md:justify-end">
                        <div className="flex items-center gap-2 px-3 py-1.5 bg-slate-50 rounded-xl border border-slate-100">
                          <Calendar size={14} className="text-slate-400" />
                          <span className="text-[11px] font-bold text-slate-600">
                            {task.due_date ? new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' }) : "No date"}
                          </span>
                        </div>

                        <div className="flex items-center gap-2 opacity-0 group-hover:opacity-100 transition-all">
                          <button
                            onClick={(e) => { e.stopPropagation(); navigate(`/tasks/edit/${task.id}`); }}
                            className="p-2 text-slate-400 hover:text-indigo-600 hover:bg-white rounded-lg transition-colors ring-1 ring-transparent hover:ring-slate-100"
                          >
                            <Edit3 size={18} />
                          </button>
                          <button
                            onClick={(e) => handleDelete(e, task.id)}
                            className="p-2 text-slate-400 hover:text-rose-600 hover:bg-white rounded-lg transition-colors ring-1 ring-transparent hover:ring-slate-100"
                          >
                            <Trash2 size={18} />
                          </button>
                        </div>
                      </div>
                    </div>
                  </motion.div>
                );
              })}
            </AnimatePresence>
          )}
        </div>
      </div>

      {/* Slide-over Panel */}
      <AnimatePresence>
        {selectedTask && (
          <motion.div
            initial={{ x: "100%" }}
            animate={{ x: 0 }}
            exit={{ x: "100%" }}
            transition={{ type: "spring", damping: 25, stiffness: 180 }}
            className="fixed top-0 right-0 w-full lg:w-[400px] h-screen bg-white border-l border-slate-100 shadow-2xl z-50 flex flex-col p-8"
          >
            <div className="flex items-center justify-between mb-8">
              <h2 className="text-lg font-bold text-slate-900">Task Details</h2>
              <button onClick={() => setSelectedTask(null)} className="p-2 text-slate-400 hover:text-slate-900 rounded-lg transition-colors">
                <X size={20} />
              </button>
            </div>

            <div className="flex-1 overflow-y-auto space-y-8 pr-2 custom-scrollbar">
              <div className="space-y-4">
                <div className={`w-fit px-3 py-1.5 rounded-lg text-[10px] font-bold uppercase tracking-wider border ${getTimeStatus(selectedTask.due_date, selectedTask.status).color}`}>
                  {normalizeStatus(selectedTask.status)}
                </div>
                <h3 className="text-2xl font-bold text-slate-900 leading-tight">{selectedTask.title}</h3>
                <p className="text-sm text-slate-500 leading-relaxed font-medium">
                  {selectedTask.description || "No description provided for this task."}
                </p>
              </div>

              <div className="space-y-4 pt-6 border-t border-slate-50">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 bg-slate-50 rounded-xl flex items-center justify-center text-slate-400">
                      <Calendar size={18} />
                    </div>
                    <div>
                      <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">Due Date</p>
                      <p className="text-sm font-bold text-slate-700">
                        {selectedTask.due_date ? new Date(selectedTask.due_date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' }) : "No date set"}
                      </p>
                    </div>
                  </div>
                </div>

                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 bg-slate-50 rounded-xl flex items-center justify-center text-slate-400">
                      <UserIcon size={18} />
                    </div>
                    <div>
                      <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">Assigned To</p>
                      <p className="text-sm font-bold text-slate-700">{selectedTask.assigned_to_user?.name || "Unassigned"}</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="pt-6 border-t border-slate-50 grid grid-cols-2 gap-3 mt-auto">
              <button
                onClick={() => navigate(`/tasks/edit/${selectedTask.id}`)}
                className="w-full py-3 bg-indigo-600 text-white font-bold text-xs rounded-xl hover:bg-indigo-700 transition-colors shadow-lg shadow-indigo-100"
              >
                Edit Task
              </button>
              <button
                onClick={(e) => handleDelete(e, selectedTask.id)}
                className="w-full py-3 bg-white text-rose-600 border border-rose-100 font-bold text-xs rounded-xl hover:bg-rose-50 transition-colors"
              >
                Delete
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default TasksPage;
