import React, { useState } from "react";
import { Calendar, Clock, FileText, ArrowLeft, CheckCircle2, Link as LinkIcon, Sparkles, Activity, Shield, Zap, Globe, Briefcase, Loader2, Video, ArrowUpRight } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { meetingService } from "../../services/meetingService";

const ScheduleMeetingPage: React.FC = () => {
  const navigate = useNavigate();
  const [meeting, setMeeting] = useState({
    title: "",
    date: "",
    time: "",
    end_time: "",
    notes: "",
    meeting_link: "",
    is_recurring_mon_fri: false
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!meeting.title.trim() || !meeting.date || !meeting.time || !meeting.end_time) {
      return;
    }

    setLoading(true);
    try {
      const createMeetingData = (dateStr: string) => ({
        title: meeting.title,
        description: meeting.notes,
        start_time: new Date(`${dateStr}T${meeting.time}:00`).toISOString(),
        end_time: new Date(`${dateStr}T${meeting.end_time}:00`).toISOString(),
        meeting_link: meeting.meeting_link,
        location: "Virtual Access" // Defaulting since field is removed
      });

      if (meeting.is_recurring_mon_fri) {
        const baseDate = new Date(meeting.date);
        const datesToCreate: string[] = [];
        const day = baseDate.getDay();
        const diff = baseDate.getDate() - day + (day === 0 ? -6 : 1);
        const monday = new Date(baseDate.setDate(diff));

        for (let i = 0; i < 5; i++) {
          const d = new Date(monday);
          d.setDate(monday.getDate() + i);
          datesToCreate.push(d.toISOString().split('T')[0]);
        }
        await Promise.all(datesToCreate.map(d => meetingService.createMeeting(createMeetingData(d))));
      } else {
        await meetingService.createMeeting(createMeetingData(meeting.date));
      }
      navigate("/meetings");
    } catch (error) {
      console.error("Failed to schedule meeting", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-white text-black p-10 lg:p-16 animate-fade-in font-sans relative">
      <div className="max-w-[1000px] mx-auto">

        {/* Header Navigation */}
        <div className="flex items-center justify-between mb-20 border-b border-zinc-100 pb-10">
          <button
            onClick={() => navigate("/meetings")}
            className="flex items-center gap-4 text-zinc-300 hover:text-black transition-all group"
          >
            <ArrowLeft size={18} className="group-hover:-translate-x-1 transition-transform" />
            <span className="text-[10px] font-black uppercase tracking-[0.2em]">RETURN TO TIMELINE</span>
          </button>

          <div className="flex items-center gap-4 text-zinc-400">
            <Video size={14} className="text-zinc-200" />
            <span className="text-[10px] font-black uppercase tracking-[0.3em]">SYNC PROTOCOL</span>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-12 gap-16 items-start">
          {/* Left: Info */}
          <div className="lg:col-span-4 space-y-12">
            <div>
              <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">TEMPORAL / CALIBRATION</p>
              <h1 className="text-5xl font-bold tracking-tighter leading-none uppercase mb-6">SCHEDULE<br />EVENT</h1>
              <p className="text-[11px] font-medium text-zinc-500 uppercase tracking-tight leading-relaxed">
                CALIBRATE THE TEMPORAL COORDINATES FOR YOUR NEXT HIGH-IMPACT SQUAD ALIGNMENT.
              </p>
            </div>

            <div className="space-y-px bg-zinc-100 border border-zinc-100">
              <div className="flex items-start gap-6 p-8 bg-white">
                <Shield size={18} className="text-black shrink-0 mt-1" />
                <div>
                  <p className="text-[10px] font-black text-black uppercase tracking-widest mb-1.5">SECURE LINK</p>
                  <p className="text-[10px] font-bold text-zinc-400 uppercase tracking-tight leading-snug">IDENTITY VERIFIED ACCESS NODES.</p>
                </div>
              </div>
              <div className="flex items-start gap-6 p-8 bg-white">
                <Zap size={18} className="text-black shrink-0 mt-1" />
                <div>
                  <p className="text-[10px] font-black text-black uppercase tracking-widest mb-1.5">AUTO SYNC</p>
                  <p className="text-[10px] font-bold text-zinc-400 uppercase tracking-tight leading-snug">CALENDAR INTEGRATION ACROSS SQUAD.</p>
                </div>
              </div>
            </div>
          </div>

          {/* Right: Form */}
          <div className="lg:col-span-8">
            <div className="bg-white border border-zinc-100 p-12 lg:p-16 shadow-2xl relative">
              <div className="absolute -top-px -left-px w-10 h-10 border-t border-l border-black" />
              <div className="absolute -bottom-px -right-px w-10 h-10 border-b border-r border-black" />

              <form onSubmit={handleSubmit} className="space-y-12">
                <div className="space-y-4">
                  <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">EVENT IDENTIFIER</label>
                  <div className="border-b border-zinc-200 focus-within:border-black transition-all">
                    <input
                      type="text"
                      value={meeting.title}
                      onChange={(e) => setMeeting({ ...meeting, title: e.target.value })}
                      placeholder="SYNC MISSION..."
                      className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none placeholder:text-zinc-100 uppercase"
                      required
                    />
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                  <div className="space-y-4">
                    <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">DEPLOYMENT DATE</label>
                    <div className="border-b border-zinc-200 focus-within:border-black transition-all">
                      <input
                        type="date"
                        value={meeting.date}
                        onChange={(e) => setMeeting({ ...meeting, date: e.target.value })}
                        className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none transition-all uppercase"
                        required
                      />
                    </div>
                  </div>
                  <div className="grid grid-cols-2 gap-8">
                    <div className="space-y-4">
                      <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">START</label>
                      <div className="border-b border-zinc-200 focus-within:border-black transition-all">
                        <input
                          type="time"
                          value={meeting.time}
                          onChange={(e) => setMeeting({ ...meeting, time: e.target.value })}
                          className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none transition-all uppercase"
                          required
                        />
                      </div>
                    </div>
                    <div className="space-y-4">
                      <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">END</label>
                      <div className="border-b border-zinc-200 focus-within:border-black transition-all">
                        <input
                          type="time"
                          value={meeting.end_time}
                          onChange={(e) => setMeeting({ ...meeting, end_time: e.target.value })}
                          className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none transition-all uppercase"
                          required
                        />
                      </div>
                    </div>
                  </div>
                </div>

                <div
                  className={`p-10 border transition-all cursor-pointer flex items-center justify-between gap-6 ${meeting.is_recurring_mon_fri ? 'bg-black text-white border-black' : 'bg-zinc-50 border-zinc-100'}`}
                  onClick={() => setMeeting({ ...meeting, is_recurring_mon_fri: !meeting.is_recurring_mon_fri })}
                >
                  <div className="flex items-center gap-6">
                    <div className={`w-12 h-12 flex items-center justify-center transition-all ${meeting.is_recurring_mon_fri ? 'bg-zinc-900 text-white' : 'bg-white border border-zinc-100 text-zinc-200'}`}>
                      <Activity size={20} />
                    </div>
                    <div>
                      <p className={`text-lg font-bold uppercase tracking-tighter leading-none mb-2 ${meeting.is_recurring_mon_fri ? 'text-white' : 'text-black'}`}>WEEKLY PULSE LOOP</p>
                      <p className={`text-[9px] font-black uppercase tracking-widest ${meeting.is_recurring_mon_fri ? 'text-zinc-500' : 'text-zinc-400'}`}>MON - FRI AUTOMATION</p>
                    </div>
                  </div>
                  <div className={`w-12 h-6 relative transition-all ${meeting.is_recurring_mon_fri ? 'bg-zinc-800' : 'bg-zinc-200'}`}>
                    <div className={`absolute top-1 w-4 h-4 bg-white transition-all ${meeting.is_recurring_mon_fri ? 'left-7' : 'left-1'}`} />
                  </div>
                </div>

                <div className="space-y-4">
                  <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">NEURAL ENTRY (SECURE LINK)</label>
                  <div className="border-b border-zinc-200 focus-within:border-black transition-all">
                    <input
                      type="url"
                      value={meeting.meeting_link}
                      onChange={(e) => setMeeting({ ...meeting, meeting_link: e.target.value })}
                      placeholder="HTTPS://VC.NEXUS/STREAM/..."
                      className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none placeholder:text-zinc-100 uppercase"
                    />
                  </div>
                </div>

                <div className="space-y-4">
                  <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">MISSION BRIEF (NOTES)</label>
                  <div className="border-b border-zinc-200 focus-within:border-black transition-all">
                    <textarea
                      value={meeting.notes}
                      onChange={(e) => setMeeting({ ...meeting, notes: e.target.value })}
                      rows={4}
                      placeholder="OUTLINE THE OPERATIONAL GOALS..."
                      className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none placeholder:text-zinc-100 uppercase resize-none leading-relaxed"
                    />
                  </div>
                </div>

                <div className="pt-10 flex items-center justify-end gap-10 border-t border-zinc-50">
                  <button
                    type="button"
                    onClick={() => navigate("/meetings")}
                    className="text-[10px] font-black text-zinc-300 hover:text-black transition-colors uppercase tracking-[0.3em]"
                  >
                    ABORT
                  </button>
                  <button
                    type="submit"
                    disabled={loading}
                    className="flex items-center gap-6 px-12 py-5 bg-black text-white text-[11px] font-black tracking-[0.4em] uppercase hover:bg-zinc-800 transition-all active:scale-95 disabled:opacity-20 flex items-center gap-2"
                  >
                    {loading ? <Loader2 size={18} className="animate-spin" /> : (
                      <>
                        COMMIT SYNC
                        <ArrowUpRight size={18} />
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ScheduleMeetingPage;
