import React, { useState, useEffect } from "react";
import {
  Plus,
  Search,
  Loader2,
  Trash2,
  Edit3,
  Calendar,
  User as UserIcon,
  Filter,
  AlertCircle,
  X,
  Clock,
  CheckCircle2,
  Zap,
  ArrowRight,
  Shield,
  Layers,
  ChevronRight,
  ArrowUpRight,
  MoreVertical,
  ClipboardList
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";


interface TaskCardProps {
  task: Task;
  onClick: () => void;
  selected: boolean;
  getTimeStatus: (d: string | null | undefined, s: string) => { label: string; style: string };
}

const TaskCard: React.FC<TaskCardProps> = ({ task, onClick, selected, getTimeStatus }) => {
  const timeStatus = getTimeStatus(task.due_date, task.status);
  return (
    <motion.div
      layout
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, scale: 0.95 }}
      onClick={onClick}
      className={`group relative p-5 bg-white border rounded-2xl transition-all duration-300 cursor-pointer flex flex-col gap-3 ${selected
        ? 'border-blue-500 ring-2 ring-blue-500/10 shadow-xl shadow-blue-500/10 z-10'
        : 'border-slate-100/80 hover:border-blue-200 hover:shadow-lg hover:shadow-blue-100/50 hover:-translate-y-1'
        }`}
    >
      <div className="flex justify-between items-start">
        <span className={`px-2.5 py-1 text-[9px] font-bold uppercase tracking-wider rounded-lg border ${timeStatus.style}`}>
          {timeStatus.label}
        </span>
        {task.priority === 'High' && (
          <AlertCircle size={14} className="text-rose-500" />
        )}
      </div>

      <div className="space-y-1">
        <h4 className={`text-sm font-bold text-slate-900 leading-snug group-hover:text-blue-600 transition-colors ${task.status === 'Completed' || task.status === 'Done' ? 'line-through text-slate-400' : ''}`}>
          {task.title}
        </h4>
        <p className="text-xs text-slate-500 font-medium line-clamp-2 opacity-80">
          {task.description || "No description."}
        </p>
      </div>

      <div className="flex items-center justify-between pt-3 border-t border-slate-50 mt-1">
        <div className="flex items-center gap-1.5 text-slate-400 group-hover:text-blue-500 transition-colors">
          <Calendar size={12} />
          <span className="text-[10px] font-bold">
            {task.due_date ? new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' }) : 'No Date'}
          </span>
        </div>
        {task.assigned_to_user && (
          <div className="w-5 h-5 rounded-full bg-blue-50 text-blue-600 text-[9px] font-bold flex items-center justify-center border border-blue-100">
            {task.assigned_to_user.name.substring(0, 1)}
          </div>
        )}
      </div>
    </motion.div>
  );
};

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("All");
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  useEffect(() => {
    fetchTasks();
    const pollInterval = setInterval(() => {
      fetchTasks(false);
    }, 30000);

    return () => {
      clearInterval(pollInterval);
    };
  }, []);

  const fetchTasks = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in_progress" || s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getTimeStatus = (dueDate: string | null | undefined, status: string) => {
    const normStatus = normalizeStatus(status);
    if (normStatus === "Completed") return { label: "Completed", style: "bg-emerald-50 text-emerald-600 border-emerald-100" };
    if (!dueDate) return { label: "Planned", style: "bg-slate-50 text-slate-500 border-slate-100" };

    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const due = new Date(dueDate);
    due.setHours(0, 0, 0, 0);

    if (due < today) return { label: "Overdue", style: "bg-rose-50 text-rose-600 border-rose-100" };
    if (due.getTime() === today.getTime()) return { label: "Due Today", style: "bg-amber-50 text-amber-600 border-amber-100" };

    return { label: "Active", style: "bg-blue-50 text-blue-600 border-blue-100" };
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Are you sure you want to delete this task?")) return;
    try {
      await taskService.deleteTask(id);
      setTasks(prev => prev.filter(t => t.id !== id));
      if (selectedTask?.id === id) setSelectedTask(null);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const baseFiltered = tasks.filter(task => {
    return task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (task.description && task.description.toLowerCase().includes(searchQuery.toLowerCase()));
  });

  const today = new Date();
  today.setHours(0, 0, 0, 0);

  const tasksInProcess = baseFiltered.filter(t => {
    const status = normalizeStatus(t.status);
    if (status === 'Completed') return false;
    if (status === 'In Progress') return true;
    if (!t.due_date) return true;
    const d = new Date(t.due_date); d.setHours(0, 0, 0, 0);
    return d <= today;
  });

  const tasksUpcoming = baseFiltered.filter(t => {
    const status = normalizeStatus(t.status);
    if (status === 'Completed') return false;
    if (status === 'In Progress') return false;
    if (!t.due_date) return false;
    const d = new Date(t.due_date); d.setHours(0, 0, 0, 0);
    return d > today;
  });

  const tasksCompleted = baseFiltered.filter(t => normalizeStatus(t.status) === 'Completed');

  return (
    <div className="min-h-screen text-slate-900 pb-12 animate-fade-in relative">

      {/* Background Decor */}
      {/* Background Decor Removed */}

      {/* Main Content Area */}
      <div className="transition-all duration-500">
        <div className="max-w-[1600px] mx-auto px-4 sm:px-6 lg:px-8 py-8">

          <div className="flex items-center justify-between mb-8">
            <div>
              <h1 className="text-2xl font-bold tracking-tight text-slate-900 leading-none mb-2">My Tasks</h1>
              <p className="text-sm text-slate-500 font-medium">Auto-organized board</p>
            </div>
            <button
              onClick={() => navigate('/tasks/new')}
              className="btn-premium"
            >
              <Plus size={20} strokeWidth={2.5} />
              New Task
            </button>
          </div>

          {!loading && (
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-full">
              {/* Column 1: In Process / Today */}
              <div className="flex flex-col gap-4">
                <div className="flex items-center gap-2 pb-2 border-b-2 border-blue-500/20">
                  <span className="w-2 h-2 rounded-full bg-blue-500 animate-pulse" />
                  <h3 className="font-bold text-slate-900 uppercase tracking-widest text-xs">In Process</h3>
                  <span className="ml-auto text-xs font-bold text-slate-400">{
                    tasksInProcess.length
                  }</span>
                </div>
                <div className="space-y-3">
                  <AnimatePresence>
                    {tasksInProcess.length === 0 && (
                      <motion.div
                        initial={{ opacity: 0, y: 10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -10 }}
                        className="p-6 text-center border border-dashed border-slate-200 rounded-2xl"
                      >
                        <span className="text-xs text-slate-400 font-medium">No tasks in process</span>
                      </motion.div>
                    )}
                    {tasksInProcess.map(task => (
                      <TaskCard key={task.id} task={task} onClick={() => setSelectedTask(task)} selected={selectedTask?.id === task.id} getTimeStatus={getTimeStatus} />
                    ))}
                  </AnimatePresence>
                </div>
              </div>

              {/* Column 2: Upcoming */}
              <div className="flex flex-col gap-4">
                <div className="flex items-center gap-2 pb-2 border-b-2 border-purple-500/20">
                  <span className="w-2 h-2 rounded-full bg-purple-500" />
                  <h3 className="font-bold text-slate-900 uppercase tracking-widest text-xs">Upcoming</h3>
                  <span className="ml-auto text-xs font-bold text-slate-400">{
                    tasksUpcoming.length
                  }</span>
                </div>
                <div className="space-y-3">
                  <AnimatePresence>
                    {tasksUpcoming.length === 0 && (
                      <motion.div
                        initial={{ opacity: 0, y: 10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -10 }}
                        className="p-6 text-center border border-dashed border-slate-200 rounded-2xl"
                      >
                        <span className="text-xs text-slate-400 font-medium">No upcoming tasks</span>
                      </motion.div>
                    )}
                    {tasksUpcoming.map(task => (
                      <TaskCard key={task.id} task={task} onClick={() => setSelectedTask(task)} selected={selectedTask?.id === task.id} getTimeStatus={getTimeStatus} />
                    ))}
                  </AnimatePresence>
                </div>
              </div>

              {/* Column 3: Completed */}
              <div className="flex flex-col gap-4">
                <div className="flex items-center gap-2 pb-2 border-b-2 border-emerald-500/20">
                  <span className="w-2 h-2 rounded-full bg-emerald-500" />
                  <h3 className="font-bold text-slate-900 uppercase tracking-widest text-xs">Completed</h3>
                  <span className="ml-auto text-xs font-bold text-slate-400">{
                    tasksCompleted.length
                  }</span>
                </div>
                <div className="space-y-3 opacity-80">
                  <AnimatePresence>
                    {tasksCompleted.length === 0 && (
                      <motion.div
                        initial={{ opacity: 0, y: 10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -10 }}
                        className="p-6 text-center border border-dashed border-slate-200 rounded-2xl"
                      >
                        <span className="text-xs text-slate-400 font-medium">No completed tasks</span>
                      </motion.div>
                    )}
                    {tasksCompleted.map(task => (
                      <TaskCard key={task.id} task={task} onClick={() => setSelectedTask(task)} selected={selectedTask?.id === task.id} getTimeStatus={getTimeStatus} />
                    ))}
                  </AnimatePresence>
                </div>
              </div>
            </div>
          )}

          {loading && (
            <div className="py-32 text-center bg-white border border-slate-200 shadow-sm rounded-xl">
              <div className="w-16 h-16 border-4 border-blue-50 border-t-blue-600 rounded-full animate-spin mx-auto mb-6" />
              <p className="text-sm font-bold text-slate-400 uppercase tracking-widest animate-pulse">Syncing tasks...</p>
            </div>
          )}

          {!loading && tasks.length === 0 && (
            <div className="py-32 text-center glass-card rounded-[32px]">
              <div className="w-20 h-20 bg-slate-50 rounded-[24px] flex items-center justify-center mx-auto mb-6 shadow-inner">
                <ClipboardList className="text-slate-300" size={32} />
              </div>
              <h4 className="text-xl font-bold text-slate-900">No tasks found</h4>
              <p className="text-slate-400 font-medium mt-2">Time to create a new one!</p>
            </div>
          )}

        </div>

        {/* Task Detail Modal */}
        <AnimatePresence>
          {selectedTask && (
            <div className="fixed inset-0 z-50 flex items-center justify-center p-4 lg:p-8">
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="absolute inset-0 bg-slate-900/20 backdrop-blur-sm"
                onClick={() => setSelectedTask(null)}
              />

              <motion.div
                layoutId={`task-${selectedTask.id}`}
                className="bg-white w-full max-w-2xl rounded-[32px] shadow-2xl overflow-hidden relative z-10 flex flex-col max-h-[90vh]"
                initial={{ scale: 0.95, opacity: 0, y: 20 }}
                animate={{ scale: 1, opacity: 1, y: 0 }}
                exit={{ scale: 0.95, opacity: 0, y: 20 }}
                transition={{ type: "spring", damping: 25, stiffness: 300 }}
              >
                {/* Modal Header */}
                <div className="p-8 pb-0 flex justify-between items-start shrink-0">
                  <div className="flex items-center gap-3">
                    <span className={`px-3 py-1.5 rounded-xl text-xs font-bold border flex items-center gap-2 ${getTimeStatus(selectedTask.due_date, selectedTask.status).style}`}>
                      <span className="w-1.5 h-1.5 rounded-full bg-current" />
                      {normalizeStatus(selectedTask.status).toUpperCase()}
                    </span>
                    {selectedTask.priority === 'High' && (
                      <span className="px-3 py-1.5 rounded-xl text-xs font-bold border bg-rose-50 text-rose-600 border-rose-100 flex items-center gap-1.5">
                        <AlertCircle size={14} strokeWidth={2.5} /> Urgent
                      </span>
                    )}
                  </div>
                  <button
                    onClick={() => setSelectedTask(null)}
                    className="w-10 h-10 rounded-full bg-slate-50 text-slate-400 hover:bg-slate-100 hover:text-slate-900 flex items-center justify-center transition-all"
                  >
                    <X size={20} />
                  </button>
                </div>

                {/* Modal Body */}
                <div className="p-8 overflow-y-auto custom-scrollbar">
                  <h2 className="text-3xl font-bold text-slate-900 leading-tight mb-8">
                    {selectedTask.title}
                  </h2>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
                    <div className="p-4 rounded-2xl bg-slate-50 border border-slate-100">
                      <div className="flex items-center gap-2 text-slate-400 mb-2">
                        <Calendar size={14} />
                        <span className="text-[10px] font-bold uppercase tracking-wider">Due Date</span>
                      </div>
                      <p className="text-sm font-bold text-slate-700">
                        {selectedTask.due_date ? new Date(selectedTask.due_date).toLocaleDateString(undefined, { weekday: 'short', month: 'short', day: 'numeric' }) : 'No date set'}
                      </p>
                    </div>
                    <div className="p-4 rounded-2xl bg-slate-50 border border-slate-100">
                      <div className="flex items-center gap-2 text-slate-400 mb-2">
                        <UserIcon size={14} />
                        <span className="text-[10px] font-bold uppercase tracking-wider">Assignee</span>
                      </div>
                      <p className="text-sm font-bold text-slate-700 flex items-center gap-2">
                        {selectedTask.assigned_to_user?.name ? (
                          <>
                            <span className="w-5 h-5 rounded-full bg-blue-100 text-blue-600 flex items-center justify-center text-[9px]">
                              {selectedTask.assigned_to_user.name.substring(0, 1)}
                            </span>
                            {selectedTask.assigned_to_user.name}
                          </>
                        ) : 'Unassigned'}
                      </p>
                    </div>
                    <div className="p-4 rounded-2xl bg-slate-50 border border-slate-100">
                      <div className="flex items-center gap-2 text-slate-400 mb-2">
                        <Zap size={14} />
                        <span className="text-[10px] font-bold uppercase tracking-wider">Impact</span>
                      </div>
                      <p className="text-sm font-bold text-slate-700">
                        {selectedTask.priority || 'Normal'} Priority
                      </p>
                    </div>
                  </div>

                  <div className="prose prose-slate prose-sm max-w-none">
                    <h3 className="text-sm font-bold text-slate-900 mb-3 flex items-center gap-2">
                      <Layers size={16} className="text-blue-500" />
                      Description & Notes
                    </h3>
                    <div className="p-6 rounded-2xl border border-slate-100 bg-white shadow-sm text-slate-600 leading-relaxed whitespace-pre-wrap">
                      {selectedTask.description || "No additional notes found for this task."}
                    </div>
                  </div>
                </div>

                {/* Modal Footer */}
                <div className="p-6 border-t border-slate-100 bg-slate-50/50 flex justify-end gap-3 shrink-0">
                  <button
                    onClick={(e) => handleDelete(e, selectedTask.id)}
                    className="px-5 py-2.5 rounded-xl font-bold text-sm text-rose-600 bg-white border border-slate-200 hover:bg-rose-50 hover:border-rose-100 hover:shadow-sm transition-all flex items-center gap-2"
                  >
                    <Trash2 size={16} /> Delete
                  </button>
                  <button
                    onClick={() => navigate(`/tasks/edit/${selectedTask.id}`)}
                    className="px-5 py-2.5 rounded-xl font-bold text-sm text-white bg-blue-600 hover:bg-blue-700 hover:shadow-lg hover:shadow-blue-200 hover:-translate-y-0.5 transition-all flex items-center gap-2"
                  >
                    <Edit3 size={16} /> Edit Task
                  </button>
                </div>
              </motion.div>
            </div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default TasksPage;
