import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  Workflow,
  ClipboardList,
  LogOut,
  UsersRound,
  X,
  ChevronRight,
  Sparkles,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({ isOpen, onClose }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard" },
    { label: "Chat", icon: MessageSquare, path: "/chat" },
    { label: "Tasks", icon: ClipboardList, path: "/tasks" },
    { label: "Meetings", icon: UsersRound, path: "/meetings" },
    { label: "Calendar", icon: CalendarDays, path: "/calendar" },
    { label: "Files", icon: FileUp, path: "/files" },
    { label: "Workflows", icon: Workflow, path: "/workflows" },
  ];

  return (
    <>
      {/* üå¨Ô∏è Light Glass Overlay (mobile) */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-white/10 backdrop-blur-sm z-40 lg:hidden"
            onClick={onClose}
          />
        )}
      </AnimatePresence>

      <motion.aside
        initial={false}
        animate={{
          x: isOpen ? 0 : -320,
        }}
        transition={{ type: "spring", damping: 25, stiffness: 200 }}
        className={`
          fixed top-4 bottom-4 left-4 z-50 w-72 
          bg-[#0a0f1d]/70 backdrop-blur-2xl 
          rounded-[2.5rem] border border-white/10 
          shadow-[0_20px_50px_rgba(0,0,0,0.5)]
          flex flex-col overflow-hidden
          lg:translate-x-0
          ${isOpen ? "translate-x-0" : "-translate-x-full lg:translate-x-0"}
        `}
      >
        {/* ‚ú® Glass Glows */}
        <div className="absolute inset-0 pointer-events-none">
          <div className="absolute top-0 left-0 w-full h-[150px] bg-gradient-to-b from-blue-500/10 to-transparent" />
          <div className="absolute bottom-0 left-0 w-full h-[100px] bg-gradient-to-t from-indigo-500/10 to-transparent" />
        </div>

        {/* üì± Mobile Close */}
        <div className="lg:hidden flex justify-end p-6 relative z-10">
          <motion.button
            whileTap={{ scale: 0.9 }}
            onClick={onClose}
            className="p-2 bg-white/5 hover:bg-white/10 text-white/60 rounded-full border border-white/10"
          >
            <X size={18} />
          </motion.button>
        </div>

        {/* üí´ Brand Header */}
        <div
          className="flex flex-col items-center py-10 px-6 cursor-pointer group relative z-10"
          onClick={() => {
            navigate("/dashboard");
            onClose();
          }}
        >
          <motion.div
            whileHover={{ rotate: 15, scale: 1.1 }}
            className="p-4 bg-gradient-to-tr from-blue-600/20 to-indigo-500/20 rounded-[2rem] border border-white/10 shadow-xl backdrop-blur-md"
          >
            <img
              src="/logo1.png"
              alt="Collabryta"
              className="w-10 h-10 brightness-125 saturate-150"
            />
          </motion.div>
          <h1 className="mt-5 text-2xl font-black tracking-tight text-white flex items-center gap-2">
            Collabryta
            <Sparkles size={16} className="text-blue-400 animate-pulse" />
          </h1>
          <p className="mt-1 text-[10px] uppercase tracking-[0.3em] font-bold text-blue-400/60 transition-all group-hover:tracking-[0.4em]">Next-Gen Pro</p>
        </div>

        {/* üß≠ Navigation Area */}
        <nav className="flex-1 px-6 space-y-2.5 overflow-y-auto custom-scrollbar relative z-10 pt-4">
          {navItems.map(({ label, icon: Icon, path }) => {
            const active = location.pathname === path;
            return (
              <motion.button
                key={path}
                whileHover={{ scale: 1.02, x: 5 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => {
                  navigate(path);
                  onClose();
                }}
                className={`w-full flex items-center justify-between px-5 py-4 rounded-[1.5rem] text-sm font-bold transition-all border relative overflow-hidden group
                  ${active
                    ? "bg-gradient-to-r from-blue-600/80 to-indigo-600/80 text-white border-white/20 shadow-lg shadow-blue-500/20"
                    : "text-white/50 border-transparent hover:bg-white/5 hover:text-white/90"
                  }`}
              >
                {/* Active Reflection */}
                {active && (
                  <motion.div
                    className="absolute inset-0 bg-gradient-to-tr from-white/20 to-transparent"
                    animate={{ x: ["-100%", "100%"] }}
                    transition={{ repeat: Infinity, duration: 3, ease: "linear" }}
                  />
                )}

                <div className="flex items-center gap-4 relative z-10">
                  <Icon size={18} className={`${active ? "text-white" : "text-white/40 group-hover:text-blue-400"} transition-colors`} />
                  {label}
                </div>

                {active ? (
                  <motion.div layoutId="floatingActive" className="w-1.5 h-1.5 rounded-full bg-white shadow-[0_0_10px_white]" />
                ) : (
                  <ChevronRight size={14} className="opacity-0 group-hover:opacity-40 translate-x-[-4px] group-hover:translate-x-0 transition-all" />
                )}
              </motion.button>
            );
          })}
        </nav>

        {/* ‚ö° Action Slot */}
        <div className="px-6 py-8 relative z-10">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => navigate("/login")}
            className="w-full flex items-center justify-center gap-3 py-4 bg-white text-[#0a0f1d] rounded-2xl text-sm font-black shadow-2xl hover:bg-blue-50 transition-all border-t border-white/20"
          >
            <LogOut size={18} />
            Logout Securely
          </motion.button>
        </div>
      </motion.aside>
    </>
  );
};

export default Sidebar;
