// Mock data for now as backend doesn't have messages yet
export interface ConversationUser {
    id: number;
    name: string;
    avatar?: string;
    unread_count?: number;
    status?: 'online' | 'offline' | 'busy';
    is_active?: boolean;
    last_message?: string;
    last_message_time?: string;
}

export interface Message {
    id: number;
    sender_id: number | string;
    content: string;
    timestamp: string;
    is_read?: boolean;
}

import api from './api';

export const messageService = {
    getConversations: async (): Promise<ConversationUser[]> => {
        const response = await api.get('/messages/conversations/');
        return response.data;
    },

    getMessages: async (userId: number): Promise<Message[]> => {
        const response = await api.get(`/messages/${userId}`);
        return response.data;
    },

    sendMessage: async (userId: number, content: string): Promise<Message> => {
        const response = await api.post('/messages/', {
            receiver_id: userId,
            content: content
        });
        return response.data;
    }
};
