import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  Workflow,
  ClipboardList,
  LogOut,
  UsersRound,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";

const Sidebar: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard" },
    { label: "Messages", icon: MessageSquare, path: "/chat", badge: 3 },
    { label: "Tasks", icon: ClipboardList, path: "/tasks", badge: 5 },
    { label: "Meetings", icon: UsersRound, path: "/meetings" },
    { label: "Calendar", icon: CalendarDays, path: "/calendar" },
    { label: "Files", icon: FileUp, path: "/files" },
    { label: "Workflows", icon: Workflow, path: "/workflows" },
  ];

  return (
    <aside className="h-screen w-64 flex flex-col bg-white border-r border-slate-200">
      {/* Logo Section */}
      <div
        className="flex items-center gap-3 px-6 py-5 border-b border-slate-200 cursor-pointer group"
        onClick={() => navigate("/dashboard")}
      >
        <div className="relative">
          <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center shadow-md group-hover:shadow-lg transition-shadow">
            <img
              src="/logo1.png"
              alt="Logo"
              className="w-6 h-6 object-contain brightness-0 invert"
            />
          </div>
        </div>
        <div>
          <h1 className="text-lg font-bold text-slate-900">Collabryta</h1>
          <p className="text-xs text-slate-500">Workspace</p>
        </div>
      </div>

      {/* Navigation */}
      <nav className="flex-1 overflow-y-auto p-3 space-y-1">
        {navItems.map(({ label, icon: Icon, path, badge }) => {
          const active = location.pathname === path;
          return (
            <button
              key={path}
              onClick={() => navigate(path)}
              className={`
                relative w-full flex items-center gap-3 px-3 py-2.5 rounded-lg
                font-medium text-sm transition-all duration-200
                ${active
                  ? "bg-blue-50 text-blue-600 border border-blue-100"
                  : "text-slate-700 hover:bg-slate-50"
                }
              `}
            >
              <Icon size={20} strokeWidth={active ? 2.5 : 2} />
              <span className="flex-1 text-left">{label}</span>
              {badge && (
                <span className="px-2 py-0.5 text-xs font-bold rounded-full bg-red-500 text-white">
                  {badge}
                </span>
              )}
            </button>
          );
        })}
      </nav>

      {/* User Profile */}
      <div className="p-3 border-t border-slate-200">
        <div className="flex items-center gap-3 p-2 rounded-lg hover:bg-slate-50 transition-colors">
          <div className="w-9 h-9 rounded-lg bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center text-white font-bold text-sm">
            AK
          </div>
          <div className="flex-1 min-w-0">
            <p className="text-sm font-semibold text-slate-900 truncate">Ashok Kumar</p>
            <p className="text-xs text-slate-500">Admin</p>
          </div>
          <button
            onClick={() => navigate("/login")}
            className="p-1.5 rounded-lg hover:bg-red-50 text-slate-400 hover:text-red-600 transition-colors"
            aria-label="Logout"
          >
            <LogOut size={16} />
          </button>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
