import React, { useState } from "react";
import { Calendar, Clock, FileText, ArrowLeft, CheckCircle2, MapPin } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { meetingService } from "../../services/meetingService";

const ScheduleMeetingPage: React.FC = () => {
  const navigate = useNavigate();
  const [meeting, setMeeting] = useState({
    title: "",
    date: "",
    time: "",
    end_time: "",
    notes: "",
    location: "",
    meeting_link: "",
    is_recurring_mon_fri: false
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!meeting.title.trim() || !meeting.date || !meeting.time || !meeting.end_time) {
      alert("⚠️ Please fill in all required fields.");
      return;
    }

    setLoading(true);
    try {
      const createMeetingData = (dateStr: string) => ({
        title: meeting.title,
        description: meeting.notes,
        start_time: new Date(`${dateStr}T${meeting.time}:00`).toISOString(),
        end_time: new Date(`${dateStr}T${meeting.end_time}:00`).toISOString(),
        location: meeting.location,
        meeting_link: meeting.meeting_link
      });

      if (meeting.is_recurring_mon_fri) {
        // Find the start date and generate dates for the rest of the week (Mon-Fri)
        const baseDate = new Date(meeting.date);
        const datesToCreate: string[] = [];

        // Go 5 days forward from the selected date's week (Mon-Fri)
        // Actually, simple way: find the Monday of that week and add Mon, Tue, Wed, Thu, Fri
        // Or if the user picks a Wed, maybe they want Wed-Fri + next Mon-Tue? 
        // User said "monday to friday at a sametime". 
        // Let's assume they want to create for the current week's Mon-Fri.
        const day = baseDate.getDay(); // 0 is Sun, 1 is Mon...
        const diff = baseDate.getDate() - day + (day === 0 ? -6 : 1); // Adjust when day is sunday
        const monday = new Date(baseDate.setDate(diff));

        for (let i = 0; i < 5; i++) {
          const d = new Date(monday);
          d.setDate(monday.getDate() + i);
          datesToCreate.push(d.toISOString().split('T')[0]);
        }

        await Promise.all(datesToCreate.map(d => meetingService.createMeeting(createMeetingData(d))));
        alert(`✅ Recurring Meetings "${meeting.title}" (Mon-Fri) Scheduled!`);
      } else {
        await meetingService.createMeeting(createMeetingData(meeting.date));
        alert(`✅ Meeting "${meeting.title}" Scheduled!`);
      }

      navigate("/meetings");
    } catch (error) {
      console.error("Failed to schedule meeting", error);
      alert("Failed to schedule meeting. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800 p-10">
      <main className="max-w-2xl mx-auto bg-white rounded-2xl shadow-lg border border-gray-200 p-10">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <button
            onClick={() => navigate("/meetings")}
            className="flex items-center text-gray-600 hover:text-blue-600 font-medium transition"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back
          </button>
          <div className="flex items-center gap-2 text-sm text-gray-500">
            <CheckCircle2 className="w-4 h-4 text-blue-600" />
            <span>Plan your next meeting</span>
          </div>
        </div>

        <h1 className="text-3xl font-bold text-gray-900 mb-8">
          Schedule New Meeting
        </h1>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-semibold mb-2 text-gray-700">
              Meeting Title
            </label>
            <input
              type="text"
              value={meeting.title}
              onChange={(e) => setMeeting({ ...meeting, title: e.target.value })}
              placeholder="e.g., Design Review Session"
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
              required
            />
          </div>

          <div className="grid grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-semibold mb-2 text-gray-700 flex items-center gap-2">
                <Calendar className="w-4 h-4 text-blue-600" /> Date
              </label>
              <input
                type="date"
                value={meeting.date}
                onChange={(e) => setMeeting({ ...meeting, date: e.target.value })}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-semibold mb-2 text-gray-700 flex items-center gap-2">
                <Clock className="w-4 h-4 text-blue-600" /> Start Time
              </label>
              <input
                type="time"
                value={meeting.time}
                onChange={(e) => setMeeting({ ...meeting, time: e.target.value })}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-2 text-gray-700 flex items-center gap-2">
                <Clock className="w-4 h-4 text-blue-600" /> End Time
              </label>
              <input
                type="time"
                value={meeting.end_time}
                onChange={(e) => setMeeting({ ...meeting, end_time: e.target.value })}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                required
              />
            </div>
          </div>

          <div className="flex items-center gap-3 p-4 bg-blue-50 rounded-xl border border-blue-100">
            <input
              type="checkbox"
              id="recurring"
              checked={meeting.is_recurring_mon_fri}
              onChange={(e) => setMeeting({ ...meeting, is_recurring_mon_fri: e.target.checked })}
              className="w-5 h-5 rounded text-blue-600 focus:ring-blue-500"
            />
            <label htmlFor="recurring" className="text-sm font-semibold text-blue-900 cursor-pointer">
              Repeat every day (Monday to Friday)
            </label>
          </div>

          <div>
            <label className="block text-sm font-semibold mb-2 text-gray-700">
              Meeting Link (Optional)
            </label>
            <input
              type="url"
              value={meeting.meeting_link}
              onChange={(e) => setMeeting({ ...meeting, meeting_link: e.target.value })}
              placeholder="https://zoom.us/j/..."
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
            />
          </div>

          <div>
            <label className="block text-sm font-semibold mb-2 text-gray-700 flex items-center gap-2">
              <FileText className="w-4 h-4 text-blue-600" /> Notes
            </label>
            <textarea
              value={meeting.notes}
              onChange={(e) => setMeeting({ ...meeting, notes: e.target.value })}
              rows={4}
              placeholder="Add meeting agenda or participants..."
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none resize-none"
            />
          </div>

          <div className="flex justify-end items-center gap-4 mt-6">
            <button
              type="button"
              onClick={() => navigate("/meetings")}
              className="px-5 py-2.5 rounded-lg border border-gray-300 text-gray-700 hover:bg-gray-100 transition"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading}
              className={`bg-blue-600 hover:bg-blue-700 text-white px-6 py-2.5 rounded-lg font-medium transition ${loading ? 'opacity-70 cursor-not-allowed' : ''}`}
            >
              {loading ? "Scheduling..." : "Schedule Meeting"}
            </button>
          </div>
        </form>
      </main>
    </div>
  );
};

export default ScheduleMeetingPage;
