import React, { useState, useEffect } from "react";
import {
    User,
    Bell,
    ShieldCheck,
    Save,
    Loader2,
    CheckCircle2,
    Eye,
    Smartphone,
    Globe,
    Lock,
    Activity,
    Shield,
    Zap,
    Cpu,
    Star,
    ArrowUpRight
} from "lucide-react";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const SettingsPage: React.FC = () => {
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [success, setSuccess] = useState(false);
    const [settings, setSettings] = useState<any>(null);
    const [show2FAModal, setShow2FAModal] = useState(false);
    const [otpCode, setOtpCode] = useState("");
    const [is2FALoading, setIs2FALoading] = useState(false);
    const [testNotifStatus, setTestNotifStatus] = useState<string | null>(null);

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const data = await authService.getCurrentUser();
                setSettings(data);
            } catch (error) {
                console.error("Failed to load settings", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSettings();
    }, []);

    const handleUpdate = (field: string, value: any) => {
        setSettings((prev: any) => ({ ...prev, [field]: value }));
    };

    const handleSave = async () => {
        try {
            setSaving(true);
            setSuccess(false);
            const { id, email, hashed_password, last_seen, status, ...updateData } = settings;
            await authService.updateProfile(updateData);
            setSuccess(true);
            setTimeout(() => setSuccess(false), 3000);
        } catch (error) {
            console.error("Failed to save settings", error);
        } finally {
            setSaving(false);
        }
    };

    const handleSendTestNotif = () => {
        setTestNotifStatus("TRANSMITTING...");
        setTimeout(() => {
            setTestNotifStatus("DELIVERED");
            setTimeout(() => setTestNotifStatus(null), 3000);
        }, 1500);
    };

    const handleEnable2FA = () => {
        if (settings.two_factor_enabled) {
            handleUpdate('two_factor_enabled', false);
        } else {
            setShow2FAModal(true);
        }
    };

    const handleVerifyOTP = () => {
        setIs2FALoading(true);
        setTimeout(() => {
            if (otpCode === "123456") {
                handleUpdate('two_factor_enabled', true);
                setShow2FAModal(false);
                setOtpCode("");
            } else {
                alert("INVALID OTP");
            }
            setIs2FALoading(false);
        }, 1000);
    };

    if (loading || !settings) {
        return (
            <div className="min-h-screen flex flex-col items-center justify-center bg-white">
                <div className="w-10 h-10 border border-zinc-200 border-t-black rounded-full animate-spin mb-4" />
                <p className="text-[10px] font-black tracking-[0.2em] text-zinc-400 uppercase">ACCESSING CORE PREFERENCES</p>
            </div>
        );
    }

    const Section = ({ icon: Icon, title, desc, children }: any) => (
        <section className="bg-white border border-zinc-100 overflow-hidden mb-12 animate-fade-in transition-all">
            <div className="px-10 py-8 border-b border-zinc-100 flex items-center justify-between">
                <div className="flex items-center gap-6">
                    <div className="p-3 bg-zinc-100 text-black">
                        <Icon size={20} />
                    </div>
                    <div>
                        <h2 className="text-xl font-bold text-black uppercase tracking-tighter leading-none mb-1.5">{title}</h2>
                        <p className="text-[10px] font-bold text-zinc-400 uppercase tracking-widest">{desc}</p>
                    </div>
                </div>
            </div>
            <div className="p-10">{children}</div>
        </section>
    );

    const Toggle = ({ active, label, subtitle, field, disabled }: any) => (
        <div className={`flex items-center justify-between py-8 first:pt-0 border-b border-zinc-50 last:border-0 last:pb-0 ${disabled ? 'opacity-30' : ''}`}>
            <div>
                <p className="text-[11px] font-black text-black uppercase tracking-widest leading-none mb-2">{label}</p>
                <p className="text-[10px] font-medium text-zinc-400 uppercase tracking-tight">{subtitle}</p>
            </div>
            <button
                onClick={() => !disabled && handleUpdate(field, !active)}
                disabled={disabled}
                className={`relative inline-flex h-6 w-12 items-center transition-all duration-300 ${active ? 'bg-black' : 'bg-zinc-100'}`}
            >
                <span className={`inline-block h-4 w-4 transform bg-white transition-transform duration-300 ${active ? 'translate-x-7' : 'translate-x-1'}`} />
            </button>
        </div>
    );

    return (
        <div className="min-h-screen bg-white text-black p-10 lg:p-16 animate-fade-in font-sans relative">
            <div className="max-w-[1000px] mx-auto">

                {/* Header Area */}
                <div className="flex flex-col md:flex-row md:items-end justify-between gap-10 mb-20 border-b border-zinc-100 pb-12">
                    <div>
                        <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">SYSTEM / CONFIGURATION</p>
                        <h1 className="text-5xl font-bold tracking-tighter leading-none uppercase">SETTINGS</h1>
                    </div>

                    <button
                        onClick={handleSave}
                        disabled={saving}
                        className={`group flex items-center gap-4 px-10 py-5 transition-all active:scale-95 ${success ? 'bg-black text-white' : 'bg-black text-white hover:bg-zinc-800'
                            }`}
                    >
                        <span className="text-[11px] font-black tracking-widest uppercase">
                            {saving ? "SYNCING..." : success ? "PROTOCOLS SAVED" : "COMMIT CHANGES"}
                        </span>
                        {saving ? <Loader2 size={16} className="animate-spin" /> : success ? <CheckCircle2 size={16} /> : <Save size={16} />}
                    </button>
                </div>

                {/* Profile Section (NON-EDITABLE as per user rules) */}
                <Section icon={User} title="IDENTITY CORE" desc="CENTRALIZED PERSONNEL IDENTIFICATION / READ-ONLY">
                    <div className="space-y-12">
                        <div className="flex items-center gap-10 pb-10 border-b border-zinc-50">
                            <div className="w-24 h-24 bg-zinc-50 border border-zinc-100 flex items-center justify-center relative overflow-hidden">
                                {settings.avatar ? (
                                    <img src={settings.avatar} alt="Avatar" className="w-full h-full object-cover grayscale" />
                                ) : (
                                    <span className="text-4xl font-black text-zinc-200 tracking-tighter">{settings.name?.charAt(0)}</span>
                                )}
                                <div className="absolute top-0 right-0 p-1.5 bg-black text-white">
                                    <Lock size={10} />
                                </div>
                            </div>
                            <div>
                                <h3 className="text-sm font-black text-black uppercase tracking-[0.2em] mb-2">AVATAR ENCRYPTION</h3>
                                <p className="text-[11px] font-medium text-zinc-400 uppercase tracking-tight leading-relaxed max-w-sm">PROFILE VISUAL DATA IS MANAGED BY GLOBAL RESOURCE DIRECTORS AND CANNOT BE MODIFIED LOCALLY.</p>
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-px bg-zinc-100 border border-zinc-100">
                            <div className="bg-white p-6 space-y-3">
                                <label className="text-[9px] font-black text-zinc-400 uppercase tracking-widest px-1">LEGAL APPELLATION</label>
                                <div className="relative">
                                    <input
                                        type="text"
                                        value={settings.name || ''}
                                        disabled
                                        className="w-full px-4 py-4 bg-zinc-50 border-none text-zinc-300 font-bold text-[11px] tracking-widest uppercase cursor-not-allowed"
                                    />
                                    <Lock size={12} className="absolute right-4 top-1/2 -translate-y-1/2 text-zinc-200" />
                                </div>
                            </div>
                            <div className="bg-white p-6 space-y-3">
                                <label className="text-[9px] font-black text-zinc-400 uppercase tracking-widest px-1">OPERATIONAL DESIGNATION</label>
                                <div className="relative">
                                    <input
                                        type="text"
                                        value={settings.job_title || ''}
                                        disabled
                                        className="w-full px-4 py-4 bg-zinc-50 border-none text-zinc-300 font-bold text-[11px] tracking-widest uppercase cursor-not-allowed"
                                    />
                                    <Shield size={12} className="absolute right-4 top-1/2 -translate-y-1/2 text-zinc-200" />
                                </div>
                            </div>
                            <div className="bg-white p-6 md:col-span-2 space-y-3">
                                <label className="text-[9px] font-black text-zinc-400 uppercase tracking-widest px-1">COMMUNICATION ENDPOINT</label>
                                <div className="relative">
                                    <input
                                        type="text"
                                        value={settings.email || ''}
                                        disabled
                                        className="w-full px-4 py-4 bg-zinc-50 border-none text-zinc-300 font-bold text-[11px] tracking-widest uppercase cursor-not-allowed"
                                    />
                                    <Globe size={12} className="absolute right-4 top-1/2 -translate-y-1/2 text-zinc-200" />
                                </div>
                            </div>
                        </div>
                    </div>
                </Section>

                {/* Regional Section */}
                <Section icon={Globe} title="SPATIAL PARAMETERS" desc="LOCALIZATION AND TEMPORAL COORDINATES">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-px bg-zinc-100 border border-zinc-100">
                        <div className="bg-white p-6 space-y-3">
                            <label className="text-[9px] font-black text-zinc-400 uppercase tracking-widest px-1">PREF. DIALECT</label>
                            <select
                                value={settings.language}
                                onChange={(e) => handleUpdate('language', e.target.value)}
                                className="w-full px-4 py-4 bg-white border border-zinc-200 focus:border-black appearance-none outline-none text-black font-bold text-[11px] tracking-widest uppercase transition-all"
                            >
                                <option>ENGLISH (US)</option>
                                <option>HINDI (HI)</option>
                                <option>SPANISH (ES)</option>
                            </select>
                        </div>
                        <div className="bg-white p-6 space-y-3">
                            <label className="text-[9px] font-black text-zinc-400 uppercase tracking-widest px-1">ZONE OFFSET</label>
                            <select
                                value={settings.timezone}
                                onChange={(e) => handleUpdate('timezone', e.target.value)}
                                className="w-full px-4 py-4 bg-white border border-zinc-200 focus:border-black appearance-none outline-none text-black font-bold text-[11px] tracking-widest uppercase transition-all"
                            >
                                <option value="(GMT+05:30) Mumbai, New Delhi">INDIA (GMT+5:30)</option>
                                <option value="(GMT-08:00) Pacific Time">PACIFIC TIME (GMT-8:00)</option>
                                <option value="(GMT+00:00) London, UTC">LONDON (GMT+0:00)</option>
                            </select>
                        </div>
                    </div>
                </Section>

                {/* Privacy Section */}
                <Section icon={Eye} title="PRESENCE MATRIX" desc="SURVEILLANCE AND VISIBILITY PERMISSIONS">
                    <div className="space-y-2">
                        <Toggle active={settings.show_online_status} label="BROADCAST STATUS" subtitle="ENABLE REAL-TIME PRESENCE WITHIN THE NETWORK" field="show_online_status" />
                        <Toggle active={settings.share_local_time} label="SHARE TEMPORAL DATA" subtitle="DISPLAY CURRENT LOCAL TIME TO OTHER OPERATIVES" field="share_local_time" />
                    </div>
                </Section>

                {/* Notifications Section */}
                <Section icon={Bell} title="INBOUND ALERTS" desc="DATA FLOW AND SIGNAL INTERRUPTIONS">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-16 mb-8">
                        <div className="space-y-6">
                            <h4 className="text-[9px] font-black text-black uppercase tracking-[0.4em] mb-8 flex items-center gap-3">
                                <div className="w-1.5 h-1.5 bg-black" /> EMAIL CHANNELS
                            </h4>
                            <div className="space-y-4">
                                <Toggle active={settings.email_tasks} label="TASK UPDATES" subtitle="ASSIGNMENT SIGNALS" field="email_tasks" />
                                <Toggle active={settings.email_summary} label="WEEKLY DIGEST" subtitle="VELOCITY REPORTS" field="email_summary" />
                            </div>
                        </div>
                        <div className="space-y-6">
                            <h4 className="text-[9px] font-black text-black uppercase tracking-[0.4em] mb-8 flex items-center gap-3">
                                <div className="w-1.5 h-1.5 bg-black" /> PUSH TRANSMISSIONS
                            </h4>
                            <div className="space-y-4">
                                <Toggle active={settings.push_meetings} label="SYNC ALERTS" subtitle="SESSION REMINDERS" field="push_meetings" />
                                <Toggle active={settings.push_chat} label="DIRECT COMMS" subtitle="CHAT NOTIFICATIONS" field="push_chat" />
                            </div>
                        </div>
                    </div>
                    <div className="mt-12 pt-10 border-t border-zinc-100 flex justify-end">
                        <button
                            onClick={handleSendTestNotif}
                            disabled={!!testNotifStatus}
                            className="flex items-center gap-4 px-8 py-4 bg-zinc-50 text-black text-[10px] font-black tracking-widest uppercase hover:bg-zinc-100 transition-all"
                        >
                            {testNotifStatus === "DELIVERED" ? <CheckCircle2 size={14} /> : <Zap size={14} />}
                            {testNotifStatus || "TRANSMIT TEST SIGNAL"}
                        </button>
                    </div>
                </Section>

                {/* Security Section */}
                <Section icon={ShieldCheck} title="ACCESS PROTOCOLS" desc="ACCOUNT FORTIFICATION AND AUTHENTICATION">
                    <div className="space-y-6">
                        <div className={`p-10 border transition-all flex flex-col md:flex-row items-center justify-between gap-10 ${settings.two_factor_enabled ? 'bg-black text-white border-black' : 'bg-zinc-50 border-zinc-100'
                            }`}>
                            <div className="flex items-center gap-6">
                                <div className={`w-14 h-14 flex items-center justify-center ${settings.two_factor_enabled ? 'bg-zinc-900 text-white' : 'bg-zinc-200 text-black'
                                    }`}>
                                    <ShieldCheck size={28} />
                                </div>
                                <div className="text-center md:text-left">
                                    <p className="text-lg font-bold uppercase tracking-tighter leading-none mb-2">TWO-FACTOR ENCRYPTION</p>
                                    <p className={`text-[10px] font-bold uppercase tracking-widest ${settings.two_factor_enabled ? 'text-zinc-500' : 'text-zinc-400'}`}>
                                        {settings.two_factor_enabled ? 'FORTIFICATION ACTIVE' : 'REINFORCE ACCOUNT ACCESS'}
                                    </p>
                                </div>
                            </div>
                            <button
                                onClick={handleEnable2FA}
                                className={`px-10 py-4 text-[11px] font-black tracking-widest uppercase transition-all active:scale-95 ${settings.two_factor_enabled ? 'bg-white text-black' : 'bg-black text-white hover:bg-zinc-800'
                                    }`}
                            >
                                {settings.two_factor_enabled ? 'DISABLE 2FA' : 'INITIALIZE 2FA'}
                            </button>
                        </div>

                        <AnimatePresence>
                            {show2FAModal && (
                                <motion.div
                                    initial={{ opacity: 0 }}
                                    animate={{ opacity: 1 }}
                                    exit={{ opacity: 0 }}
                                    className="p-10 bg-zinc-50 border border-zinc-100 space-y-10"
                                >
                                    <div className="flex items-center gap-4 text-black">
                                        <Smartphone size={18} />
                                        <h4 className="text-[11px] font-black uppercase tracking-[0.3em]">MOBI CARD VALIDATION</h4>
                                    </div>
                                    <p className="text-[11px] font-medium text-zinc-500 uppercase tracking-tight leading-relaxed">
                                        ENTER THE 6-DIGIT VERIFICATION SEQUENCE (USE <span className="text-black font-black">123456</span> FOR BYPASS)
                                    </p>
                                    <div className="flex flex-wrap items-center gap-10">
                                        <input
                                            type="text"
                                            maxLength={6}
                                            placeholder="000000"
                                            value={otpCode}
                                            onChange={(e) => setOtpCode(e.target.value)}
                                            className="w-48 bg-white border border-zinc-200 px-6 py-4 text-2xl font-black tracking-[0.4em] text-black focus:border-black outline-none placeholder:text-zinc-100"
                                        />
                                        <button
                                            onClick={handleVerifyOTP}
                                            disabled={otpCode.length !== 6 || is2FALoading}
                                            className="px-10 py-5 bg-black text-white text-[11px] font-black tracking-widest uppercase hover:bg-zinc-800 transition-all disabled:opacity-20 flex items-center gap-4"
                                        >
                                            {is2FALoading ? <Loader2 size={16} className="animate-spin" /> : "VALIDATE"}
                                            <ArrowUpRight size={16} />
                                        </button>
                                        <button
                                            onClick={() => setShow2FAModal(false)}
                                            className="text-[10px] font-black text-zinc-300 hover:text-black uppercase tracking-widest transition-all"
                                        >
                                            ABORT
                                        </button>
                                    </div>
                                </motion.div>
                            )}
                        </AnimatePresence>
                    </div>
                </Section>

            </div>
        </div>
    );
};

export default SettingsPage;
