import React, { useState, useEffect } from "react";
import {
  Plus,
  Calendar,
  User as UserIcon,
  Search,
  Loader2,
  Trash2,
  ChevronRight,
  Filter,
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [activeTab, setActiveTab] = useState<string>("All");

  // Load Tasks
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      setLoading(false);
    }
  };

  // Helper to normalize status for display
  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getFilteredTasks = () => {
    return tasks.filter(t => {
      const matchesSearch = t.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (t.description && t.description.toLowerCase().includes(searchQuery.toLowerCase()));
      const matchesTab = activeTab === "All" || normalizeStatus(t.status) === activeTab;
      return matchesSearch && matchesTab;
    });
  };

  const handleStatusUpdate = async (e: React.MouseEvent, task: Task, newStatus: string) => {
    e.stopPropagation();
    // Optimistic Update
    setTasks(prev => prev.map(t => t.id === task.id ? { ...t, status: newStatus } : t));
    try {
      await taskService.updateTask(task.id, { status: newStatus });
    } catch (error) {
      console.error("Failed to update status");
      fetchTasks(); // Revert on error
    }
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Delete this task?")) return;
    setTasks(prev => prev.filter(t => t.id !== id));
    try {
      await taskService.deleteTask(id);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  return (
    <div className="min-h-screen bg-[#F8FAFC] p-6 md:p-10 space-y-10 animate-in fade-in duration-500">

      {/* üöÄ Header Section */}
      <div className="max-w-6xl mx-auto flex flex-col md:flex-row md:items-end justify-between gap-6">
        <div className="space-y-2">
          <h1 className="text-4xl font-extrabold text-slate-900 tracking-tight">Active Tasks</h1>
          <p className="text-slate-500 font-medium">Streamline your workflow with precision.</p>
        </div>

        <button
          onClick={() => navigate("/tasks/new")}
          className="flex items-center gap-2 bg-slate-900 hover:bg-slate-800 text-white px-6 py-3 rounded-2xl font-bold shadow-xl shadow-slate-200 transition-all active:scale-95 group"
        >
          <Plus size={20} className="group-hover:rotate-90 transition-transform duration-300" />
          <span>Add New Project</span>
        </button>
      </div>

      {/* üîç Search & Filters Bar */}
      <div className="max-w-6xl mx-auto bg-white p-2 rounded-[24px] shadow-sm border border-slate-100 flex flex-col md:flex-row items-center gap-2">
        <div className="relative flex-1 w-full md:w-auto">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={20} />
          <input
            type="text"
            placeholder="Search tasks, descriptions..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-12 pr-6 py-4 bg-transparent text-slate-700 font-medium outline-none placeholder:text-slate-300"
          />
        </div>

        <div className="flex bg-slate-50 p-1.5 rounded-2xl gap-1 w-full md:w-auto">
          {["All", "Upcoming", "In Progress", "Completed"].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`px-4 py-2 rounded-xl text-sm font-bold transition-all ${activeTab === tab ? 'bg-white text-slate-900 shadow-sm' : 'text-slate-400 hover:text-slate-600'}`}
            >
              {tab}
            </button>
          ))}
        </div>
      </div>

      {/* üìã Long Bar List Layout */}
      <div className="max-w-6xl mx-auto space-y-4">
        {loading ? (
          <div className="py-20 flex flex-col items-center justify-center space-y-4 text-slate-400">
            <Loader2 className="w-10 h-10 animate-spin text-indigo-600" />
            <span className="font-bold text-lg">Initializing Workspace...</span>
          </div>
        ) : (
          <AnimatePresence mode="popLayout">
            {getFilteredTasks().length === 0 ? (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="py-20 text-center bg-white rounded-[32px] border-2 border-dashed border-slate-100"
              >
                <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-slate-50 text-slate-300 mb-4">
                  <Filter size={32} />
                </div>
                <h3 className="text-xl font-bold text-slate-800">No matches found</h3>
                <p className="text-slate-400 mt-1">Try adjusting your filters or search terms.</p>
              </motion.div>
            ) : (
              getFilteredTasks().map((task, idx) => (
                <TaskBar
                  key={task.id}
                  task={task}
                  index={idx}
                  onDelete={handleDelete}
                  onUpdateStatus={handleStatusUpdate}
                  navigate={navigate}
                  normalizeStatus={normalizeStatus}
                />
              ))
            )}
          </AnimatePresence>
        )}
      </div>
    </div>
  );
};

/* üß© Task Bar Sub-Component (Long Type Bar Style) */

const TaskBar = ({ task, index, onDelete, onUpdateStatus, navigate, normalizeStatus }: any) => {
  const statusLabels = ["Upcoming", "In Progress", "Completed"];
  const currentStatus = normalizeStatus(task.status);

  return (
    <motion.div
      layout
      initial={{ opacity: 0, x: -20 }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, scale: 0.95 }}
      transition={{ delay: index * 0.05 }}
      onClick={() => navigate(`/tasks/edit/${task.id}`)}
      className="group bg-white p-5 rounded-[24px] border border-slate-100 shadow-sm hover:shadow-xl hover:shadow-slate-200/50 hover:border-indigo-100 transition-all duration-300 cursor-pointer flex flex-col md:flex-row items-center gap-6"
    >
      {/* Status Dot */}
      <div className="flex-shrink-0">
        <div className={`w-4 h-4 rounded-full ${currentStatus === 'Completed' ? 'bg-green-500 shadow-[0_0_12px_rgba(34,197,94,0.4)]' : currentStatus === 'In Progress' ? 'bg-orange-500 shadow-[0_0_12px_rgba(249,115,22,0.4)]' : 'bg-indigo-500 shadow-[0_0_12px_rgba(99,102,241,0.4)]'}`}></div>
      </div>

      {/* Main Info Box */}
      <div className="flex-1 min-w-0 space-y-1">
        <div className="flex items-center gap-3">
          <h3 className="font-bold text-slate-800 text-lg truncate group-hover:text-indigo-600 transition-colors uppercase tracking-tight">{task.title}</h3>
        </div>
        <p className="text-slate-400 text-sm font-medium line-clamp-1 italic">{task.description || "No project description provided."}</p>
      </div>

      {/* Metrics Group */}
      <div className="flex items-center gap-10">
        {/* Assignee */}
        <div className="flex items-center gap-3 bg-slate-50 px-4 py-2 rounded-2xl border border-slate-100/50">
          {task.assigned_to_user ? (
            <div className="w-8 h-8 rounded-full bg-white flex items-center justify-center text-xs font-bold text-indigo-600 border border-indigo-100 shadow-sm">
              {task.assigned_to_user.name.charAt(0)}
            </div>
          ) : (
            <div className="w-8 h-8 rounded-full bg-white flex items-center justify-center text-slate-300 border border-slate-100 shadow-sm">
              <UserIcon size={14} />
            </div>
          )}
          <div className="hidden lg:block">
            <span className="block text-[10px] font-bold text-slate-400 uppercase tracking-widest">Assignee</span>
            <span className="text-xs font-bold text-slate-700">{task.assigned_to_user?.name || "Unassigned"}</span>
          </div>
        </div>

        {/* Due Date */}
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 rounded-2xl bg-indigo-50 flex items-center justify-center text-indigo-500">
            <Calendar size={18} />
          </div>
          <div>
            <span className="block text-[10px] font-bold text-slate-400 uppercase tracking-widest">Target Date</span>
            <span className="text-xs font-bold text-slate-700">
              {task.due_date ? new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' }) : "--/--/--"}
            </span>
          </div>
        </div>
      </div>

      {/* Quick Status Control bar */}
      <div className="hidden xl:flex bg-slate-50 p-1 rounded-2xl gap-1">
        {statusLabels.map((s) => (
          <button
            key={s}
            onClick={(e) => onUpdateStatus(e, task, s)}
            className={`px-3 py-1.5 rounded-xl text-[10px] font-black uppercase tracking-tighter transition-all ${currentStatus === s ? (s === 'Upcoming' ? 'bg-indigo-500 text-white' : s === 'In Progress' ? 'bg-orange-500 text-white' : 'bg-green-500 text-white') : 'text-slate-300 hover:text-slate-500'}`}
          >
            {s}
          </button>
        ))}
      </div>

      {/* Actions */}
      <div className="flex items-center gap-2">
        <button
          onClick={(e) => onDelete(e, task.id)}
          className="w-10 h-10 flex items-center justify-center rounded-2xl text-slate-300 hover:text-red-500 hover:bg-red-50 transition-all border border-transparent hover:border-red-100"
        >
          <Trash2 size={18} />
        </button>
        <div className="w-10 h-10 flex items-center justify-center rounded-2xl bg-slate-900 text-white group-hover:bg-indigo-600 transition-all">
          <ChevronRight size={18} />
        </div>
      </div>

    </motion.div>
  );
};

export default TasksPage;
