import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Plus,
  Calendar as CalendarIcon,
  FileUp,
  MessageSquare,
  ClipboardCheck,
  Users,
  Bell,
  ArrowRight
} from "lucide-react";
import { taskService, Task } from "../../services/taskService";
import { messageService, Chat } from "../../services/messageService";
import { userService } from "../../services/userService";
import { notificationService, Notification } from "../../services/notificationService";
import { authService } from "../../services/authService";

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState<any>(null);

  // Stats
  const [stats, setStats] = useState({
    tasksDue: 0,
    meetingsToday: 0, // Mock for now
    unreadMessages: 0,
    activeTeam: 0
  });

  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [recentTasks, setRecentTasks] = useState<Task[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // 1. User
        const user = await authService.getCurrentUser();
        setCurrentUser(user);

        // 2. Tasks
        const tasks = await taskService.getTasks();
        // Calculate "Due Today" or "Overdue" or just "Pending"
        const pending = tasks.filter(t => t.status !== "Completed").length;
        setRecentTasks(tasks.slice(0, 3)); // Top 3

        // 3. Messages
        const chats = await messageService.getChats();
        const unreadCount = chats.reduce((acc: number, curr: Chat) => acc + (curr.unread_count || 0), 0);

        // 4. Team
        const users = await userService.getAllUsers();
        const activeUsers = users.length; // Simply count all for now

        // 5. Notifications
        const notifs = await notificationService.getNotifications(0, 5);
        setNotifications(notifs);

        setStats({
          tasksDue: pending,
          meetingsToday: 0,
          unreadMessages: unreadCount,
          activeTeam: activeUsers
        });

      } catch (error) {
        console.error("Failed to fetch dashboard data", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const statCards = [
    { icon: ClipboardCheck, title: "Pending Tasks", value: stats.tasksDue, color: "bg-blue-100 text-blue-600", link: "/tasks" },
    { icon: CalendarIcon, title: "Meetings Today", value: stats.meetingsToday, color: "bg-green-100 text-green-600", link: "/meetings" },
    { icon: MessageSquare, title: "Unread Messages", value: stats.unreadMessages, color: "bg-purple-100 text-purple-600", link: "/messages" },
    { icon: Users, title: "Team Members", value: stats.activeTeam, color: "bg-orange-100 text-orange-600", link: "/messages" }, // Link to messages/contacts
  ];

  return (
    <div className="min-h-screen bg-white text-slate-800 px-6 py-8 space-y-8 animate-in fade-in duration-500">
      {/* Header Section */}
      <header className="flex flex-col md:flex-row md:items-center md:justify-between">
        <div>
          <h2 className="text-3xl font-bold tracking-tight text-slate-900">Dashboard</h2>
          <p className="text-slate-500 mt-1">
            {loading ? "Loading..." : `Welcome back, ${currentUser?.name || 'User'}! ðŸ‘‹`}
          </p>
        </div>
        <div className="mt-4 md:mt-0">
          <span className="text-sm font-medium text-slate-400">
            {new Date().toLocaleDateString(undefined, { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}
          </span>
        </div>
      </header>

      {/* Stats Summary */}
      <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {statCards.map((card, idx) => (
          <div
            key={idx}
            onClick={() => navigate(card.link)}
            className="group flex items-center gap-4 p-5 bg-white rounded-2xl shadow-sm hover:shadow-md border border-slate-100 cursor-pointer transition-all hover:-translate-y-1"
          >
            <div className={`p-3.5 rounded-xl ${card.color} transition-transform group-hover:scale-110`}>
              <card.icon size={22} />
            </div>
            <div>
              <p className="text-sm text-slate-500 font-medium">{card.title}</p>
              <h3 className="text-2xl font-bold text-slate-800">
                {loading ? "-" : (
                  card.title === "Unread Messages" && card.value === 0
                    ? "All caught up!"
                    : card.value
                )}
              </h3>
            </div>
          </div>
        ))}
      </section>

      {/* AI Summary + Quick Actions */}
      <section className="grid grid-cols-1 lg:grid-cols-3 gap-8">

        {/* Main Feed */}
        <div className="lg:col-span-2 space-y-8">

          {/* AI Insight / High Priority */}
          <div className="bg-slate-50 border border-slate-100 rounded-2xl p-6 relative overflow-hidden">
            <div className="absolute top-0 right-0 p-4 opacity-10">
              <ClipboardCheck size={100} />
            </div>
            <h3 className="text-lg font-bold text-slate-800 mb-2">Priority Overview</h3>
            <p className="text-slate-600 max-w-xl relative z-10">
              You have <span className="font-bold text-blue-600">{stats.tasksDue} pending tasks</span> and <span className="font-bold text-purple-600">{stats.unreadMessages} unread messages</span>.
              {stats.tasksDue > 5 ? " It looks like a busy day ahead! Prioritize your tasks." : " You are on top of things!"}
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Recent Notifications */}
            <div className="bg-white border border-slate-100 rounded-2xl shadow-sm p-6 max-h-[400px] overflow-y-auto">
              <div className="flex items-center justify-between mb-4">
                <h4 className="font-bold text-slate-800 flex items-center gap-2">
                  <Bell size={18} className="text-yellow-500" /> Notifications
                </h4>
              </div>

              <div className="space-y-4">
                {loading ? (
                  <p className="text-sm text-slate-400">Loading...</p>
                ) : notifications.length === 0 ? (
                  <p className="text-sm text-slate-400 italic">No new notifications</p>
                ) : (
                  notifications.map((notif) => (
                    <div key={notif.id} className="flex gap-3 items-start pb-3 border-b border-slate-50 last:border-0">
                      <div className={`w-2 h-2 mt-1.5 rounded-full flex-shrink-0 ${notif.is_read ? 'bg-slate-200' : 'bg-blue-500'}`}></div>
                      <div>
                        <p className="text-sm text-slate-700 font-medium">{notif.title}</p>
                        <p className="text-xs text-slate-500 mt-0.5 line-clamp-2">{notif.description}</p>
                        <p className="text-[10px] text-slate-400 mt-1">
                          {new Date(notif.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                        </p>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>

            {/* Recent Tasks */}
            <div className="bg-white border border-slate-100 rounded-2xl shadow-sm p-6">
              <div className="flex items-center justify-between mb-4">
                <h4 className="font-bold text-slate-800 flex items-center gap-2">
                  <ClipboardCheck size={18} className="text-blue-500" /> Recent Tasks
                </h4>
                <button onClick={() => navigate('/tasks')} className="text-xs font-semibold text-blue-600 hover:text-blue-700 flex items-center">
                  View All <ArrowRight size={12} className="ml-1" />
                </button>
              </div>
              <div className="space-y-3">
                {loading ? (
                  <p className="text-sm text-slate-400">Loading...</p>
                ) : recentTasks.length === 0 ? (
                  <p className="text-sm text-slate-400 italic">No tasks yet</p>
                ) : (
                  recentTasks.map(task => (
                    <div key={task.id} className="group flex items-center justify-between p-3 rounded-xl bg-slate-50 hover:bg-blue-50/50 transition-colors border border-transparent hover:border-blue-100 cursor-pointer" onClick={() => navigate('/tasks')}>
                      <div className="flex items-center gap-3">
                        <div className={`w-2 h-8 rounded-full ${task.status === 'Completed' ? 'bg-green-500' : 'bg-blue-500'}`}></div>
                        <div>
                          <p className="text-sm font-semibold text-slate-700 line-clamp-1">{task.title}</p>
                          <p className="text-xs text-slate-500">{task.status}</p>
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Quick Actions Sidebar */}
        <div className="space-y-6">
          <div className="bg-white border border-slate-100 rounded-2xl shadow-sm p-6 sticky top-24">
            <h3 className="text-lg font-bold mb-4 text-slate-800">Quick Actions</h3>

            <div className="grid grid-cols-1 gap-3">
              <button
                onClick={() => navigate("/tasks")}
                className="flex items-center gap-3 bg-blue-600 text-white p-3 rounded-xl hover:bg-blue-700 transition shadow-sm hover:shadow active:scale-95"
              >
                <Plus size={18} /> <span className="font-medium">Create New Task</span>
              </button>

              <button
                onClick={() => navigate("/messages")}
                className="flex items-center gap-3 bg-white text-slate-700 border border-slate-200 p-3 rounded-xl hover:bg-slate-50 transition"
              >
                <MessageSquare size={18} className="text-purple-500" /> <span className="font-medium">Send Message</span>
              </button>

              <button
                onClick={() => navigate("/meetings/schedule")}
                className="flex items-center gap-3 bg-white text-slate-700 border border-slate-200 p-3 rounded-xl hover:bg-slate-50 transition"
              >
                <CalendarIcon size={18} className="text-green-500" /> <span className="font-medium">Schedule Meeting</span>
              </button>

              <button
                onClick={() => navigate("/files/upload")}
                className="flex items-center gap-3 bg-white text-slate-700 border border-slate-200 p-3 rounded-xl hover:bg-slate-50 transition"
              >
                <FileUp size={18} className="text-orange-500" /> <span className="font-medium">Upload File</span>
              </button>
            </div>
          </div>
        </div>

      </section>
    </div>
  );
};

export default DashboardPage;
