import React from "react";
import { Bell, Menu, X, Search, Settings, Command } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();

  return (
    <header className="sticky top-0 z-30 w-full bg-white/80 backdrop-blur-md border-b border-[#e2e2e4] px-6 sm:px-8 py-2.5 flex justify-between items-center transition-all duration-300">
      <div className="flex items-center gap-4 flex-1">
        <motion.button
          whileTap={{ scale: 0.95 }}
          onClick={onMenuClick}
          className="lg:hidden p-1.5 hover:bg-slate-100 rounded-md transition-colors text-slate-500 border border-[#e2e2e4]/60"
        >
          {isSidebarOpen ? <X size={18} /> : <Menu size={18} />}
        </motion.button>

        {/* üìê Precision Search */}
        <div className="hidden md:flex items-center bg-[#f7f7f8] border border-[#e2e2e4] focus-within:border-[#5e6ad2] focus-within:bg-white px-3 py-1.5 rounded-lg w-72 transition-all shadow-sm">
          <Search size={14} className="text-slate-400 mr-2" />
          <input
            type="text"
            placeholder="Search or jump to..."
            className="bg-transparent border-none outline-none text-xs w-full text-slate-700 placeholder:text-slate-400 font-medium"
          />
          <div className="flex items-center gap-0.5 ml-2 px-1 py-0.5 bg-white border border-[#e2e2e4] rounded text-[9px] font-black text-slate-400 shadow-sm">
            <Command size={10} />
            <span>K</span>
          </div>
        </div>
      </div>

      <div className="flex items-center gap-4">
        <div className="hidden lg:flex items-center gap-2">
          <div className="px-2 py-0.5 bg-slate-100 rounded text-[9px] font-bold text-slate-500 border border-slate-200">FREE PLAN</div>
        </div>

        <div className="flex items-center gap-1">
          <motion.button
            whileHover={{ backgroundColor: "rgba(0,0,0,0.03)" }}
            className="p-1.5 text-slate-500 hover:text-[#111113] rounded-md transition-all relative"
          >
            <Bell size={18} />
            <span className="absolute top-1.5 right-1.5 w-1.5 h-1.5 bg-[#5e6ad2] rounded-full border border-white" />
          </motion.button>

          <motion.button
            whileHover={{ backgroundColor: "rgba(0,0,0,0.03)" }}
            className="p-1.5 text-slate-500 hover:text-[#111113] rounded-md transition-all"
          >
            <Settings size={18} />
          </motion.button>
        </div>

        <div className="h-4 w-[1px] bg-[#e2e2e4] mx-1" />

        <motion.button
          whileHover={{ scale: 1.02 }}
          onClick={() => navigate("/profile")}
          className="flex items-center gap-2.5 group"
        >
          <div className="w-7 h-7 rounded-lg bg-[#5e6ad2] flex items-center justify-center text-white font-bold text-xs shadow-sm">
            A
          </div>
          <span className="hidden sm:block text-xs font-bold text-[#111113]">Ashok S.</span>
        </motion.button>
      </div>
    </header>
  );
};

export default Topbar;
