import React, { useEffect, useState, useRef } from "react";
import { Menu, Search, Bell, Sparkles } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { authService } from "../services/authService";
import { User } from "../services/userService";
import { useNotifications } from "../context/NotificationContext";
import { AnimatePresence, motion } from "framer-motion";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState<User | null>(null);
  const { notifications, unreadCount, markAsRead, markAllAsRead } = useNotifications();
  const [showNotifications, setShowNotifications] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const currentUser = await authService.getCurrentUser();
        setUser(currentUser);
      } catch (error) {
        console.error("Failed to fetch user", error);
      }
    };
    fetchUser();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setShowNotifications(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .substring(0, 2);
  };

  return (
    <header className="sticky top-0 z-30 w-full bg-white/70 backdrop-blur-xl border-b border-slate-100 flex items-center justify-between px-6 py-4">
      {/* Left: Mobile Menu & Search */}
      <div className="flex items-center gap-6 flex-1">
        <button
          onClick={onMenuClick}
          className="lg:hidden p-3 text-slate-500 hover:bg-slate-50 rounded-2xl transition-all"
        >
          <Menu size={20} />
        </button>

        <div className="hidden sm:flex items-center w-full max-w-lg bg-slate-50/50 rounded-2xl px-4 py-2.5 border-2 border-transparent focus-within:border-indigo-600 focus-within:bg-white focus-within:shadow-xl focus-within:shadow-indigo-500/5 transition-all duration-300 group">
          <Search size={18} className="text-slate-400 mr-3 group-focus-within:text-indigo-600 transition-colors" />
          <input
            type="text"
            placeholder="Quick search (Ctrl + K)"
            className="bg-transparent border-none outline-none text-[13px] font-bold w-full text-slate-900 placeholder:text-slate-400"
          />
        </div>
      </div>

      {/* Right: Actions & Profile */}
      <div className="flex items-center gap-6">
        {/* Upgrade / Promo */}
        <div className="hidden xl:flex items-center gap-2 px-4 py-2 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-widest border border-indigo-100">
          <Sparkles size={14} className="animate-pulse" />
          Go Pro
        </div>

        {/* Notifications */}
        <div className="relative" ref={dropdownRef}>
          <button
            onClick={() => setShowNotifications(!showNotifications)}
            className={`relative p-3 rounded-2xl transition-all duration-300 ${showNotifications ? "bg-indigo-600 text-white shadow-lg shadow-indigo-200" : "text-slate-500 hover:bg-slate-100"
              }`}
          >
            <Bell size={20} />
            {unreadCount > 0 && (
              <span className="absolute top-2 right-2 w-5 h-5 bg-rose-500 text-white text-[10px] font-black flex items-center justify-center rounded-full border-2 border-white shadow-md">
                {unreadCount > 9 ? '9+' : unreadCount}
              </span>
            )}
          </button>

          <AnimatePresence>
            {showNotifications && (
              <motion.div
                initial={{ opacity: 0, y: 20, scale: 0.95 }}
                animate={{ opacity: 1, y: 0, scale: 1 }}
                exit={{ opacity: 0, y: 20, scale: 0.95 }}
                className="absolute right-0 mt-4 w-96 bg-white border border-slate-100 rounded-[32px] shadow-2xl shadow-indigo-500/10 overflow-hidden z-50 p-2"
              >
                <div className="p-6 border-b border-slate-50">
                  <div className="flex justify-between items-center mb-1">
                    <h3 className="text-lg font-[900] text-slate-900">Activity</h3>
                    {unreadCount > 0 && (
                      <button
                        onClick={() => markAllAsRead()}
                        className="text-[10px] font-black uppercase tracking-widest text-indigo-600 hover:text-indigo-700 underline underline-offset-4"
                      >
                        Clear All
                      </button>
                    )}
                  </div>
                  <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Real-time alerts</p>
                </div>
                <div className="max-h-[400px] overflow-y-auto custom-scrollbar p-1">
                  {notifications.length > 0 ? (
                    notifications.map((notif) => (
                      <motion.div
                        layout
                        key={notif.id}
                        onClick={() => markAsRead(notif.id)}
                        className={`p-4 rounded-2xl mb-1 cursor-pointer transition-all duration-300 flex gap-4 items-start ${!notif.is_read ? 'bg-indigo-50/50 hover:bg-indigo-50' : 'hover:bg-slate-50'}`}
                      >
                        <div className={`w-3 h-3 mt-1.5 rounded-full shrink-0 shadow-lg ${!notif.is_read ? 'bg-indigo-600 shadow-indigo-200' : 'bg-transparent'}`} />
                        <div className="flex-1">
                          <p className="text-sm font-black text-slate-900 leading-tight mb-1">{notif.title}</p>
                          <p className="text-xs text-slate-500 font-medium leading-relaxed">{notif.description}</p>
                          <p className="text-[10px] font-black text-slate-400 mt-2 uppercase tracking-tight">{new Date(notif.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</p>
                        </div>
                      </motion.div>
                    ))
                  ) : (
                    <div className="py-20 text-center">
                      <div className="w-16 h-16 bg-slate-50 rounded-3xl flex items-center justify-center mx-auto mb-4 border border-slate-100">
                        <Bell size={24} className="text-slate-300" />
                      </div>
                      <h4 className="text-sm font-black text-slate-900">All caught up!</h4>
                      <p className="text-xs text-slate-400 font-medium px-10 mt-1">No new activity to show right now.</p>
                    </div>
                  )}
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* Profile */}
        <button
          onClick={() => navigate("/profile")}
          className="flex items-center gap-3 bg-slate-50/50 hover:bg-white hover:shadow-xl hover:shadow-indigo-500/5 p-1.5 pl-2 pr-4 rounded-2xl border-2 border-transparent hover:border-indigo-100 transition-all duration-300 group"
        >
          <div className="w-9 h-9 rounded-xl bg-gradient-to-tr from-indigo-600 to-violet-600 flex items-center justify-center text-white font-black text-xs shadow-lg shadow-indigo-200 group-hover:scale-105 transition-transform">
            {user?.name ? getInitials(user.name) : "U"}
          </div>
          <div className="hidden sm:block text-left">
            <p className="text-[13px] font-[900] text-slate-900 leading-none mb-1">
              {user?.name || "User"}
            </p>
            <p className="text-[10px] font-black text-indigo-400 uppercase tracking-widest">{user?.email?.split('@')[0] || "Member"}</p>
          </div>
        </button>
      </div>
    </header>
  );
};

export default Topbar;
