import React, { useState, useEffect, useRef, useCallback } from "react";
import {
    Search, Users, Paperclip, Smile, Send,
    Check, ArrowLeft, X, Sparkles, Zap, Shield, Plus
} from "lucide-react";
import { messageService, Chat, Message } from "../../services/messageService";
import { userService, User } from "../../services/userService";
import { authService } from "../../services/authService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";
import { motion, AnimatePresence } from "framer-motion";

/**
 * Modal component for creating a new group chat.
 */
const CreateGroupModal = ({ isOpen, onClose, users, onCreate }: any) => {
    const [groupName, setGroupName] = useState("");
    const [selectedIds, setSelectedIds] = useState<number[]>([]);

    const toggleUser = (id: number) => {
        setSelectedIds(prev => prev.includes(id) ? prev.filter(x => x !== id) : [...prev, id]);
    };

    const handleSubmit = () => {
        if (!groupName || selectedIds.length === 0) return;
        onCreate(groupName, selectedIds);
        onClose();
        setGroupName("");
        setSelectedIds([]);
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <div className="fixed inset-0 z-[100] flex items-center justify-center p-4">
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="absolute inset-0 bg-slate-900/60 backdrop-blur-md"
                        onClick={onClose}
                    />
                    <motion.div
                        initial={{ scale: 0.9, opacity: 0, y: 20 }}
                        animate={{ scale: 1, opacity: 1, y: 0 }}
                        exit={{ scale: 0.9, opacity: 0, y: 20 }}
                        className="bg-white rounded-[40px] w-full max-w-xl p-10 shadow-2xl relative z-10 overflow-hidden"
                    >
                        {/* Background Decoration */}
                        <div className="absolute top-0 right-0 w-40 h-40 bg-indigo-50 rounded-full blur-3xl -mr-20 -mt-20 opacity-50" />

                        <div className="relative z-10">
                            <div className="flex justify-between items-center mb-10">
                                <div>
                                    <h3 className="text-3xl font-[1000] text-slate-900 tracking-tight leading-none uppercase">Squad Genesis</h3>
                                    <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-2">Initialize team communication</p>
                                </div>
                                <button
                                    onClick={onClose}
                                    className="p-3 bg-slate-50 text-slate-400 hover:text-rose-500 rounded-2xl transition-all"
                                >
                                    <X size={24} />
                                </button>
                            </div>

                            <div className="space-y-8">
                                <div className="space-y-3">
                                    <label className="text-xs font-black text-slate-500 uppercase tracking-widest ml-1">Manifest Name</label>
                                    <input
                                        className="w-full px-8 py-5 bg-slate-50 border-2 border-transparent rounded-[24px] focus:bg-white focus:border-indigo-600 transition-all font-bold text-slate-900 outline-none text-lg placeholder:text-slate-300"
                                        value={groupName}
                                        onChange={e => setGroupName(e.target.value)}
                                        placeholder="e.g. ALPHA_TERMINAL"
                                    />
                                </div>

                                <div className="space-y-3">
                                    <label className="text-xs font-black text-slate-500 uppercase tracking-widest ml-1">Assemble Unit (Min. 1)</label>
                                    <div className="max-h-72 overflow-y-auto space-y-2 pr-2 custom-scrollbar">
                                        {users.map((u: User) => (
                                            <button
                                                key={u.id}
                                                onClick={() => toggleUser(u.id)}
                                                className={`w-full flex items-center gap-5 p-4 rounded-[24px] transition-all group ${selectedIds.includes(u.id)
                                                        ? 'bg-indigo-600 shadow-xl shadow-indigo-200'
                                                        : 'bg-slate-50 hover:bg-slate-100'
                                                    }`}
                                            >
                                                <div className={`w-12 h-12 rounded-2xl flex items-center justify-center text-xs font-black transition-all ${selectedIds.includes(u.id) ? 'bg-white text-indigo-600' : 'bg-slate-200 text-slate-500'
                                                    }`}>
                                                    {u.name.substring(0, 2).toUpperCase()}
                                                </div>
                                                <span className={`text-lg font-black tracking-tight ${selectedIds.includes(u.id) ? 'text-white' : 'text-slate-700'}`}>
                                                    {u.name}
                                                </span>
                                                {selectedIds.includes(u.id) && <motion.div initial={{ scale: 0 }} animate={{ scale: 1 }} className="ml-auto w-6 h-6 bg-white rounded-full flex items-center justify-center"><Check size={14} className="text-indigo-600" /></motion.div>}
                                            </button>
                                        ))}
                                    </div>
                                </div>

                                <div className="flex gap-4 pt-4">
                                    <button
                                        onClick={onClose}
                                        className="flex-1 py-5 text-slate-400 font-black uppercase tracking-widest text-xs hover:text-slate-900 transition-all"
                                    >
                                        Cancel Protocol
                                    </button>
                                    <button
                                        onClick={handleSubmit}
                                        disabled={!groupName || selectedIds.length === 0}
                                        className="flex-1 py-5 bg-slate-900 text-white rounded-[24px] font-black uppercase tracking-widest text-xs disabled:opacity-30 hover:bg-indigo-600 hover:shadow-2xl shadow-indigo-200 transition-all active:scale-95"
                                    >
                                        Establish Squad
                                    </button>
                                </div>
                            </div>
                        </div>
                    </motion.div>
                </div>
            )}
        </AnimatePresence>
    );
};

const MessagesPage: React.FC = () => {
    const [chats, setChats] = useState<Chat[]>([]);
    const [messages, setMessages] = useState<Message[]>([]);
    const [currentUser, setCurrentUser] = useState<any>(null);
    const [activeChatId, setActiveChatId] = useState<number | null>(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [allUsers, setAllUsers] = useState<User[]>([]);
    const [inputText, setInputText] = useState("");
    const [isMobileChatOpen, setIsMobileChatOpen] = useState(false);
    const [isGroupModalOpen, setIsGroupModalOpen] = useState(false);

    const messagesContainerRef = useRef<HTMLDivElement>(null);

    const loadChats = useCallback(async () => {
        try {
            const data = await messageService.getChats();
            setChats(data);
        } catch (e) {
            console.error("Failed to load chats:", e);
        }
    }, []);

    const loadUserAndData = useCallback(async () => {
        try {
            const user = await authService.getCurrentUser();
            setCurrentUser(user);
            await loadChats();
            const users = await userService.getAllUsers();
            setAllUsers(users.filter(u => u.id !== user.id));
        } catch (e) {
            console.error("Failed to load user data:", e);
        }
    }, [loadChats]);

    const loadMessages = useCallback(async (chatId: number) => {
        try {
            const msgs = await messageService.getMessages(chatId);
            setMessages(msgs);
            await messageService.markAsRead(chatId);
            loadChats();
        } catch (e) {
            console.error("Failed to load messages:", e);
        }
    }, [loadChats]);

    const scrollToBottom = useCallback((smooth = true) => {
        if (messagesContainerRef.current) {
            const { scrollHeight, clientHeight } = messagesContainerRef.current;
            messagesContainerRef.current.scrollTo({
                top: scrollHeight - clientHeight,
                behavior: smooth ? 'smooth' : 'auto'
            });
        }
    }, []);

    useEffect(() => {
        loadUserAndData();
    }, [loadUserAndData]);

    useEffect(() => {
        const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
            if (message.type === 'message') {
                const msg = message.data;
                if (activeChatId && msg.chat_id === activeChatId) {
                    setMessages(prev => {
                        if (prev.some(m => m.id === msg.id)) return prev;
                        return [...prev, msg];
                    });
                    messageService.markAsRead(activeChatId);
                }
                loadChats();
            }
        });
        return () => {
            unsubscribe();
        };
    }, [activeChatId, loadChats]);

    useEffect(() => {
        if (activeChatId) {
            loadMessages(activeChatId);
            setTimeout(() => scrollToBottom(false), 100);
        }
    }, [activeChatId, loadMessages, scrollToBottom]);

    useEffect(() => {
        scrollToBottom();
    }, [messages, scrollToBottom]);

    const handleSendMessage = async () => {
        if (!inputText.trim() || !activeChatId) return;
        try {
            await messageService.sendMessage(activeChatId, inputText);
            setInputText("");
            loadMessages(activeChatId);
            loadChats();
            setTimeout(() => scrollToBottom(), 100);
        } catch (e) {
            console.error("Failed to send message:", e);
        }
    };

    const handleCreateP2PChat = async (userId: number) => {
        try {
            const chat = await messageService.createChat([userId], undefined, false);
            setChats(prev => prev.find(c => c.id === chat.id) ? prev : [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
            setSearchQuery("");
        } catch (e) {
            console.error("Failed to create P2P chat:", e);
        }
    };

    const handleCreateGroup = async (name: string, userIds: number[]) => {
        try {
            const chat = await messageService.createChat(userIds, name, true);
            setChats(prev => [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
        } catch (e) {
            console.error("Failed to create group chat:", e);
        }
    };

    const filteredChats = chats.filter(c => c.name?.toLowerCase().includes(searchQuery.toLowerCase()));
    const suggestedUsers = searchQuery ? allUsers.filter(u => u.name.toLowerCase().includes(searchQuery.toLowerCase())) : [];
    const activeChat = chats.find(c => c.id === activeChatId);

    return (
        <div className="flex h-screen bg-[#f8fafc] overflow-hidden font-sans">
            <CreateGroupModal
                isOpen={isGroupModalOpen}
                onClose={() => setIsGroupModalOpen(false)}
                users={allUsers}
                onCreate={handleCreateGroup}
            />

            {/* Sidebar with Elite Aesthetics */}
            <div className={`w-full md:w-[400px] lg:w-[450px] bg-white border-r border-slate-100 flex flex-col p-8 space-y-10 ${isMobileChatOpen ? 'hidden md:flex' : 'flex'}`}>
                {/* Brand/Header */}
                <div className="space-y-8">
                    <div className="flex justify-between items-center">
                        <div className="space-y-1">
                            <h1 className="text-3xl font-[1000] text-slate-900 tracking-tight leading-none uppercase">Terminal</h1>
                            <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-1">Encrypted Space</p>
                        </div>
                        <motion.button
                            whileHover={{ scale: 1.1, rotate: 90 }}
                            whileTap={{ scale: 0.9 }}
                            onClick={() => setIsGroupModalOpen(true)}
                            className="w-14 h-14 bg-slate-900 text-white rounded-[20px] flex items-center justify-center shadow-2xl shadow-slate-200 transition-all"
                            title="Manifest Squad"
                        >
                            <Plus size={24} />
                        </motion.button>
                    </div>

                    <div className="relative group">
                        <Search className="absolute left-6 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={20} />
                        <input
                            className="w-full bg-slate-50 border-2 border-transparent rounded-[24px] py-5 pl-16 pr-8 text-sm font-bold text-slate-900 focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-300"
                            placeholder="Search operatives..."
                            value={searchQuery}
                            onChange={e => setSearchQuery(e.target.value)}
                        />
                    </div>
                </div>

                {/* Chat Feed */}
                <div className="flex-1 overflow-y-auto custom-scrollbar pr-2 -mr-2 space-y-4">
                    {filteredChats.map(chat => (
                        <div
                            key={chat.id}
                            onClick={() => { setActiveChatId(chat.id); setIsMobileChatOpen(true); }}
                            className={`group relative p-6 rounded-[32px] flex items-center gap-5 cursor-pointer transition-all duration-500 border-2 ${activeChatId === chat.id
                                    ? 'bg-indigo-600 border-indigo-600 shadow-2xl shadow-indigo-500/20'
                                    : 'bg-white border-slate-50 hover:border-indigo-100 hover:shadow-xl hover:shadow-slate-200/50'
                                }`}
                        >
                            <div className={`w-16 h-16 rounded-[24px] flex items-center justify-center text-white font-black text-lg shadow-xl shadow-black/5 ${activeChatId === chat.id ? 'bg-white/20' : 'bg-gradient-to-tr from-slate-800 to-slate-900'
                                }`}>
                                {chat.is_group ? <Users size={24} /> : (chat.name?.substring(0, 2).toUpperCase() || "?")}
                            </div>

                            <div className="flex-1 min-w-0">
                                <div className="flex justify-between items-center mb-1">
                                    <h3 className={`text-lg font-black tracking-tight truncate ${activeChatId === chat.id ? 'text-white' : 'text-slate-900'}`}>{chat.name}</h3>
                                    <span className={`text-[10px] font-black uppercase tracking-tighter ${activeChatId === chat.id ? 'text-white/60' : 'text-slate-300'}`}>
                                        {chat.last_message_time ? new Date(chat.last_message_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : ''}
                                    </span>
                                </div>
                                <div className="flex justify-between items-center">
                                    <p className={`text-xs font-medium truncate max-w-[85%] ${activeChatId === chat.id ? 'text-white/70' : 'text-slate-500'}`}>
                                        {chat.last_message || "Awaiting transmission..."}
                                    </p>
                                    {chat.unread_count && chat.unread_count > 0 && (
                                        <div className={`w-6 h-6 rounded-full flex items-center justify-center text-[10px] font-black shadow-lg ${activeChatId === chat.id ? 'bg-white text-indigo-600' : 'bg-indigo-600 text-white'
                                            }`}>
                                            {chat.unread_count}
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>
                    ))}

                    {suggestedUsers.length > 0 && (
                        <div className="pt-8 space-y-4">
                            <h4 className="px-6 text-[10px] font-black text-slate-400 uppercase tracking-[0.3em]"> operatives found</h4>
                            {suggestedUsers.map(user => (
                                <div
                                    key={user.id}
                                    onClick={() => handleCreateP2PChat(user.id)}
                                    className="p-5 bg-slate-50 rounded-[28px] flex items-center gap-5 cursor-pointer hover:bg-indigo-50 hover:scale-[1.02] transition-all group"
                                >
                                    <div className="w-12 h-12 rounded-2xl bg-white border border-slate-100 flex items-center justify-center text-indigo-600 font-black text-xs shadow-sm group-hover:bg-indigo-600 group-hover:text-white transition-all">
                                        {user.name.substring(0, 2).toUpperCase()}
                                    </div>
                                    <div className="flex-1">
                                        <p className="text-sm font-black text-slate-900">{user.name}</p>
                                        <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-1">Commence Protocol</p>
                                    </div>
                                    <ArrowRight size={18} className="text-slate-200 group-hover:text-indigo-600 group-hover:translate-x-1 transition-all" />
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Conversational Core View */}
            {activeChat ? (
                <div className={`flex-1 flex flex-col bg-white overflow-hidden ${isMobileChatOpen ? 'flex' : 'hidden md:flex'}`}>
                    {/* Glass Header */}
                    <div className="h-24 bg-white/70 backdrop-blur-xl border-b border-slate-50 flex items-center justify-between px-10 relative z-20">
                        <div className="flex items-center gap-6">
                            <button onClick={() => setIsMobileChatOpen(false)} className="md:hidden p-3 bg-slate-50 text-slate-500 rounded-2xl hover:bg-slate-100 transition-all">
                                <ArrowLeft size={20} />
                            </button>
                            <div className="relative">
                                <div className={`w-14 h-14 rounded-[20px] flex items-center justify-center text-white font-black text-lg shadow-2xl shadow-slate-200 ${activeChat.is_group ? 'bg-indigo-600' : 'bg-slate-900'
                                    }`}>
                                    {activeChat.is_group ? <Users size={24} /> : activeChat.name?.substring(0, 2).toUpperCase()}
                                </div>
                                <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-emerald-500 rounded-full border-4 border-white shadow-sm" />
                            </div>
                            <div>
                                <h2 className="text-xl font-[1000] text-slate-900 tracking-tight leading-none mb-1">{activeChat.name}</h2>
                                <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest">
                                    {activeChat.is_group ? "Squad Environment" : "Secure Connection Established"}
                                </p>
                            </div>
                        </div>

                        <div className="flex items-center gap-4">
                            <button className="p-3 bg-slate-50 text-slate-400 hover:text-indigo-600 rounded-2xl transition-all"><Shield size={20} /></button>
                            <button className="p-3 bg-slate-50 text-slate-400 hover:text-indigo-600 rounded-2xl transition-all"><Sparkles size={20} /></button>
                        </div>
                    </div>

                    {/* Infinite Grid Background for Chat area */}
                    <div className="flex-1 relative flex flex-col overflow-hidden">
                        <div className="absolute inset-0 bg-[radial-gradient(#e5e7eb_1px,transparent_1px)] [background-size:32px_32px] opacity-30" />

                        <div ref={messagesContainerRef} className="flex-1 overflow-y-auto p-10 space-y-8 relative z-10 custom-scrollbar">
                            <AnimatePresence mode="popLayout">
                                {messages.map((msg, idx) => {
                                    const isMe = msg.sender_id === currentUser?.id;
                                    const showSender = !isMe && activeChat.is_group && (idx === 0 || messages[idx - 1].sender_id !== msg.sender_id);

                                    return (
                                        <motion.div
                                            initial={{ opacity: 0, y: 10, scale: 0.95 }}
                                            animate={{ opacity: 1, y: 0, scale: 1 }}
                                            key={msg.id}
                                            className={`flex w-full ${isMe ? 'justify-end' : 'justify-start'}`}
                                        >
                                            <div className={`flex flex-col max-w-[80%] md:max-w-[65%] lg:max-w-[55%] ${isMe ? 'items-end' : 'items-start'}`}>
                                                {showSender && <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest mb-2 ml-4">{msg.sender?.name || 'Unknown Unit'}</span>}
                                                <div className={`relative px-8 py-5 rounded-[32px] text-base leading-relaxed font-medium transition-all duration-300 ${isMe
                                                        ? 'bg-slate-900 text-white rounded-br-none shadow-2xl shadow-slate-900/10'
                                                        : 'bg-white text-slate-800 border-2 border-slate-50 rounded-bl-none shadow-xl shadow-slate-200/40'
                                                    }`}>
                                                    <p>{msg.content}</p>
                                                    {/* Double Check Icon for Me */}
                                                    {isMe && (
                                                        <div className="absolute bottom-1 right-2 opacity-30">
                                                            <Check size={10} className="-mr-1 mb-1 inline" />
                                                            <Check size={10} className="inline" />
                                                        </div>
                                                    )}
                                                </div>
                                                <span className="text-[10px] font-black text-slate-300 mt-2 px-4 uppercase tracking-[0.2em]">
                                                    {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                                </span>
                                            </div>
                                        </motion.div>
                                    );
                                })}
                            </AnimatePresence>
                        </div>
                    </div>

                    {/* Matrix Style Input Panel */}
                    <div className="p-8 bg-white/70 backdrop-blur-xl border-t border-slate-50 overflow-hidden relative">
                        <div className="flex items-center gap-6 max-w-6xl mx-auto">
                            <button className="p-4 bg-slate-50 text-slate-400 hover:text-indigo-600 rounded-3xl transition-all hover:scale-110 active:scale-90 shadow-sm"><Paperclip size={24} /></button>
                            <div className="flex-1 relative">
                                <input
                                    className="w-full px-8 py-5 bg-slate-50 border-2 border-transparent rounded-[32px] text-base font-bold text-slate-900 focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-300"
                                    placeholder="Enter transmission data..."
                                    value={inputText}
                                    onChange={e => setInputText(e.target.value)}
                                    onKeyDown={e => e.key === 'Enter' && handleSendMessage()}
                                />
                                <button className="absolute right-6 top-1/2 -translate-y-1/2 text-slate-300 hover:text-indigo-600"><Smile size={24} /></button>
                            </div>
                            <motion.button
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.9 }}
                                onClick={handleSendMessage}
                                disabled={!inputText.trim()}
                                className="p-5 bg-slate-900 text-white rounded-[32px] hover:bg-indigo-600 hover:shadow-2xl shadow-indigo-200 transition-all disabled:opacity-20 shadow-xl shadow-slate-900/10 group overflow-hidden relative"
                            >
                                <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/10 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-1000" />
                                <Send size={24} fill="currentColor" strokeWidth={3} className="relative z-10" />
                            </motion.button>
                        </div>
                    </div>
                </div>
            ) : (
                <div className="hidden md:flex flex-1 flex-col items-center justify-center bg-slate-50/30 overflow-hidden relative">
                    <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[800px] h-[800px] bg-indigo-50/50 rounded-full blur-[150px] -z-10" />

                    <div className="relative group">
                        <div className="absolute inset-0 bg-indigo-600 rounded-[50px] blur-3xl opacity-20 scale-125 group-hover:scale-150 transition-transform duration-700" />
                        <div className="relative w-40 h-40 bg-white rounded-[50px] flex items-center justify-center mb-10 shadow-2xl border border-slate-100">
                            <MessageSquareIcon size={64} className="text-indigo-600 animate-pulse fill-indigo-50" />
                        </div>
                    </div>

                    <h3 className="text-4xl font-[1000] text-slate-900 tracking-tight mb-4 uppercase">Transmission Center</h3>
                    <p className="text-slate-400 font-bold uppercase tracking-[0.4em] text-xs max-w-sm text-center leading-relaxed">
                        Secure end-to-end communication matrix active. Select operative to initialize.
                    </p>

                    <div className="mt-12 flex gap-4">
                        <div className="flex items-center gap-2 px-4 py-2 bg-white/50 border border-slate-100 rounded-2xl text-[10px] font-black text-slate-400 uppercase tracking-widest"><Shield size={14} /> Encrypted</div>
                        <div className="flex items-center gap-2 px-4 py-2 bg-white/50 border border-slate-100 rounded-2xl text-[10px] font-black text-slate-400 uppercase tracking-widest"><Zap size={14} /> High Velocity</div>
                    </div>
                </div>
            )}
        </div>
    );
};

const MessageSquareIcon = ({ size, className }: { size: number, className?: string }) => (
    <svg
        className={className}
        xmlns="http://www.w3.org/2000/svg"
        width={size}
        height={size}
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        strokeWidth="2.5"
        strokeLinecap="round"
        strokeLinejoin="round"
    >
        <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z" />
    </svg>
);

export default MessagesPage;
