import React, { useState } from "react";
import { Eye, EyeOff, Shield, Zap, Activity, Star, ChevronRight, Mail, Lock, Loader2, ArrowUpRight, CheckCircle2 } from "lucide-react";
import { Link, useNavigate } from "react-router-dom";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const AuthPage: React.FC = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      await authService.login(email, password);
      navigate("/dashboard");
    } catch (error: any) {
      console.error("Login failed:", error);
      alert(error.response?.data?.detail || "Login failed. Please check your credentials.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#fcfdfe] flex flex-col items-center justify-center p-6 font-sans relative overflow-hidden">

      {/* Decorative background elements */}
      <div className="absolute top-0 left-0 w-full h-full pointer-events-none">
        <div className="absolute top-[-10%] right-[-10%] w-[40%] h-[40%] bg-indigo-50/50 rounded-full blur-[120px]" />
        <div className="absolute bottom-[-10%] left-[-10%] w-[40%] h-[40%] bg-blue-50/50 rounded-full blur-[120px]" />
      </div>

      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, ease: "easeOut" }}
        className="w-full max-w-[480px] relative z-10"
      >
        {/* Logo & Welcome */}
        <div className="flex flex-col items-center mb-10 text-center">
          <div className="w-16 h-16 bg-indigo-600 rounded-[22px] flex items-center justify-center mb-6 shadow-xl shadow-indigo-100 ring-8 ring-indigo-50">
            <Activity size={32} className="text-white" />
          </div>
          <h1 className="text-4xl font-bold tracking-tight text-slate-900 mb-2">Welcome Back</h1>
          <p className="text-slate-500 font-medium tracking-tight">Access your workspace and continue collaborating.</p>
        </div>

        {/* Login Form Card */}
        <div className="bg-white rounded-[40px] border border-slate-100 p-10 md:p-12 shadow-2xl shadow-slate-200/60 relative">
          <form onSubmit={handleLogin} className="space-y-8">
            <div className="space-y-2">
              <label className="text-xs font-bold text-slate-400 uppercase tracking-[0.2em] ml-1">Email Address</label>
              <div className="relative group">
                <Mail className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-300 group-focus-within:text-indigo-600 transition-colors" size={18} />
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="input-field pl-14"
                  placeholder="name@company.com"
                  required
                />
              </div>
            </div>

            <div className="space-y-2">
              <div className="flex items-center justify-between px-1">
                <label className="text-xs font-bold text-slate-400 uppercase tracking-[0.2em]">Password</label>
                <Link to="/forgot-password" className="text-[11px] font-bold text-indigo-500 hover:text-indigo-700 transition-all uppercase tracking-wider">Forgot Key?</Link>
              </div>
              <div className="relative group">
                <Lock className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-300 group-focus-within:text-indigo-600 transition-colors" size={18} />
                <input
                  type={showPassword ? "text" : "password"}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="input-field pl-14 pr-14"
                  placeholder="••••••••••••"
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-5 top-1/2 -translate-y-1/2 text-slate-300 hover:text-slate-600 transition-colors"
                >
                  {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                </button>
              </div>
            </div>

            <div className="flex items-center justify-between px-1">
              <label className="flex items-center gap-3 cursor-pointer group">
                <div className="relative">
                  <input
                    type="checkbox"
                    className="peer hidden"
                  />
                  <div className="w-5 h-5 border-2 border-slate-200 rounded-lg group-hover:border-indigo-400 peer-checked:bg-indigo-600 peer-checked:border-indigo-600 transition-all" />
                  <CheckCircle2 className="absolute top-0 left-0 w-5 h-5 text-white scale-0 peer-checked:scale-75 transition-transform" />
                </div>
                <span className="text-xs font-bold text-slate-500 group-hover:text-slate-700 transition-colors uppercase tracking-wider">Remember Me</span>
              </label>
            </div>

            <button
              disabled={isLoading}
              type="submit"
              className="w-full btn-primary h-14 text-sm shadow-xl shadow-indigo-100 group"
            >
              {isLoading ? (
                <Loader2 className="animate-spin" size={20} />
              ) : (
                <>
                  Establish Access
                  <ArrowUpRight size={20} className="group-hover:translate-x-0.5 group-hover:-translate-y-0.5 transition-transform" />
                </>
              )}
            </button>
          </form>

          <div className="mt-12 pt-8 border-t border-slate-50 text-center">
            <p className="text-xs font-bold text-slate-400 uppercase tracking-widest">
              New operative?{" "}
              <Link to="/register" className="text-indigo-600 hover:text-indigo-800 transition-colors ml-2 font-bold decoration-2 underline-offset-4">
                Join Network
              </Link>
            </p>
          </div>
        </div>

        <div className="mt-10 flex items-center justify-center gap-6">
          <div className="flex items-center gap-2 px-4 py-2 bg-white rounded-full border border-slate-100 shadow-sm">
            <Shield size={14} className="text-emerald-500" />
            <span className="text-[10px] font-bold text-slate-500 uppercase tracking-widest">End-to-End Secure</span>
          </div>
          <div className="w-1.5 h-1.5 bg-slate-200 rounded-full" />
          <div className="flex items-center gap-2 px-4 py-2 bg-white rounded-full border border-slate-100 shadow-sm">
            <Activity size={14} className="text-indigo-500" />
            <span className="text-[10px] font-bold text-slate-500 uppercase tracking-widest">Node: Delta-01</span>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default AuthPage;
