import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  Workflow,
  ClipboardList,
  LogOut,
  UsersRound,
  ChevronRight,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";

const Sidebar: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard", badge: null },
    { label: "Chat", icon: MessageSquare, path: "/chat", badge: 3 },
    { label: "Tasks", icon: ClipboardList, path: "/tasks", badge: 5 },
    { label: "Meetings", icon: UsersRound, path: "/meetings", badge: null },
    { label: "Calendar", icon: CalendarDays, path: "/calendar", badge: null },
    { label: "Files", icon: FileUp, path: "/files", badge: null },
    { label: "Workflows", icon: Workflow, path: "/workflows", badge: null },
  ];

  return (
    <aside className="h-screen w-64 flex flex-col bg-gradient-to-b from-white via-slate-50/50 to-white border-r border-slate-200">
      {/* Logo Section with Card Style */}
      <div
        className="m-4 mb-6 p-4 cursor-pointer group rounded-2xl bg-white border border-slate-200 shadow-sm hover:shadow-md transition-all duration-300"
        onClick={() => navigate("/dashboard")}
      >
        <div className="flex items-center gap-3">
          <div className="relative">
            {/* Animated gradient ring */}
            <div className="absolute inset-0 rounded-xl bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 opacity-20 group-hover:opacity-30 blur-sm transition-opacity duration-300" />
            <div className="relative w-12 h-12 rounded-xl bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center shadow-lg">
              <img
                src="/logo1.png"
                alt="Collabryta Logo"
                className="w-8 h-8 object-contain brightness-0 invert"
              />
            </div>
          </div>
          <div className="flex-1">
            <h1 className="text-lg font-black text-slate-800 group-hover:text-blue-600 transition-colors duration-300">
              Collabryta
            </h1>
            <p className="text-xs text-slate-500 font-medium">Workspace</p>
          </div>
        </div>
      </div>

      {/* Navigation Section */}
      <div className="flex-1 overflow-y-auto px-4 space-y-1.5 scrollbar-thin scrollbar-thumb-slate-300 scrollbar-track-transparent">
        {/* Section Label */}
        <div className="px-3 py-2">
          <p className="text-xs font-bold text-slate-400 uppercase tracking-wider">Main Menu</p>
        </div>

        {navItems.map(({ label, icon: Icon, path, badge }) => {
          const active = location.pathname === path;
          return (
            <button
              key={path}
              onClick={() => navigate(path)}
              className={`
                relative w-full flex items-center gap-3 px-3 py-2.5 rounded-xl
                font-medium text-sm transition-all duration-300 group/item
                ${active
                  ? "bg-white text-blue-600 shadow-md border border-blue-100"
                  : "text-slate-600 hover:bg-white hover:text-slate-900 hover:shadow-sm"
                }
              `}
            >
              {/* Icon with background */}
              <div className={`
                w-9 h-9 rounded-lg flex items-center justify-center transition-all duration-300
                ${active
                  ? "bg-gradient-to-br from-blue-500 to-purple-600 text-white shadow-sm"
                  : "bg-slate-100 text-slate-600 group-hover/item:bg-slate-200"
                }
              `}>
                <Icon size={18} />
              </div>

              {/* Label */}
              <span className="flex-1 text-left">{label}</span>

              {/* Badge or Arrow */}
              {badge && badge > 0 ? (
                <span className="px-2 py-0.5 text-xs font-bold rounded-full bg-gradient-to-r from-red-500 to-pink-500 text-white min-w-[20px] text-center">
                  {badge}
                </span>
              ) : (
                <ChevronRight
                  size={16}
                  className={`transition-all duration-300 ${active
                    ? "opacity-100 translate-x-0"
                    : "opacity-0 -translate-x-2 group-hover/item:opacity-50 group-hover/item:translate-x-0"
                    }`}
                />
              )}
            </button>
          );
        })}
      </div>

      {/* User Profile Card */}
      <div className="m-4 mt-auto p-3 rounded-xl bg-gradient-to-br from-slate-100 to-slate-50 border border-slate-200">
        <div className="flex items-center gap-3 mb-3">
          <div className="relative">
            <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center text-white font-bold shadow-md">
              A
            </div>
            <span className="absolute bottom-0 right-0 w-3 h-3 bg-green-500 border-2 border-white rounded-full"></span>
          </div>
          <div className="flex-1 min-w-0">
            <p className="text-sm font-semibold text-slate-800 truncate">Ashok Kumar</p>
            <p className="text-xs text-slate-500">Admin</p>
          </div>
        </div>

        {/* Logout Button */}
        <button
          onClick={() => navigate("/login")}
          className="
            w-full flex items-center justify-center gap-2 px-3 py-2 rounded-lg
            font-medium text-sm transition-all duration-300
            bg-white text-slate-700 hover:bg-slate-50 border border-slate-200
            hover:border-red-200 hover:text-red-600
            group
          "
        >
          <LogOut size={16} className="transition-transform duration-300 group-hover:-translate-x-0.5" />
          <span>Logout</span>
        </button>
      </div>
    </aside>
  );
};

export default Sidebar;
