import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import { notificationService, Notification } from '../services/notificationService';
import { authService } from '../services/authService';
import { Bell, CheckCircle, Info, AlertTriangle, AlertCircle, X } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

/**
 * Context interface for managing global application notifications.
 */
interface NotificationContextType {
    notifications: Notification[];
    unreadCount: number;
    addNotification: (notification: Notification) => void;
    markAsRead: (id: number) => Promise<void>;
    markAllAsRead: () => Promise<void>;
    refresh: () => Promise<void>;
    showToast: (title: string, message: string, type?: 'info' | 'success' | 'warning' | 'error') => void;
}

const NotificationContext = createContext<NotificationContextType | undefined>(undefined);

import { webSocketService, WebSocketMessage } from '../services/websocketService';

export const NotificationProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [notifications, setNotifications] = useState<Notification[]>([]);
    const [unreadCount, setUnreadCount] = useState(0);
    const [toast, setToast] = useState<{ title: string; message: string; type: string } | null>(null);

    const showToast = useCallback((title: string, message: string, type: 'info' | 'success' | 'warning' | 'error' = 'info') => {
        setToast({ title, message, type });
        setTimeout(() => setToast(null), 5000);
    }, []);

    const fetchNotifications = useCallback(async () => {
        if (!authService.isAuthenticated()) return;

        try {
            const data = await notificationService.getNotifications(0, 50);
            setNotifications(data);
            setUnreadCount(data.filter(n => !n.is_read).length);
        } catch (err) {
            console.error('Error fetching notifications:', err);
        }
    }, []);

    const addNotification = useCallback((notification: Notification) => {
        setNotifications(prev => {
            // Check if notification already exists to avoid duplicates
            if (prev.some(n => n.id === notification.id)) return prev;

            // Show toast for new live notifications
            showToast(notification.title, notification.description, notification.type);

            return [notification, ...prev];
        });
        setUnreadCount(prev => prev + 1);
    }, [showToast]);

    const handleWebSocketMessage = useCallback((message: WebSocketMessage) => {
        console.log('Received WebSocket message:', message);

        switch (message.type) {
            case 'notification':
                addNotification(message.data as Notification);
                break;
            case 'message':
                showToast('New Message', message.data.content, 'info');
                break;
            case 'task':
                // The backend might already send a notification for task creation/update
                // If not, we could show a toast here. 
                // Based on my backend changes, I send BOTH.
                // So I'll let the 'notification' case handle the toast to avoid double toast.
                break;
            case 'meeting':
            case 'file':
                break;
            default:
                break;
        }
    }, [addNotification, showToast]);

    const markAsRead = async (id: number) => {
        try {
            await notificationService.markAsRead(id);
            setNotifications(prev => prev.map(n => n.id === id ? { ...n, is_read: true } : n));
            setUnreadCount(prev => Math.max(0, prev - 1));
        } catch (err) {
            console.error('Error marking notification as read:', err);
        }
    };

    const markAllAsRead = async () => {
        try {
            await notificationService.markAllAsRead();
            setNotifications(prev => prev.map(n => ({ ...n, is_read: true })));
            setUnreadCount(0);
        } catch (err) {
            console.error('Error marking all notifications as read:', err);
        }
    };

    useEffect(() => {
        if (authService.isAuthenticated()) {
            fetchNotifications();

            const token = localStorage.getItem('token');
            if (token) {
                webSocketService.connect(token);
                const unsubscribe = webSocketService.subscribe(handleWebSocketMessage);
                return () => {
                    unsubscribe();
                    // We don't necessarily want to disconnect here if the component unmounts/remounts
                    // but since NotificationProvider is usually top-level, it's fine.
                };
            }
        } else {
            webSocketService.disconnect();
        }
    }, [fetchNotifications, handleWebSocketMessage]);

    const getIconForToastType = (type: string) => {
        switch (type) {
            case 'success': return <CheckCircle className="h-5 w-5 text-green-500" />;
            case 'info': return <Info className="h-5 w-5 text-blue-500" />;
            case 'warning': return <AlertTriangle className="h-5 w-5 text-yellow-500" />;
            case 'error': return <AlertCircle className="h-5 w-5 text-red-500" />;
            default: return <Bell className="h-5 w-5 text-gray-500" />;
        }
    };

    const getBorderColorForToastType = (type: string) => {
        switch (type) {
            case 'success': return 'border-green-500';
            case 'info': return 'border-blue-500';
            case 'warning': return 'border-yellow-500';
            case 'error': return 'border-red-500';
            default: return 'border-gray-300';
        }
    };

    return (
        <NotificationContext.Provider value={{
            notifications,
            unreadCount,
            addNotification,
            markAsRead,
            markAllAsRead,
            refresh: fetchNotifications,
            showToast
        }}>
            {children}
            <AnimatePresence>
                {toast && (
                    <motion.div
                        initial={{ opacity: 0, x: 50, scale: 0.9 }}
                        animate={{ opacity: 1, x: 0, scale: 1 }}
                        exit={{ opacity: 0, x: 50, scale: 0.9 }}
                        className={`fixed bottom-5 right-5 z-[9999] min-w-[320px] max-w-md bg-white rounded-2xl shadow-2xl border-l-4 ${getBorderColorForToastType(toast.type)} p-4 flex gap-4`}
                    >
                        <div className="flex-shrink-0 mt-0.5">
                            {getIconForToastType(toast.type)}
                        </div>
                        <div className="flex-1">
                            <h4 className="text-sm font-bold text-gray-900">{toast.title}</h4>
                            <p className="text-xs text-gray-500 mt-1 line-clamp-2">{toast.message}</p>
                        </div>
                        <button
                            onClick={() => setToast(null)}
                            className="flex-shrink-0 self-start p-1 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors"
                        >
                            <X size={16} />
                        </button>
                    </motion.div>
                )}
            </AnimatePresence>
        </NotificationContext.Provider>
    );
};

export const useNotifications = () => {
    const context = useContext(NotificationContext);
    if (context === undefined) {
        throw new Error('useNotifications must be used within a NotificationProvider');
    }
    return context;
};
