import React, { useState, useEffect, useRef, useCallback } from "react";
import {
    Search, Users, Paperclip, Smile, Send,
    Check, ArrowLeft, X, Sparkles, Zap, Shield, Plus, ArrowUpRight, ChevronRight, MessageSquare as MessageIcon
} from "lucide-react";
import { messageService, Chat, Message } from "../../services/messageService";
import { userService, User } from "../../services/userService";
import { authService } from "../../services/authService";

import { motion, AnimatePresence } from "framer-motion";

const CreateGroupModal = ({ isOpen, onClose, users, onCreate }: any) => {
    const [groupName, setGroupName] = useState("");
    const [selectedIds, setSelectedIds] = useState<number[]>([]);

    const toggleUser = (id: number) => {
        setSelectedIds(prev => prev.includes(id) ? prev.filter(x => x !== id) : [...prev, id]);
    };

    const handleSubmit = () => {
        if (!groupName || selectedIds.length === 0) return;
        onCreate(groupName, selectedIds);
        onClose();
        setGroupName("");
        setSelectedIds([]);
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <div className="fixed inset-0 z-[100] flex items-center justify-center p-4">
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="absolute inset-0 bg-slate-900/10 backdrop-blur-sm"
                        onClick={onClose}
                    />
                    <motion.div
                        initial={{ opacity: 0, scale: 0.95, y: 10 }}
                        animate={{ opacity: 1, scale: 1, y: 0 }}
                        exit={{ opacity: 0, scale: 0.95, y: 10 }}
                        className="bg-white rounded-[32px] border border-slate-100 w-full max-w-md p-8 relative z-10 shadow-2xl"
                    >
                        <div className="flex justify-between items-center mb-8">
                            <div>
                                <h3 className="text-2xl font-bold tracking-tight text-slate-900">Create Group</h3>
                                <p className="text-sm text-slate-500 font-medium">Start a new team conversation</p>
                            </div>
                            <button
                                onClick={onClose}
                                className="w-10 h-10 flex items-center justify-center bg-slate-50 text-slate-400 hover:bg-slate-100 hover:text-slate-900 rounded-xl transition-all"
                            >
                                <X size={20} />
                            </button>
                        </div>

                        <div className="space-y-6">
                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Group Name</label>
                                <input
                                    className="input-field"
                                    value={groupName}
                                    onChange={e => setGroupName(e.target.value)}
                                    placeholder="Enter group name..."
                                />
                            </div>

                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Select Members</label>
                                <div className="max-h-[250px] overflow-y-auto space-y-1 pr-2 custom-scrollbar">
                                    {users.map((u: User) => (
                                        <button
                                            key={u.id}
                                            onClick={() => toggleUser(u.id)}
                                            className={`w-full flex items-center gap-4 p-3 rounded-2xl transition-all ${selectedIds.includes(u.id) ? 'bg-indigo-50 border border-indigo-100' : 'bg-white border border-transparent hover:bg-slate-50'
                                                }`}
                                        >
                                            <div className={`w-10 h-10 rounded-xl flex items-center justify-center text-xs font-bold transition-all shadow-sm ${selectedIds.includes(u.id) ? 'bg-indigo-600 text-white' : 'bg-slate-100 text-slate-500'
                                                }`}>
                                                {u.name.substring(0, 2).toUpperCase()}
                                            </div>
                                            <span className={`text-sm font-semibold ${selectedIds.includes(u.id) ? 'text-indigo-600' : 'text-slate-700'}`}>{u.name}</span>
                                            {selectedIds.includes(u.id) && (
                                                <div className="ml-auto p-1 bg-indigo-600 rounded-lg text-white">
                                                    <Check size={14} />
                                                </div>
                                            )}
                                        </button>
                                    ))}
                                </div>
                            </div>

                            <button
                                onClick={handleSubmit}
                                disabled={!groupName || selectedIds.length === 0}
                                className="btn-primary w-full shadow-lg shadow-indigo-100 disabled:opacity-50 disabled:shadow-none mt-4"
                            >
                                <Plus size={18} />
                                Create Group
                            </button>
                        </div>
                    </motion.div>
                </div>
            )}
        </AnimatePresence>
    );
};

const MessagesPage: React.FC = () => {
    const [chats, setChats] = useState<Chat[]>([]);
    const [messages, setMessages] = useState<Message[]>([]);
    const [currentUser, setCurrentUser] = useState<any>(null);
    const [activeChatId, setActiveChatId] = useState<number | null>(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [allUsers, setAllUsers] = useState<User[]>([]);
    const [inputText, setInputText] = useState("");
    const [isMobileChatOpen, setIsMobileChatOpen] = useState(false);
    const [isGroupModalOpen, setIsGroupModalOpen] = useState(false);

    const messagesContainerRef = useRef<HTMLDivElement>(null);

    const loadChats = useCallback(async () => {
        try {
            const data = await messageService.getChats();
            setChats(data);
        } catch (e) {
            console.error("Failed to load chats:", e);
        }
    }, []);

    const loadUserAndData = useCallback(async () => {
        try {
            const user = await authService.getCurrentUser();
            setCurrentUser(user);
            await loadChats();
            const users = await userService.getAllUsers();
            setAllUsers(users.filter(u => u.id !== user.id));
        } catch (e) {
            console.error("Failed to load user data:", e);
        }
    }, [loadChats]);

    const loadMessages = useCallback(async (chatId: number) => {
        try {
            const msgs = await messageService.getMessages(chatId);
            setMessages(msgs);
            await messageService.markAsRead(chatId);
            loadChats();
        } catch (e) {
            console.error("Failed to load messages:", e);
        }
    }, [loadChats]);

    const scrollToBottom = useCallback((smooth = true) => {
        if (messagesContainerRef.current) {
            const { scrollHeight, clientHeight } = messagesContainerRef.current;
            messagesContainerRef.current.scrollTo({
                top: scrollHeight - clientHeight,
                behavior: smooth ? 'smooth' : 'auto'
            });
        }
    }, []);

    useEffect(() => {
        loadUserAndData();
    }, [loadUserAndData]);

    useEffect(() => {
        const pollInterval = setInterval(() => {
            loadChats();
            if (activeChatId) {
                loadMessages(activeChatId);
            }
        }, 10000);

        return () => {
            clearInterval(pollInterval);
        };
    }, [activeChatId, loadChats, loadMessages]);

    useEffect(() => {
        if (activeChatId) {
            loadMessages(activeChatId);
            setTimeout(() => scrollToBottom(false), 100);
        }
    }, [activeChatId, loadMessages, scrollToBottom]);

    useEffect(() => {
        scrollToBottom();
    }, [messages, scrollToBottom]);

    const handleSendMessage = async () => {
        if (!inputText.trim() || !activeChatId) return;
        try {
            await messageService.sendMessage(activeChatId, inputText);
            setInputText("");
            loadMessages(activeChatId);
            loadChats();
            setTimeout(() => scrollToBottom(), 100);
        } catch (e) {
            console.error("Failed to send message:", e);
        }
    };

    const handleCreateP2PChat = async (userId: number) => {
        try {
            const chat = await messageService.createChat([userId], undefined, false);
            setChats(prev => prev.find(c => c.id === chat.id) ? prev : [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
            setSearchQuery("");
        } catch (e) {
            console.error("Failed to create P2P chat:", e);
        }
    };

    const handleCreateGroup = async (name: string, userIds: number[]) => {
        try {
            const chat = await messageService.createChat(userIds, name, true);
            setChats(prev => [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
        } catch (e) {
            console.error("Failed to create group chat:", e);
        }
    };

    const filteredChats = chats.filter(c => c.name?.toLowerCase().includes(searchQuery.toLowerCase()));
    const suggestedUsers = searchQuery ? allUsers.filter(u => u.name.toLowerCase().includes(searchQuery.toLowerCase())) : [];
    const activeChat = chats.find(c => c.id === activeChatId);

    return (
        <div className="flex h-screen bg-[#fcfdfe] overflow-hidden animate-fade-in font-sans relative">
            {/* Background Decor */}
            <div className="fixed top-0 left-0 w-full h-[300px] bg-gradient-to-b from-indigo-50/50 to-transparent pointer-events-none" />

            <CreateGroupModal
                isOpen={isGroupModalOpen}
                onClose={() => setIsGroupModalOpen(false)}
                users={allUsers}
                onCreate={handleCreateGroup}
            />

            {/* Sidebar (Chat List) */}
            <div className={`w-full md:w-[380px] lg:w-[440px] bg-white/60 backdrop-blur-xl border-r border-slate-200/60 flex flex-col z-20 ${isMobileChatOpen ? 'hidden md:flex' : 'flex'}`}>
                <div className="p-8 pb-6 flex items-center justify-between">
                    <div>
                        <h1 className="text-4xl font-black tracking-tight text-slate-900 leading-none mb-2">Messages</h1>
                        <p className="text-xs font-bold text-slate-400 uppercase tracking-widest flex items-center gap-2">
                            <span className="w-2 h-2 rounded-full bg-emerald-500 animate-pulse shadow-[0_0_10px_rgba(16,185,129,0.5)]" />
                            Online Status
                        </p>
                    </div>
                    <button
                        onClick={() => setIsGroupModalOpen(true)}
                        className="w-12 h-12 flex items-center justify-center bg-gradient-to-tr from-indigo-600 to-violet-600 text-white shadow-xl shadow-indigo-200 hover:scale-110 active:scale-95 rounded-2xl transition-all duration-300 group"
                        title="New Group"
                    >
                        <Plus size={24} strokeWidth={2.5} className="group-hover:rotate-90 transition-transform duration-300" />
                    </button>
                </div>

                <div className="px-8 pb-4">
                    <div className="relative group bg-white border border-slate-200 rounded-[24px] shadow-sm transition-all focus-within:shadow-md focus-within:border-indigo-200 focus-within:ring-4 focus-within:ring-indigo-100">
                        <Search className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={20} />
                        <input
                            className="w-full bg-transparent py-4 pl-14 pr-4 text-sm font-bold text-slate-900 focus:outline-none placeholder:text-slate-400"
                            placeholder="Search conversations..."
                            value={searchQuery}
                            onChange={e => setSearchQuery(e.target.value)}
                        />
                    </div>
                </div>

                <div className="flex-1 overflow-y-auto custom-scrollbar p-6 pt-2 space-y-3">
                    {filteredChats.map((chat, idx) => (
                        <motion.button
                            initial={{ opacity: 0, x: -20 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ delay: idx * 0.05 }}
                            key={chat.id}
                            onClick={() => { setActiveChatId(chat.id); setIsMobileChatOpen(true); }}
                            className={`w-full p-4 flex items-center gap-4 rounded-[28px] transition-all duration-300 border group ${activeChatId === chat.id
                                ? 'bg-gradient-to-r from-indigo-600 to-violet-600 shadow-xl shadow-indigo-200 border-transparent'
                                : 'bg-transparent border-transparent hover:bg-white hover:border-slate-100 hover:shadow-md'
                                }`}
                        >
                            <div className={`w-14 h-14 rounded-[20px] flex items-center justify-center font-black text-sm shrink-0 transition-all ${activeChatId === chat.id
                                ? 'bg-white/20 text-white backdrop-blur-sm shadow-inner overlay-hard'
                                : 'bg-white shadow-sm text-slate-400 border border-slate-100 group-hover:scale-110 group-hover:shadow-md group-hover:border-indigo-100 group-hover:text-indigo-600'
                                }`}>
                                {chat.is_group ? <Users size={22} className={activeChatId === chat.id ? "text-white" : "text-slate-300 group-hover:text-indigo-500"} /> : (chat.name?.substring(0, 2).toUpperCase())}
                            </div>

                            <div className="flex-1 min-w-0 text-left">
                                <div className="flex justify-between items-center mb-1">
                                    <h3 className={`text-base font-black truncate leading-tight ${activeChatId === chat.id ? 'text-white' : 'text-slate-900 group-hover:text-indigo-900'}`}>{chat.name}</h3>
                                    <span className={`text-[10px] font-bold shrink-0 ml-2 ${activeChatId === chat.id ? 'text-indigo-200' : 'text-slate-400'}`}>
                                        {chat.last_message_time ? new Date(chat.last_message_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : ''}
                                    </span>
                                </div>
                                <div className="flex justify-between items-center">
                                    <p className={`text-xs font-semibold truncate mr-4 ${activeChatId === chat.id ? 'text-white/80' : 'text-slate-500 group-hover:text-slate-600'}`}>{chat.last_message || "Start messaging..."}</p>
                                    {chat.unread_count && chat.unread_count > 0 && (
                                        <div className={`min-w-[20px] h-[20px] px-1.5 rounded-full flex items-center justify-center text-[10px] font-black shadow-lg ${activeChatId === chat.id
                                            ? 'bg-white text-indigo-600 shadow-black/10'
                                            : 'bg-rose-500 text-white shadow-rose-200'}`}>
                                            {chat.unread_count}
                                        </div>
                                    )}
                                </div>
                            </div>
                        </motion.button>
                    ))}

                    {suggestedUsers.length > 0 && (
                        <div className="mt-8 pt-6 border-t border-slate-100">
                            <h4 className="px-2 mb-4 text-[10px] font-black text-slate-300 uppercase tracking-[0.2em]">Suggestions</h4>
                            {suggestedUsers.map(user => (
                                <button
                                    key={user.id}
                                    onClick={() => handleCreateP2PChat(user.id)}
                                    className="w-full p-3 rounded-[24px] hover:bg-indigo-50/50 transition-all flex items-center justify-between group px-5 border border-transparent hover:border-indigo-100"
                                >
                                    <div className="flex items-center gap-4">
                                        <div className="w-10 h-10 bg-white rounded-[14px] flex items-center justify-center text-slate-400 font-bold text-xs group-hover:bg-indigo-600 group-hover:text-white transition-all shadow-sm border border-slate-100 group-hover:shadow-indigo-200">
                                            {user.name.substring(0, 2).toUpperCase()}
                                        </div>
                                        <span className="text-sm font-bold text-slate-700 group-hover:text-indigo-900">{user.name}</span>
                                    </div>
                                    <div className="w-8 h-8 rounded-full bg-slate-50 flex items-center justify-center group-hover:bg-indigo-100 transition-colors text-slate-300 group-hover:text-indigo-600">
                                        <Plus size={16} strokeWidth={3} />
                                    </div>
                                </button>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Chat Area */}
            {activeChat ? (
                <div className={`flex-1 flex flex-col relative ${isMobileChatOpen ? 'flex absolute inset-0 z-30 bg-white' : 'hidden md:flex'}`}>
                    {/* Mobile Back Button Background when open */}
                    {isMobileChatOpen && <div className="absolute top-0 left-0 w-full h-[300px] bg-gradient-to-b from-indigo-50/50 to-transparent pointer-events-none" />}

                    {/* Chat Header */}
                    <div className="h-28 flex items-center justify-between px-8 shrink-0 relative z-20 m-4 mb-0 bg-white/40 backdrop-blur-md rounded-[32px] border border-white/60 shadow-sm">
                        <div className="flex items-center gap-5">
                            <button onClick={() => setIsMobileChatOpen(false)} className="md:hidden p-3 -ml-2 text-slate-400 hover:text-slate-900 transition-all bg-white rounded-xl shadow-sm border border-slate-100">
                                <ArrowLeft size={20} />
                            </button>
                            <div className="relative">
                                <div className={`w-14 h-14 rounded-[22px] flex items-center justify-center font-black text-lg shrink-0 shadow-lg shadow-indigo-200/50 ${activeChat.is_group
                                    ? 'bg-gradient-to-tr from-slate-800 to-slate-900 text-white'
                                    : 'bg-gradient-to-tr from-indigo-500 to-violet-600 text-white'
                                    }`}>
                                    {activeChat.is_group ? <Users size={24} /> : activeChat.name?.substring(0, 2).toUpperCase()}
                                </div>
                                <div className="absolute -bottom-1 -right-1 w-5 h-5 bg-emerald-500 border-[4px] border-white rounded-full shadow-sm" />
                            </div>
                            <div>
                                <h2 className="text-2xl font-black text-slate-900 leading-tight tracking-tight">{activeChat.name}</h2>
                                <p className="text-[11px] font-bold text-indigo-500 uppercase tracking-widest flex items-center gap-1.5 mt-1">
                                    <Zap size={14} className="fill-indigo-500" />
                                    Active Now
                                </p>
                            </div>
                        </div>
                        <div className="flex items-center gap-3">
                            <div className="hidden sm:flex items-center gap-2 px-4 py-2 bg-slate-100 text-slate-600 rounded-xl text-[10px] font-black uppercase tracking-wider border border-slate-200">
                                <Shield size={12} strokeWidth={2.5} /> E2E Encrypted
                            </div>
                        </div>
                    </div>

                    {/* Chat Messages */}
                    <div className="flex-1 relative flex flex-col overflow-hidden z-10 before:absolute before:inset-0 before:bg-[radial-gradient(#e0e7ff_1px,transparent_1px)] before:[background-size:20px_20px] before:opacity-30">
                        <div ref={messagesContainerRef} className="flex-1 overflow-y-auto p-4 md:p-8 space-y-8 custom-scrollbar relative z-10 scroll-smooth">
                            <AnimatePresence mode="popLayout">
                                {messages.map((msg, idx) => {
                                    const isMe = msg.sender_id === currentUser?.id;
                                    const showSender = !isMe && activeChat.is_group && (idx === 0 || messages[idx - 1].sender_id !== msg.sender_id);

                                    return (
                                        <motion.div
                                            initial={{ opacity: 0, y: 20, scale: 0.95 }}
                                            animate={{ opacity: 1, y: 0, scale: 1 }}
                                            key={msg.id}
                                            className={`flex w-full group ${isMe ? 'justify-end' : 'justify-start'}`}
                                        >
                                            <div className={`flex flex-col max-w-[85%] md:max-w-[70%] lg:max-w-[60%] ${isMe ? 'items-end' : 'items-start'}`}>
                                                {showSender && <span className="text-[10px] font-black text-slate-400 mb-2 px-2 uppercase tracking-widest flex items-center gap-2">
                                                    <div className="w-1 h-1 rounded-full bg-slate-300" />
                                                    {msg.sender?.name}
                                                </span>}
                                                <div className={`p-6 rounded-[28px] text-[15px] font-medium leading-relaxed shadow-sm transition-all hover:shadow-lg hover:scale-[1.01] ${isMe
                                                    ? 'bg-gradient-to-br from-indigo-600 to-violet-700 text-white rounded-tr-none shadow-indigo-200'
                                                    : 'bg-white text-slate-700 border border-slate-100 rounded-tl-none shadow-slate-100'
                                                    }`}>
                                                    <p>{msg.content}</p>
                                                </div>
                                                <div className={`flex items-center gap-2 mt-2 px-2 opacity-0 group-hover:opacity-100 transition-opacity duration-300 ${isMe ? 'flex-row-reverse' : ''}`}>
                                                    <span className="text-[10px] font-bold text-slate-300">
                                                        {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                                    </span>
                                                    {isMe && <Check size={14} className="text-indigo-400" strokeWidth={3} />}
                                                </div>
                                            </div>
                                        </motion.div>
                                    );
                                })}
                            </AnimatePresence>
                        </div>
                    </div>

                    {/* Chat Input */}
                    <div className="p-6 relative z-20">
                        <div className="bg-white/80 backdrop-blur-xl p-3 rounded-[36px] flex items-center gap-2 max-w-5xl mx-auto shadow-2xl shadow-slate-200/50 border border-white/60 ring-1 ring-slate-100">
                            <button className="w-12 h-12 flex items-center justify-center text-slate-400 hover:text-indigo-600 hover:bg-indigo-50 rounded-full transition-all group">
                                <Paperclip size={20} strokeWidth={2.5} className="group-hover:rotate-45 transition-transform" />
                            </button>
                            <div className="flex-1 relative group">
                                <input
                                    className="w-full py-4 px-4 bg-transparent text-sm font-bold text-slate-900 placeholder:text-slate-400 focus:outline-none"
                                    placeholder="Type your message..."
                                    value={inputText}
                                    onChange={e => setInputText(e.target.value)}
                                    onKeyDown={e => e.key === 'Enter' && handleSendMessage()}
                                />
                            </div>
                            <button className="w-10 h-10 flex items-center justify-center text-slate-400 hover:text-amber-500 transition-colors">
                                <Smile size={22} strokeWidth={2.5} />
                            </button>
                            <button
                                onClick={handleSendMessage}
                                disabled={!inputText.trim()}
                                className="w-14 h-14 flex items-center justify-center bg-gradient-to-tr from-indigo-600 to-violet-600 text-white rounded-[24px] hover:shadow-lg hover:shadow-indigo-300 transition-all disabled:opacity-50 disabled:shadow-none hover:rotate-[-10deg] active:scale-95 active:rotate-0"
                            >
                                <Send size={22} className="ml-1" fill="currentColor" />
                            </button>
                        </div>
                    </div>
                </div>
            ) : (
                <div className="hidden md:flex flex-1 flex-col items-center justify-center p-10 relative overflow-hidden bg-slate-50/50">
                    <div className="absolute inset-0 z-0 opacity-50 pointer-events-none" style={{ backgroundImage: 'radial-gradient(circle at 2px 2px, rgba(99, 102, 241, 0.05) 1px, transparent 0)', backgroundSize: '40px 40px' }}></div>

                    <div className="absolute top-[20%] right-[20%] w-[500px] h-[500px] bg-indigo-200/20 rounded-full blur-[120px] animate-pulse" />
                    <div className="absolute bottom-[20%] left-[20%] w-[400px] h-[400px] bg-violet-200/20 rounded-full blur-[100px]" />

                    <div className="bg-white/60 backdrop-blur-xl p-16 rounded-[48px] text-center max-w-md relative z-10 shadow-2xl shadow-indigo-900/5 border border-white">
                        <div className="w-28 h-28 bg-gradient-to-tr from-indigo-50 to-white rounded-[32px] border border-white shadow-xl flex items-center justify-center mb-8 mx-auto transform -rotate-6 hover:rotate-0 transition-all duration-500">
                            <MessageIcon size={48} className="text-indigo-500 drop-shadow-lg" />
                        </div>
                        <h3 className="text-4xl font-black text-slate-900 tracking-tight mb-4">Hello, {currentUser?.name?.split(' ')[0]}!</h3>
                        <p className="text-slate-500 font-medium leading-relaxed text-lg">
                            Ready to collaborate? Select a conversation from the sidebar or start a new encrypted channel.
                        </p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MessagesPage;
