import React from "react";
import { useNavigate } from "react-router-dom";
import { Bell, Menu, X } from "lucide-react";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();

  return (
    <div className="flex items-center justify-between px-4 sm:px-6 py-4 bg-white text-gray-900">
      {/* Left - Mobile Menu + Welcome */}
      <div className="flex items-center gap-4">
        {/* Mobile Menu Button */}
        <button
          onClick={onMenuClick}
          className="lg:hidden p-2 rounded-lg hover:bg-gray-100 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500"
          aria-label={isSidebarOpen ? "Close menu" : "Open menu"}
          aria-expanded={isSidebarOpen}
        >
          {isSidebarOpen ? (
            <X size={24} className="text-gray-700" />
          ) : (
            <Menu size={24} className="text-gray-700" />
          )}
        </button>

        {/* Welcome Message */}
        <div>
          <h1 className="text-base sm:text-lg font-semibold">
            Welcome back, <span className="text-blue-600">Ashok ðŸ‘‹</span>
          </h1>
          <p className="hidden sm:block text-sm text-gray-500">
            Let's collaborate and make progress today!
          </p>
        </div>
      </div>

      {/* Right - Actions */}
      <div className="flex items-center gap-2 sm:gap-4">
        {/* Notifications */}
        <button
          className="relative p-2 rounded-full hover:bg-gray-100 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500"
          aria-label="Notifications"
        >
          <Bell size={18} className="text-gray-700" />
          <span
            className="absolute top-1 right-1 w-2 h-2 bg-red-500 rounded-full"
            aria-label="Unread notifications"
          ></span>
        </button>

        {/* Avatar */}
        <button
          onClick={() => navigate("/profile")}
          className="w-9 h-9 flex items-center justify-center rounded-full font-semibold cursor-pointer bg-blue-100 text-blue-700 hover:bg-blue-200 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500"
          aria-label="User profile"
        >
          A
        </button>
      </div>
    </div>
  );
};

export default Topbar;
