import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Calendar,
    User,
    Save,
    Loader2,
    AlignLeft,
    Clock,
    Activity
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";
import { motion } from "framer-motion";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal", // Hidden but kept for data integrity
        assigned_to: "",
        due_date: new Date().toISOString().split('T')[0]
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : taskData.status,
                        priority: taskData.priority,
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : new Date().toISOString().split('T')[0]
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, navigate]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: formData.status as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (error) {
            console.error(error);
            setSubmitting(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-[#F3F4F6] flex items-center justify-center">
                <Loader2 className="w-10 h-10 text-slate-400 animate-spin" />
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-[#F3F4F6] p-4 md:p-10 flex items-center justify-center">
            <motion.form
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className="w-full max-w-6xl"
                onSubmit={handleSubmit}
            >
                {/* üì¶ Layout Grid: Distinct & Separated Boxes */}
                <div className="grid grid-cols-1 md:grid-cols-12 gap-6">

                    {/* üîô Navigation Box */}
                    <div className="md:col-span-1">
                        <button
                            type="button"
                            onClick={() => navigate(-1)}
                            className="w-12 h-12 bg-white rounded-2xl flex items-center justify-center text-slate-400 hover:text-slate-800 hover:shadow-md transition-all border border-slate-200/50"
                        >
                            <ChevronLeft size={24} />
                        </button>
                    </div>

                    {/* üè∑Ô∏è Title Box (Taking up most top space) */}
                    <div className="md:col-span-8 bg-white rounded-3xl p-8 shadow-sm border border-slate-200/50 flex flex-col justify-center">
                        <label className="text-xs font-bold text-slate-400 uppercase tracking-widest pl-1 mb-2">
                            Task Name
                        </label>
                        <input
                            type="text"
                            value={formData.title}
                            onChange={e => setFormData({ ...formData, title: e.target.value })}
                            placeholder="Enter task name..."
                            required
                            className="w-full text-4xl font-bold text-slate-800 placeholder:text-slate-200 outline-none bg-transparent"
                        />
                    </div>

                    {/* üíæ Save Box */}
                    <div className="md:col-span-3 bg-slate-900 rounded-3xl p-8 shadow-lg shadow-slate-300 flex flex-col items-center justify-center text-center cursor-pointer hover:bg-slate-800 transition-colors group relative overflow-hidden">
                        <button type="submit" disabled={submitting} className="absolute inset-0 z-10 w-full h-full"></button>
                        <div className="relative z-0 flex flex-col items-center">
                            {submitting ? <Loader2 size={32} className="text-white animate-spin mb-2" /> : <Save size={32} className="text-white mb-2 group-hover:scale-110 transition-transform" />}
                            <span className="text-white font-bold text-lg">{isEditing ? 'Save Changes' : 'Create Now'}</span>
                        </div>
                    </div>


                    {/* üìù Description Box (Large Left Panel) */}
                    <div className="md:col-span-8 md:row-span-2 bg-white rounded-3xl p-8 shadow-sm border border-slate-200/50 flex flex-col min-h-[500px]">
                        <div className="flex items-center gap-3 mb-6 pb-4 border-b border-slate-50">
                            <div className="w-10 h-10 rounded-2xl bg-indigo-50 flex items-center justify-center text-indigo-500">
                                <AlignLeft size={20} />
                            </div>
                            <div>
                                <h3 className="font-bold text-slate-800 text-lg">Details</h3>
                                <p className="text-xs text-slate-400">Project requirements & notes</p>
                            </div>
                        </div>
                        <textarea
                            value={formData.description}
                            onChange={e => setFormData({ ...formData, description: e.target.value })}
                            placeholder="Start typing..."
                            className="flex-1 w-full bg-slate-50 rounded-2xl p-6 border-2 border-transparent focus:bg-white focus:border-indigo-100 outline-none transition-all resize-none text-slate-600 leading-relaxed text-lg"
                        />
                    </div>


                    {/* üß© Sidebar Boxes (Stacked Right Layout) */}

                    {/* Status Box (Replaced Priority) */}
                    <div className="md:col-span-4 bg-white rounded-3xl p-6 shadow-sm border border-slate-200/50 flex items-center justify-between">
                        <div className="flex items-center gap-3">
                            <div className="w-12 h-12 rounded-2xl bg-orange-50 flex items-center justify-center text-orange-500">
                                <Activity size={24} />
                            </div>
                            <div>
                                <label className="block text-xs font-bold text-slate-400 uppercase tracking-wide">Status</label>
                                <span className="text-slate-800 font-bold">{formData.status}</span>
                            </div>
                        </div>
                        <div className="w-3 h-3 rounded-full bg-green-500 animate-pulse"></div>
                    </div>

                    {/* Assignee Box */}
                    <div className="md:col-span-4 bg-white rounded-3xl p-6 shadow-sm border border-slate-200/50 hover:border-indigo-100 transition-colors">
                        <label className="block text-xs font-bold text-slate-400 uppercase tracking-wide mb-3 pl-1">Assignee</label>
                        <div className="relative group">
                            <div className="flex items-center gap-3">
                                <div className="w-12 h-12 rounded-full bg-slate-100 flex items-center justify-center text-slate-500 border-2 border-white shadow-sm">
                                    <User size={24} />
                                </div>
                                <select
                                    value={formData.assigned_to}
                                    onChange={e => setFormData({ ...formData, assigned_to: e.target.value })}
                                    className="w-full bg-transparent font-bold text-slate-700 outline-none text-lg cursor-pointer"
                                >
                                    <option value="">Unassigned</option>
                                    {users.map(u => (
                                        <option key={u.id} value={u.id}>{u.name}</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                    </div>

                    {/* Due Date Box */}
                    <div className="md:col-span-4 bg-indigo-500 rounded-3xl p-6 shadow-lg shadow-indigo-200 text-white flex flex-col justify-center min-h-[140px]">
                        <label className="block text-xs font-bold text-indigo-200 uppercase tracking-wide mb-1">Target Date</label>
                        <div className="flex items-center gap-3">
                            <Clock size={28} className="text-indigo-200" />
                            <input
                                type="date"
                                value={formData.due_date}
                                onChange={e => setFormData({ ...formData, due_date: e.target.value })}
                                className="bg-transparent text-2xl font-bold text-white outline-none w-full placeholder-indigo-200 cursor-pointer"
                            />
                        </div>
                    </div>

                </div>
            </motion.form>
        </div>
    );
};

export default AddTaskPage;
