import React, { useEffect, useState } from "react";
import {
    Bell,
    CheckCircle,
    Info,
    AlertTriangle,
    XCircle,
    CheckCheck,
    Clock
} from "lucide-react";
import { notificationService, Notification } from "../../services/notificationService";

const NotificationPage: React.FC = () => {
    const [notifications, setNotifications] = useState<Notification[]>([]);
    const [loading, setLoading] = useState(true);
    const [filter, setFilter] = useState<'all' | 'unread'>('all');

    const fetchNotifications = async () => {
        setLoading(true);
        try {
            const data = await notificationService.getNotifications(0, 50);
            setNotifications(data);
        } catch (error) {
            console.error("Failed to load notifications", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchNotifications();
    }, []);

    const handleMarkAsRead = async (id: number) => {
        try {
            await notificationService.markAsRead(id);
            setNotifications(prev => prev.map(n => n.id === id ? { ...n, is_read: true } : n));
        } catch (error) {
            console.error("Failed to mark as read", error);
        }
    };

    const handleMarkAllRead = async () => {
        try {
            await notificationService.markAllAsRead();
            setNotifications(prev => prev.map(n => ({ ...n, is_read: true })));
        } catch (error) {
            console.error("Failed to mark all as read", error);
        }
    };

    const filteredNotifications = notifications.filter(n => {
        if (filter === 'unread') return !n.is_read;
        return true;
    });

    const getIcon = (type: string) => {
        switch (type) {
            case 'success': return <CheckCircle className="text-green-500" size={20} />;
            case 'warning': return <AlertTriangle className="text-yellow-500" size={20} />;
            case 'error': return <XCircle className="text-red-500" size={20} />;
            default: return <Info className="text-blue-500" size={20} />;
        }
    };

    const getBgColor = (type: string) => {
        switch (type) {
            case 'success': return 'bg-green-50';
            case 'warning': return 'bg-yellow-50';
            case 'error': return 'bg-red-50';
            default: return 'bg-blue-50';
        }
    };

    return (
        <div className="max-w-4xl mx-auto space-y-6 fade-in">
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                <div>
                    <h1 className="text-2xl font-bold text-slate-900 flex items-center gap-2">
                        <Bell className="text-slate-400" /> Notifications
                    </h1>
                    <p className="text-slate-500 mt-1">Stay updated with activity in your workspace.</p>
                </div>

                <div className="flex items-center gap-3">
                    <div className="bg-white border p-1 rounded-lg flex items-center">
                        <button
                            onClick={() => setFilter('all')}
                            className={`px-3 py-1.5 text-sm font-medium rounded-md transition ${filter === 'all' ? 'bg-slate-100 text-slate-800' : 'text-slate-500 hover:bg-slate-50'}`}
                        >
                            All
                        </button>
                        <button
                            onClick={() => setFilter('unread')}
                            className={`px-3 py-1.5 text-sm font-medium rounded-md transition ${filter === 'unread' ? 'bg-blue-50 text-blue-600' : 'text-slate-500 hover:bg-slate-50'}`}
                        >
                            Unread
                        </button>
                    </div>

                    <button
                        onClick={handleMarkAllRead}
                        className="flex items-center gap-2 px-3 py-2 text-sm font-medium text-slate-600 hover:text-slate-800 bg-white border border-slate-200 hover:border-slate-300 rounded-lg transition shadow-sm"
                    >
                        <CheckCheck size={16} /> Mark all read
                    </button>
                </div>
            </div>

            <div className="bg-white rounded-xl border border-slate-200 shadow-sm overflow-hidden">
                {loading ? (
                    <div className="p-10 text-center text-slate-400">Loading notifications...</div>
                ) : filteredNotifications.length === 0 ? (
                    <div className="p-16 text-center">
                        <div className="w-16 h-16 bg-slate-50 rounded-full flex items-center justify-center mx-auto mb-4">
                            <Bell className="text-slate-300" size={32} />
                        </div>
                        <h3 className="text-slate-800 font-medium mb-1">All caught up!</h3>
                        <p className="text-slate-400 text-sm">No notifications to display.</p>
                    </div>
                ) : (
                    <div className="divide-y divide-slate-100">
                        {filteredNotifications.map((notif) => (
                            <div
                                key={notif.id}
                                className={`p-5 flex gap-4 transition-colors hover:bg-slate-50 group ${!notif.is_read ? 'bg-blue-50/30' : ''}`}
                            >
                                <div className={`mt-1 w-10 h-10 rounded-full flex items-center justify-center flex-shrink-0 ${getBgColor(notif.type)}`}>
                                    {getIcon(notif.type)}
                                </div>

                                <div className="flex-1 min-w-0">
                                    <div className="flex justify-between items-start">
                                        <h3 className={`text-sm font-semibold ${!notif.is_read ? 'text-slate-900' : 'text-slate-700'}`}>
                                            {notif.title}
                                        </h3>
                                        <span className="text-xs text-slate-400 flex items-center gap-1 flex-shrink-0 ml-2">
                                            <Clock size={12} />
                                            {new Date(notif.created_at).toLocaleString()}
                                        </span>
                                    </div>
                                    <p className="text-sm text-slate-600 mt-1 leading-relaxed">
                                        {notif.description}
                                    </p>
                                </div>

                                {!notif.is_read && (
                                    <button
                                        onClick={() => handleMarkAsRead(notif.id)}
                                        className="self-center p-2 text-blue-600 hover:bg-blue-50 rounded-full opacity-0 group-hover:opacity-100 transition-opacity"
                                        title="Mark as read"
                                    >
                                        <div className="w-2.5 h-2.5 bg-blue-600 rounded-full"></div>
                                    </button>
                                )}
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default NotificationPage;
