import React, { useState, useEffect, useRef, useCallback } from "react";
import {
    Search, Users, Paperclip, Smile, Send,
    Check, ArrowLeft, X, Sparkles, Zap, Shield, Plus, ArrowUpRight, ChevronRight, MessageSquare as MessageIcon
} from "lucide-react";
import { messageService, Chat, Message } from "../../services/messageService";
import { userService, User } from "../../services/userService";
import { authService } from "../../services/authService";

import { motion, AnimatePresence } from "framer-motion";

const CreateGroupModal = ({ isOpen, onClose, users, onCreate }: any) => {
    const [groupName, setGroupName] = useState("");
    const [selectedIds, setSelectedIds] = useState<number[]>([]);

    const toggleUser = (id: number) => {
        setSelectedIds(prev => prev.includes(id) ? prev.filter(x => x !== id) : [...prev, id]);
    };

    const handleSubmit = () => {
        if (!groupName || selectedIds.length === 0) return;
        onCreate(groupName, selectedIds);
        onClose();
        setGroupName("");
        setSelectedIds([]);
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <div className="fixed inset-0 z-[100] flex items-center justify-center p-4">
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="absolute inset-0 bg-slate-900/10 backdrop-blur-sm"
                        onClick={onClose}
                    />
                    <motion.div
                        initial={{ opacity: 0, scale: 0.95, y: 10 }}
                        animate={{ opacity: 1, scale: 1, y: 0 }}
                        exit={{ opacity: 0, scale: 0.95, y: 10 }}
                        className="bg-white rounded-[32px] border border-slate-100 w-full max-w-md p-8 relative z-10 shadow-2xl"
                    >
                        <div className="flex justify-between items-center mb-8">
                            <div>
                                <h3 className="text-2xl font-bold tracking-tight text-slate-900">Create Group</h3>
                                <p className="text-sm text-slate-500 font-medium">Start a new team conversation</p>
                            </div>
                            <button
                                onClick={onClose}
                                className="w-10 h-10 flex items-center justify-center bg-slate-50 text-slate-400 hover:bg-slate-100 hover:text-slate-900 rounded-xl transition-all"
                            >
                                <X size={20} />
                            </button>
                        </div>

                        <div className="space-y-6">
                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Group Name</label>
                                <input
                                    className="input-field"
                                    value={groupName}
                                    onChange={e => setGroupName(e.target.value)}
                                    placeholder="Enter group name..."
                                />
                            </div>

                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Select Members</label>
                                <div className="max-h-[250px] overflow-y-auto space-y-1 pr-2 custom-scrollbar">
                                    {users.map((u: User) => (
                                        <button
                                            key={u.id}
                                            onClick={() => toggleUser(u.id)}
                                            className={`w-full flex items-center gap-4 p-3 rounded-2xl transition-all ${selectedIds.includes(u.id) ? 'bg-blue-50 border border-blue-100' : 'bg-white border border-transparent hover:bg-slate-50'
                                                }`}
                                        >
                                            <div className={`w-10 h-10 rounded-xl flex items-center justify-center text-xs font-bold transition-all shadow-sm ${selectedIds.includes(u.id) ? 'bg-blue-600 text-white' : 'bg-slate-100 text-slate-500'
                                                }`}>
                                                {u.name.substring(0, 2).toUpperCase()}
                                            </div>
                                            <span className={`text-sm font-semibold ${selectedIds.includes(u.id) ? 'text-blue-600' : 'text-slate-700'}`}>{u.name}</span>
                                            {selectedIds.includes(u.id) && (
                                                <div className="ml-auto p-1 bg-blue-600 rounded-lg text-white">
                                                    <Check size={14} />
                                                </div>
                                            )}
                                        </button>
                                    ))}
                                </div>
                            </div>

                            <button
                                onClick={handleSubmit}
                                disabled={!groupName || selectedIds.length === 0}
                                className="btn-primary w-full shadow-lg shadow-blue-100 disabled:opacity-50 disabled:shadow-none mt-4"
                            >
                                <Plus size={18} />
                                Create Group
                            </button>
                        </div>
                    </motion.div>
                </div>
            )}
        </AnimatePresence>
    );
};

const MessagesPage: React.FC = () => {
    const [chats, setChats] = useState<Chat[]>([]);
    const [messages, setMessages] = useState<Message[]>([]);
    const [currentUser, setCurrentUser] = useState<any>(null);
    const [activeChatId, setActiveChatId] = useState<number | null>(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [allUsers, setAllUsers] = useState<User[]>([]);
    const [inputText, setInputText] = useState("");
    const [isMobileChatOpen, setIsMobileChatOpen] = useState(false);
    const [isGroupModalOpen, setIsGroupModalOpen] = useState(false);

    const messagesContainerRef = useRef<HTMLDivElement>(null);
    const messagesEndRef = useRef<HTMLDivElement>(null);

    const loadChats = useCallback(async () => {
        try {
            const data = await messageService.getChats();
            setChats(data);
        } catch (e) {
            console.error("Failed to load chats:", e);
        }
    }, []);

    const loadUserAndData = useCallback(async () => {
        try {
            const user = await authService.getCurrentUser();
            setCurrentUser(user);
            await loadChats();
            const users = await userService.getAllUsers();
            setAllUsers(users.filter(u => u.id !== user.id));
        } catch (e) {
            console.error("Failed to load user data:", e);
        }
    }, [loadChats]);

    const loadMessages = useCallback(async (chatId: number) => {
        try {
            const msgs = await messageService.getMessages(chatId);
            setMessages(msgs);
            await messageService.markAsRead(chatId);
            loadChats();
        } catch (e) {
            console.error("Failed to load messages:", e);
        }
    }, [loadChats]);

    const scrollToBottom = useCallback((smooth = true) => {
        messagesEndRef.current?.scrollIntoView({ behavior: smooth ? 'smooth' : 'auto' });
    }, []);

    useEffect(() => {
        loadUserAndData();
    }, [loadUserAndData]);

    useEffect(() => {
        const pollInterval = setInterval(() => {
            loadChats();
            if (activeChatId) {
                loadMessages(activeChatId);
            }
        }, 10000);

        return () => {
            clearInterval(pollInterval);
        };
    }, [activeChatId, loadChats, loadMessages]);

    useEffect(() => {
        if (activeChatId) {
            loadMessages(activeChatId);
            setTimeout(() => scrollToBottom(false), 100);
        }
    }, [activeChatId, loadMessages, scrollToBottom]);

    useEffect(() => {
        scrollToBottom();
    }, [messages, scrollToBottom]);

    const handleSendMessage = async () => {
        if (!inputText.trim() || !activeChatId) return;
        try {
            await messageService.sendMessage(activeChatId, inputText);
            setInputText("");
            loadMessages(activeChatId);
            loadChats();
            setTimeout(() => scrollToBottom(), 100);
        } catch (e) {
            console.error("Failed to send message:", e);
        }
    };

    const handleCreateP2PChat = async (userId: number) => {
        try {
            const chat = await messageService.createChat([userId], undefined, false);
            setChats(prev => prev.find(c => c.id === chat.id) ? prev : [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
            setSearchQuery("");
        } catch (e) {
            console.error("Failed to create P2P chat:", e);
        }
    };

    const handleCreateGroup = async (name: string, userIds: number[]) => {
        try {
            const chat = await messageService.createChat(userIds, name, true);
            setChats(prev => [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
        } catch (e) {
            console.error("Failed to create group chat:", e);
        }
    };

    const filteredChats = chats.filter(c => c.name?.toLowerCase().includes(searchQuery.toLowerCase()));
    const suggestedUsers = searchQuery ? allUsers.filter(u => u.name.toLowerCase().includes(searchQuery.toLowerCase())) : [];
    const activeChat = chats.find(c => c.id === activeChatId);

    return (
        <div className="flex h-screen bg-white overflow-hidden animate-fade-in font-sans">
            {/* Background Decor Removed */}

            <CreateGroupModal
                isOpen={isGroupModalOpen}
                onClose={() => setIsGroupModalOpen(false)}
                users={allUsers}
                onCreate={handleCreateGroup}
            />

            {/* Sidebar (Chat List) */}
            <div className={`w-full md:w-[350px] lg:w-[420px] bg-white border-r border-slate-200 flex flex-col ${isMobileChatOpen ? 'hidden md:flex' : 'flex'}`}>
                <div className="p-6 border-b border-slate-100 flex items-center justify-between">
                    <div>
                        <h1 className="text-2xl font-bold tracking-tight text-slate-900 leading-none mb-1">Messages</h1>
                        <p className="text-xs font-semibold text-slate-400 uppercase tracking-wide flex items-center gap-2">
                            <span className="w-2 h-2 rounded-full bg-green-500" />
                            Online
                        </p>
                    </div>
                    <button
                        onClick={() => setIsGroupModalOpen(true)}
                        className="w-10 h-10 flex items-center justify-center bg-blue-600 text-white shadow-sm hover:bg-blue-700 active:scale-95 rounded-lg transition-all"
                        title="New Group"
                    >
                        <Plus size={20} strokeWidth={2.5} />
                    </button>
                </div>

                <div className="px-6 py-4">
                    <div className="relative">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                        <input
                            type="text"
                            placeholder="Search conversations..."
                            className="w-full pl-10 pr-4 py-2.5 bg-slate-50 border border-slate-200 rounded-lg focus:bg-white focus:border-blue-500 focus:ring-2 focus:ring-blue-100 transition-all outline-none font-medium text-slate-900"
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                    </div>
                </div>

                <div className="flex-1 overflow-y-auto custom-scrollbar">
                    {filteredChats.map(chat => (
                        <div
                            key={chat.id}
                            onClick={() => {
                                setActiveChatId(chat.id);
                                setIsMobileChatOpen(true);
                            }}
                            className={`p-4 mx-3 mb-1 rounded-lg cursor-pointer transition-all border border-transparent ${activeChatId === chat.id
                                ? 'bg-blue-50 border-blue-100'
                                : 'hover:bg-slate-50 hover:border-slate-100'
                                }`}
                        >
                            <div className="flex items-start gap-4">
                                <div className={`w-12 h-12 rounded-lg flex items-center justify-center text-lg font-bold shrink-0 ${activeChatId === chat.id
                                    ? 'bg-blue-600 text-white'
                                    : 'bg-slate-100 text-slate-500'
                                    }`}>
                                    {chat.is_group ? <Users size={20} /> : chat.name?.substring(0, 2).toUpperCase()}
                                </div>
                                <div className="flex-1 min-w-0">
                                    <div className="flex justify-between items-baseline mb-1">
                                        <h3 className={`font-bold truncate ${activeChatId === chat.id ? 'text-blue-900' : 'text-slate-900'}`}>
                                            {chat.name}
                                        </h3>
                                        {chat.last_message_time && ( // Changed from chat.last_message_at
                                            <span className="text-[10px] font-medium text-slate-400 shrink-0">
                                                {new Date(chat.last_message_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                            </span>
                                        )}
                                    </div>
                                    <p className={`text-sm truncate ${activeChatId === chat.id ? 'text-blue-600/80 font-medium' : 'text-slate-500'}`}>
                                        {chat.last_message || <span className="italic text-slate-400">No messages yet</span>} {/* Changed from chat.last_message_content */}
                                    </p>
                                </div>
                                {(chat.unread_count || 0) > 0 && (
                                    <div className="w-5 h-5 bg-blue-600 text-white text-[10px] font-bold rounded-full flex items-center justify-center mt-1 shrink-0 shadow-sm">
                                        {chat.unread_count}
                                    </div>
                                )}
                            </div>
                        </div>
                    ))}

                    {suggestedUsers.length > 0 && (
                        <div className="mt-8 pt-6 border-t border-slate-100">
                            <h4 className="px-2 mb-4 text-[10px] font-bold text-slate-300 uppercase tracking-[0.2em]">Suggestions</h4>
                            {suggestedUsers.map(user => (
                                <button
                                    key={user.id}
                                    onClick={() => handleCreateP2PChat(user.id)}
                                    className="w-full p-3 rounded-[24px] hover:bg-blue-50/50 transition-all flex items-center justify-between group px-5 border border-transparent hover:border-blue-100"
                                >
                                    <div className="flex items-center gap-4">
                                        <div className="w-10 h-10 bg-white rounded-[14px] flex items-center justify-center text-slate-400 font-bold text-xs group-hover:bg-blue-600 group-hover:text-white transition-all shadow-sm border border-slate-100 group-hover:shadow-blue-200">
                                            {user.name.substring(0, 2).toUpperCase()}
                                        </div>
                                        <span className="text-sm font-bold text-slate-700 group-hover:text-blue-900">{user.name}</span>
                                    </div>
                                    <div className="w-8 h-8 rounded-full bg-slate-50 flex items-center justify-center group-hover:bg-blue-100 transition-colors text-slate-300 group-hover:text-blue-600">
                                        <Plus size={16} strokeWidth={3} />
                                    </div>
                                </button>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Chat Area */}
            {activeChat ? (
                <div className={`flex-1 flex flex-col relative ${isMobileChatOpen ? 'flex absolute inset-0 z-30 bg-white' : 'hidden md:flex'}`}>

                    {/* Chat Header */}
                    <div className="h-20 bg-white border-b border-slate-200 flex items-center justify-between px-6 shrink-0 z-20">
                        <div className="flex items-center gap-4">
                            <button onClick={() => setIsMobileChatOpen(false)} className="md:hidden p-2 -ml-2 text-slate-500 hover:bg-slate-100 rounded-lg transition-all">
                                <ArrowLeft size={20} />
                            </button>
                            <div className="relative">
                                <div className="w-10 h-10 rounded-lg flex items-center justify-center font-bold text-sm shrink-0 bg-slate-900 text-white">
                                    {activeChat.is_group ? <Users size={18} /> : activeChat.name?.substring(0, 2).toUpperCase()}
                                </div>
                                <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-green-500 border-2 border-white rounded-full" />
                            </div>
                            <div>
                                <h2 className="text-lg font-bold text-slate-900 leading-tight">{activeChat.name}</h2>
                                <p className="text-xs font-medium text-slate-500 flex items-center gap-1">
                                    Active Now
                                </p>
                            </div>
                        </div>
                        <div className="flex items-center gap-3">
                            <div className="hidden sm:flex items-center gap-2 px-3 py-1.5 bg-slate-50 text-slate-600 rounded-md text-[10px] font-bold uppercase tracking-wider border border-slate-200">
                                <Shield size={12} strokeWidth={2.5} /> E2E Encrypted
                            </div>
                        </div>
                    </div>

                    {/* Chat Messages */}
                    <div className="flex-1 relative flex flex-col overflow-hidden z-10 bg-slate-50">
                        <div ref={messagesContainerRef} className="flex-1 overflow-y-auto p-6 space-y-6 custom-scrollbar relative z-10 scroll-smooth">
                            <AnimatePresence mode="popLayout">
                                {messages.map((msg, idx) => {
                                    const isMe = msg.sender_id === currentUser?.id; // Added null check for currentUser
                                    return (
                                        <motion.div
                                            key={msg.id || idx}
                                            initial={{ opacity: 0, y: 10, scale: 0.95 }}
                                            animate={{ opacity: 1, y: 0, scale: 1 }}
                                            className={`flex ${isMe ? 'justify-end' : 'justify-start'}`}
                                        >
                                            <div className={`flex flex-col ${isMe ? 'items-end' : 'items-start'} max-w-[75%]`}>
                                                {!isMe && (
                                                    <span className="text-[10px] font-bold text-slate-400 mb-1 ml-1 uppercase tracking-wide">
                                                        {allUsers.find(u => u.id === msg.sender_id)?.name.split(' ')[0]}
                                                    </span>
                                                )}
                                                <div className={`px-5 py-3 rounded-2xl text-sm font-medium leading-relaxed shadow-sm relative ${isMe
                                                    ? 'bg-blue-600 text-white rounded-br-none'
                                                    : 'bg-white text-slate-700 border border-slate-200 rounded-bl-none'
                                                    }`}>
                                                    {msg.content}
                                                </div>
                                                <span className="text-[10px] font-semibold text-slate-300 mt-1 mx-1">
                                                    {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} {/* Changed from msg.created_at */}
                                                </span>
                                            </div>
                                        </motion.div>
                                    );
                                })}
                            </AnimatePresence>
                            <div ref={messagesEndRef} />
                        </div>
                    </div>

                    {/* Chat Input */}
                    <div className="p-4 bg-white border-t border-slate-200 z-20">
                        <form
                            onSubmit={(e) => { e.preventDefault(); handleSendMessage(); }}
                            className="flex items-center gap-3 max-w-4xl mx-auto"
                        >
                            <div className="flex-1 relative">
                                <input
                                    type="text"
                                    value={inputText} // Changed from newMessage
                                    onChange={(e) => setInputText(e.target.value)} // Changed from setNewMessage
                                    placeholder="Type your message..."
                                    className="w-full pl-5 pr-12 py-3 bg-slate-50 border border-slate-200 rounded-lg focus:bg-white focus:border-blue-500 focus:ring-2 focus:ring-blue-100 transition-all outline-none font-medium text-slate-900 placeholder:text-slate-400"
                                />
                                <button type="button" className="absolute right-3 top-1/2 -translate-y-1/2 p-1.5 text-slate-400 hover:text-slate-600 rounded-md transition-colors">
                                    <Paperclip size={18} />
                                </button>
                            </div>
                            <button
                                type="submit"
                                disabled={!inputText.trim()} // Changed from newMessage
                                className="p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 active:scale-95 disabled:opacity-50 disabled:active:scale-100 transition-all shadow-sm"
                            >
                                <Send size={20} />
                            </button>
                        </form>
                    </div>
                </div>
            ) : (
                <div className="hidden md:flex flex-1 flex-col items-center justify-center bg-slate-50 text-center p-12">
                    <div className="w-24 h-24 bg-white rounded-2xl flex items-center justify-center mb-6 shadow-sm border border-slate-200">
                        <MessageIcon className="text-blue-500" size={48} />
                    </div>
                    <h3 className="text-2xl font-bold text-slate-900 mb-2">Hello, {currentUser?.name?.split(' ')[0]}!</h3>
                    <p className="text-slate-500 font-medium max-w-md">
                        Ready to collaborate? Select a conversation from the sidebar or start a new team chat.
                    </p>
                </div>
            )}
        </div>
    );
};

export default MessagesPage;
