import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  Workflow,
  ClipboardList,
  LogOut,
  UsersRound,
  Circle,
  Settings,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";

const Sidebar: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard", badge: null },
    { label: "Messages", icon: MessageSquare, path: "/chat", badge: 3 },
    { label: "Tasks", icon: ClipboardList, path: "/tasks", badge: 5 },
    { label: "Meetings", icon: UsersRound, path: "/meetings", badge: null },
    { label: "Calendar", icon: CalendarDays, path: "/calendar", badge: null },
    { label: "Files", icon: FileUp, path: "/files", badge: null },
    { label: "Workflows", icon: Workflow, path: "/workflows", badge: null },
  ];

  return (
    <aside className="h-screen w-64 flex flex-col bg-gradient-to-b from-slate-50 to-white border-r border-slate-200">
      {/* Header with Logo */}
      <div className="p-6 pb-4">
        <div
          className="flex items-center gap-3 cursor-pointer group"
          onClick={() => navigate("/dashboard")}
        >
          <div className="relative">
            <div className="absolute inset-0 bg-gradient-to-br from-blue-400 to-purple-500 rounded-xl blur-md opacity-40 group-hover:opacity-60 transition-opacity duration-300" />
            <img
              src="/logo1.png"
              alt="Collabryta"
              className="relative w-11 h-11 object-contain"
            />
          </div>
          <div>
            <h1 className="text-xl font-black text-slate-900 tracking-tight">
              Collabryta
            </h1>
            <p className="text-xs text-slate-500 font-medium -mt-0.5">Workspace</p>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <nav className="flex-1 overflow-y-auto px-3 pb-3 space-y-0.5">
        {navItems.map(({ label, icon: Icon, path, badge }) => {
          const active = location.pathname === path;
          return (
            <button
              key={path}
              onClick={() => navigate(path)}
              className={`
                relative w-full flex items-center gap-3 px-3 py-2.5 rounded-lg
                font-medium text-sm transition-all duration-200
                ${active
                  ? "bg-white text-blue-600 shadow-sm border border-blue-100"
                  : "text-slate-600 hover:text-slate-900 hover:bg-white/60"
                }
              `}
            >
              {/* Left Border Accent */}
              {active && (
                <div className="absolute left-0 top-1/2 -translate-y-1/2 w-1 h-6 bg-gradient-to-b from-blue-500 to-purple-500 rounded-r-full" />
              )}

              {/* Icon */}
              <Icon
                size={19}
                strokeWidth={active ? 2.5 : 2}
                className={`flex-shrink-0 ${active ? "text-blue-600" : ""}`}
              />

              {/* Label */}
              <span className="flex-1 text-left">{label}</span>

              {/* Badge */}
              {badge && badge > 0 && (
                <span className="px-1.5 py-0.5 text-xs font-bold rounded-md bg-red-500 text-white min-w-[18px] text-center">
                  {badge}
                </span>
              )}
            </button>
          );
        })}
      </nav>

      {/* Settings Button */}
      <div className="px-3 pb-2">
        <button
          onClick={() => navigate("/settings")}
          className="w-full flex items-center gap-3 px-3 py-2.5 rounded-lg text-slate-600 hover:text-slate-900 hover:bg-white/60 font-medium text-sm transition-all duration-200"
        >
          <Settings size={19} strokeWidth={2} className="flex-shrink-0" />
          <span className="flex-1 text-left">Settings</span>
        </button>
      </div>

      {/* Divider */}
      <div className="h-px bg-gradient-to-r from-transparent via-slate-300 to-transparent mx-3 mb-3" />

      {/* User Profile */}
      <div className="px-3 pb-4">
        <div className="flex items-center gap-3 p-2.5 rounded-xl bg-white border border-slate-200 shadow-sm">
          <div className="relative flex-shrink-0">
            <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center text-white font-bold shadow-sm">
              AK
            </div>
            <Circle
              size={10}
              className="absolute -bottom-0.5 -right-0.5 text-green-500 fill-green-500"
            />
          </div>
          <div className="flex-1 min-w-0">
            <p className="text-sm font-bold text-slate-900 truncate">Ashok Kumar</p>
            <p className="text-xs text-slate-500">Administrator</p>
          </div>
          <button
            onClick={() => navigate("/login")}
            className="p-1.5 rounded-lg hover:bg-slate-100 text-slate-400 hover:text-red-600 transition-colors duration-200 group"
            aria-label="Sign out"
          >
            <LogOut size={16} className="transition-transform duration-200 group-hover:-translate-x-0.5" />
          </button>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
