  Search,
  Loader2,
  Trash2,
  ChevronRight,
  Filter,
  ArrowRight,
  Layers,
  CheckCircle,
  Clock,
  Layout
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [activeTab, setActiveTab] = useState<string>("All");

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getFilteredTasks = () => {
    return tasks.filter(t => {
      const matchesSearch = t.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (t.description && t.description.toLowerCase().includes(searchQuery.toLowerCase()));
      const matchesTab = activeTab === "All" || normalizeStatus(t.status) === activeTab;
      return matchesSearch && matchesTab;
    });
  };

  const handleStatusUpdate = async (e: React.MouseEvent, task: Task, newStatus: string) => {
    e.stopPropagation();
    setTasks(prev => prev.map(t => t.id === task.id ? { ...t, status: newStatus } : t));
    try {
      await taskService.updateTask(task.id, { status: newStatus });
    } catch (error) {
      console.error("Failed to update status");
      fetchTasks();
    }
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Delete this task?")) return;
    setTasks(prev => prev.filter(t => t.id !== id));
    try {
      await taskService.deleteTask(id);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const stats = {
    total: tasks.length,
    upcoming: tasks.filter(t => normalizeStatus(t.status) === "Upcoming").length,
    progress: tasks.filter(t => normalizeStatus(t.status) === "In Progress").length,
    done: tasks.filter(t => normalizeStatus(t.status) === "Completed").length,
  };

  return (
    <div className="min-h-screen bg-[#f1f5f9] p-6 lg:p-12 animate-in fade-in duration-500">
      <div className="max-w-7xl mx-auto space-y-10">

        {/* üè∑Ô∏è Minimalist Page Header */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">
          <div>
            <h1 className="text-4xl font-black text-slate-900 tracking-tight flex items-center gap-3">
              <Layers className="text-indigo-600" size={36} />
              Task Workspace
            </h1>
            <p className="text-slate-500 font-medium mt-2">Manage your engineering pipeline effectively.</p>
          </div>
          <button
            onClick={() => navigate("/tasks/new")}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-8 py-4 rounded-[20px] font-bold shadow-lg shadow-indigo-200 transition-all active:scale-95 flex items-center gap-2 group"
          >
            <Plus size={20} className="group-hover:rotate-180 transition-transform duration-500" />
            New Milestone
          </button>
        </div>

        {/* ÔøΩ High-Level Stats Cards */}
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
          <StatBox label="Total Archive" value={stats.total} color="slate" icon={<Layout size={18} />} />
          <StatBox label="Scheduled" value={stats.upcoming} color="indigo" icon={<Clock size={18} />} />
          <StatBox label="In Development" value={stats.progress} color="orange" icon={<Loader2 size={18} className="animate-spin-slow" />} />
          <StatBox label="Verified" value={stats.done} color="green" icon={<CheckCircle size={18} />} />
        </div>

        {/* üõ†Ô∏è Dashboard Toolbar */}
        <div className="bg-white/80 backdrop-blur-md p-4 rounded-[28px] border border-white shadow-sm flex flex-col lg:flex-row items-center gap-4">
          <div className="relative flex-1 w-full">
            <Search className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-400" size={20} />
            <input
              type="text"
              placeholder="Search in workspace..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-14 pr-6 py-4 bg-slate-50/50 rounded-2xl border border-slate-100 text-slate-700 font-bold outline-none placeholder:text-slate-300 focus:bg-white transition-all"
            />
          </div>
          <div className="flex bg-slate-100/50 p-1.5 rounded-2xl gap-1 overflow-x-auto w-full lg:w-auto">
            {["All", "Upcoming", "In Progress", "Completed"].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`px-6 py-3 rounded-xl text-sm font-black transition-all whitespace-nowrap ${activeTab === tab ? 'bg-white text-indigo-600 shadow-xl shadow-slate-200/50' : 'text-slate-400 hover:text-slate-600'}`}
              >
                {tab.toUpperCase()}
              </button>
            ))}
          </div>
        </div>

        {/* üìã Modern List Rows */}
        <div className="space-y-4">
          {loading ? (
            <div className="py-32 flex flex-col items-center justify-center space-y-4">
              <Loader2 className="w-12 h-12 animate-spin text-indigo-600" />
              <span className="font-black text-slate-400 uppercase tracking-widest text-sm">Synchronizing...</span>
            </div>
          ) : (
            <AnimatePresence mode="popLayout">
              {getFilteredTasks().length === 0 ? (
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  className="py-24 text-center bg-white rounded-[40px] border border-slate-100 shadow-inner"
                >
                  <Filter size={48} className="mx-auto text-slate-100 mb-4" />
                  <h3 className="text-2xl font-black text-slate-300 uppercase tracking-tight">Workspace Clear</h3>
                </motion.div>
              ) : (
                getFilteredTasks().map((task, idx) => (
                  <ModernTaskRow
                    key={task.id}
                    task={task}
                    index={idx}
                    onDelete={handleDelete}
                    onUpdateStatus={handleStatusUpdate}
                    navigate={navigate}
                    normalizeStatus={normalizeStatus}
                  />
                ))
              )}
            </AnimatePresence>
          )}
        </div>
      </div>
    </div>
  );
};

/* üß© Component: StatBox */
const StatBox = ({ label, value, color, icon }: any) => {
  const colors: any = {
    slate: "bg-slate-900 text-white",
    indigo: "bg-white text-indigo-600 border-indigo-50",
    orange: "bg-white text-orange-600 border-orange-50",
    green: "bg-white text-green-600 border-green-50",
  };
  return (
    <div className={`${colors[color]} p-6 rounded-[32px] border border-slate-200/50 shadow-sm flex flex-col justify-between h-32 relative overflow-hidden group hover:scale-[1.02] transition-transform`}>
      <div className="flex items-center justify-between relative z-10">
        <span className="text-[10px] font-black uppercase tracking-widest opacity-60">{label}</span>
        <div className="opacity-40 group-hover:rotate-12 transition-transform">{icon}</div>
      </div>
      <span className="text-4xl font-black relative z-10 leading-none">{value}</span>
      <div className={`absolute -right-4 -bottom-4 w-20 h-20 rounded-full opacity-5 ${color === 'slate' ? 'bg-white' : 'bg-current'}`}></div>
    </div>
  );
};

/* üß© Component: ModernTaskRow (The Long Type Bar Alternative) */
const ModernTaskRow = ({ task, index, onDelete, onUpdateStatus, navigate, normalizeStatus }: any) => {
  const status = normalizeStatus(task.status);

  return (
    <motion.div
      layout
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, scale: 0.98 }}
      transition={{ delay: index * 0.05 }}
      onClick={() => navigate(`/tasks/edit/${task.id}`)}
      className="group relative bg-white rounded-[32px] overflow-hidden border border-slate-100 shadow-sm hover:shadow-2xl hover:shadow-indigo-100/50 hover:-translate-y-1 transition-all duration-500 cursor-pointer"
    >
      {/* Left Status Marker */}
      <div className={`absolute top-0 left-0 bottom-0 w-2 ${status === 'Completed' ? 'bg-green-500' : status === 'In Progress' ? 'bg-orange-500' : 'bg-indigo-500'}`}></div>

      <div className="p-6 md:p-8 flex flex-col lg:flex-row items-center gap-8">

        {/* 1. Title & Description Cluster */}
        <div className="flex-1 min-w-0 w-full space-y-1">
          <div className="flex items-center gap-3">
            <span className="text-[10px] font-black text-slate-300 uppercase">#{task.id}</span>
            <h3 className="text-xl font-bold text-slate-800 truncate group-hover:text-indigo-600 transition-colors uppercase tracking-tight">{task.title}</h3>
          </div>
          <p className="text-slate-400 text-sm font-medium line-clamp-1 pl-8 border-l-2 border-slate-50 italic">{task.description || "No specific details provided."}</p>
        </div>

        {/* 2. Metadata Grid */}
        <div className="flex items-center gap-8 w-full lg:w-auto">

          {/* Assignee Card */}
          <div className="flex items-center gap-4 bg-slate-50/50 p-3 rounded-2xl border border-slate-100">
            <div className="w-10 h-10 rounded-full border-2 border-white bg-indigo-50 text-indigo-600 flex items-center justify-center font-black text-xs shadow-sm">
              {task.assigned_to_user ? task.assigned_to_user.name.charAt(0) : <UserIcon size={16} />}
            </div>
            <div className="hidden xl:block">
              <p className="text-[8px] font-black text-slate-400 uppercase tracking-tighter">Assigned To</p>
              <p className="text-xs font-bold text-slate-700">{task.assigned_to_user?.name || "Open"}</p>
            </div>
          </div>

          {/* Timeline Card */}
          <div className="flex items-center gap-4 bg-slate-50/50 p-3 rounded-2xl border border-slate-100">
            <div className="w-10 h-10 rounded-2xl border-2 border-white bg-white text-slate-400 flex items-center justify-center shadow-sm">
              <Calendar size={18} />
            </div>
            <div>
              <p className="text-[8px] font-black text-slate-400 uppercase tracking-tighter">Deadline</p>
              <p className="text-xs font-bold text-slate-700">{task.due_date ? new Date(task.due_date).toLocaleDateString() : 'N/A'}</p>
            </div>
          </div>

          {/* Status Pill Actions */}
          <div className="flex bg-slate-100/50 p-1 rounded-2xl gap-1">
            {["Upcoming", "In Progress", "Completed"].map((s) => (
              <button
                key={s}
                onClick={(e) => onUpdateStatus(e, task, s)}
                className={`w-8 h-8 rounded-xl flex items-center justify-center transition-all ${status === s ? 'bg-white shadow-md text-indigo-600' : 'text-slate-300 hover:text-slate-500'}`}
                title={s}
              >
                {s === 'Upcoming' ? <Clock size={16} /> : s === 'In Progress' ? <Loader2 size={16} className={status === s ? 'animate-spin' : ''} /> : <CheckCircle size={16} />}
              </button>
            ))}
          </div>

          {/* Action Circle */}
          <div className="flex items-center gap-3">
            <button
              onClick={(e) => handleDelete(e, task.id)}
              className="w-10 h-10 rounded-full flex items-center justify-center text-slate-300 hover:text-red-500 hover:bg-red-50 hover:border-red-100 border border-transparent transition-all"
            >
              <Trash2 size={18} />
            </button>
            <div className="w-12 h-12 rounded-[20px] bg-slate-900 border border-slate-800 text-white flex items-center justify-center group-hover:bg-indigo-600 group-hover:rotate-90 transition-all duration-500 shadow-xl shadow-slate-200">
              <ChevronRight size={22} />
            </div>
          </div>

        </div>

      </div>
    </motion.div>
  );
};

export default TasksPage;
