import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar as CalendarIcon,
  MessageSquare,
  ArrowRight,
  Video,
  Sparkles,
  ChevronRight,
  Clock,
  ArrowUpRight,
  CheckCircle2,
  ListTodo,
  TrendingUp,
  Layout,
  Briefcase,
  Layers,
  Search
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { meetingService, Meeting } from "../../services/meetingService";
import { messageService, Chat } from "../../services/messageService";
import { taskService, Task } from "../../services/taskService";
import { authService } from "../../services/authService";

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [chats, setChats] = useState<Chat[]>([]);
  const [tasks, setTasks] = useState<Task[]>([]);
  const [meetings, setMeetings] = useState<Meeting[]>([]);

  const [stats, setStats] = useState({
    meetingsToday: 0,
    unreadMessages: 0,
    tasksCompleted: 0,
    totalTasks: 0,
    completionRate: 0
  });

  const fetchData = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const [user, allMeetings, allChats, allTasks] = await Promise.all([
        authService.getCurrentUser(),
        meetingService.getAllMeetings(),
        messageService.getChats(),
        taskService.getAllTasks()
      ]);

      setCurrentUser(user);
      setChats(allChats.slice(0, 4)); // Show recent 4 chats
      setTasks(allTasks);
      setMeetings(allMeetings);

      const unreadCount = allChats.reduce((acc: number, curr: Chat) => acc + (curr.unread_count || 0), 0);
      const todayStr = new Date().toLocaleDateString("en-CA");
      const todayMeetings = allMeetings.filter(m => new Date(m.start_time).toLocaleDateString("en-CA") === todayStr);

      const completedTasks = allTasks.filter(t => t.status === "Completed").length;
      const totalTasks = allTasks.length;
      const rate = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

      setStats({
        meetingsToday: todayMeetings.length,
        unreadMessages: unreadCount,
        tasksCompleted: completedTasks,
        totalTasks: totalTasks,
        completionRate: rate
      });

    } catch (error) {
      console.error("Failed to load dashboard data", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    const pollInterval = setInterval(() => {
      fetchData(false);
    }, 60000);
    return () => clearInterval(pollInterval);
  }, []);

  if (loading) {
    return (
      <div className="min-h-[80vh] flex flex-col items-center justify-center">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
          className="w-16 h-16 border-4 border-indigo-100 border-t-indigo-600 rounded-full"
        />
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, repeat: Infinity, repeatType: "reverse" }}
          className="mt-8 text-sm font-bold text-slate-400 uppercase tracking-widest"
        >
          Powering up your workspace...
        </motion.p>
      </div>
    );
  }

  const upcomingMeetings = meetings
    .filter(m => new Date(m.start_time) > new Date())
    .sort((a, b) => new Date(a.start_time).getTime() - new Date(b.start_time).getTime())
    .slice(0, 3);

  return (
    <div className="min-h-screen pb-20 space-y-8 animate-in fade-in duration-700">
      {/* Dynamic Header */}
      <header className="relative overflow-hidden bg-white/40 backdrop-blur-xl border border-white/40 rounded-[32px] p-8 lg:p-12 shadow-2xl shadow-slate-200/50">
        <div className="absolute top-0 right-0 p-8">
          <div className="flex -space-x-3">
            {[1, 2, 3, 4].map(i => (
              <div key={i} className="w-10 h-10 rounded-full border-2 border-white bg-slate-100 flex items-center justify-center overflow-hidden grayscale hover:grayscale-0 transition-all cursor-pointer">
                <img src={`https://i.pravatar.cc/100?u=${i}`} alt="team" />
              </div>
            ))}
            <div className="w-10 h-10 rounded-full border-2 border-white bg-indigo-600 text-white flex items-center justify-center text-xs font-bold">+12</div>
          </div>
        </div>

        <div className="max-w-3xl space-y-6">
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="flex items-center gap-3"
          >
            <div className="px-4 py-1.5 rounded-full bg-indigo-50 text-indigo-600 text-[10px] font-black uppercase tracking-[0.2em] flex items-center gap-2">
              <span className="w-1.5 h-1.5 rounded-full bg-indigo-600 animate-pulse" />
              Live Workspace
            </div>
            <span className="text-slate-300 font-light">/</span>
            <span className="text-slate-400 text-[10px] font-bold uppercase tracking-widest">
              {new Date().toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' })}
            </span>
          </motion.div>

          <h1 className="text-4xl lg:text-6xl font-black tracking-tight text-slate-900 leading-[1.1]">
            How's the mission <br />
            <span className="text-transparent bg-clip-text bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-500">
              looking today, {currentUser?.name?.split(' ')[0] || 'Explorer'}?
            </span>
          </h1>

          <p className="text-lg text-slate-500 font-medium leading-relaxed max-w-xl">
            You've completed <span className="text-indigo-600 font-bold">{stats.completionRate}%</span> of your objectives. {stats.unreadMessages > 0 ? `Don't forget the ${stats.unreadMessages} conversations waiting for you.` : "Your inbox is impressively clear."}
          </p>

          <div className="flex items-center gap-4 pt-4">
            <button
              onClick={() => navigate('/meetings/schedule')}
              className="bg-indigo-600 hover:bg-indigo-700 text-white px-8 py-4 rounded-2xl font-bold text-sm transition-all shadow-xl shadow-indigo-200 hover:-translate-y-1 active:scale-95 flex items-center gap-3"
            >
              <Video size={18} />
              New Meeting
            </button>
            <button
              onClick={() => navigate('/tasks/add')}
              className="bg-white border-2 border-slate-100 hover:border-indigo-100 px-8 py-4 rounded-2xl font-bold text-sm transition-all hover:-translate-y-1 active:scale-95 flex items-center gap-3"
            >
              <ListTodo size={18} className="text-slate-400" />
              Assign Task
            </button>
          </div>
        </div>
      </header>

      {/* Main Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">

        {/* Left Column - Stats & Tasks */}
        <div className="lg:col-span-8 space-y-8">

          {/* Stats Cards */}
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-6">
            <StatMetric
              label="Upcoming Meetings"
              value={stats.meetingsToday}
              icon={CalendarIcon}
              color="from-blue-500 to-indigo-600"
              percentage={Math.min(100, (stats.meetingsToday / 10) * 100)}
            />
            <StatMetric
              label="Tasks Progress"
              value={`${stats.tasksCompleted}/${stats.totalTasks}`}
              subValue={`${stats.completionRate}%`}
              icon={CheckCircle2}
              color="from-emerald-500 to-teal-600"
              percentage={stats.completionRate}
            />
            <StatMetric
              label="Inbox Pulse"
              value={stats.unreadMessages}
              subValue="unread"
              icon={MessageSquare}
              color="from-amber-500 to-orange-600"
              percentage={Math.min(100, (stats.unreadMessages / 20) * 100)}
            />
          </div>

          {/* Task Overview */}
          <div className="bg-white rounded-[32px] p-8 border border-slate-100 shadow-xl shadow-slate-100/50">
            <div className="flex items-center justify-between mb-8">
              <div>
                <h3 className="text-xl font-black text-slate-900 tracking-tight">Active Operations</h3>
                <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mt-1">Managed Tasks</p>
              </div>
              <button onClick={() => navigate('/tasks')} className="p-3 rounded-xl bg-slate-50 text-slate-400 hover:bg-indigo-50 hover:text-indigo-600 transition-all">
                <ArrowUpRight size={20} />
              </button>
            </div>

            <div className="space-y-4">
              {tasks.filter(t => t.status !== "Completed").slice(0, 3).map((task, i) => (
                <motion.div
                  key={task.id}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: i * 0.1 }}
                  className="group flex items-center gap-4 p-5 rounded-2xl border border-slate-50 hover:border-indigo-100 hover:bg-indigo-50/30 transition-all cursor-pointer"
                >
                  <div className={`w-12 h-12 rounded-xl flex items-center justify-center shrink-0 shadow-sm transition-transform group-hover:scale-110 ${task.priority === "High" ? "bg-rose-50 text-rose-500" :
                      task.priority === "Medium" ? "bg-amber-50 text-amber-500" :
                        "bg-emerald-50 text-emerald-500"
                    }`}>
                    <Layers size={20} />
                  </div>
                  <div className="flex-1 min-w-0">
                    <h4 className="text-sm font-bold text-slate-900 group-hover:text-indigo-600 transition-colors truncate">{task.title}</h4>
                    <div className="flex items-center gap-3 mt-1">
                      <span className="text-[10px] font-bold text-slate-400 uppercase tracking-tighter flex items-center gap-1">
                        <Clock size={12} />
                        {task.end_date ? new Date(task.end_date).toLocaleDateString() : 'No deadline'}
                      </span>
                      <span className={`text-[9px] font-black uppercase px-2 py-0.5 rounded-full ${task.priority === "High" ? "bg-rose-100 text-rose-600" :
                          task.priority === "Medium" ? "bg-amber-100 text-amber-600" :
                            "bg-emerald-100 text-emerald-600"
                        }`}>
                        {task.priority}
                      </span>
                    </div>
                  </div>
                  <button className="w-10 h-10 rounded-full border border-slate-100 flex items-center justify-center text-slate-300 group-hover:bg-white group-hover:text-indigo-600 group-hover:border-indigo-100 shadow-sm transition-all">
                    <ChevronRight size={18} />
                  </button>
                </motion.div>
              ))}
              {tasks.length === 0 && (
                <div className="text-center py-10">
                  <p className="text-slate-400 font-bold uppercase tracking-widest text-xs">All clear on the task front</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Right Column - Messages & Meetings */}
        <div className="lg:col-span-4 space-y-8">

          {/* Quick Messages */}
          <div className="bg-slate-900 rounded-[32px] p-8 text-white shadow-2xl shadow-indigo-900/20 relative overflow-hidden">
            <div className="absolute -top-10 -right-10 w-40 h-40 bg-indigo-600/20 rounded-full blur-3xl" />

            <div className="relative">
              <div className="flex items-center justify-between mb-8">
                <div>
                  <h3 className="text-xl font-black tracking-tight">Transmission</h3>
                  <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mt-1">Live Chats</p>
                </div>
                <div className="w-10 h-10 rounded-xl bg-white/10 flex items-center justify-center text-white/60">
                  <MessageSquare size={20} />
                </div>
              </div>

              <div className="space-y-4">
                {chats.map((chat, i) => (
                  <div
                    key={chat.id}
                    onClick={() => navigate('/messages')}
                    className="flex items-center gap-4 p-4 rounded-2xl hover:bg-white/5 transition-all cursor-pointer border border-transparent hover:border-white/10 group"
                  >
                    <div className="relative">
                      <div className="w-11 h-11 rounded-full bg-slate-800 border border-white/10 overflow-hidden">
                        <img src={`https://i.pravatar.cc/100?u=${chat.id}`} alt={chat.name} />
                      </div>
                      {chat.status === 'online' && (
                        <div className="absolute bottom-0 right-0 w-3 h-3 bg-emerald-500 border-2 border-slate-900 rounded-full" />
                      )}
                    </div>
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between mb-0.5">
                        <h4 className="text-xs font-bold truncate group-hover:text-indigo-400 transition-colors uppercase tracking-wide">{chat.name}</h4>
                        {chat.unread_count && chat.unread_count > 0 ? (
                          <span className="w-4 h-4 rounded-full bg-indigo-500 text-[10px] font-bold flex items-center justify-center">{chat.unread_count}</span>
                        ) : (
                          <span className="text-[10px] text-slate-500 font-bold uppercase">{chat.last_message_time ? 'Recent' : ''}</span>
                        )}
                      </div>
                      <p className="text-[11px] text-slate-400 truncate font-medium">{chat.last_message || 'No messages yet'}</p>
                    </div>
                  </div>
                ))}
              </div>

              <button
                onClick={() => navigate('/messages')}
                className="w-full mt-6 py-4 rounded-2xl bg-white/5 hover:bg-white/10 text-white/60 hover:text-white text-xs font-bold uppercase tracking-widest transition-all border border-white/5"
              >
                Enter Terminal
              </button>
            </div>
          </div>

          {/* Upcoming Schedule */}
          <div className="bg-white rounded-[32px] p-8 border border-slate-100 shadow-xl shadow-slate-100/50">
            <div className="flex items-center justify-between mb-8">
              <div>
                <h3 className="text-xl font-black text-slate-900 tracking-tight">Sync Ups</h3>
                <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mt-1">Upcoming Meetings</p>
              </div>
            </div>

            <div className="space-y-6">
              {upcomingMeetings.map((meeting, i) => (
                <div key={meeting.id} className="relative pl-6 border-l-2 border-slate-100 hover:border-indigo-400 transition-colors group">
                  <div className="absolute -left-[5px] top-0 w-2 h-2 rounded-full bg-slate-200 group-hover:bg-indigo-400 transition-colors" />
                  <div className="space-y-1">
                    <p className="text-[10px] font-black text-indigo-600 uppercase tracking-wider">
                      {new Date(meeting.start_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </p>
                    <h4 className="text-[13px] font-bold text-slate-900 group-hover:text-indigo-600 transition-colors">{meeting.title}</h4>
                    <div className="flex items-center gap-2 text-[10px] text-slate-400 font-bold uppercase">
                      <Video size={12} />
                      Virtual Meet
                    </div>
                  </div>
                </div>
              ))}
              {upcomingMeetings.length === 0 && (
                <p className="text-center py-6 text-slate-400 text-xs font-bold uppercase tracking-widest">Clear Schedule</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

