import React, { useState, DragEvent, ChangeEvent } from "react";
import { Upload, FileText, ArrowLeft, Activity, Shield, Zap, Sparkles, CheckCircle2, Loader2, Database, ArrowUpRight } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { fileService } from "../../services/fileService";
import { motion, AnimatePresence } from "framer-motion";

const UploadFilePage: React.FC = () => {
  const navigate = useNavigate();
  const [dragActive, setDragActive] = useState(false);
  const [fileName, setFileName] = useState<string | null>(null);
  const [file, setFile] = useState<File | null>(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [uploading, setUploading] = useState(false);

  const handleDrag = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFile(e.dataTransfer.files[0]);
      setFileName(e.dataTransfer.files[0].name);
    }
  };

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
      setFileName(e.target.files[0].name);
    }
  };

  const handleSubmit = async () => {
    if (!file || !title) return;
    setUploading(true);
    try {
      await fileService.uploadFile(file, title, description);
      navigate("/files");
    } catch (error) {
      console.error("Upload failed", error);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="min-h-screen bg-white text-black p-10 lg:p-16 animate-fade-in font-sans relative">
      <div className="max-w-[1000px] mx-auto">

        {/* Header Navigation */}
        <div className="flex items-center justify-between mb-20 border-b border-zinc-100 pb-10">
          <button
            onClick={() => navigate("/files")}
            className="flex items-center gap-4 text-zinc-300 hover:text-black transition-all group"
          >
            <ArrowLeft size={18} className="group-hover:-translate-x-1 transition-transform" />
            <span className="text-[10px] font-black uppercase tracking-[0.2em]">RETURN TO ARCHIVE</span>
          </button>

          <div className="flex items-center gap-4 text-zinc-400">
            <Database size={14} className="text-zinc-200" />
            <span className="text-[10px] font-black uppercase tracking-[0.3em]">RESOURCE INJECTION</span>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-12 gap-16 items-start">
          {/* Left: Info */}
          <div className="lg:col-span-4 space-y-12">
            <div>
              <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">TRANSMISSION / CORE</p>
              <h1 className="text-5xl font-bold tracking-tighter leading-none uppercase mb-6">DATA<br />UPLOAD</h1>
              <p className="text-[11px] font-medium text-zinc-500 uppercase tracking-tight leading-relaxed">
                SECURELY INJECT MISSION-CRITICAL ASSETS INTO THE ENCRYPTED WORKSPACE VAULT.
              </p>
            </div>

            <div className="space-y-px bg-zinc-100 border border-zinc-100">
              <div className="flex items-start gap-6 p-8 bg-white">
                <Zap size={18} className="text-black shrink-0 mt-1" />
                <div>
                  <p className="text-[10px] font-black text-black uppercase tracking-widest mb-1.5">HIGH VELOCITY</p>
                  <p className="text-[10px] font-bold text-zinc-400 uppercase tracking-tight leading-snug">OPTIMIZED CLOUD DELIVERY FLOW.</p>
                </div>
              </div>
              <div className="flex items-start gap-6 p-8 bg-white">
                <Shield size={18} className="text-black shrink-0 mt-1" />
                <div>
                  <p className="text-[10px] font-black text-black uppercase tracking-widest mb-1.5">SECURE MATRIX</p>
                  <p className="text-[10px] font-bold text-zinc-400 uppercase tracking-tight leading-snug">END-TO-END OPERATIONAL ENCRYPTION.</p>
                </div>
              </div>
            </div>
          </div>

          {/* Right: Dropzone & Form */}
          <div className="lg:col-span-8">
            <div className="bg-white border border-zinc-100 p-12 lg:p-16 shadow-2xl relative">
              <div className="absolute -top-px -left-px w-10 h-10 border-t border-l border-black" />
              <div className="absolute -bottom-px -right-px w-10 h-10 border-b border-r border-black" />

              {/* Drag & Drop Zone */}
              <div
                className={`relative border-2 border-dashed p-16 text-center transition-all group overflow-hidden mb-16 ${dragActive ? "border-black bg-zinc-50" : "border-zinc-100 bg-white hover:border-zinc-200"
                  }`}
                onDragEnter={handleDrag}
                onDragOver={handleDrag}
                onDragLeave={handleDrag}
                onDrop={handleDrop}
              >
                <div className={`w-20 h-20 flex items-center justify-center mx-auto mb-10 transition-all ${fileName ? 'bg-black text-white' : 'bg-zinc-50 text-black group-hover:bg-zinc-100'
                  }`}>
                  <Upload size={28} />
                </div>

                <h3 className="text-2xl font-bold text-black uppercase tracking-tighter mb-4">
                  {fileName ? "MANIFEST DETECTED" : "DATA DROPZONE"}
                </h3>
                <p className="text-[11px] font-bold text-zinc-400 uppercase tracking-[0.2em] max-w-xs mx-auto mb-10 leading-relaxed">
                  {fileName ? fileName : "DRAG AND DROP ASSETS OR SCAN LOCAL REPOSITORY."}
                </p>

                {!fileName && (
                  <div>
                    <label
                      htmlFor="fileUpload"
                      className="px-10 py-4 bg-white border border-black text-black text-[10px] font-black tracking-widest uppercase hover:bg-zinc-50 cursor-pointer transition-all inline-flex items-center"
                    >
                      BROWSE SYSTEM
                    </label>
                    <input id="fileUpload" type="file" className="hidden" onChange={handleFileChange} />
                  </div>
                )}
              </div>

              {/* Metadata Section */}
              <div className="space-y-10">
                <div className="space-y-4">
                  <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">ASSET IDENTIFIER</label>
                  <div className="border-b border-zinc-200 focus-within:border-black transition-all">
                    <input
                      type="text"
                      placeholder="E.G. PERFORMANCE_MATRIX_2024"
                      value={title}
                      onChange={(e) => setTitle(e.target.value)}
                      className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none placeholder:text-zinc-100 uppercase"
                    />
                  </div>
                </div>

                <div className="space-y-4">
                  <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">CONTEXTUAL METADATA</label>
                  <div className="border-b border-zinc-200 focus-within:border-black transition-all">
                    <textarea
                      placeholder="ENTER TACTICAL DATA BRIEFING..."
                      value={description}
                      onChange={(e) => setDescription(e.target.value)}
                      rows={4}
                      className="w-full bg-transparent py-5 text-[11px] font-black tracking-widest text-black outline-none placeholder:text-zinc-100 uppercase resize-none leading-relaxed"
                    />
                  </div>
                </div>

                <div className="pt-10 flex items-center justify-end gap-10">
                  <button
                    onClick={() => navigate("/files")}
                    className="text-[10px] font-black text-zinc-300 hover:text-black transition-colors uppercase tracking-[0.3em]"
                  >
                    ABORT PROTOCOL
                  </button>
                  <button
                    onClick={handleSubmit}
                    disabled={!fileName || !title || uploading}
                    className="flex items-center gap-6 px-12 py-5 bg-black text-white text-[11px] font-black tracking-[0.4em] uppercase hover:bg-zinc-800 transition-all disabled:opacity-20 active:scale-95"
                  >
                    {uploading ? <Loader2 size={18} className="animate-spin" /> : (
                      <>
                        ESTABLISH UPLINK
                        <ArrowUpRight size={18} />
                      </>
                    )}
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UploadFilePage;
