import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { authService } from "../../services/authService";
import { Mail, Phone, MapPin, Briefcase, Camera, Save, Loader2, Lock, Star, Shield, Activity, Zap, ArrowRight, UserCheck } from "lucide-react";

const ProfilePage: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [profile, setProfile] = useState<any>({
    name: "",
    email: "",
    role: "Operative",
    job_title: "",
    avatar: "https://i.pravatar.cc/150?u=a042581f4e29026704d",
    phone: "",
    address: "",
    bio: "",
    two_factor_enabled: false
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userData = await authService.getCurrentUser();
        setProfile({
          ...userData,
          avatar: userData.avatar || "https://i.pravatar.cc/150?u=a042581f4e29026704d"
        });
      } catch (error) {
        console.error("Failed to load profile", error);
      } finally {
        setLoading(false);
      }
    };
    fetchUser();
  }, []);

  const handleSave = async () => {
    try {
      setSaving(true);
      const { id, email, hashed_password, last_seen, status, ...updateData } = profile;
      await authService.updateProfile(updateData);
      setIsEditing(false);
    } catch (error) {
      console.error("Failed to update profile", error);
    } finally {
      setSaving(false);
    }
  };

  const fileInputRef = React.useRef<HTMLInputElement>(null);

  const handleImageClick = () => {
    if (isEditing) {
      fileInputRef.current?.click();
    }
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfile((prev: any) => ({ ...prev, avatar: reader.result as string }));
      };
      reader.readAsDataURL(file);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-[#fcfdfe]">
        <Loader2 className="w-10 h-10 text-indigo-600 animate-spin" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#fcfdfe] text-slate-900 font-sans">
      <div className="max-w-[1200px] mx-auto px-6 py-10 lg:py-12 relative z-10">

        {/* Header */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
          <div>
            <h1 className="text-3xl font-bold tracking-tight text-slate-900 mb-1">Profile</h1>
            <p className="text-slate-500 font-medium">Manage your personal identity and professional details.</p>
          </div>

          <div className="flex items-center gap-3">
            {isEditing && (
              <button
                onClick={() => setIsEditing(false)}
                className="px-5 py-2.5 bg-white border border-slate-200 text-slate-500 rounded-xl font-bold text-sm hover:text-slate-900 transition-all"
              >
                Cancel
              </button>
            )}
            <button
              onClick={() => isEditing ? handleSave() : setIsEditing(true)}
              disabled={saving}
              className={`flex items-center justify-center gap-2 px-6 py-3 rounded-xl font-bold text-sm transition-all shadow-lg ${isEditing ? 'bg-indigo-600 text-white shadow-indigo-100' : 'bg-slate-900 text-white shadow-slate-200 hover:bg-slate-800'
                }`}
            >
              {saving ? <Loader2 size={18} className="animate-spin" /> : isEditing ? <Save size={18} /> : <Zap size={18} />}
              {saving ? "Saving..." : isEditing ? "Save Profile" : "Edit Profile"}
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">

          {/* Left Column: Avatar & Summary */}
          <div className="space-y-6">
            <div className="bg-white rounded-3xl border border-slate-100 p-8 shadow-sm flex flex-col items-center text-center">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                accept="image/*"
                className="hidden"
              />
              <div
                className={`relative mb-8 ${isEditing ? 'cursor-pointer' : ''}`}
                onClick={handleImageClick}
              >
                <div className="w-40 h-40 rounded-3xl overflow-hidden border-4 border-slate-50 shadow-md">
                  <img
                    src={profile.avatar}
                    alt={profile.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                {isEditing && (
                  <div className="absolute inset-0 flex items-center justify-center bg-black/30 rounded-3xl opacity-0 hover:opacity-100 transition-opacity">
                    <Camera className="text-white w-8 h-8" />
                  </div>
                )}
                <div className="absolute -bottom-2 -right-2 w-10 h-10 bg-indigo-600 text-white rounded-xl flex items-center justify-center border-4 border-white shadow-lg">
                  <Shield size={18} fill="white" />
                </div>
              </div>

              <h2 className="text-xl font-bold text-slate-900 mb-1">{profile.name}</h2>
              <p className="text-sm font-semibold text-indigo-600 mb-6 uppercase tracking-wider">{profile.job_title || profile.role}</p>

              <div className="w-full pt-6 border-t border-slate-50 space-y-4">
                <div className="flex items-center gap-4 text-left p-4 rounded-2xl bg-slate-50/50">
                  <div className="w-9 h-9 bg-white rounded-lg flex items-center justify-center text-slate-400 border border-slate-100">
                    <Mail size={16} />
                  </div>
                  <div>
                    <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest leading-none mb-1">Email</p>
                    <p className="text-sm font-semibold text-slate-700 truncate max-w-[180px]">{profile.email}</p>
                  </div>
                </div>
                <div className="flex items-center gap-4 text-left p-4 rounded-2xl bg-slate-50/50">
                  <div className="w-9 h-9 bg-white rounded-lg flex items-center justify-center text-slate-400 border border-slate-100">
                    <Briefcase size={16} />
                  </div>
                  <div>
                    <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest leading-none mb-1">Access Level</p>
                    <p className="text-sm font-semibold text-slate-700">{profile.role}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Right Column: Detailed Info */}
          <div className="lg:col-span-2 space-y-8">
            <div className="bg-white rounded-3xl border border-slate-100 shadow-sm overflow-hidden">
              <div className="px-8 py-6 bg-slate-50/50 border-b border-slate-50">
                <h3 className="text-lg font-bold text-slate-900">Personal Details</h3>
                <p className="text-xs font-semibold text-slate-400 mt-0.5">Update your professional information and contact details.</p>
              </div>
              <div className="p-8 space-y-8">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="space-y-2">
                    <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Display Name</label>
                    <input
                      type="text"
                      disabled={!isEditing}
                      value={profile.name}
                      onChange={(e) => setProfile({ ...profile, name: e.target.value })}
                      className="w-full px-4 py-3 bg-white border border-slate-200 rounded-xl focus:border-indigo-500 focus:outline-none text-slate-700 font-semibold text-sm transition-all disabled:bg-slate-50 disabled:text-slate-400"
                    />
                  </div>
                  <div className="space-y-2">
                    <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Professional Title</label>
                    <input
                      type="text"
                      disabled={!isEditing}
                      placeholder="e.g. Senior Developer"
                      value={profile.job_title || ''}
                      onChange={(e) => setProfile({ ...profile, job_title: e.target.value })}
                      className="w-full px-4 py-3 bg-white border border-slate-200 rounded-xl focus:border-indigo-500 focus:outline-none text-slate-700 font-semibold text-sm transition-all disabled:bg-slate-50 disabled:text-slate-400"
                    />
                  </div>
                  <div className="space-y-2">
                    <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Email Address (Locked)</label>
                    <div className="relative">
                      <input
                        type="email"
                        disabled={true}
                        value={profile.email}
                        className="w-full px-4 py-3 bg-slate-50 border border-slate-100 rounded-xl text-slate-400 font-semibold text-sm cursor-not-allowed"
                      />
                      <Lock size={14} className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-200" />
                    </div>
                  </div>
                  <div className="space-y-2">
                    <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Mobile Contact</label>
                    <div className="relative">
                      <Phone className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300" size={14} />
                      <input
                        type="text"
                        disabled={!isEditing}
                        placeholder="+1 (000) 000-0000"
                        value={profile.phone || ''}
                        onChange={(e) => setProfile({ ...profile, phone: e.target.value })}
                        className="w-full pl-11 pr-4 py-3 bg-white border border-slate-200 rounded-xl focus:border-indigo-500 focus:outline-none text-slate-700 font-semibold text-sm transition-all disabled:bg-slate-50 disabled:text-slate-400"
                      />
                    </div>
                  </div>
                  <div className="md:col-span-2 space-y-2">
                    <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Biography / Expertise</label>
                    <textarea
                      rows={4}
                      disabled={!isEditing}
                      value={profile.bio || ''}
                      onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                      placeholder="Describe your background and core competencies..."
                      className="w-full px-4 py-3 bg-white border border-slate-200 rounded-2xl focus:border-indigo-500 focus:outline-none text-slate-700 font-semibold text-sm transition-all disabled:bg-slate-50 disabled:text-slate-400 resize-none"
                    />
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-slate-900 rounded-3xl p-8 text-white shadow-xl relative overflow-hidden flex flex-col md:flex-row items-center justify-between gap-8">
              <div className="flex items-center gap-6 relative z-10 text-center md:text-left">
                <div className="w-14 h-14 bg-white/10 rounded-2xl flex items-center justify-center text-indigo-400 shadow-inner">
                  <Lock size={24} />
                </div>
                <div>
                  <h4 className="text-lg font-bold mb-1">Account Security</h4>
                  <p className="text-xs font-medium text-slate-400 leading-relaxed uppercase tracking-wider">Two-factor authentication â€¢ Neural encryption active</p>
                </div>
              </div>
              <button className="px-6 py-3 bg-white text-slate-900 rounded-xl font-bold text-xs hover:bg-slate-100 transition-all flex items-center gap-2 group shrink-0">
                Audit Settings <ArrowRight size={14} className="group-hover:translate-x-1 transition-transform" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
