import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Users, Mail, Shield, MoreVertical, RefreshCw, Activity, Zap, Star, ShieldCheck, Plus, Search, Loader2, ArrowUpRight } from "lucide-react";
import { userService, User } from "../../services/userService";

const TeamPage: React.FC = () => {
    const [members, setMembers] = useState<User[]>([]);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState("");

    const fetchTeam = async () => {
        try {
            setLoading(true);
            const data = await userService.getAllUsers();
            setMembers(data);
        } catch (error) {
            console.error("Failed to fetch team", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchTeam();
        const interval = setInterval(fetchTeam, 60000);
        return () => clearInterval(interval);
    }, []);

    const onlineCount = members.filter(m => m.status === 'Online').length;
    const adminCount = members.filter(m => m.role === 'Admin').length;

    const getInitials = (name: string) => {
        return name?.split(" ").map(n => n[0]).join("").toUpperCase().substring(0, 2) || "U";
    };

    const filteredMembers = members.filter(m =>
        m.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        m.email?.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="min-h-screen bg-white text-black p-10 lg:p-16 animate-fade-in font-sans relative">
            <div className="max-w-[1200px] mx-auto">

                {/* Header */}
                <div className="flex flex-col md:flex-row md:items-end justify-between gap-10 mb-20 border-b border-zinc-100 pb-12">
                    <div>
                        <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">OPERATIVES / SQUAD</p>
                        <h1 className="text-5xl font-bold tracking-tighter leading-none uppercase">TEAM</h1>
                    </div>

                    <div className="flex items-center gap-6">
                        <div className="relative border-b border-zinc-200 focus-within:border-black transition-all">
                            <Search className="absolute left-0 top-1/2 -translate-y-1/2 text-zinc-300" size={14} />
                            <input
                                className="w-64 bg-transparent border-none py-3 pl-8 pr-4 text-[11px] font-bold tracking-widest text-black focus:ring-0 outline-none transition-all placeholder:text-zinc-200 uppercase"
                                placeholder="SEARCH PERSONNEL..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                        </div>
                        <button
                            onClick={fetchTeam}
                            disabled={loading}
                            className="p-4 bg-white border border-zinc-100 text-zinc-300 hover:text-black transition-all"
                        >
                            <RefreshCw size={18} className={loading ? "animate-spin" : ""} />
                        </button>
                        <button
                            className="flex items-center gap-4 px-10 py-4 bg-black text-white hover:bg-zinc-800 transition-all active:scale-95"
                        >
                            <span className="text-[11px] font-black tracking-widest uppercase">INVITE</span>
                            <Plus size={16} />
                        </button>
                    </div>
                </div>

                {/* Stats Grid */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-px bg-zinc-100 border border-zinc-100 mb-20">
                    <div className="bg-white p-10 lg:p-12 relative group overflow-hidden">
                        <Users size={18} className="text-zinc-100 mb-10" />
                        <div className="flex items-end justify-between">
                            <div>
                                <h3 className="text-5xl font-bold tracking-tighter text-black leading-none">{members.length}</h3>
                                <p className="text-[9px] font-black text-zinc-300 tracking-[0.2em] uppercase mt-4">TOTAL PERSONNEL</p>
                            </div>
                        </div>
                    </div>

                    <div className="bg-black p-10 lg:p-12 relative group overflow-hidden">
                        <Activity size={18} className="text-zinc-800 mb-10" />
                        <div className="flex items-end justify-between">
                            <div>
                                <h3 className="text-5xl font-bold tracking-tighter text-white leading-none">{onlineCount}</h3>
                                <p className="text-[9px] font-black text-zinc-600 tracking-[0.2em] uppercase mt-4">ACTIVE SESSIONS</p>
                            </div>
                        </div>
                    </div>

                    <div className="bg-white p-10 lg:p-12 relative group overflow-hidden">
                        <ShieldCheck size={18} className="text-zinc-100 mb-10" />
                        <div className="flex items-end justify-between">
                            <div>
                                <h3 className="text-5xl font-bold tracking-tighter text-black leading-none">{adminCount}</h3>
                                <p className="text-[9px] font-black text-zinc-300 tracking-[0.2em] uppercase mt-4">CORE DIRECTORS</p>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Team Grid */}
                <div className="space-y-12">
                    <div className="flex items-end justify-between border-b border-zinc-100 pb-6">
                        <div>
                            <h3 className="text-2xl font-bold tracking-tighter uppercase leading-none">PERSONNEL MANIFEST</h3>
                            <p className="text-[10px] font-bold tracking-widest text-zinc-400 uppercase mt-2">Active Resource Grid</p>
                        </div>
                    </div>

                    {loading && members.length === 0 ? (
                        <div className="py-40 text-center">
                            <Loader2 className="w-8 h-8 text-black animate-spin mx-auto mb-4" />
                            <p className="text-[10px] font-black tracking-widest text-zinc-400 uppercase">SYNCHRONIZING MANIFEST</p>
                        </div>
                    ) : (
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-px bg-zinc-100 border border-zinc-100">
                            {filteredMembers.map((member) => (
                                <div
                                    key={member.id}
                                    className="bg-white p-10 lg:p-12 group relative transition-all hover:bg-zinc-50"
                                >
                                    <button className="absolute top-10 right-10 p-2 text-zinc-200 hover:text-black opacity-0 group-hover:opacity-100 transition-all">
                                        <MoreVertical size={16} />
                                    </button>

                                    <div className="flex items-center gap-8 mb-10">
                                        <div className="relative shrink-0">
                                            <div className={`w-20 h-20 overflow-hidden bg-zinc-50 border border-zinc-100 transition-all group-hover:bg-black group-hover:text-white ${!member.avatar && 'flex items-center justify-center text-black group-hover:text-white text-xl font-black tracking-tighter uppercase'}`}>
                                                {member.avatar ? (
                                                    <img src={member.avatar} alt={member.name} className="w-full h-full object-cover grayscale group-hover:grayscale-0 transition-all" />
                                                ) : getInitials(member.name)}
                                            </div>
                                            <div className={`absolute -bottom-1 -right-1 w-3 h-3 border-2 border-white ${member.status === 'Online' ? 'bg-black' : 'bg-zinc-200'}`} />
                                        </div>
                                        <div className="min-w-0">
                                            <h3 className="text-xl font-bold text-black uppercase tracking-tight truncate mb-1.5">{member.name || 'OPERATIVE'}</h3>
                                            <p className="text-[10px] font-bold text-zinc-400 truncate uppercase tracking-widest">{member.email}</p>
                                        </div>
                                    </div>

                                    <div className="flex items-center gap-6 mb-10">
                                        <div className={`px-4 py-1.5 text-[9px] font-black uppercase tracking-[0.2em] transition-all ${member.role === 'Admin' ? 'bg-black text-white' : 'bg-zinc-100 text-black'}`}>
                                            {member.role || 'MEMBER'}
                                        </div>
                                        <span className={`text-[9px] font-black uppercase tracking-[0.2em] ${member.status === 'Online' ? 'text-black' : 'text-zinc-300'}`}>
                                            {member.status === 'Online' ? 'ACTIVE' : 'IDLE'}
                                        </span>
                                    </div>

                                    <div className="pt-8 border-t border-zinc-50 flex items-center justify-between">
                                        <button className="text-[10px] font-black text-black uppercase tracking-widest hover:underline underline-offset-8">VIEW DOSSIER</button>
                                        <button className="p-3 text-zinc-200 hover:text-black transition-all"><Mail size={16} /></button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default TeamPage;
