import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  FileText,
  FileSpreadsheet,
  Image as ImageIcon,
  File,
  Video,
  Upload,
  Search,
  MoreHorizontal,
  Download,
  Trash2,
  Clock,
  ChevronRight,
  HardDrive,
  Zap,
  Layers,
  Activity,
  Shield,
  Star,
  Plus
} from "lucide-react";
import { fileService, FileData } from "../../services/fileService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";
import { motion, AnimatePresence } from "framer-motion";

const FilesPage: React.FC = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [recentFiles, setRecentFiles] = useState<FileData[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);

  const fetchFiles = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const files = await fileService.getAllFiles();
      setRecentFiles(files);
    } catch (error) {
      console.error("Failed to load files", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  const getFileCounts = (files: FileData[]) => {
    const counts = { word: 0, pdf: 0, spread: 0, image: 0, other: 0 };
    files.forEach(f => {
      const ext = f.filename.split('.').pop()?.toLowerCase() || '';
      if (['doc', 'docx'].includes(ext)) counts.word++;
      else if (['pdf'].includes(ext)) counts.pdf++;
      else if (['xls', 'xlsx', 'csv'].includes(ext)) counts.spread++;
      else if (['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext)) counts.image++;
      else counts.other++;
    });
    return counts;
  };

  const counts = getFileCounts(recentFiles);

  const fileCategories = [
    { id: "word", name: "Documents", count: counts.word, icon: <FileText size={20} />, color: "from-blue-600 to-indigo-600", light: "bg-blue-50 text-blue-600" },
    { id: "pdf", name: "Archival", count: counts.pdf, icon: <FileText size={20} />, color: "from-rose-600 to-orange-600", light: "bg-rose-50 text-rose-600" },
    { id: "spread", name: "Data Matrix", count: counts.spread, icon: <FileSpreadsheet size={20} />, color: "from-emerald-600 to-teal-600", light: "bg-emerald-50 text-emerald-600" },
    { id: "image", name: "Visuals", count: counts.image, icon: <ImageIcon size={20} />, color: "from-purple-600 to-fuchsia-600", light: "bg-purple-50 text-purple-600" },
    { id: "other", name: "Unclassified", count: counts.other, icon: <File size={20} />, color: "from-slate-600 to-slate-900", light: "bg-slate-50 text-slate-600" },
  ];

  useEffect(() => {
    fetchFiles();

    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (message.type === 'file') {
        fetchFiles(false);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const formatSize = (bytes: number) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const mapFileType = (ftype: string | undefined, filename: string) => {
    const ext = filename.split('.').pop()?.toLowerCase() || '';
    if (['doc', 'docx'].includes(ext)) return <FileText size={24} className="text-blue-600" />;
    if (['pdf'].includes(ext)) return <FileText size={24} className="text-rose-600" />;
    if (['xls', 'xlsx', 'csv'].includes(ext)) return <FileSpreadsheet size={24} className="text-emerald-600" />;
    if (['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext)) return <ImageIcon size={24} className="text-purple-600" />;
    if (['mp4', 'mov', 'avi'].includes(ext)) return <Video size={24} className="text-indigo-600" />;
    return <File size={24} className="text-slate-400" />;
  };

  const filteredFiles = recentFiles.filter(file => {
    const matchesSearch = file.filename.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (file.title && file.title.toLowerCase().includes(searchQuery.toLowerCase()));

    if (!selectedCategory) return matchesSearch;

    const ext = file.filename.split('.').pop()?.toLowerCase() || '';
    let isCategoryMatch = false;

    switch (selectedCategory) {
      case 'word': isCategoryMatch = ['doc', 'docx'].includes(ext); break;
      case 'pdf': isCategoryMatch = ['pdf'].includes(ext); break;
      case 'spread': isCategoryMatch = ['xls', 'xlsx', 'csv'].includes(ext); break;
      case 'image': isCategoryMatch = ['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext); break;
      case 'other':
        isCategoryMatch = !['doc', 'docx', 'pdf', 'xls', 'xlsx', 'csv', 'jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext);
        break;
      default: isCategoryMatch = true;
    }

    return matchesSearch && isCategoryMatch;
  });

  const toggleCategory = (id: string) => {
    setSelectedCategory(selectedCategory === id ? null : id);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    show: {
      opacity: 1,
      transition: { staggerChildren: 0.1 }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    show: { opacity: 1, y: 0 }
  };

  return (
    <div className="min-h-screen bg-[#f8fafc] text-slate-900 p-8 lg:p-12 overflow-hidden relative font-sans">
      {/* Background Orbs */}
      <div className="fixed top-0 right-0 w-[700px] h-[700px] bg-indigo-50/50 rounded-full blur-[150px] -mr-64 -mt-64 animate-pulse" />
      <div className="fixed bottom-0 left-0 w-[500px] h-[500px] bg-sky-50/50 rounded-full blur-[120px] -ml-48 -mb-48" />

      <div className="max-w-[1400px] mx-auto relative z-10">

        {/* Elite Header */}
        <div className="flex flex-col lg:flex-row lg:items-end justify-between gap-12 mb-20">
          <div className="space-y-6">
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              className="inline-flex items-center gap-2 px-4 py-2 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-[0.3em] border border-indigo-100 shadow-sm"
            >
              <HardDrive size={14} className="fill-indigo-600" /> Secure Assets
            </motion.div>
            <h1 className="text-7xl font-[1000] tracking-tight leading-[0.95] text-slate-900">
              Data <span className="gradient-text">Repository.</span>
            </h1>
            <p className="text-slate-500 font-medium text-xl max-w-xl leading-relaxed">
              Centralized intelligence hub for mission-critical assets, encrypted and distributed across the team matrix.
            </p>
          </div>

          <div className="flex items-center gap-6">
            <div className="relative group">
              <Search className="absolute left-6 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={18} />
              <input
                className="w-72 bg-white border-2 border-slate-100 rounded-[24px] py-4 pl-14 pr-8 text-sm font-bold text-slate-900 focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-300 shadow-sm"
                placeholder="Query assets..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => navigate("/files/upload")}
              className="h-16 px-12 bg-slate-900 text-white rounded-[28px] font-black uppercase tracking-widest text-[10px] shadow-2xl shadow-slate-200 hover:bg-indigo-600 transition-all flex items-center gap-4"
            >
              <Plus size={20} className="stroke-[3]" /> Upload Data
            </motion.button>
          </div>
        </div>

        {/* Categories Grid */}
        <div className="grid grid-cols-2 md:grid-cols-5 gap-6 mb-16">
          {fileCategories.map((cat) => (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              key={cat.id}
              onClick={() => toggleCategory(cat.id)}
              className={`cursor-pointer group p-8 rounded-[40px] border-2 transition-all duration-500 relative overflow-hidden ${selectedCategory === cat.id
                  ? 'bg-slate-900 border-slate-900 shadow-2xl scale-105'
                  : 'bg-white border-slate-50 shadow-xl shadow-slate-200/40 hover:border-indigo-100'
                }`}
            >
              {selectedCategory === cat.id && (
                <div className="absolute top-0 right-0 w-24 h-24 bg-white/5 rounded-full blur-3xl -mr-12 -mt-12" />
              )}

              <div className={`w-14 h-14 rounded-2xl flex items-center justify-center mb-6 transition-transform group-hover:scale-110 group-hover:rotate-6 ${selectedCategory === cat.id ? 'bg-white/10 text-white' : cat.light
                }`}>
                {cat.icon}
              </div>

              <h3 className={`text-lg font-[1000] tracking-tight leading-none uppercase ${selectedCategory === cat.id ? 'text-white' : 'text-slate-900'}`}>{cat.name}</h3>
              <p className={`text-[10px] font-black uppercase tracking-widest mt-2 ${selectedCategory === cat.id ? 'text-white/40' : 'text-slate-400'}`}>
                {cat.count} Operations
              </p>

              {selectedCategory === cat.id && (
                <motion.div layoutId="catDot" className="absolute bottom-4 right-4 w-2 h-2 bg-indigo-500 rounded-full" />
              )}
            </motion.div>
          ))}
        </div>

        {/* File Table / Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-16 items-start">

          {/* Asset List */}
          <div className="lg:col-span-8 space-y-8">
            <div className="flex items-center justify-between border-b border-slate-100 pb-8">
              <h2 className="text-xs font-black uppercase tracking-[0.4em] text-slate-400">
                {selectedCategory ? `${fileCategories.find(c => c.id === selectedCategory)?.name} Stream` : "Transmission Feed"}
              </h2>
              {selectedCategory && (
                <button onClick={() => setSelectedCategory(null)} className="text-[10px] font-black text-indigo-600 uppercase tracking-widest hover:underline">Clear Protocol</button>
              )}
            </div>

            {loading ? (
              <div className="py-20 flex flex-col items-center">
                <div className="w-16 h-16 bg-slate-100 rounded-[32px] flex items-center justify-center animate-pulse">
                  <Activity size={32} className="text-slate-300" />
                </div>
              </div>
            ) : filteredFiles.length === 0 ? (
              <div className="p-24 bg-white/50 backdrop-blur-sm border-2 border-dashed border-slate-100 rounded-[56px] text-center">
                <Layers size={40} className="text-slate-200 mx-auto mb-8" />
                <h3 className="text-2xl font-[1000] text-slate-900 tracking-tight">Zero Assets Detected</h3>
                <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-2">Initialize upload to start sequence.</p>
              </div>
            ) : (
              <motion.div
                variants={containerVariants}
                initial="hidden"
                animate="show"
                className="space-y-4"
              >
                {filteredFiles.map((file) => (
                  <motion.div
                    key={file.id}
                    variants={itemVariants}
                    whileHover={{ scale: 1.02, x: 8 }}
                    className="p-6 bg-white border-2 border-slate-50 rounded-[32px] hover:border-indigo-100 hover:shadow-2xl transition-all flex items-center justify-between group cursor-pointer"
                  >
                    <div className="flex items-center gap-6">
                      <div className="w-16 h-16 rounded-2xl bg-white border-2 border-slate-50 flex items-center justify-center shadow-lg group-hover:bg-slate-900 group-hover:border-slate-900 group-hover:text-white transition-all duration-300">
                        {mapFileType(file.file_type, file.filename)}
                      </div>
                      <div>
                        <h4 className="text-lg font-[1000] text-slate-900 tracking-tight leading-none truncate max-w-[300px] group-hover:text-indigo-600 transition-colors">
                          {file.filename}
                        </h4>
                        <div className="flex items-center gap-4 mt-2">
                          <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">{file.title || "Unknown Package"}</span>
                          <span className="w-1 h-1 bg-slate-200 rounded-full" />
                          <span className="text-[10px] font-black text-slate-500 uppercase tracking-widest">{formatSize(file.file_size_bytes)}</span>
                          <span className="w-1 h-1 bg-slate-200 rounded-full" />
                          <span className="text-[10px] font-black text-indigo-400 uppercase tracking-widest">{new Date(file.uploaded_at).toLocaleDateString()}</span>
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center gap-3">
                      <button className="p-4 bg-slate-50 text-slate-400 hover:text-indigo-600 hover:bg-indigo-50 rounded-2xl transition-all" title="Download">
                        <Download size={20} />
                      </button>
                      <button className="p-4 bg-slate-50 text-slate-400 hover:text-rose-600 hover:bg-rose-50 rounded-2xl transition-all" title="Delete">
                        <Trash2 size={20} />
                      </button>
                      <button className="p-4 bg-slate-50 text-slate-400 hover:text-slate-900 hover:bg-slate-100 rounded-2xl transition-all">
                        <MoreHorizontal size={20} />
                      </button>
                    </div>
                  </motion.div>
                ))}
              </motion.div>
            )}
          </div>

          {/* Storage Analytics Sidebar */}
          <aside className="lg:col-span-4 space-y-12 sticky top-12">
            <div className="bg-slate-900 rounded-[56px] p-12 text-white shadow-2xl space-y-12 relative overflow-hidden group">
              <div className="absolute top-0 right-0 p-12 opacity-5 scale-150 rotate-12 transition-transform group-hover:rotate-0">
                <Shield size={200} />
              </div>

              <div className="space-y-4">
                <div className="flex items-center gap-3">
                  <Star size={16} className="text-amber-400 fill-amber-400" />
                  <h3 className="text-[10px] font-black uppercase tracking-[0.4em] text-white/40">Vault Analytics</h3>
                </div>
                <p className="text-4xl font-[1000] leading-tight">Storage <br /><span className="text-indigo-400 underline decoration-indigo-600/30 underline-offset-8">Optimized</span></p>
              </div>

              <div className="space-y-8">
                <div>
                  <div className="flex justify-between items-center mb-4">
                    <span className="text-[10px] font-black uppercase tracking-widest text-white/60">Data Allocation</span>
                    <span className="text-xs font-black text-indigo-400 uppercase tracking-widest">42% Used</span>
                  </div>
                  <div className="w-full h-3 bg-white/5 rounded-full overflow-hidden p-1 border border-white/10">
                    <div className="w-[42%] h-full bg-gradient-to-r from-indigo-500 via-violet-500 to-sky-400 rounded-full" />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-6">
                  <div className="p-8 bg-white/5 rounded-[32px] border border-white/5">
