import React from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Bell, CheckCircle, AlertTriangle, XCircle, X } from 'lucide-react';
import { Notification } from '../services/notificationService';

interface NotificationToastProps {
    notification: Notification;
    onClose: () => void;
}

const NotificationToast: React.FC<NotificationToastProps> = ({ notification, onClose }) => {
    const navigate = useNavigate();

    const getIcon = () => {
        switch (notification.type) {
            case 'success': return <CheckCircle className="w-5 h-5 text-emerald-400" />;
            case 'error': return <XCircle className="w-5 h-5 text-rose-400" />;
            case 'warning': return <AlertTriangle className="w-5 h-5 text-amber-400" />;
            default: return <Bell className="w-5 h-5 text-indigo-400" />;
        }
    };

    const getBorderColor = () => {
        switch (notification.type) {
            case 'success': return 'border-emerald-500/20';
            case 'error': return 'border-rose-500/20';
            case 'warning': return 'border-amber-500/20';
            default: return 'border-indigo-500/20';
        }
    };

    return (
        <motion.div
            initial={{ opacity: 0, y: 50, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 20, scale: 0.95 }}
            onClick={() => {
                navigate('/notifications');
                onClose();
            }}
            className={`w-full max-w-sm overflow-hidden bg-slate-900/90 backdrop-blur-xl border ${getBorderColor()} rounded-2xl shadow-2xl shadow-black/40 pointer-events-auto cursor-pointer group hover:bg-slate-800/90 transition-all`}
        >
            <div className="p-4 flex gap-4">
                <div className={`mt-1 flex-shrink-0 w-10 h-10 rounded-xl bg-slate-800 flex items-center justify-center group-hover:bg-slate-700 transition-colors`}>
                    {getIcon()}
                </div>
                <div className="flex-1 min-w-0">
                    <h4 className="text-sm font-semibold text-white truncate">
                        {notification.title}
                    </h4>
                    <p className="mt-1 text-sm text-slate-400 line-clamp-2">
                        {notification.description}
                    </p>
                </div>
                <button
                    onClick={(e) => {
                        e.stopPropagation();
                        onClose();
                    }}
                    className="flex-shrink-0 w-6 h-6 flex items-center justify-center rounded-lg text-slate-500 hover:text-white hover:bg-slate-700 transition-colors"
                >
                    <X className="w-4 h-4" />
                </button>
            </div>
            {/* Progress bar */}
            <motion.div
                initial={{ width: '100%' }}
                animate={{ width: '0%' }}
                transition={{ duration: 5, ease: 'linear' }}
                className={`h-1 bg-gradient-to-r ${notification.type === 'success' ? 'from-emerald-500 to-teal-400' :
                    notification.type === 'error' ? 'from-rose-500 to-pink-400' :
                        notification.type === 'warning' ? 'from-amber-500 to-orange-400' :
                            'from-indigo-500 to-purple-400'
                    }`}
            />
        </motion.div>
    );
};

export default NotificationToast;
