import React, { useState, useEffect } from "react";
import { taskService, Task } from "../../services/taskService";
import { meetingService, Meeting } from "../../services/meetingService";

interface CalendarEvent {
  id: string | number;
  title: string;
  time?: string;
  date: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  isOverdue?: boolean;
}

const CalendarPage: React.FC = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [events, setEvents] = useState<CalendarEvent[]>([]);
  const [loading, setLoading] = useState<boolean>(true);

  const monthNames = [
    "January", "February", "March", "April",
    "May", "June", "July", "August",
    "September", "October", "November", "December"
  ];

  const daysOfWeek = ["S", "M", "T", "W", "T", "F", "S"];

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const startingDay = firstDay.getDay();
  const totalDays = lastDay.getDate();

  const daysArray: number[] = [];
  for (let i = 1; i <= totalDays; i++) daysArray.push(i);

  const today = new Date();
  const todayStr = today.toISOString().split("T")[0];

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [tasks, meetings] = await Promise.all([
          taskService.getTasks(),
          meetingService.getAllMeetings()
        ]);

        const taskEvents: CalendarEvent[] = tasks
          .filter(t => t.due_date) // Only tasks with due dates
          .map(t => ({
            id: `task-${t.id}`,
            title: t.title,
            date: t.due_date ? new Date(t.due_date).toLocaleDateString("en-CA") : "",
            type: "task",
            status: t.status,
            details: t.priority,
            isOverdue: t.is_overdue
          }));

        const meetingEvents: CalendarEvent[] = meetings.map(m => {
          const startDate = new Date(m.start_time);
          const endDate = new Date(m.end_time);
          const timeStr = `${startDate.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} – ${endDate.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;

          return {
            id: `meeting-${m.id}`,
            title: m.title,
            time: timeStr,
            date: startDate.toLocaleDateString("en-CA"),
            type: "meeting",
            details: m.location || "Online"
          };
        });

        setEvents([...taskEvents, ...meetingEvents]);
      } catch (error) {
        console.error("Failed to fetch calendar data", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const selectedDateStr = currentDate.toLocaleDateString("en-CA");
  const dayEvents = events.filter((e) => e.date === selectedDateStr);

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800 p-10">
      <header className="flex justify-between items-center mb-10">
        <h1 className="text-3xl font-bold text-gray-900">Calendar</h1>
        <button
          onClick={() => setCurrentDate(new Date())}
          className="px-4 py-2 text-sm font-medium bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
        >
          Today
        </button>
      </header>

      {/* Month Navigation */}
      <div className="flex items-center justify-between mb-6">
        <button
          onClick={() => setCurrentDate(new Date(year, month - 1, 1))}
          className="text-gray-500 hover:text-blue-600 text-2xl"
        >
          ‹
        </button>
        <h3 className="text-xl font-semibold">
          {monthNames[month]} {year}
        </h3>
        <button
          onClick={() => setCurrentDate(new Date(year, month + 1, 1))}
          className="text-gray-500 hover:text-blue-600 text-2xl"
        >
          ›
        </button>
      </div>

      {/* Calendar Grid */}
      <div className="bg-white rounded-2xl border border-gray-200 p-6 mb-10 shadow-sm">
        <div className="grid grid-cols-7 text-center font-semibold text-gray-500 mb-3">
          {daysOfWeek.map((d, i) => (
            <div key={i}>{d}</div>
          ))}
        </div>

        <div className="grid grid-cols-7 text-center gap-y-2">
          {Array(startingDay)
            .fill(null)
            .map((_, i) => (
              <div key={`empty-${i}`} />
            ))}
          {daysArray.map((day) => {
            // Be careful with timezones here. Creating date from year, month, day is local.
            // toISOString() converts to UTC.
            // We need to ensure we are comparing local dates to local dates string or stick to one.
            // Simplified approach: Construct YYYY-MM-DD manually to match our event parsing.
            const d = new Date(year, month, day);
            // Adjust for timezone offset to get correct YYYY-MM-DD part locally
            const offset = d.getTimezoneOffset() * 60000;
            const localDate = new Date(d.getTime() - offset);
            const dateStr = localDate.toISOString().split("T")[0];

            const isToday = dateStr === todayStr;
            const hasEvent = events.some((e) => e.date === dateStr);
            const isSelected = dateStr === selectedDateStr;

            return (
              <div
                key={day}
                onClick={() => {
                  // We want to set currentDate to this day, preserving local time logic
                  setCurrentDate(new Date(year, month, day));
                }}
                className={`py-3 rounded-lg cursor-pointer text-sm transition relative ${isToday
                  ? "bg-blue-600 text-white font-semibold"
                  : isSelected
                    ? "bg-blue-50 text-blue-600 font-medium border border-blue-200"
                    : "hover:bg-gray-100"
                  }`}
              >
                <div>{day}</div>
                {hasEvent && (
                  <div className={`w-1.5 h-1.5 mx-auto mt-1 rounded-full ${isToday ? "bg-white" : "bg-blue-500"}`}></div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      {/* Schedule List */}
      <div className="bg-white border border-gray-200 rounded-2xl shadow-sm p-6">
        <h2 className="text-xl font-semibold mb-4">
          Schedule for{" "}
          <span className="text-blue-600">
            {currentDate.toDateString()}
          </span>
        </h2>

        {loading ? (
          <div className="text-center py-6 text-gray-500">Loading schedule...</div>
        ) : dayEvents.length > 0 ? (
          <ul className="space-y-3">
            {dayEvents.map((event, i) => (
              <li
                key={event.id}
                className={`flex items-center justify-between p-4 rounded-lg border hover:shadow-sm transition ${event.type === 'meeting' ? 'bg-indigo-50 border-indigo-100' : 'bg-white border-gray-100'
                  }`}
              >
                <div className="flex items-center">
                  <div className={`w-1 h-10 rounded-full mr-4 ${event.type === 'meeting' ? 'bg-indigo-500' : 'bg-green-500'}`}></div>
                  <div>
                    <div className="flex items-center gap-2">
                      <p className="font-medium text-gray-800">{event.title}</p>
                      <span className={`text-xs px-2 py-0.5 rounded-full ${event.type === 'meeting' ? 'bg-indigo-100 text-indigo-700' : 'bg-green-100 text-green-700'}`}>
                        {event.type === 'meeting' ? 'Meeting' : 'Task'}
                      </span>
                    </div>
                    {event.time && <p className="text-sm text-gray-500 mt-1">{event.time}</p>}
                    {event.type === 'task' && event.status && (
                      <p className="text-xs text-gray-400 mt-1 uppercase tracking-wider">{event.status} • {event.details}</p>
                    )}
                  </div>
                </div>
                {event.type === 'meeting' && (
                  <span className="text-indigo-500 text-sm font-semibold">{event.details}</span>
                )}
                {event.type === 'task' && event.isOverdue && (
                  <span className="text-red-500 text-sm font-semibold">Overdue</span>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <div className="text-gray-500 text-center py-6">
            No events scheduled for this date.
          </div>
        )}
      </div>
    </div>
  );
};

export default CalendarPage;
