import React, { useState, DragEvent, ChangeEvent } from "react";
import { Upload, FileText, ArrowLeft } from "lucide-react";
import { useNavigate } from "react-router-dom";

const UploadFilePage: React.FC = () => {
  const navigate = useNavigate();
  const [dragActive, setDragActive] = useState(false);
  const [fileName, setFileName] = useState<string | null>(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  const handleDrag = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFileName(e.dataTransfer.files[0].name);
    }
  };

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFileName(e.target.files[0].name);
    }
  };

  const handleSubmit = () => {
    if (!fileName || !title) {
      alert("Please select a file and enter a title before uploading.");
      return;
    }

    // Simulate successful upload
    alert(`✅ File "${title}" uploaded successfully!`);
    setTimeout(() => navigate("/files"), 1000); // redirect to Files page
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-50 text-gray-900 p-6">
      <div className="w-full max-w-2xl bg-white rounded-2xl shadow-xl p-10 transition-all hover:shadow-2xl">
        {/* Top Bar */}
        <div className="flex items-center justify-between mb-8">
          <h2 className="text-3xl font-bold text-gray-800">Upload File</h2>
          <button
            onClick={() => navigate("/files")}
            className="flex items-center space-x-2 text-sm font-medium text-gray-600 hover:text-blue-600 transition"
          >
            <ArrowLeft className="w-4 h-4" />
            <span>Back to Files</span>
          </button>
        </div>

        <p className="text-gray-500 text-sm mb-6 text-center">
          Drag & drop your file below, or browse manually to upload.
        </p>

        {/* Upload Area */}
        <div
          className={`border-2 border-dashed rounded-xl p-10 text-center transition ${
            dragActive ? "border-blue-400 bg-blue-50" : "border-gray-300 bg-gray-50"
          }`}
          onDragEnter={handleDrag}
          onDragOver={handleDrag}
          onDragLeave={handleDrag}
          onDrop={handleDrop}
        >
          <Upload className="mx-auto text-blue-500 w-12 h-12 mb-4" />
          <p className="text-gray-600 mb-3">
            {fileName ? (
              <>
                <span className="font-semibold text-blue-600">{fileName}</span>{" "}
                ready to upload
              </>
            ) : (
              "Drag and drop a file here or click below"
            )}
          </p>
          <label
            htmlFor="fileUpload"
            className="inline-block bg-blue-600 text-white px-5 py-2 rounded-lg text-sm font-medium hover:bg-blue-700 cursor-pointer transition"
          >
            Browse File
          </label>
          <input
            id="fileUpload"
            type="file"
            className="hidden"
            onChange={handleFileChange}
          />
        </div>

        {/* File Info Fields */}
        <div className="mt-8 space-y-5">
          <div>
            <label className="block text-sm font-semibold mb-2">
              File Title
            </label>
            <input
              type="text"
              placeholder="Enter file title (e.g., Project Report)"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
            />
          </div>

          <div>
            <label className="block text-sm font-semibold mb-2">
              Description
            </label>
            <textarea
              placeholder="Add a short description about this file..."
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows={3}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm resize-none"
            />
          </div>

          <button
            onClick={handleSubmit}
            className="w-full mt-4 flex items-center justify-center space-x-2 bg-blue-600 text-white py-3 rounded-lg text-sm font-medium hover:bg-blue-700 transition"
          >
            <FileText className="w-5 h-5" />
            <span>Upload File</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default UploadFilePage;
