import React, { useState, useEffect } from "react";
import {
  Plus,
  Search,
  Loader2,
  Trash2,
  Edit3,
  Calendar,
  User as UserIcon,
  Filter,
  CheckCircle2,
  Clock,
  AlertCircle,
  ArrowRight,
  History
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("All");

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in_progress" || s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  // üïí Auto-Status Logic based on time
  const getTimeStatus = (dueDate: string | null, status: string) => {
    if (normalizeStatus(status) === "Completed") return { label: "Completed", class: "bg-emerald-50 text-emerald-700 border-emerald-100" };
    if (!dueDate) return { label: "Scheduled", class: "bg-slate-50 text-slate-600 border-slate-100" };

    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const due = new Date(dueDate);
    due.setHours(0, 0, 0, 0);

    if (due < today) return { label: "Overdue", class: "bg-rose-50 text-rose-700 border-rose-100 animate-pulse" };
    if (due.getTime() === today.getTime()) return { label: "Due Today", class: "bg-amber-50 text-amber-700 border-amber-100" };

    return { label: "On Track", class: "bg-indigo-50 text-indigo-700 border-indigo-100" };
  };

  const handleStatusUpdate = async (id: number, newStatus: string) => {
    try {
      await taskService.updateTask(id, { status: newStatus });
      setTasks(prev => prev.map(t => t.id === id ? { ...t, status: newStatus } : t));
    } catch (error) {
      console.error("Failed to update status");
    }
  };

  const handleDelete = async (id: number) => {
    if (!window.confirm("Are you sure you want to delete this task?")) return;
    try {
      await taskService.deleteTask(id);
      setTasks(prev => prev.filter(t => t.id !== id));
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (task.description && task.description.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesStatus = statusFilter === "All" || normalizeStatus(task.status) === statusFilter;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="min-h-screen bg-[#F8FAFC] p-4 md:p-10 font-sans">
      <div className="max-w-7xl mx-auto space-y-8">

        {/* ‚ú® Premium Header */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 pb-2">
          <div className="space-y-1">
            <h1 className="text-3xl font-extrabold text-slate-900 tracking-tight">Task Center</h1>
            <p className="text-slate-500 font-medium">Automatic monitoring of your team milestones.</p>
          </div>
          <button
            onClick={() => navigate("/tasks/new")}
            className="flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-3 rounded-2xl font-bold transition-all shadow-xl shadow-indigo-100 active:scale-95 group"
          >
            <Plus size={20} className="group-hover:rotate-90 transition-transform duration-300" />
            New Task Node
          </button>
        </div>

        {/* üîç Smart Discovery Bar */}
        <div className="bg-white p-2 rounded-3xl shadow-sm border border-slate-100 flex flex-col md:flex-row items-center gap-2">
          <div className="relative flex-1 w-full">
            <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={20} />
            <input
              type="text"
              placeholder="Find task by name, ID or details..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-12 pr-4 py-4 bg-transparent text-slate-700 font-bold outline-none placeholder:text-slate-300"
            />
          </div>
          <div className="flex bg-slate-50 p-1.5 rounded-2xl gap-1 w-full md:w-auto overflow-x-auto">
            {["All", "Upcoming", "In Progress", "Completed"].map(tab => (
              <button
                key={tab}
                onClick={() => setStatusFilter(tab)}
                className={`px-6 py-2.5 rounded-xl text-xs font-black uppercase tracking-widest transition-all whitespace-nowrap ${statusFilter === tab ? 'bg-white text-indigo-600 shadow-sm' : 'text-slate-400 hover:text-slate-600'}`}
              >
                {tab}
              </button>
            ))}
          </div>
        </div>

        {/* üìã Enhanced Task Registry */}
        <div className="space-y-4">
          {loading ? (
            <div className="py-24 flex flex-col items-center justify-center gap-4">
              <Loader2 className="animate-spin text-indigo-600" size={40} />
              <p className="text-slate-400 font-black uppercase tracking-widest text-xs">Syncing Registry...</p>
            </div>
          ) : (
            <AnimatePresence mode="popLayout">
              {filteredTasks.length === 0 ? (
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  className="py-24 bg-white rounded-[40px] border-2 border-dashed border-slate-200 text-center space-y-4"
                >
                  <div className="w-16 h-16 bg-slate-50 rounded-full flex items-center justify-center mx-auto text-slate-300">
                    <AlertCircle size={32} />
                  </div>
                  <div className="space-y-1">
                    <h3 className="text-slate-900 font-black text-xl">NO ACTIVE RECORDS</h3>
                    <p className="text-slate-400 font-medium">Clear filters or create a new entry to begin tracking.</p>
                  </div>
                </motion.div>
              ) : (
                filteredTasks.map((task, idx) => {
                  const timeStatus = getTimeStatus(task.due_date, task.status);
                  return (
                    <motion.div
                      key={task.id}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, scale: 0.95 }}
                      transition={{ delay: idx * 0.05 }}
                      className="bg-white p-6 rounded-[32px] border border-slate-100 shadow-sm hover:shadow-xl hover:shadow-indigo-100/5 transition-all flex flex-col md:flex-row items-center gap-8 group relative overflow-hidden"
                    >
                      <div className={`absolute top-0 left-0 bottom-0 w-1.5 ${normalizeStatus(task.status) === 'Completed' ? 'bg-emerald-500' : 'bg-indigo-500'}`}></div>

                      {/* Status Badge Group */}
                      <div className="w-full md:w-40 flex flex-col gap-2">
                        <div className="flex items-center gap-2">
                          <div className={`w-2 h-2 rounded-full ${normalizeStatus(task.status) === 'Completed' ? 'bg-emerald-500' : normalizeStatus(task.status) === 'In Progress' ? 'bg-amber-500' : 'bg-blue-500'}`}></div>
                          <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">{normalizeStatus(task.status)}</span>
                        </div>
                        <div className={`px-3 py-1.5 rounded-xl border text-[10px] font-black uppercase tracking-widest text-center ${timeStatus.class}`}>
                          {timeStatus.label}
                        </div>
                      </div>

                      {/* Core Information */}
                      <div className="flex-1 min-w-0 w-full">
                        <div className="flex items-center gap-2 mb-1">
                          <h3 className="text-slate-900 font-black text-lg truncate group-hover:text-indigo-600 transition-colors uppercase tracking-tight">{task.title}</h3>
                        </div>
                        <p className="text-slate-400 text-sm font-medium line-clamp-1 italic">{task.description || "System: No detailed technical documentation provided."}</p>
                      </div>

                      {/* Resource Mapping */}
                      <div className="flex items-center gap-10 w-full md:w-auto px-4">
                        <div className="flex items-center gap-3">
                          <div className="w-10 h-10 rounded-full border-2 border-white bg-slate-100 flex items-center justify-center text-[10px] font-black text-slate-600 shadow-sm overflow-hidden uppercase">
                            {task.assigned_to_user ? task.assigned_to_user.name.charAt(0) : <UserIcon size={16} />}
                          </div>
                          <div className="hidden lg:block">
                            <span className="block text-[8px] font-black text-slate-400 uppercase tracking-tighter">Resource</span>
                            <span className="text-xs font-bold text-slate-700">{task.assigned_to_user?.name || "Unassigned"}</span>
                          </div>
                        </div>
                        <div className="flex items-center gap-3">
                          <div className="w-10 h-10 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 border border-slate-100">
                            <Calendar size={18} />
                          </div>
                          <div>
                            <span className="block text-[8px] font-black text-slate-400 uppercase tracking-tighter">Timeline</span>
                            <span className="text-xs font-bold text-slate-700">{task.due_date ? new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' }) : "--/--"}</span>
                          </div>
                        </div>
                      </div>

                      {/* Action Control Panel */}
                      <div className="flex items-center gap-3 w-full md:w-auto justify-end">
                        <div className="hidden group-hover:flex items-center gap-2 pr-4 border-r border-slate-100 animate-in fade-in slide-in-from-right-2">
                          <button
                            onClick={() => navigate(`/tasks/edit/${task.id}`)}
                            className="p-3 text-slate-400 hover:text-indigo-600 hover:bg-indigo-50 rounded-2xl transition-all"
                            title="Modify Cluster"
                          >
                            <Edit3 size={20} />
                          </button>
                          <button
                            onClick={() => handleDelete(task.id)}
                            className="p-3 text-slate-400 hover:text-red-600 hover:bg-red-50 rounded-2xl transition-all"
                            title="Decommission Node"
                          >
                            <Trash2 size={20} />
                          </button>
                        </div>
                        <button
                          onClick={() => navigate(`/tasks/edit/${task.id}`)}
                          className="w-12 h-12 rounded-2xl bg-slate-900 border border-slate-800 text-white flex items-center justify-center shadow-xl shadow-slate-200 transition-all active:scale-90"
                        >
                          <ArrowRight size={22} />
                        </button>
                      </div>
                    </motion.div>
                  );
                })
              )}
            </AnimatePresence>
          )}
        </div>
      </div>
    </div>
  );
};

export default TasksPage;
