import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar,
  Clock,
  Plus,
  Trash2,
  ExternalLink,
  Users,
  Video,
  History,
  AlertCircle,
  MapPin,
  ChevronRight
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { meetingService, Meeting } from "../../services/meetingService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";

const MeetingsPage: React.FC = () => {
  const navigate = useNavigate();
  const [meetings, setMeetings] = useState<Meeting[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  const fetchMeetings = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await meetingService.getAllMeetings();
      setMeetings(data);
    } catch (error) {
      console.error("Failed to fetch meetings", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    fetchMeetings();

    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (message.type === 'meeting') {
        fetchMeetings(false);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const handleDelete = async (id: number) => {
    if (window.confirm("Delete this meeting?")) {
      try {
        await meetingService.deleteMeeting(id);
        setMeetings(prev => prev.filter(m => m.id !== id));
      } catch (error) {
        console.error("Failed to delete meeting", error);
      }
    }
  };

  const getStatus = (start: string, end: string) => {
    const now = currentTime;
    const startTime = new Date(start);
    const endTime = new Date(end);
    if (now >= startTime && now <= endTime) return 'present';
    if (now > endTime) return 'past';
    return 'upcoming';
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    }).toUpperCase();
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString([], { weekday: 'short', month: 'short', day: 'numeric' });
  };

  const isToday = (dateString: string) => {
    const d = new Date(dateString);
    const today = new Date();
    return d.getFullYear() === today.getFullYear() &&
      d.getMonth() === today.getMonth() &&
      d.getDate() === today.getDate();
  };

  const presentMeetings = meetings.filter(m => getStatus(m.start_time, m.end_time) === 'present');

  const upcomingMeetings = meetings.filter(m => {
    return getStatus(m.start_time, m.end_time) === 'upcoming' && isToday(m.start_time);
  }).sort((a, b) => new Date(a.start_time).getTime() - new Date(b.start_time).getTime());

  const pastMeetings = meetings.filter(m => {
    if (getStatus(m.start_time, m.end_time) !== 'past') return false;
    const endTime = new Date(m.end_time);
    const fortyEightHoursAgo = new Date(currentTime.getTime() - (48 * 60 * 60 * 1000));
    return endTime > fortyEightHoursAgo;
  }).sort((a, b) => new Date(b.start_time).getTime() - new Date(a.start_time).getTime());

  const MeetingCard = ({ meeting, type }: { meeting: Meeting, type: 'present' | 'upcoming' | 'past' }) => (
    <motion.div
      layout
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, scale: 0.95 }}
      whileHover={{ y: -4 }}
      className={`group relative overflow-hidden bg-white rounded-2xl border ${type === 'present' ? 'border-emerald-200 shadow-emerald-100 shadow-xl' : 'border-slate-200 shadow-sm'
        } hover:shadow-lg transition-all p-5 md:p-6`}
    >
      <div className="flex flex-col md:flex-row gap-6">
        {/* Left Side: Time info */}
        <div className="flex flex-row md:flex-col items-center md:items-start gap-4 md:w-32 shrink-0 md:border-r md:border-slate-100 pr-4">
          <div className={`p-3 rounded-2xl ${type === 'present' ? 'bg-emerald-50 text-emerald-600' :
              type === 'past' ? 'bg-slate-100 text-slate-500' : 'bg-indigo-50 text-indigo-600'
            }`}>
            {type === 'present' ? <Video size={24} /> : type === 'past' ? <History size={24} /> : <Calendar size={24} />}
          </div>
          <div>
            <p className="text-sm font-bold text-slate-900 group-hover:text-indigo-600 transition-colors uppercase tracking-tight">
              {formatTime(meeting.start_time).split(' ')[0]}
              <span className="text-[10px] ml-0.5 opacity-60 font-semibold">{formatTime(meeting.start_time).split(' ')[1]}</span>
            </p>
            <div className="flex items-center gap-1 text-[11px] text-slate-500 font-medium">
              <Clock size={10} />
              <span>Duration: 1h</span>
            </div>
          </div>
        </div>

        {/* Content Side */}
        <div className="flex-1 min-w-0">
          <div className="flex items-center gap-2 mb-2">
            {type === 'present' && (
              <span className="flex items-center gap-1.5 px-2.5 py-1 bg-emerald-100/50 text-emerald-700 text-[10px] font-bold rounded-full uppercase tracking-wider">
                <span className="w-1.5 h-1.5 bg-emerald-500 rounded-full animate-pulse" />
                Live Now
              </span>
            )}
            <span className="text-xs font-medium text-slate-400">{formatDate(meeting.start_time)}</span>
          </div>

          <h3 className="text-xl font-bold text-slate-900 mb-2 truncate">
            {meeting.title}
          </h3>

          <div className="flex flex-wrap items-center gap-x-5 gap-y-2">
            <div className="flex items-center gap-1.5 text-slate-500 text-sm">
              <Users size={16} className="text-slate-400" />
              <span className="font-medium">{meeting.participants?.length || 0} Participants</span>
            </div>
            {meeting.location && (
              <div className="flex items-center gap-1.5 text-slate-500 text-sm">
                <MapPin size={16} className="text-slate-400" />
                <span className="font-medium truncate max-w-[150px]">{meeting.location}</span>
              </div>
            )}
          </div>

          <div className="mt-5 flex items-center justify-between gap-4">
            <div className="flex -space-x-2 overflow-hidden">
              {[1, 2, 3].map((i) => (
                <div key={i} className="inline-block h-8 w-8 rounded-full ring-2 ring-white bg-slate-200 flex items-center justify-center text-[10px] font-bold text-slate-600">
                  {String.fromCharCode(64 + i)}
                </div>
              ))}
              {(meeting.participants?.length || 0) > 3 && (
                <div className="inline-block h-8 w-8 rounded-full ring-2 ring-white bg-slate-100 flex items-center justify-center text-[10px] font-bold text-slate-400">
                  +{(meeting.participants?.length || 0) - 3}
                </div>
              )}
            </div>

            <div className="flex items-center gap-2">
              {type !== 'past' && meeting.meeting_link && (
                <motion.button
                  whileTap={{ scale: 0.95 }}
                  onClick={() => window.open(meeting.meeting_link, '_blank')}
                  className={`px-5 py-2.5 rounded-xl text-sm font-bold flex items-center gap-2 transition-all shadow-sm ${type === 'present'
                      ? 'bg-emerald-600 text-white hover:bg-emerald-700 shadow-emerald-200'
                      : 'bg-slate-900 text-white hover:bg-indigo-600 shadow-slate-200'
                    }`}
                >
                  Join Session <ExternalLink size={16} />
                </motion.button>
              )}
              <button
                onClick={() => handleDelete(meeting.id)}
                className="p-2.5 text-slate-400 hover:text-rose-600 hover:bg-rose-50 rounded-xl transition-all"
                title="Delete Meeting"
              >
                <Trash2 size={20} />
              </button>
            </div>
          </div>
        </div>
      </div>
    </motion.div>
  );

  return (
    <div className="max-w-7xl mx-auto p-6 md:p-12 min-h-screen bg-[#fcfdfe]">
      {/* Header Section */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="flex flex-col lg:flex-row lg:items-center justify-between gap-8 mb-16"
      >
        <div className="space-y-2">
          <div className="inline-flex items-center gap-2 px-3 py-1 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-bold uppercase tracking-[0.1em]">
            <Calendar size={12} /> Work Schedule
          </div>
          <h1 className="text-5xl font-extrabold text-slate-900 tracking-tight">
            Your <span className="text-transparent bg-clip-text bg-gradient-to-r from-indigo-600 to-violet-600">Meetings</span>
          </h1>
          <p className="text-slate-500 text-lg max-w-xl">
            Stay on top of your schedule and collaborate seamlessly with your team in real-time.
          </p>
        </div>

        <div className="flex items-center gap-4">
          <button
            onClick={() => navigate("/calendar")}
            className="group px-6 py-3 border-2 border-slate-200 font-bold text-slate-700 rounded-2xl bg-white hover:border-indigo-600 hover:text-indigo-600 transition-all flex items-center gap-2"
          >
            Calendar View <ChevronRight size={18} className="group-hover:translate-x-1 transition-transform" />
          </button>
          <motion.button
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => navigate("/meetings/schedule")}
            className="px-8 py-3.5 bg-indigo-600 text-white rounded-2xl font-bold shadow-xl shadow-indigo-100 hover:bg-indigo-700 hover:shadow-indigo-200 transition-all flex items-center gap-2"
          >
            <Plus size={20} strokeWidth={3} /> New Meeting
          </motion.button>
        </div>
      </motion.div>

      {loading ? (
        <div className="py-32 flex flex-col items-center justify-center space-y-4">
          <div className="relative w-16 h-16">
            <div className="absolute inset-0 border-4 border-slate-100 rounded-full"></div>
            <div className="absolute inset-0 border-4 border-indigo-600 rounded-full border-t-transparent animate-spin"></div>
          </div>
          <p className="text-slate-400 font-bold uppercase tracking-widest text-xs">Syncing Schedule</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-12">

          <div className="lg:col-span-8 space-y-16">
            {/* Active/Upcoming Section */}
            {(presentMeetings.length > 0 || upcomingMeetings.length > 0) ? (
              <div className="space-y-12">
                {presentMeetings.length > 0 && (
                  <section className="space-y-6">
                    <h2 className="text-sm font-black text-emerald-600 uppercase tracking-[0.2em] flex items-center gap-4">
                      <span className="w-8 h-[2px] bg-emerald-100"></span>
                      Live & Ongoing
                    </h2>
                    <div className="space-y-6">
                      <AnimatePresence mode='popLayout'>
                        {presentMeetings.map(m => <MeetingCard key={m.id} meeting={m} type="present" />)}
                      </AnimatePresence>
                    </div>
                  </section>
                )}

                <section className="space-y-6">
                  <h2 className="text-sm font-black text-indigo-600 uppercase tracking-[0.2em] flex items-center gap-4">
                    <span className="w-8 h-[2px] bg-indigo-100"></span>
                    Upcoming Today
                  </h2>
                  {upcomingMeetings.length > 0 ? (
                    <div className="space-y-6">
                      <AnimatePresence mode='popLayout'>
                        {upcomingMeetings.map(m => <MeetingCard key={m.id} meeting={m} type="upcoming" />)}
                      </AnimatePresence>
                    </div>
                  ) : (
                    <motion.div
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      className="p-12 bg-white/50 border-2 border-dashed border-slate-200 rounded-3xl text-center"
                    >
                      <div className="w-16 h-16 bg-slate-50 rounded-2xl flex items-center justify-center mx-auto mb-4">
                        <AlertCircle size={32} className="text-slate-300" />
                      </div>
                      <h4 className="text-slate-900 font-bold">Clear Schedule</h4>
                      <p className="text-slate-500 text-sm mt-1">No more meetings for the rest of the day.</p>
                    </motion.div>
                  )}
                </section>
              </div>
            ) : (
              <div className="py-20 text-center space-y-6 bg-slate-50/50 rounded-[40px] border border-slate-100">
                <div className="w-24 h-24 bg-white rounded-full flex items-center justify-center mx-auto shadow-sm">
                  <Calendar size={48} className="text-slate-200" />
                </div>
                <div>
                  <h3 className="text-2xl font-black text-slate-800">Your Day is Free!</h3>
                  <p className="text-slate-500 font-medium">No meetings scheduled for today.</p>
                </div>
                <button onClick={() => navigate("/meetings/schedule")} className="text-indigo-600 font-bold hover:underline">Schedule your first sync</button>
              </div>
            )}
          </div>

          {/* Sidebar Section: Past Meetings */}
          <aside className="lg:col-span-4 space-y-8">
            <div className="sticky top-28">
              <section className="space-y-6">
                <h2 className="text-sm font-black text-slate-400 uppercase tracking-[0.2em] flex items-center gap-4">
                  <span className="w-8 h-[2px] bg-slate-100"></span>
                  Recent History
                </h2>
                {pastMeetings.length > 0 ? (
                  <div className="space-y-4">
                    {pastMeetings.map(m => (
                      <div key={m.id} className="p-4 bg-white border border-slate-100 rounded-2xl opacity-60 hover:opacity-100 transition-opacity flex items-center gap-4 group cursor-default">
                        <div className="p-2 bg-slate-50 rounded-xl text-slate-400 group-hover:bg-slate-900 group-hover:text-white transition-colors">
                          <History size={18} />
                        </div>
                        <div className="flex-1 min-w-0">
                          <h4 className="text-xs font-bold text-slate-800 truncate mb-1">{m.title}</h4>
                          <p className="text-[10px] text-slate-400 font-bold uppercase tracking-tighter">
                            {formatDate(m.start_time)}  {formatTime(m.start_time)}
                          </p>
                        </div>
                        <button
                          onClick={() => handleDelete(m.id)}
                          className="p-1.5 text-slate-300 hover:text-rose-500 transition-colors"
                        >
                          <Trash2 size={16} />
                        </button>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-slate-400 text-xs font-bold text-center italic py-8 border border-slate-50 rounded-2xl">No recent history</p>
                )}
              </section>

              {/* Stats Card */}
              <div className="mt-12 p-8 bg-gradient-to-br from-indigo-900 to-slate-900 rounded-[32px] text-white shadow-2xl overflow-hidden relative group">
                <div className="absolute top-0 right-0 p-8 opacity-10 group-hover:scale-110 transition-transform">
                  <Users size={120} />
                </div>
                <h3 className="text-lg font-bold mb-6">Weekly Outlook</h3>
                <div className="space-y-4 relative z-10">
                  <div className="flex justify-between items-center py-2 border-b border-white/10">
                    <span className="text-indigo-200 text-sm font-medium">Total syncs</span>
                    <span className="text-2xl font-black">{meetings.length}</span>
                  </div>
                  <div className="flex justify-between items-center py-2 border-b border-white/10">
                    <span className="text-indigo-200 text-sm font-medium">Active today</span>
                    <span className="text-2xl font-black">{presentMeetings.length + upcomingMeetings.length}</span>
                  </div>
                </div>
                <p className="mt-8 text-[11px] text-indigo-300 font-bold uppercase tracking-widest leading-relaxed">
                  Keep up the great work! Your team dependencies are looking healthy.
                </p>
              </div>
            </div>
          </aside>

        </div>
      )}
    </div>
  );
};

export default MeetingsPage;
