import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Bell,
  Calendar,
  CalendarDays,
  Clock,
  PlusCircle,
  ChevronRight,
} from "lucide-react";

interface Meeting {
  title: string;
  time: string;
  date: string;
}

const MeetingsPage: React.FC = () => {
  const navigate = useNavigate();

  const [meetings] = useState<Meeting[]>([
    { title: "Sprint Planning", time: "10:00 AM – 11:00 AM", date: "2025-10-29" },
    { title: "Client Call", time: "2:00 PM – 3:00 PM", date: "2025-10-29" },
    { title: "Design Review", time: "Tomorrow, 11:00 AM", date: "2025-10-30" },
    { title: "Demo Preparation", time: "Friday, 4:00 PM", date: "2025-11-01" },
  ]);

  const todayStr = new Date().toISOString().split("T")[0];
  const todayMeetings = meetings.filter((m) => m.date === todayStr);
  const upcomingMeetings = meetings.filter((m) => m.date !== todayStr);

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 p-8">
      {/* Header */}
      <header className="flex justify-between items-center mb-10">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Meetings Overview</h1>
          <p className="text-gray-500 text-sm mt-1">
            Stay on top of your team’s schedule and upcoming sessions
          </p>
        </div>

        <div className="flex items-center space-x-3">
          <button
            onClick={() => navigate("/calendar")}
            className="flex items-center gap-2 px-4 py-2 border border-gray-300 rounded-lg bg-white hover:bg-gray-50 transition text-gray-700 font-medium"
          >
            <Calendar className="w-4 h-4" /> Calendar
          </button>

          <button
            onClick={() => navigate("/meetings/schedule")}
            className="flex items-center gap-2 px-4 py-2 rounded-lg bg-blue-600 hover:bg-blue-700 transition text-white font-medium shadow-sm"
          >
            <PlusCircle className="w-4 h-4" /> Schedule
          </button>

          <button className="relative p-2 rounded-full hover:bg-gray-200 transition">
            <Bell className="w-5 h-5 text-gray-600" />
            <span className="absolute top-1 right-1 w-2 h-2 bg-red-500 rounded-full"></span>
          </button>
        </div>
      </header>

      {/* Today’s Meetings */}
      <section className="mb-12">
        <h2 className="text-xl font-semibold text-gray-800 flex items-center gap-2 mb-5">
          <CalendarDays className="w-5 h-5 text-blue-600" /> Today’s Meetings
        </h2>

        {todayMeetings.length > 0 ? (
          <div className="grid md:grid-cols-2 gap-5">
            {todayMeetings.map((meeting, index) => (
              <div
                key={index}
                className="bg-white border border-gray-200 rounded-2xl p-5 shadow-sm hover:shadow-md transition flex justify-between items-center"
              >
                <div>
                  <h3 className="text-gray-900 font-semibold">{meeting.title}</h3>
                  <p className="text-gray-500 flex items-center gap-2 mt-1">
                    <Clock className="w-4 h-4 text-gray-400" /> {meeting.time}
                  </p>
                </div>
                <button className="flex items-center gap-1 text-blue-600 hover:text-blue-700 font-medium text-sm">
                  View <ChevronRight className="w-4 h-4" />
                </button>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-500 italic">No meetings scheduled for today.</p>
        )}
      </section>

      {/* Upcoming Meetings */}
      <section>
        <h2 className="text-xl font-semibold text-gray-800 mb-5">Upcoming Meetings</h2>

        {upcomingMeetings.length > 0 ? (
          <div className="grid md:grid-cols-3 gap-5">
            {upcomingMeetings.map((meeting, index) => (
              <div
                key={index}
                className="bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-100 rounded-2xl p-5 hover:shadow-md transition flex flex-col justify-between"
              >
                <div>
                  <h3 className="font-semibold text-gray-900">{meeting.title}</h3>
                  <p className="text-gray-600 flex items-center gap-2 mt-2">
                    <Clock className="w-4 h-4 text-blue-500" /> {meeting.time}
                  </p>
                </div>
                <div className="flex justify-between items-center mt-4">
                  <span className="text-sm text-gray-500">{meeting.date}</span>
                  <button className="text-blue-600 hover:text-blue-700 text-sm font-medium flex items-center gap-1">
                    View <ChevronRight className="w-4 h-4" />
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-500 italic">No upcoming meetings found.</p>
        )}
      </section>
    </div>
  );
};

export default MeetingsPage;
