import React from "react";
import { Github, Linkedin, Twitter, Mail } from "lucide-react";

interface FooterProps {
  darkMode?: boolean;
}

const Footer: React.FC<FooterProps> = ({ darkMode }) => {
  const year = new Date().getFullYear();

  const socialLinks = [
    { icon: Github, url: "https://github.com/", label: "GitHub" },
    { icon: Linkedin, url: "https://linkedin.com/", label: "LinkedIn" },
    { icon: Twitter, url: "https://twitter.com/", label: "Twitter" },
    { icon: Mail, url: "mailto:info@collabryta.com", label: "Email" },
  ];

  const navLinks = [
    { name: "About", url: "/about" },
    { name: "Features", url: "/features" },
    { name: "Docs", url: "/docs" },
    { name: "Support", url: "/support" },
  ];

  return (
    <footer
      className={`w-full mt-0 border-t transition-colors duration-500 ${darkMode
          ? "bg-gray-950 border-gray-800 text-gray-300"
          : "bg-white border-gray-200 text-gray-700"
        }`}
    >
      <div className="max-w-7xl mx-auto px-6 py-10 flex flex-col md:flex-row justify-between items-center gap-10">
        {/* üß© Logo & Info */}
        <div className="flex flex-col md:flex-row items-center gap-4 text-center md:text-left">
          <img
            src="/logo1.png"
            alt="Collabryta Logo"
            className="w-14 h-14 object-contain drop-shadow-lg"
          />
          <div>
            <h2
              className={`text-2xl font-semibold tracking-wide ${darkMode ? "text-white" : "text-gray-800"
                }`}
            >
              Collabryta
            </h2>
            <p
              className={`text-sm ${darkMode ? "text-gray-400" : "text-gray-500"
                } max-w-xs`}
            >
              Empowering teams with seamless collaboration and AI-driven
              insights.
            </p>
          </div>
        </div>

        {/* üîó Navigation Links */}
        <nav className="flex flex-wrap justify-center gap-6 text-sm font-medium">
          {navLinks.map((link) => (
            <a
              key={link.name}
              href={link.url}
              className={`transition-all duration-300 ${darkMode
                  ? "text-gray-400 hover:text-blue-400"
                  : "text-gray-600 hover:text-blue-600"
                }`}
            >
              {link.name}
            </a>
          ))}
        </nav>

        {/* üåê Social Icons */}
        <div className="flex items-center gap-5">
          {socialLinks.map(({ icon: Icon, url, label }) => (
            <a
              key={label}
              href={url}
              target="_blank"
              rel="noopener noreferrer"
              aria-label={label}
              className={`p-2 rounded-full border transition-all duration-300 ${darkMode
                  ? "border-gray-700 text-gray-400 hover:text-blue-400 hover:border-blue-400 hover:bg-gray-800"
                  : "border-gray-200 text-gray-600 hover:text-blue-600 hover:border-blue-600 hover:bg-blue-50"
                }`}
            >
              <Icon size={18} />
            </a>
          ))}
        </div>
      </div>

      {/* üßæ Bottom Line */}
      <div
        className={`text-center text-xs py-4 border-t ${darkMode
            ? "border-gray-800 text-gray-500"
            : "border-gray-200 text-gray-500"
          }`}
      >
        ¬© {year} <span className="font-semibold">Collabryta</span>. All rights
        reserved.
      </div>
    </footer>
  );
};

export default Footer;
