import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { authService } from "../../services/authService";
import { Mail, Phone, MapPin, Briefcase, Camera, Save, Loader2, Lock, Star, Shield, Activity, Zap, ArrowRight, UserCheck, ArrowUpRight } from "lucide-react";

const ProfilePage: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [profile, setProfile] = useState<any>({
    name: "",
    email: "",
    role: "Operative",
    job_title: "",
    avatar: "https://i.pravatar.cc/150?u=a042581f4e29026704d",
    phone: "",
    address: "",
    bio: "",
    two_factor_enabled: false
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userData = await authService.getCurrentUser();
        setProfile({
          ...userData,
          avatar: userData.avatar || "https://i.pravatar.cc/150?u=a042581f4e29026704d"
        });
      } catch (error) {
        console.error("Failed to load profile", error);
      } finally {
        setLoading(false);
      }
    };
    fetchUser();
  }, []);

  const handleSave = async () => {
    try {
      setSaving(true);
      const { id, email, hashed_password, last_seen, status, ...updateData } = profile;
      await authService.updateProfile(updateData);
      setIsEditing(false);
    } catch (error) {
      console.error("Failed to update profile", error);
    } finally {
      setSaving(false);
    }
  };

  const fileInputRef = React.useRef<HTMLInputElement>(null);

  const handleImageClick = () => {
    if (isEditing) {
      fileInputRef.current?.click();
    }
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfile((prev: any) => ({ ...prev, avatar: reader.result as string }));
      };
      reader.readAsDataURL(file);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-white">
        <div className="w-10 h-10 border border-zinc-200 border-t-black rounded-full animate-spin mb-4" />
        <p className="text-[10px] font-black tracking-[0.2em] text-zinc-400 uppercase">SYNCHRONIZING PROFILE DATA</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white text-black p-10 lg:p-16 animate-fade-in font-sans relative">
      <div className="max-w-[1200px] mx-auto">

        {/* Header Area */}
        <div className="flex flex-col md:flex-row md:items-end justify-between gap-10 mb-20 border-b border-zinc-100 pb-12">
          <div>
            <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">PERSONNEL / DOSSIER</p>
            <h1 className="text-5xl font-bold tracking-tighter leading-none uppercase">PROFILE</h1>
          </div>

          <div className="flex items-center gap-6">
            {isEditing && (
              <button
                onClick={() => setIsEditing(false)}
                className="px-10 py-5 bg-white border border-black text-black text-[11px] font-black tracking-widest uppercase hover:bg-zinc-50 transition-all active:scale-95"
              >
                ABORT
              </button>
            )}
            <button
              onClick={() => isEditing ? handleSave() : setIsEditing(true)}
              disabled={saving}
              className={`flex items-center gap-4 px-10 py-5 transition-all active:scale-95 ${isEditing ? 'bg-black text-white hover:bg-zinc-800' : 'bg-black text-white hover:bg-zinc-800'}`}
            >
              <span className="text-[11px] font-black tracking-widest uppercase">
                {saving ? "SYNCING..." : isEditing ? "COMMIT CHANGES" : "EDIT DOSSIER"}
              </span>
              {saving ? <Loader2 size={16} className="animate-spin" /> : isEditing ? <Save size={16} /> : <Zap size={16} />}
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-16">

          {/* Left Column: Avatar & Summary */}
          <div className="space-y-12">
            <div className="bg-white border border-zinc-100 p-10 lg:p-12 relative group shadow-sm">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                accept="image/*"
                className="hidden"
              />
              <div
                className={`relative mb-12 flex justify-center ${isEditing ? 'cursor-pointer' : ''}`}
                onClick={handleImageClick}
              >
                <div className="w-48 h-48 bg-zinc-50 border border-zinc-100 transition-all group-hover:border-black overflow-hidden relative grayscale">
                  <img
                    src={profile.avatar}
                    alt={profile.name}
                    className="w-full h-full object-cover"
                  />
                  {isEditing && (
                    <div className="absolute inset-0 flex items-center justify-center bg-black/60 opacity-0 hover:opacity-100 transition-all">
                      <Camera className="text-white w-8 h-8" />
                    </div>
                  )}
                </div>
                <div className="absolute -bottom-2 -right-2 p-3 bg-black text-white border-2 border-white">
                  <Shield size={18} />
                </div>
              </div>

              <div className="text-center">
                <h2 className="text-3xl font-bold text-black uppercase tracking-tighter mb-2">{profile.name}</h2>
                <p className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] mb-10">{profile.job_title || profile.role}</p>

                <div className="space-y-px bg-zinc-100 border border-zinc-100">
                  <div className="bg-white p-6 flex flex-col items-center gap-2">
                    <p className="text-[9px] font-black text-zinc-300 uppercase tracking-widest leading-none">COMMUNICATION</p>
                    <p className="text-[11px] font-bold text-black tracking-widest uppercase truncate w-full">{profile.email}</p>
                  </div>
                  <div className="bg-white p-6 flex flex-col items-center gap-2">
                    <p className="text-[9px] font-black text-zinc-300 uppercase tracking-widest leading-none">ACCESS CLEARANCE</p>
                    <p className="text-[11px] font-bold text-black tracking-widest uppercase">{profile.role}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Right Column: Detailed Info */}
          <div className="lg:col-span-2 space-y-12">
            <div className="bg-white border border-zinc-100 overflow-hidden shadow-sm">
              <div className="px-10 py-8 border-b border-zinc-100">
                <h3 className="text-2xl font-bold text-black uppercase tracking-tighter leading-none mb-2">PERSONAL PARAMETERS</h3>
                <p className="text-[10px] font-bold text-zinc-400 uppercase tracking-widest">Update professional identification and contact nodes.</p>
              </div>
              <div className="p-10">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-px bg-zinc-100 border border-zinc-100">
                  <div className="bg-white p-6 space-y-3">
                    <label className="text-[9px] font-black text-zinc-400 uppercase tracking-widest px-1">APPELLATION</label>
                    <input
                      type="text"
                      disabled={!isEditing}
                      value={profile.name}
                      onChange={(e) => setProfile({ ...profile, name: e.target.value })}
                      className="w-full px-4 py-4 bg-white border border-zinc-200 focus:border-black outline-none text-black font-bold text-[11px] tracking-widest uppercase transition-all disabled:bg-zinc-50 disabled:text-zinc-200 disabled:border-transparent"
                    />
                  </div>
                  <div className="bg-white p-6 space-y-3">
                    <label className="text-[9px] font-black text-zinc-400 uppercase tracking-widest px-1">DESIGNATION</label>
                    <input
                      type="text"
                      disabled={!isEditing}
                      placeholder="E.G. CORE ARCHITECT"
                      value={profile.job_title || ''}
                      onChange={(e) => setProfile({ ...profile, job_title: e.target.value })}
                      className="w-full px-4 py-4 bg-white border border-zinc-200 focus:border-black outline-none text-black font-bold text-[11px] tracking-widest uppercase transition-all disabled:bg-zinc-50 disabled:text-zinc-200 disabled:border-transparent"
                    />
                  </div>
                  <div className="bg-white p-6 space-y-3">
                    <label className="text-[9px] font-black text-zinc-400 uppercase tracking-widest px-1">SIGNAL ENDPOINT (LOCKED)</label>
                    <div className="relative">
                      <input
                        type="email"
                        disabled={true}
                        value={profile.email}
                        className="w-full px-4 py-4 bg-zinc-50 border-none text-zinc-200 font-bold text-[11px] tracking-widest uppercase cursor-not-allowed"
                      />
                      <Lock size={12} className="absolute right-4 top-1/2 -translate-y-1/2 text-zinc-100" />
                    </div>
                  </div>
                  <div className="bg-white p-6 space-y-3">
                    <label className="text-[9px] font-black text-zinc-400 uppercase tracking-widest px-1">MOBILE CONTACT</label>
                    <div className="relative">
                      <input
                        type="text"
                        disabled={!isEditing}
                        placeholder="+0 (000) 000-0000"
                        value={profile.phone || ''}
                        onChange={(e) => setProfile({ ...profile, phone: e.target.value })}
                        className="w-full px-4 py-4 bg-white border border-zinc-200 focus:border-black outline-none text-black font-bold text-[11px] tracking-widest uppercase transition-all disabled:bg-zinc-50 disabled:text-zinc-200 disabled:border-transparent"
                      />
                    </div>
                  </div>
                  <div className="md:col-span-2 bg-white p-6 space-y-3">
                    <label className="text-[9px] font-black text-zinc-400 uppercase tracking-widest px-1">DOSSIER / EXPERTISE BREVIARY</label>
                    <textarea
                      rows={5}
                      disabled={!isEditing}
                      value={profile.bio || ''}
                      onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                      placeholder="ENTER PROFESSIONAL BACKGROUND AND CORE COMPETENCIES..."
                      className="w-full px-6 py-6 bg-white border border-zinc-200 focus:border-black outline-none text-black font-bold text-[11px] tracking-widest uppercase transition-all disabled:bg-zinc-50 disabled:text-zinc-200 disabled:border-transparent resize-none leading-relaxed"
                    />
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-black text-white p-12 flex flex-col md:flex-row items-center justify-between gap-10">
              <div className="flex items-center gap-8 text-center md:text-left">
                <div className="w-16 h-16 bg-zinc-900 flex items-center justify-center text-white border border-zinc-800">
                  <ShieldCheck size={28} className="text-zinc-600" />
                </div>
                <div>
                  <h4 className="text-xl font-bold uppercase tracking-tighter leading-none mb-3">ACCOUNT FORTIFICATION</h4>
                  <p className="text-[10px] font-black text-zinc-500 uppercase tracking-[0.2em]">TWO-FACTOR AUTHENTICATION â€¢ NEURAL ENCRYPTION ACTIVE</p>
                </div>
              </div>
              <button className="px-10 py-5 bg-white text-black text-[11px] font-black tracking-widest uppercase hover:bg-zinc-100 transition-all flex items-center gap-4 group shrink-0 active:scale-95">
                AUDIT SECURITY
                <ArrowUpRight size={16} />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const ShieldCheck = ({ size, className }: { size?: number, className?: string }) => (
  <svg className={className} width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10" />
    <path d="m9 12 2 2 4-4" />
  </svg>
);

export default ProfilePage;
