import React from "react";
import { Bell, Menu, X, Search, Settings, Command } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();

  return (
    <header className="sticky top-0 z-30 w-full bg-white border-b border-[#111111] flex flex-col sm:flex-row items-stretch transition-all duration-300">
      {/* Editorial Search Section */}
      <div className="flex-1 flex items-center px-6 sm:px-12 py-4 border-b sm:border-b-0 sm:border-r border-[#111111]">
        <motion.button
          whileTap={{ scale: 0.95 }}
          onClick={onMenuClick}
          className="lg:hidden mr-4 p-2 border border-[#111111] hover:bg-[#111111] hover:text-white transition-all"
        >
          {isSidebarOpen ? <X size={18} /> : <Menu size={18} />}
        </motion.button>

        <div className="flex items-center flex-1 group">
          <span className="swiss-label mr-4 text-zinc-400 group-focus-within:text-[#111111]">Search:</span>
          <input
            type="text"
            placeholder="Type command..."
            className="bg-transparent border-none outline-none text-xs w-full text-[#111111] font-mono font-bold uppercase tracking-widest placeholder:text-zinc-300"
          />
          <div className="hidden sm:flex items-center gap-1 px-2 py-0.5 border border-[#111111] text-[9px] font-mono font-bold">
            <Command size={10} /> <span>CTRL+K</span>
          </div>
        </div>
      </div>

      {/* Utilities Section */}
      <div className="flex items-center px-6 sm:px-12 py-4 gap-6">
        <div className="flex items-center gap-4">
          <button className="text-zinc-400 hover:text-[#ff3e00] transition-colors relative">
            <Bell size={20} />
            <span className="absolute -top-1 -right-1 w-2 h-2 bg-[#ff3e00] border border-white" />
          </button>
          <button className="text-zinc-400 hover:text-[#111111] transition-colors">
            <Settings size={20} />
          </button>
        </div>

        <div className="h-6 w-[1px] bg-[#111111]" />

        <button
          onClick={() => navigate("/profile")}
          className="flex items-center gap-4 group"
        >
          <div className="text-right hidden sm:block">
            <p className="text-[10px] font-black uppercase tracking-widest text-[#111111]">Ashok S.</p>
            <p className="text-[8px] font-mono font-bold text-[#ff3e00] uppercase tracking-tighter">Verified User</p>
          </div>
          <div className="w-10 h-10 border border-[#111111] flex items-center justify-center p-1 group-hover:bg-[#111111] transition-all">
            <div className="w-full h-full bg-[#ff3e00] flex items-center justify-center text-white font-black text-sm">
              A
            </div>
          </div>
        </button>
      </div>
    </header>
  );
};

export default Topbar;
